export const chis={treat:"Ветхий завет",title:"Числа",chapters:[{chapter:"1",verses:[{num:1,verse:"И сказал Господь Моисею в пустыне Синайской, в скинии собрания, в первый [день] второго месяца, во второй год по выходе их из земли Египетской, говоря:"},{num:2,verse:"исчислите все общество сынов Израилевых по родам их, по семействам их, по числу имен, всех мужеского пола поголовно:"},{num:3,verse:"от двадцати лет и выше, всех годных для войны у Израиля, по ополчениям их исчислите их – ты и Аарон;"},{num:4,verse:"с вами должны быть из каждого колена по одному человеку, который в роде своем есть главный."},{num:5,verse:"И вот имена мужей, которые будут с вами: от Рувима Елицур, сын Шедеура;"},{num:6,verse:"от Симеона Шелумиил, сын Цуришаддая;"},{num:7,verse:"от Иуды Наассон, сын Аминадава;"},{num:8,verse:"от Иссахара Нафанаил, сын Цуара;"},{num:9,verse:"от Завулона Елиав, сын Хелона;"},{num:10,verse:"от сынов Иосифа: от Ефрема Елишама, сын Аммиуда; от Манассии Гамалиил, сын Педацура;"},{num:11,verse:"от Вениамина Авидан, сын Гидеония;"},{num:12,verse:"от Дана Ахиезер, сын Аммишаддая;"},{num:13,verse:"от Асира Пагиил, сын Охрана;"},{num:14,verse:"от Гада Елиасаф, сын Регуила;"},{num:15,verse:"от Неффалима Ахира, сын Енана."},{num:16,verse:"Это – избранные мужи общества, начальники колен отцов своих, главы тысяч Израилевых."},{num:0,verse:""},{num:17,verse:"И взял Моисей и Аарон мужей сих, которые названы поименно,"},{num:18,verse:"и собрали они все общество в первый [день] второго месяца. И объявили они родословия свои, по родам их, по семействам их, по числу имен, от двадцати лет и выше, поголовно,"},{num:19,verse:"как повелел Господь Моисею. И сделал он счисление им в пустыне Синайской."},{num:20,verse:"И было сынов Рувима, первенца Израилева, по родам их, по племенам их, по семействам их, по числу имен, поголовно, всех мужеского пола, от двадцати лет и выше, всех годных для войны,"},{num:21,verse:"исчислено в колене Рувимовом сорок шесть тысяч пятьсот."},{num:22,verse:"Сынов Симеона по родам их, по племенам их, по семействам их, по числу имен, поголовно, всех мужеского пола, от двадцати лет и выше, всех годных для войны,"},{num:23,verse:"исчислено в колене Симеоновом пятьдесят девять тысяч триста."},{num:24,verse:"Сынов Гада по родам их, по племенам их, по семействам их, по числу имен [их, поголовно, всех мужеского пола], от двадцати лет и выше, всех годных для войны,"},{num:25,verse:"исчислено в колене Гадовом сорок пять тысяч шестьсот пятьдесят."},{num:26,verse:"Сынов Иуды по родам их, по племенам их, по семействам их, по числу имен [их, поголовно, всех мужеского пола], от двадцати лет и выше, всех годных для войны,"},{num:27,verse:"исчислено в колене Иудином семьдесят четыре тысячи шестьсот."},{num:28,verse:"Сынов Иссахара по родам их, по племенам их, по семействам их, по числу имен [их, поголовно, всех мужеского пола], от двадцати лет и выше, всех годных для войны,"},{num:29,verse:"исчислено в колене Иссахаровом пятьдесят четыре тысячи четыреста."},{num:30,verse:"Сынов Завулона по родам их, по племенам их, по семействам их, по числу имен [их, поголовно, всех мужеского пола], от двадцати лет и выше, всех годных для войны,"},{num:31,verse:"исчислено в колене Завулоновом пятьдесят семь тысяч четыреста."},{num:32,verse:"Сынов Иосифа, сынов Ефрема по родам их, по племенам их, по семействам их, по числу имен [их, поголовно, всех мужеского пола], от двадцати лет и выше, всех годных для войны,"},{num:33,verse:"исчислено в колене Ефремовом сорок тысяч пятьсот."},{num:34,verse:"Сынов Манассии по родам их, по племенам их, по семействам их, по числу имен [их, поголовно, всех мужеского пола], от двадцати лет и выше, всех годных для войны,"},{num:35,verse:"исчислено в колене Манассиином тридцать две тысячи двести."},{num:36,verse:"Сынов Вениамина по родам их, по племенам их, по семействам их, по числу имен [их, поголовно, всех мужеского пола], от двадцати лет и выше, всех годных для войны,"},{num:37,verse:"исчислено в колене Вениаминовом тридцать пять тысяч четыреста."},{num:38,verse:"Сынов Дана по родам их, по племенам их, по семействам их, по числу имен [их, поголовно, всех мужеского пола], от двадцати лет и выше, всех годных для войны,"},{num:39,verse:"исчислено в колене Дановом шестьдесят две тысячи семьсот."},{num:40,verse:"Сынов Асира по родам их, по племенам их, по семействам их, по числу имен [их, поголовно, всех мужеского пола], от двадцати лет и выше, всех годных для войны,"},{num:41,verse:"исчислено в колене Асировом сорок одна тысяча пятьсот."},{num:42,verse:"Сынов Неффалима по родам их, по племенам их, по семействам их, по числу имен [их, поголовно, всех мужеского пола], от двадцати лет и выше, всех годных для войны,"},{num:43,verse:"исчислено в колене Неффалимовом пятьдесят три тысячи четыреста."},{num:44,verse:"Вот вошедшие в исчисление, которых исчислил Моисей и Аарон и начальники Израиля – двенадцать человек, по одному человеку из каждого племени."},{num:45,verse:"И было всех, вошедших в исчисление, сынов Израилевых, по семействам их, от двадцати лет и выше, всех годных для войны у Израиля,"},{num:46,verse:"и было всех вошедших в исчисление шестьсот три тысячи пятьсот пятьдесят."},{num:47,verse:"А левиты по поколениям отцов их не были исчислены между ними."},{num:0,verse:""},{num:48,verse:"И сказал Господь Моисею, говоря:"},{num:49,verse:"только колена Левиина не вноси в перепись, и не исчисляй их вместе с сынами Израиля;"},{num:50,verse:"но поручи левитам скинию откровения и все принадлежности ее и всё, что при ней; пусть они носят скинию и все принадлежности ее, и служат при ней, и около скинии пусть ставят стан свой;"},{num:51,verse:"и когда надобно переносить скинию, пусть поднимают ее левиты, и когда надобно остановиться скинии, пусть ставят ее левиты; а если приступит кто посторонний, предан будет смерти."},{num:52,verse:"Сыны Израилевы должны становиться каждый в стане своем и каждый при своем знамени, по ополчениям своим;"},{num:53,verse:"а левиты должны ставить стан около скинии откровения, чтобы не было гнева на общество сынов Израилевых, и будут левиты стоять на страже у скинии откровения."},{num:54,verse:"И сделали сыны Израилевы; как повелел Господь Моисею, так они и сделали."}]},{chapter:"2",verses:[{num:1,verse:"И сказал Господь Моисею и Аарону, говоря:"},{num:2,verse:"сыны Израилевы должны каждый ставить стан свой при знамени своем, при знаках семейств своих; пред скиниею собрания вокруг должны ставить стан свой."},{num:3,verse:"С передней стороны к востоку ставят стан: знамя стана Иудина по ополчениям их, и начальник сынов Иуды Наассон, сын Аминадава,"},{num:4,verse:"и воинства его, вошедших в исчисление его, семьдесят четыре тысячи шестьсот;"},{num:5,verse:"после него ставит стан колено Иссахарово, и начальник сынов Иссахара Нафанаил, сын Цуара,"},{num:6,verse:"и воинства его, вошедших в исчисление его, пятьдесят четыре тысячи четыреста;"},{num:7,verse:"[далее ставит стан] колено Завулона, и начальник сынов Завулона Елиав, сын Хелона,"},{num:8,verse:"и воинства его, вошедших в исчисление его, пятьдесят семь тысяч четыреста;"},{num:9,verse:"всех вошедших в исчисление к стану Иуды сто восемьдесят шесть тысяч четыреста, по ополчениям их; первыми они должны отправляться."},{num:10,verse:"Знамя стана Рувимова к югу, по ополчениям их, и начальник сынов Рувимовых Елицур, сын Шедеура,"},{num:11,verse:"и воинства его, вошедших в исчисление его, сорок шесть тысяч пятьсот;"},{num:12,verse:"подле него ставит стан колено Симеоново, и начальник сынов Симеона Шелумиил, сын Цуришаддая,"},{num:13,verse:"и воинства его, вошедших в исчисление его, пятьдесят девять тысяч триста;"},{num:14,verse:"потом колено Гада, и начальник сынов Гада Елиасаф, сын Регуила,"},{num:15,verse:"и воинства его, вошедших в исчисление его, сорок пять тысяч шестьсот пятьдесят;"},{num:16,verse:"всех вошедших в исчисление к стану Рувима сто пятьдесят одна тысяча четыреста пятьдесят, по ополчениям их; вторыми они должны отправляться."},{num:17,verse:"Когда пойдет скиния собрания, стан левитов будет в середине станов. Как стоят, так и должны идти, каждый на своем месте при знаменах своих."},{num:18,verse:"Знамя стана Ефремова по ополчениям их к западу, и начальник сынов Ефрема Елишама, сын Аммиуда,"},{num:19,verse:"и воинства его, вошедших в исчисление его, сорок тысяч пятьсот;"},{num:20,verse:"подле него колено Манассиино, и начальник сынов Манассии Гамалиил, сын Педацура,"},{num:21,verse:"и воинства его, вошедших в исчисление его, тридцать две тысячи двести;"},{num:22,verse:"потом колено Вениамина, и начальник сынов Вениамина Авидан, сын Гидеония,"},{num:23,verse:"и воинства его, вошедших в исчисление его, тридцать пять тысяч четыреста;"},{num:24,verse:"всех вошедших в исчисление к стану Ефрема сто восемь тысяч сто, по ополчениям их; третьими они должны отправляться."},{num:25,verse:"Знамя стана Данова к северу, по ополчениям их, и начальник сынов Дана Ахиезер, сын Аммишаддая,"},{num:26,verse:"и воинства его, вошедших в исчисление его, шестьдесят две тысячи семьсот;"},{num:27,verse:"подле него ставит стан колено Асирово, и начальник сынов Асира Пагиил, сын Охрана,"},{num:28,verse:"и воинства его, вошедших в исчисление его, сорок одна тысяча пятьсот;"},{num:29,verse:"далее [ставит стан] колено Неффалима, и начальник сынов Неффалима Ахира, сын Енана,"},{num:30,verse:"и воинства его, вошедших в исчисление его, пятьдесят три тысячи четыреста;"},{num:31,verse:"всех вошедших в исчисление к стану Дана сто пятьдесят семь тысяч шестьсот; они должны идти последними при знаменах своих."},{num:32,verse:"Вот вошедшие в исчисление сыны Израиля по семействам их. Всех вошедших в исчисление в станах, по ополчениям их, шестьсот три тысячи пятьсот пятьдесят."},{num:33,verse:"А левиты не вошли в исчисление вместе с сынами Израиля, как повелел Господь Моисею."},{num:34,verse:"И сделали сыны Израилевы всё, что повелел Господь Моисею: так становились станами при знаменах своих и так шли каждый по племенам своим, по семействам своим."}]},{chapter:"3",verses:[{num:1,verse:"Вот родословие Аарона и Моисея, когда говорил Господь Моисею на горе Синае,"},{num:2,verse:"и вот имена сынов Аарона: первенец Надав, Авиуд, Елеазар и Ифамар;"},{num:3,verse:"это имена сынов Аарона, священников, помазанных, которых он посвятил, чтобы священнодействовать;"},{num:4,verse:"но Надав и Авиуд умерли пред лицем Господа, когда они принесли огонь чуждый пред лице Господа в пустыне Синайской, детей же у них не было; и остались священниками Елеазар и Ифамар при Аароне, отце своем."},{num:0,verse:""},{num:5,verse:"И сказал Господь Моисею, говоря:"},{num:6,verse:"приведи колено Левиино, и поставь его пред Аароном священником, чтоб они служили ему;"},{num:7,verse:"и пусть они будут на страже за него и на страже за все общество при скинии собрания, чтобы отправлять службы при скинии;"},{num:8,verse:"и пусть хранят все вещи скинии собрания, и будут на страже за сынов Израилевых, чтобы отправлять службы при скинии;"},{num:9,verse:"отдай левитов Аарону [брату твоему] и сынам его [священникам] <i>в</i> <i>распоряжение</i>: да будут они отданы ему из сынов Израилевых;"},{num:10,verse:"Аарону же и сынам его поручи [скинию откровения], чтобы они наблюдали священническую должность свою [и все, что при жертвеннике и за завесою]; а если приступит кто посторонний, предан будет смерти."},{num:0,verse:""},{num:11,verse:"И сказал Господь Моисею, говоря:"},{num:12,verse:"вот, Я взял левитов из сынов Израилевых вместо всех первенцев, разверзающих ложесна из сынов Израилевых [они будут взамен их]; левиты должны быть Мои,"},{num:13,verse:"ибо все первенцы – Мои; в тот день, когда поразил Я всех первенцев в земле Египетской, освятил Я Себе всех первенцев Израилевых от человека до скота; они должны быть Мои. Я Господь."},{num:0,verse:""},{num:14,verse:"И сказал Господь Моисею в пустыне Синайской, говоря:"},{num:15,verse:"исчисли сынов Левииных по семействам их, по родам их; всех мужеского пола от одного месяца и выше исчисли."},{num:16,verse:"И исчислил их Моисей [и Аарон] по слову Господню, как повелено."},{num:17,verse:"И вот сыны Левиины по именам их: Гирсон, Кааф и Мерари."},{num:18,verse:"И вот имена сынов Гирсоновых по родам их: Ливни и Шимей."},{num:19,verse:"И сыны Каафа по родам их: Амрам и Ицгар, Хеврон и Узиил."},{num:20,verse:"И сыны Мерари по родам их: Махли и Муши. Вот роды Левиины по семействам их."},{num:21,verse:"От Гирсона род Ливни и род Шимея: это роды Гирсоновы."},{num:22,verse:"Исчисленных было всех мужеского пола, от одного месяца и выше, семь тысяч пятьсот."},{num:23,verse:"Роды Гирсоновы должны становиться станом позади скинии на запад;"},{num:24,verse:"начальник поколения сынов Гирсоновых Елиасаф, сын Лаелов;"},{num:25,verse:"хранению сынов Гирсоновых в скинии собрания поручается скиния и покров ее, и завеса входа скинии собрания,"},{num:26,verse:"и завесы двора и завеса входа двора, который вокруг скинии и жертвенника, и веревки ее, со всеми их принадлежностями."},{num:27,verse:"От Каафа род Амрама и род Ицгара, и род Хеврона, и род Узиила: это роды Каафа."},{num:28,verse:"По счету всех мужеского пола, от одного месяца и выше, восемь тысяч шестьсот, которые охраняли святилище."},{num:29,verse:"Роды сынов Каафовых должны ставить стан свой на южной стороне скинии;"},{num:30,verse:"начальник же поколения родов Каафовых Елцафан, сын Узиила;"},{num:31,verse:"в хранении у них ковчег, стол, светильник, жертвенники, священные сосуды, которые употребляются при служении, и завеса со всеми принадлежностями ее."},{num:32,verse:"Начальник над начальниками левитов Елеазар, сын Аарона священника; под его надзором те, которым вверено хранение святилища."},{num:33,verse:"От Мерари род Махли и род Муши: это роды Мерари;"},{num:34,verse:"исчисленных по числу всех мужеского пола, от одного месяца и выше – шесть тысяч двести;"},{num:35,verse:"начальник поколения родов Мерари Цуриил, сын Авихаила; они должны ставить стан свой на северной стороне скинии;"},{num:36,verse:"хранению сынов Мерари поручаются брусья скинии и шесты ее, и столбы ее, и подножия ее и все вещи ее, со всем устройством их,"},{num:37,verse:"и столбы двора со всех сторон и подножия их и колья их и веревки их."},{num:38,verse:"А с передней стороны скинии, к востоку пред скиниею собрания, должны ставить стан Моисей и Аарон и сыны его, которым вверено хранение святилища за сынов Израилевых; а если приступит кто посторонний, предан будет смерти."},{num:39,verse:"Всех исчисленных левитов, которых исчислил Моисей и Аарон по повелению Господню, по родам их, всех мужеского пола, от одного месяца и выше, двадцать две тысячи."},{num:0,verse:""},{num:40,verse:"И сказал Господь Моисею: исчисли всех первенцев мужеского пола из сынов Израилевых, от одного месяца и выше, и пересчитай их поименно;"},{num:41,verse:"и возьми левитов для Меня, – Я Господь, – вместо всех первенцев из сынов Израиля, а скот левитов вместо всего первородного скота сынов Израилевых."},{num:42,verse:"И исчислил Моисей, как повелел ему Господь, всех первенцев из сынов Израилевых"},{num:43,verse:"и было всех первенцев мужеского пола, по числу имен, от одного месяца и выше, двадцать две тысячи двести семьдесят три."},{num:0,verse:""},{num:44,verse:"И сказал Господь Моисею, говоря:"},{num:45,verse:"возьми левитов вместо всех первенцев из сынов Израиля и скот левитов вместо скота их; пусть левиты будут Мои. Я Господь."},{num:46,verse:"А в выкуп двухсот семидесяти трех, которые лишние против <i>числа</i> левитов, из первенцев Израильских,"},{num:47,verse:"возьми по пяти сиклей за человека, по сиклю священному возьми, двадцать гер в сикле,"},{num:48,verse:"и отдай серебро сие Аарону и сынам его, в выкуп за излишних против <i>числа</i> их."},{num:49,verse:"И взял Моисей серебро выкупа за излишних против <i>числа</i> замененных левитами,"},{num:50,verse:"от первенцев Израилевых взял серебра тысячу триста шестьдесят пять [сиклей], по сиклю священному,"},{num:51,verse:"и отдал Моисей серебро выкупа [за излишних] Аарону и сынам его по слову Господню, как повелел Господь Моисею."}]},{chapter:"4",verses:[{num:1,verse:"И сказал Господь Моисею и Аарону, говоря:"},{num:2,verse:"исчисли сынов Каафовых из сынов Левия по родам их, по семействам их,"},{num:3,verse:"от тридцати лет и выше до пятидесяти лет, всех способных к службе, чтобы отправлять работы в скинии собрания."},{num:4,verse:"Вот служение сынов Каафовых [левитов по родам их, по семействам их,] в скинии собрания: <i>носить</i> Святое Святых."},{num:5,verse:"Когда стану надобно подняться в путь, Аарон и сыны его войдут, и снимут завесу закрывающую, и покроют ею ковчег откровения;"},{num:6,verse:"и положат на нее покров из кож синего цвета, и сверх его накинут покрывало всё из голубой <i>шерсти</i>, и вложат шесты его;"},{num:7,verse:"и стол <i>хлебов</i> предложения накроют одеждою из голубой <i>шерсти</i>, и поставят на нем блюда, тарелки, чаши и кружки для возлияния, и хлеб <i>его</i> всегдашний должен быть на нем;"},{num:8,verse:"и возложат на них одежду багряную, и покроют ее покровом из кожи синего цвета, и вложат шесты его;"},{num:9,verse:"и возьмут одежду из голубой <i>шерсти</i>, и покроют светильник и лампады его, и щипцы его, и лотки его, и все сосуды для елея, которые употребляют при нем,"},{num:10,verse:"и покроют его и все принадлежности его покровом из кож синих, и положат на носилки;"},{num:11,verse:"и на золотой жертвенник возложат одежду из голубой <i>шерсти</i>, и покроют его покровом из кож синих, и вложат шесты его."},{num:12,verse:"И возьмут все вещи служебные, которые употребляются для служения во святилище, и положат в одежду из голубой <i>шерсти</i>, и покроют их покровом из кож синих, и положат на носилки."},{num:13,verse:"И очистят жертвенник от пепла и накроют его одеждою пурпуровою;"},{num:14,verse:"и положат на него все сосуды его, которые употребляются для служения при нем – угольницы, вилки, лопатки и чаши, все сосуды жертвенника – и покроют его покровом из кож синих, и вложат шесты его. [И возьмут пурпуровую одежду, и покроют умывальник и подножия его, и положат на них кожаное синее покрывало, и поставят на носилки.]"},{num:15,verse:"Когда, при отправлении в путь стана, Аарон и сыны его покроют всё святилище и все вещи святилища, тогда сыны Каафа подойдут, чтобы нести; но не должны они касаться святилища, чтобы не умереть. Сии <i>части</i> скинии собрания должны носить сыны Каафовы."},{num:16,verse:"Елеазару, сыну Аарона священника, поручается елей для светильника и благовонное курение, и всегдашнее хлебное приношение и елей помазания, – поручается вся скиния и все, что в ней, святилище и принадлежности его."},{num:0,verse:""},{num:17,verse:"И сказал Господь Моисею и Аарону, говоря:"},{num:18,verse:"не погубите колена племен Каафовых из среды левитов,"},{num:19,verse:"но вот что сделайте им, чтобы они были живы и не умерли, когда приступают к Святому Святых: Аарон и сыны его пусть придут и поставят их каждого в служении его и у ноши его;"},{num:20,verse:"но сами они не должны подходить смотреть святыню, когда покрывают ее, чтобы не умереть."},{num:0,verse:""},{num:21,verse:"И сказал Господь Моисею, говоря:"},{num:22,verse:"исчисли и сынов Гирсона по семействам их, по родам их,"},{num:23,verse:"от тридцати лет и выше до пятидесяти лет, исчисли их всех способных к службе, чтобы отправлять работы при скинии собрания."},{num:24,verse:"Вот работы семейств Гирсоновых, при их служении и ношении тяжестей:"},{num:25,verse:"они должны носить покровы скинии и скинию собрания, и покров ее, и покров кожаный синий, который поверх его, и завесу входа скинии собрания,"},{num:26,verse:"и завесы двора, и завесу входа во двор, который вокруг скинии и жертвенника, и веревки их, и все вещи, принадлежащие к ним; и все, что делается при них, они должны работать;"},{num:27,verse:"по повелению Аарона и сынов его должны производиться все службы сынов Гирсоновых при всяком ношении тяжестей и всякой работе их, и поручите их хранению все, что они носят;"},{num:28,verse:"вот службы родов сынов Гирсоновых в скинии собрания, и вот что поручается их хранению под надзором Ифамара, сына Аарона, священника."},{num:29,verse:"Сынов Мерариных по родам их, по семействам их исчисли,"},{num:30,verse:"от тридцати лет и выше до пятидесяти лет, исчисли всех способных на службу, чтобы отправлять работы при скинии собрания."},{num:31,verse:"Вот что они должны носить, по службе их при скинии собрания: брусья скинии и шесты ее, и столбы ее и подножия ее,"},{num:32,verse:"и столбы двора со всех сторон и подножия их, и колья их, и веревки их, и все вещи при них и все принадлежности их; и поименно сосчитайте вещи, которые они обязаны носить;"},{num:33,verse:"Вот работы родов сынов Мерариных, по службе их при скинии собрания, под надзором Ифамара, сына Аарона, священника."},{num:34,verse:"И исчислили Моисей и Аарон и начальники общества сынов Каафовых по родам их и по семействам их,"},{num:35,verse:"от тридцати лет и выше до пятидесяти лет, всех способных к службе, для работ в скинии собрания;"},{num:36,verse:"и было исчислено, по родам их, две тысячи семьсот пятьдесят:"},{num:37,verse:"это – исчисленные из родов Каафовых, все служащие при скинии собрания, которых исчислил Моисей и Аарон по повелению Господню, <i>данному</i> чрез Моисея."},{num:38,verse:"И исчислены сыны Гирсона по родам их и по семействам их,"},{num:39,verse:"от тридцати лет и выше до пятидесяти лет, все способные к службе, для работ в скинии собрания;"},{num:40,verse:"и было исчислено по родам их, по семействам их, две тысячи шестьсот тридцать:"},{num:41,verse:"это – исчисленные из родов сынов Гирсона, все служащие при скинии собрания, которых исчислил Моисей и Аарон, по повелению Господню."},{num:42,verse:"И исчислены роды сынов Мерариных по родам их, по семействам их,"},{num:43,verse:"от тридцати лет и выше до пятидесяти лет, все способные к службе, для работ при скинии собрания;"},{num:44,verse:"и было исчислено по родам их, [по семействам их,] три тысячи двести:"},{num:45,verse:"это – исчисленные из родов сынов Мерариных, которых исчислил Моисей и Аарон по повелению Господню, <i>данному</i> чрез Моисея."},{num:46,verse:"И исчислены все левиты, которых исчислил Моисей и Аарон и начальники Израиля по родам их и по семействам их,"},{num:47,verse:"от тридцати лет и выше до пятидесяти лет, все способные <i>к</i> <i>службе</i> для работ и ношения в скинии собрания;"},{num:48,verse:"и было исчислено их восемь тысяч пятьсот восемьдесят;"},{num:49,verse:"по повелению Господню чрез Моисея определены они каждый к своей работе и ношению, и исчислены, как повелел Господь Моисею."}]},{chapter:"5",verses:[{num:1,verse:"И сказал Господь Моисею, говоря:"},{num:2,verse:"повели сынам Израилевым выслать из стана всех прокаженных, и всех имеющих истечение, и всех осквернившихся от мертвого,"},{num:3,verse:"и мужчин и женщин вышлите, за стан вышлите их, чтобы не оскверняли они станов своих, среди которых Я живу."},{num:4,verse:"И сделали так сыны Израилевы, и выслали их вон из стана; как говорил Господь Моисею, так и сделали сыны Израилевы."},{num:0,verse:""},{num:5,verse:"И сказал Господь Моисею, говоря:"},{num:6,verse:"скажи сынам Израилевым: если мужчина или женщина сделает какой-либо грех против человека, и чрез это сделает преступление против Господа, и виновна будет душа та,"},{num:7,verse:"то пусть исповедаются во грехе своем, который они сделали, и возвратят сполна то, в чем виновны, и прибавят к тому пятую часть и отдадут тому, против кого согрешили;"},{num:8,verse:"если же у него нет наследника, которому следовало бы возвратить за вину: то посвятить это Господу; пусть будет это священнику, сверх овна очищения, которым он очистит его;"},{num:9,verse:"и всякое возношение из всех святынь сынов Израилевых, которые они приносят к священнику, ему принадлежит,"},{num:10,verse:"и посвященное кем-либо ему принадлежит; все, что даст кто священнику, ему принадлежит."},{num:0,verse:""},{num:11,verse:"И сказал Господь Моисею, говоря:"},{num:12,verse:"объяви сынам Израилевым и скажи им: если изменит кому жена, и нарушит верность к нему,"},{num:13,verse:"и переспит кто с ней и излиет семя, и это будет скрыто от глаз мужа ее, и она осквернится тайно, и не будет на нее свидетеля, и не будет уличена,"},{num:14,verse:"и найдет на него дух ревности, и будет ревновать жену свою, когда она осквернена, или найдет на него дух ревности, и он будет ревновать жену свою, когда она не осквернена, –"},{num:15,verse:"пусть приведет муж жену свою к священнику и принесет за нее в жертву десятую часть ефы ячменной муки, но не возливает на нее елея и не кладет ливана, потому что это приношение ревнования, приношение воспоминания, напоминающее о беззаконии;"},{num:16,verse:"а священник пусть приведет и поставит ее пред лице Господне,"},{num:17,verse:"и возьмет священник святой воды в глиняный сосуд, и возьмет священник земли с полу скинии и положит в воду;"},{num:18,verse:"и поставит священник жену пред лице Господне, и обнажит голову жены, и даст ей в руки приношение воспоминания, – это приношение ревнования, в руке же у священника будет горькая вода, наводящая проклятие."},{num:19,verse:"И заклянет ее священник и скажет жене: если никто не переспал с тобою, и ты не осквернилась и не изменила мужу своему, то невредима будешь от сей горькой воды, наводящей проклятие;"},{num:20,verse:"но если ты изменила мужу твоему и осквернилась, и если кто переспал с тобою кроме мужа твоего, –"},{num:21,verse:"тогда священник пусть заклянет жену клятвою проклятия и скажет священник жене: да предаст тебя Господь проклятию и клятве в народе твоем, и да соделает Господь лоно твое опавшим и живот твой опухшим;"},{num:22,verse:"и да пройдет вода сия, наводящая проклятие, во внутренность твою, чтобы опух живот [твой] и опало лоно [твое]. И скажет жена: аминь, аминь."},{num:23,verse:"И напишет священник заклинания сии на свитке, и смоет их в горькую воду;"},{num:24,verse:"и даст жене выпить горькую воду, наводящую проклятие, и войдет в нее вода, наводящая проклятие, ко вреду ее."},{num:25,verse:"И возьмет священник из рук жены хлебное приношение ревнования, и вознесет сие приношение пред Господом, и отнесет его к жертвеннику;"},{num:26,verse:"и возьмет священник горстью из хлебного приношения часть в память, и сожжет на жертвеннике, и потом даст жене выпить воды;"},{num:27,verse:"и когда напоит ее водою, тогда, если она нечиста и сделала преступление против мужа своего, горькая вода, наводящая проклятие, войдет в нее, ко вреду ее, и опухнет чрево ее и опадет лоно ее, и будет эта жена проклятою в народе своем;"},{num:28,verse:"если же жена не осквернилась и была чиста, то останется невредимою и будет оплодотворяема семенем."},{num:29,verse:"Вот закон о ревновании, когда жена изменит мужу своему и осквернится,"},{num:30,verse:"или когда на мужа найдет дух ревности, и он будет ревновать жену свою, тогда пусть он поставит жену пред лицем Господа, и сделает с нею священник все по сему закону, –"},{num:31,verse:"и будет муж чист от греха, а жена понесет на себе грех свой."}]},{chapter:"6",verses:[{num:1,verse:"И сказал Господь Моисею, говоря:"},{num:2,verse:"объяви сынам Израилевым и скажи им: если мужчина или женщина решится дать обет назорейства, чтобы посвятить себя в назореи Господу,"},{num:3,verse:"то он должен воздержаться от вина и <i>крепкого</i> напитка, и не должен употреблять ни уксусу из вина, ни уксусу из напитка, и ничего приготовленного из винограда не должен пить, и не должен есть ни сырых, ни сушеных виноградных ягод;"},{num:4,verse:"во все дни назорейства своего не должен он есть [и пить] ничего, что делается из винограда, от зерен до кожи."},{num:5,verse:"Во все дни обета назорейства его бритва не должна касаться головы его; до исполнения дней, на которые он посвятил себя в назореи Господу, свят он: должен растить волосы на голове своей."},{num:6,verse:"Во все дни, на которые он посвятил себя в назореи Господу, не должен он подходить к мертвому телу:"},{num:7,verse:"<i>прикосновением</i> к отцу своему, и матери своей, и брату своему, и сестре своей, не должен он оскверняться, когда они умрут, потому что посвящение Богу его на главе его;"},{num:8,verse:"во все дни назорейства своего свят он Господу."},{num:9,verse:"Если же умрет при нем кто-нибудь вдруг, нечаянно, и он осквернит тем голову назорейства своего: то он должен остричь голову свою в день очищения его, в седьмой день должен остричь ее,"},{num:10,verse:"и в восьмой день должен принести двух горлиц, или двух молодых голубей, к священнику, ко входу скинии собрания;"},{num:11,verse:"священник одну <i>из</i> <i>птиц</i> принесет в жертву за грех, а другую во всесожжение, и очистит его от осквернения мертвым телом, и освятит голову его в тот день;"},{num:12,verse:"и должен он снова начать посвященные Господу дни назорейства своего и принести однолетнего агнца в жертву повинности; прежние же дни пропали, потому что назорейство его осквернено."},{num:13,verse:"И вот закон о назорее, когда исполнятся дни назорейства его: должно привести его ко входу скинии собрания,"},{num:14,verse:"и он принесет в жертву Господу одного однолетнего агнца без порока во всесожжение, и одну однолетнюю агницу без порока в жертву за грех, и одного овна без порока в жертву мирную,"},{num:15,verse:"и корзину опресноков из пшеничной муки, хлебов, испеченных с елеем, и пресных лепешек, помазанных елеем, и при них хлебное приношение и возлияние;"},{num:16,verse:"и представит <i>сие</i> священник пред Господа, и принесет жертву его за грех и всесожжение его;"},{num:17,verse:"овна принесет в жертву мирную Господу с корзиною опресноков, также совершит священник хлебное приношение его и возлияние его;"},{num:18,verse:"и острижет назорей у входа скинии собрания голову назорейства своего, и возьмет волосы головы назорейства своего, и положит на огонь, который под мирною жертвою;"},{num:19,verse:"и возьмет священник сваренное плечо овна и один пресный пирог из корзины и одну пресную лепешку, и положит на руки назорею, после того, как острижет он голову назорейства своего;"},{num:20,verse:"и вознесет сие священник, потрясая пред Господом: эта святыня – для священника, сверх груди потрясания и сверх плеча возношения. После сего назорей может пить вино."},{num:21,verse:"Вот закон о назорее, который дал обет, и жертва его Господу за назорейство свое, кроме того, что позволит ему достаток его; по обету своему, какой он даст, так и должен он делать, сверх узаконенного о назорействе его."},{num:0,verse:""},{num:22,verse:"И сказал Господь Моисею, говоря:"},{num:23,verse:"скажи Аарону и сынам его: так благословляйте сынов Израилевых, говоря им:"},{num:24,verse:"да благословит тебя Господь и сохранит тебя!"},{num:25,verse:"да призрит на тебя Господь светлым лицем Своим и помилует тебя!"},{num:26,verse:"да обратит Господь лице Свое на тебя и даст тебе мир!"},{num:27,verse:"Так пусть призывают имя Мое на сынов Израилевых, и Я [Господь] благословлю их."}]},{chapter:"7",verses:[{num:1,verse:"Когда Моисей поставил скинию, и помазал ее, и освятил ее и все принадлежности ее, и жертвенник и все принадлежности его, и помазал их и освятил их,"},{num:2,verse:"тогда пришли [двенадцать] начальников Израилевых, главы семейств их, начальники колен, заведывавшие исчислением,"},{num:3,verse:"и представили приношение свое пред Господа, шесть крытых повозок и двенадцать волов, по одной повозке от двух начальников и по одному волу от каждого, и представили сие пред скинию."},{num:0,verse:""},{num:4,verse:"И сказал Господь Моисею, говоря:"},{num:5,verse:"возьми от них; это будет для отправления работ при скинии собрания; и отдай это левитам, смотря по роду службы их."},{num:6,verse:"И взял Моисей повозки и волов, и отдал их левитам:"},{num:7,verse:"две повозки и четырех волов отдал сынам Гирсоновым, по роду служб их:"},{num:8,verse:"и четыре повозки и восемь волов отдал сынам Мерариным, по роду служб их, под надзором Ифамара, сына Аарона, священника;"},{num:9,verse:"а сынам Каафовым не дал, потому что служба их – <i>носить</i> святилище; на плечах они должны носить."},{num:10,
verse:"И принесли начальники жертвы освящения жертвенника в день помазания его, и представили начальники приношение свое пред жертвенник."},{num:0,verse:""},{num:11,verse:"И сказал Господь Моисею: по одному начальнику в день пусть приносят приношение свое для освящения жертвенника."},{num:12,verse:"В первый день принес приношение свое Наассон, сын Аминадавов, от колена Иудина;"},{num:13,verse:"приношение его было: одно серебряное блюдо, весом в сто тридцать <i>сиклей</i>, одна серебряная чаша в семьдесят сиклей, по сиклю священному, наполненные пшеничною мукою, смешанною с елеем, в приношение хлебное,"},{num:14,verse:"одна золотая кадильница в десять <i>сиклей</i>, наполненная курением,"},{num:15,verse:"один телец, один овен, один однолетний агнец, во всесожжение,"},{num:16,verse:"один козел в жертву за грех,"},{num:17,verse:"и в жертву мирную два вола, пять овнов, пять козлов, пять однолетних агнцев; вот приношение Наассона, сына Аминадавова."},{num:18,verse:"Во второй день принес Нафанаил, сын Цуара, начальник [колена] Иссахарова;"},{num:19,verse:"он принес от себя приношение: одно серебряное блюдо, весом в сто тридцать <i>сиклей</i>, одну серебряную чашу в семьдесят сиклей, по сиклю священному, наполненные пшеничною мукою, смешанною с елеем, в приношение хлебное,"},{num:20,verse:"одну золотую кадильницу в десять <i>сиклей</i>, наполненную курением,"},{num:21,verse:"одного тельца, одного овна, одного однолетнего агнца, во всесожжение,"},{num:22,verse:"одного козла в жертву за грех,"},{num:23,verse:"и в жертву мирную двух волов, пять овнов, пять козлов, пять однолетних агнцев; вот приношение Нафанаила, сына Цуарова."},{num:24,verse:"В третий день начальник сынов Завулоновых Елиав, сын Хелона;"},{num:25,verse:"приношение его: одно серебряное блюдо, весом в сто тридцать <i>сиклей</i>, одна серебряная чаша в семьдесят сиклей, по сиклю священному, наполненные пшеничною мукою, смешанною с елеем, в приношение хлебное,"},{num:26,verse:"одна золотая кадильница в десять <i>сиклей</i>, наполненная курением,"},{num:27,verse:"один телец, один овен, один однолетний агнец, во всесожжение,"},{num:28,verse:"один козел в жертву за грех,"},{num:29,verse:"и в жертву мирную два вола, пять овнов, пять козлов, пять однолетних агнцев; вот приношение Елиава, сына Хелонова."},{num:30,verse:"В четвертый день начальник сынов Рувимовых Елицур, сын Шедеуров;"},{num:31,verse:"приношение его: одно серебряное блюдо, весом в сто тридцать <i>сиклей</i>, одна серебряная чаша в семьдесят сиклей, по сиклю священному, наполненные пшеничною мукою, смешанною с елеем, в приношение хлебное,"},{num:32,verse:"одна золотая кадильница в десять <i>сиклей</i>, наполненная курением,"},{num:33,verse:"один телец, один овен, один однолетний агнец, во всесожжение,"},{num:34,verse:"один козел в жертву за грех,"},{num:35,verse:"и в жертву мирную два вола, пять овнов, пять козлов и пять однолетних агнцев; вот приношение Елицура, сына Шедеурова."},{num:36,verse:"В пятый день начальник сынов Симеоновых Шелумиил, сын Цуришаддая;"},{num:37,verse:"приношение его: одно серебряное блюдо, весом в сто тридцать <i>сиклей</i>, одна серебряная чаша в семьдесят сиклей, по сиклю священному, наполненные пшеничною мукою, смешанною с елеем, в приношение хлебное,"},{num:38,verse:"одна золотая кадильница в десять <i>сиклей</i>, наполненная курением,"},{num:39,verse:"один телец, один овен, один однолетний агнец, во всесожжение,"},{num:40,verse:"один козел в жертву за грех,"},{num:41,verse:"и в жертву мирную два вола, пять овнов, пять козлов и пять однолетних агнцев; вот приношение Шелумиила, сына Цуришаддаева."},{num:42,verse:"В шестой день начальник сынов Гадовых Елиасаф, сын Регуила;"},{num:43,verse:"приношение его: одно серебряное блюдо, весом в сто тридцать <i>сиклей</i>, одна серебряная чаша в семьдесят сиклей, по сиклю священному, наполненные пшеничною мукою, смешанною с елеем, в приношение хлебное,"},{num:44,verse:"одна золотая кадильница в десять <i>сиклей</i>, наполненная курением,"},{num:45,verse:"один телец, один овен, один однолетний агнец, во всесожжение,"},{num:46,verse:"один козел в жертву за грех,"},{num:47,verse:"и в жертву мирную два вола, пять овнов, пять козлов и пять однолетних агнцев; вот приношение Елиасафа, сына Регуилова."},{num:48,verse:"В седьмой день начальник сынов Ефремовых Елишама, сын Аммиуда."},{num:49,verse:"Приношение его: одно серебряное блюдо, весом в сто тридцать <i>сиклей</i>, одна серебряная чаша в семьдесят сиклей, по сиклю священному, наполненные пшеничною мукою, смешанною с елеем, в приношение хлебное,"},{num:50,verse:"одна золотая кадильница в десять <i>сиклей</i>, наполненная курением,"},{num:51,verse:"один телец, один овен, один однолетний агнец, во всесожжение,"},{num:52,verse:"один козел в жертву за грех,"},{num:53,verse:"и в жертву мирную два вола, пять овнов, пять козлов, пять однолетних агнцев; вот приношение Елишамы, сына Аммиудова."},{num:54,verse:"В восьмой день начальник сынов Манассииных Гамалиил, сын Педацура."},{num:55,verse:"Приношение его: одно серебряное блюдо, весом в сто тридцать <i>сиклей</i>, одна серебряная чаша в семьдесят сиклей, по сиклю священному, наполненные пшеничною мукою, смешанною с елеем, в приношение хлебное,"},{num:56,verse:"одна золотая кадильница в десять <i>сиклей</i>, наполненная курением,"},{num:57,verse:"один телец, один овен, один однолетний агнец, во всесожжение,"},{num:58,verse:"один козел в жертву за грех,"},{num:59,verse:"и в жертву мирную два вола, пять овнов, пять козлов, пять однолетних агнцев; вот приношение Гамалиила, сына Педацурова."},{num:60,verse:"В девятый день начальник сынов Вениаминовых Авидан, сын Гидеония;"},{num:61,verse:"приношение его: одно серебряное блюдо, весом в сто тридцать <i>сиклей</i>, одна серебряная чаша в семьдесят сиклей, по сиклю священному, наполненные пшеничною мукою, смешанною с елеем, в приношение хлебное,"},{num:62,verse:"одна золотая кадильница в десять <i>сиклей</i>, наполненная курением,"},{num:63,verse:"один телец, один овен, один однолетний агнец, во всесожжение,"},{num:64,verse:"один козел в жертву за грех,"},{num:65,verse:"и в жертву мирную два вола, пять овнов, пять козлов, пять однолетних агнцев; вот приношение Авидана, сына Гидеониева."},{num:66,verse:"В десятый день начальник сынов Дановых Ахиезер, сын Аммишаддая;"},{num:67,verse:"приношение его: одно серебряное блюдо, весом в сто тридцать <i>сиклей</i>, одна серебряная чаша в семьдесят сиклей, по сиклю священному, наполненные пшеничною мукою, смешанною с елеем, в приношение хлебное,"},{num:68,verse:"одна золотая кадильница в десять <i>сиклей</i>, наполненная курением,"},{num:69,verse:"один телец, один овен, один однолетний агнец, во всесожжение,"},{num:70,verse:"один козел в жертву за грех,"},{num:71,verse:"и в жертву мирную два вола, пять овнов, пять козлов, пять однолетних агнцев; вот приношение Ахиезера, сына Аммишаддаева."},{num:72,verse:"В одиннадцатый день начальник сынов Асировых Пагиил, сын Охрана;"},{num:73,verse:"приношение его: одно серебряное блюдо, весом в сто тридцать <i>сиклей</i>, одна серебряная чаша в семьдесят сиклей, по сиклю священному, наполненные пшеничною мукою, смешанною с елеем, в приношение хлебное,"},{num:74,verse:"одна золотая кадильница в десять <i>сиклей</i>, наполненная курением,"},{num:75,verse:"один телец, один овен, один однолетний агнец, во всесожжение,"},{num:76,verse:"один козел в жертву за грех,"},{num:77,verse:"и в жертву мирную два вола, пять овнов, пять козлов, пять однолетних агнцев; вот приношение Пагиила, сына Охранова."},{num:78,verse:"В двенадцатый день начальник сынов Неффалимовых Ахира, сын Енана;"},{num:79,verse:"приношение его: одно серебряное блюдо, весом в сто тридцать <i>сиклей</i>, одна серебряная чаша в семьдесят сиклей, по сиклю священному, наполненные пшеничною мукою, смешанною с елеем, в приношение хлебное,"},{num:80,verse:"одна золотая кадильница в десять <i>сиклей</i>, наполненная курением,"},{num:81,verse:"один телец, один овен, один однолетний агнец, во всесожжение,"},{num:82,verse:"один козел в жертву за грех,"},{num:83,verse:"и в жертву мирную два вола, пять овнов, пять козлов, пять однолетних агнцев; вот приношение Ахиры, сына Енанова."},{num:0,verse:""},{num:84,verse:"Вот <i>приношения</i> от начальников Израилевых при освящении жертвенника в день помазания его: двенадцать серебряных блюд, двенадцать серебряных чаш, двенадцать золотых кадильниц;"},{num:85,verse:"по сто тридцати <i>сиклей</i> серебра в каждом блюде и по семидесяти в каждой чаше: итак всего серебра в сих сосудах две тысячи четыреста <i>сиклей</i>, по сиклю священному;"},{num:86,verse:"золотых кадильниц, наполненных курением, двенадцать, в каждой кадильнице по десяти <i>сиклей</i>, по сиклю священному: всего золота в кадильницах сто двадцать <i>сиклей</i>;"},{num:87,verse:"во всесожжение всего двенадцать тельцов из скота крупного, двенадцать овнов, двенадцать однолетних агнцев и при них хлебное приношение, и в жертву за грех двенадцать козлов,"},{num:88,verse:"и в жертву мирную всего из крупного скота двадцать четыре тельца, шестьдесят овнов, шестьдесят [однолетних] козлов, шестьдесят однолетних агнцев [без порока]; вот приношения при освящении жертвенника после помазания его."},{num:89,verse:"Когда Моисей входил в скинию собрания, чтобы говорить с Господом, слышал голос, говорящий ему с крышки, которая над ковчегом откровения между двух херувимов, и он говорил ему."}]},{chapter:"8",verses:[{num:1,verse:"И сказал Господь Моисею, говоря:"},{num:2,verse:"объяви Аарону и скажи ему: когда ты будешь зажигать лампады, то на передней стороне светильника должны гореть семь лампад."},{num:3,verse:"Аарон так и сделал: на передней стороне светильника зажег лампады его, как повелел Господь Моисею."},{num:4,verse:"И вот устройство светильника: чеканный он из золота, от стебля его и до цветов чеканный; по образу, который показал Господь Моисею, он сделал светильник."},{num:0,verse:""},{num:5,verse:"И сказал Господь Моисею, говоря:"},{num:6,verse:"возьми левитов из среды сынов Израилевых и очисти их;"},{num:7,verse:"а чтобы очистить их, поступи с ними так: окропи их очистительною водою, и пусть они обреют бритвою все тело свое и вымоют одежды свои, и будут чисты;"},{num:8,verse:"и пусть возьмут тельца и хлебное приношение к нему, пшеничной муки, смешанной с елеем, и другого тельца возьми в жертву за грех;"},{num:9,verse:"и приведи левитов пред скинию собрания; и собери все общество сынов Израилевых"},{num:10,verse:"и приведи левитов их пред Господа, и пусть возложат сыны Израилевы руки свои на левитов;"},{num:11,verse:"Аарон же пусть совершит над левитами посвящение их пред Господом от сынов Израилевых, чтобы отправляли они служение Господу;"},{num:12,verse:"а левиты пусть возложат руки свои на голову тельцов, и принеси одного в жертву за грех, а другого во всесожжение Господу, для очищения левитов;"},{num:13,verse:"и поставь левитов пред Аароном и пред сынами его, и соверши над ними посвящение их Господу;"},{num:14,verse:"и так отдели левитов от сынов Израилевых, чтобы левиты были Моими."},{num:15,verse:"После сего войдут левиты служить скинии собрания, когда ты очистишь их и совершишь над ними посвящение их; ибо они отданы Мне из сынов Израилевых:"},{num:16,verse:"вместо всех первенцев из сынов Израилевых, разверзающих всякие ложесна, Я беру их Себе;"},{num:17,verse:"ибо Мои все первенцы у сынов Израилевых, от человека до скота: в тот день, когда Я поразил всех первенцев в земле Египетской, Я освятил их Себе"},{num:18,verse:"и взял левитов вместо всех первенцев у сынов Израилевых;"},{num:19,verse:"и отдал левитов Аарону и сынам его из среды сынов Израилевых, чтобы они отправляли службы за сынов Израилевых при скинии собрания и служили охранением для сынов Израилевых, чтобы не постигло сынов Израилевых поражение, когда бы сыны Израилевы приступили к святилищу."},{num:20,verse:"И сделали так Моисей и Аарон и все общество сынов Израилевых с левитами: как повелел Господь Моисею о левитах, так и сделали с ними сыны Израилевы."},{num:21,verse:"И очистились левиты и омыли одежды свои, и совершил над ними Аарон посвящение их пред Господом, и очистил их Аарон, чтобы сделать их чистыми;"},{num:22,verse:"после сего вошли левиты отправлять службы свои в скинии собрания пред Аароном и пред сынами его. Как повелел Господь Моисею о левитах, так и сделали они с ними."},{num:0,verse:""},{num:23,verse:"И сказал Господь Моисею, говоря:"},{num:24,verse:"вот <i>закон</i> о левитах: от двадцати пяти лет и выше должны вступать они в службу для работ при скинии собрания,"},{num:25,verse:"а в пятьдесят лет должны прекращать отправление работ и более не работать:"},{num:26,verse:"тогда пусть помогают они братьям своим содержать стражу при скинии собрания, работать же – пусть не работают; так поступай с левитами касательно служения их."}]},{chapter:"9",verses:[{num:1,verse:"И сказал Господь Моисею в пустыне Синайской во второй год по исшествии их из земли Египетской, в первый месяц, говоря:"},{num:2,verse:"пусть сыны Израилевы совершат Пасху в назначенное для нее время:"},{num:3,verse:"в четырнадцатый день сего месяца вечером совершите ее в назначенное для нее время, по всем постановлениям и по всем обрядам ее совершите ее."},{num:4,verse:"И сказал Моисей сынам Израилевым, чтобы совершили Пасху."},{num:5,verse:"И совершили они Пасху в первый <i>месяц</i>, в четырнадцатый день месяца вечером, в пустыне Синайской: во всем, как повелел Господь Моисею, так и поступили сыны Израилевы."},{num:0,verse:""},{num:6,verse:"Были люди, которые были нечисты от <i>прикосновения</i> к мертвым телам человеческим, и не могли совершить Пасхи в тот день; и пришли они к Моисею и Аарону в тот день,"},{num:7,verse:"и сказали ему те люди: мы нечисты от <i>прикосновения</i> к мертвым телам человеческим; для чего нас лишать того, чтобы мы принесли приношение Господу в назначенное время среди сынов Израилевых?"},{num:8,verse:"И сказал им Моисей: постойте, я послушаю, что повелит о вас Господь."},{num:0,verse:""},{num:9,verse:"И сказал Господь Моисею, говоря:"},{num:10,verse:"скажи сынам Израилевым: если кто из вас или из потомков ваших будет нечист от <i>прикосновения</i> к мертвому телу, или будет в дальней дороге, то и он должен совершить Пасху Господню;"},{num:11,verse:"в четырнадцатый день второго месяца вечером пусть таковые совершат ее и с опресноками и горькими травами пусть едят ее;"},{num:12,verse:"и пусть не оставляют от нее до утра и костей ее не сокрушают; пусть совершат ее по всем уставам о Пасхе;"},{num:13,verse:"а кто чист и не находится в [дальней] дороге и не совершит Пасхи, – истребится душа та из народа своего, ибо он не принес приношения Господу в свое время: понесет на себе грех человек тот;"},{num:14,verse:"если будет жить у вас пришелец, то и он должен совершать Пасху Господню: по уставу о Пасхе и по обряду ее он должен совершить ее; один устав пусть будет у вас и для пришельца и для туземца."},{num:0,verse:""},{num:15,verse:"В тот день, когда поставлена была скиния, облако покрыло скинию откровения, и с вечера над скиниею как бы огонь виден был до самого утра."},{num:16,verse:"Так было и всегда: облако покрывало ее [днем] и подобие огня ночью."},{num:17,verse:"И когда облако поднималось от скинии, тогда сыны Израилевы отправлялись в путь, и на месте, где останавливалось облако, там останавливались станом сыны Израилевы."},{num:18,verse:"По повелению Господню отправлялись сыны Израилевы в путь, и по повелению Господню останавливались: во все то время, когда облако стояло над скиниею, и они стояли;"},{num:19,verse:"и если облако долгое время было над скиниею, то и сыны Израилевы следовали этому указанию Господа и не отправлялись;"},{num:20,verse:"иногда же облако немного времени было над скиниею: они по указанию Господню останавливались, и по указанию Господню отправлялись в путь;"},{num:21,verse:"иногда облако стояло <i>только</i> от вечера до утра, и поутру поднималось облако, тогда и они отправлялись; или день и ночь стояло облако, и когда поднималось, и они тогда отправлялись;"},{num:22,verse:"или, если два дня, или месяц, или несколько дней стояло облако над скиниею, то и сыны Израилевы стояли и не отправлялись в путь; а когда оно поднималось, тогда отправлялись;"},{num:23,verse:"по указанию Господню останавливались, и по указанию Господню отправлялись в путь: следовали указанию Господню по повелению Господню, <i>данному</i> чрез Моисея."}]},{chapter:"10",verses:[{num:1,verse:"И сказал Господь Моисею, говоря:"},{num:2,verse:"сделай себе две серебряные трубы, чеканные сделай их, чтобы они служили тебе для созывания общества и для снятия станов;"},{num:3,verse:"когда затрубят ими, соберется к тебе все общество ко входу скинии собрания;"},{num:4,verse:"когда одною трубою затрубят, соберутся к тебе князья и тысяченачальники Израилевы;"},{num:5,verse:"когда затрубите тревогу, поднимутся станы, становящиеся к востоку;"},{num:6,verse:"когда во второй раз затрубите тревогу, поднимутся станы, становящиеся к югу; [когда затрубите в третий раз тревогу, поднимутся станы, становящиеся к морю; когда в четвертый раз затрубите тревогу, поднимутся станы, становящиеся к северу;] тревогу пусть трубят при отправлении их в путь;"},{num:7,verse:"а когда надобно собрать собрание, трубите, но не тревогу;"},{num:8,verse:"сыны Аароновы, священники, должны трубить трубами: это будет вам постановлением вечным в роды ваши;"},{num:9,verse:"и когда пойдете на войну в земле вашей против врага, наступающего на вас, трубите тревогу трубами, – и будете воспомянуты пред Господом, Богом вашим, и спасены будете от врагов ваших;"},{num:10,verse:"и в день веселия вашего, и в праздники ваши, и в новомесячия ваши трубите трубами при всесожжениях ваших и при мирных жертвах ваших, – и это будет напоминанием о вас пред Богом вашим. Я Господь, Бог ваш."},{num:0,verse:""},{num:11,verse:"Во второй год, во второй месяц, в двадцатый <i>день</i> месяца поднялось облако от скинии откровения;"},{num:12,verse:"и отправились сыны Израилевы по станам своим из пустыни Синайской, и остановилось облако в пустыне Фаран."},{num:13,verse:"И поднялись они в первый раз, по повелению Господню, <i>данному</i> чрез Моисея."},{num:14,verse:"Поднято было во-первых знамя стана сынов Иудиных по ополчениям их; над ополчением их Наассон, сын Аминадава;"},{num:15,verse:"и над ополчением колена сынов Иссахаровых Нафанаил, сын Цуара;"},{num:16,verse:"и над ополчением колена сынов Завулоновых Елиав, сын Хелона."},{num:17,verse:"И снята была скиния, и пошли сыны Гирсоновы и сыны Мерарины, носящие скинию."},{num:18,verse:"И поднято было знамя стана Рувимова по ополчениям их; и над ополчением его Елицур, сын Шедеура;"},{num:19,verse:"и над ополчением колена сынов Симеоновых Шелумиил, сын Цуришаддая;"},{num:20,verse:"и над ополчением колена сынов Гадовых Елиасаф, сын Регуила."},{num:21,verse:"Потом пошли сыны Каафовы, носящие святилище; скиния же была поставляема до прихода их."},{num:22,verse:"И поднято было знамя стана сынов Ефремовых по ополчениям их; и над ополчением их Елишама, сын Аммиуда;"},{num:23,verse:"и над ополчением колена сынов Манассииных Гамалиил, сын Педацура;"},{num:24,verse:"и над ополчением колена сынов Вениаминовых Авидан, сын Гидеония."},{num:25,verse:"Последним из всех станов поднято было знамя стана сынов Дановых с ополчениями их; и над ополчением их Ахиезер, сын Аммишаддая;"},{num:26,verse:"и над ополчением колена сынов Асировых Пагиил, сын Охрана;"},{num:27,verse:"и над ополчением колена сынов Неффалимовых Ахира, сын Енана."},{num:28,verse:"Вот <i>порядок</i> шествия сынов Израилевых по ополчениям их. И отправились они."},{num:0,verse:""},{num:29,verse:"И сказал Моисей Ховаву, сыну Рагуилову, Мадианитянину, родственнику Моисееву: мы отправляемся в то место, о котором Господь сказал: вам отдам его; иди с нами, мы сделаем тебе добро, ибо Господь доброе изрек об Израиле."},{num:30,verse:"Но он сказал ему: не пойду; я пойду в свою землю и на свою родину."},{num:31,verse:"<i>Моисей</i> же сказал: не оставляй нас, потому что ты знаешь, как располагаемся мы станом в пустыне, и будешь для нас глазом;"},{num:32,verse:"если пойдешь с нами, то добро, которое Господь сделает нам, мы сделаем тебе."},{num:33,verse:"И отправились они от горы Господней на три дня пути, и ковчег завета Господня шел пред ними три дня пути, чтоб усмотреть им место, где остановиться."},{num:34,verse:"И облако Господне осеняло их днем, когда они отправлялись из стана."},{num:35,verse:"Когда поднимался ковчег в путь, Моисей говорил: восстань, Господи, и рассыплются враги Твои, и побегут от лица Твоего ненавидящие Тебя!"},{num:36,verse:"А когда останавливался ковчег, он говорил: возвратись, Господи, к тысячам и тьмам Израилевым!"}]},{chapter:"11",verses:[{num:1,verse:"Народ стал роптать вслух Господа; и Господь услышал, и воспламенился гнев Его, и возгорелся у них огонь Господень, и начал истреблять край стана."},{num:2,verse:"И возопил народ к Моисею; и помолился Моисей Господу, и утих огонь."},{num:3,verse:"И нарекли имя месту сему: Тавера, потому что возгорелся у них огонь Господень."},{num:4,verse:"Пришельцы между ними стали обнаруживать прихоти; а с ними и сыны Израилевы сидели и плакали и говорили: кто накормит нас мясом?"},{num:5,verse:"Мы помним рыбу, которую в Египте мы ели даром, огурцы и дыни, и лук, и репчатый лук и чеснок;"},{num:6,verse:"а ныне душа наша изнывает; ничего нет, только манна в глазах наших."},{num:7,verse:"Манна же была подобна кориандровому семени, видом, как бдолах;"},{num:8,verse:"народ ходил и собирал ее, и молол в жерновах или толок в ступе, и варил в котле, и делал из нее лепешки; вкус же ее подобен был вкусу лепешек с елеем."},{num:9,verse:"И когда роса сходила на стан ночью, тогда сходила на него и манна."},{num:10,verse:"Моисей слышал, что народ плачет в семействах своих, каждый у дверей шатра своего; и сильно воспламенился гнев Господень, и прискорбно было для Моисея."},{num:11,verse:"И сказал Моисей Господу: для чего Ты мучишь раба Твоего? и почему я не нашел милости пред очами Твоими, что Ты возложил на меня бремя всего народа сего?"},{num:12,verse:"разве я носил во чреве весь народ сей, и разве я родил его, что Ты говоришь мне: неси его на руках твоих, как нянька носит ребенка, в землю, которую Ты с клятвою обещал отцам его?"},{num:13,verse:"откуда мне <i>взять</i> мяса, чтобы дать всему народу сему? ибо они плачут предо мною и говорят: дай нам есть мяса."},{num:14,verse:"Я один не могу нести всего народа сего, потому что он тяжел для меня;"},{num:15,verse:"когда Ты так поступаешь со мною, то <i>лучше</i> умертви меня, если я нашел милость пред очами Твоими, чтобы мне не видеть бедствия моего."},{num:0,verse:""},{num:16,verse:"И сказал Господь Моисею: собери Мне семьдесят мужей из старейшин Израилевых, которых ты знаешь, что они старейшины и надзиратели его, и возьми их к скинии собрания, чтобы они стали там с тобою;"},{num:17,verse:"Я сойду, и буду говорить там с тобою, и возьму от Духа, Который на тебе, и возложу на них, чтобы они несли с тобою бремя народа, а не один ты носил."},{num:18,verse:"Народу же скажи: очиститесь к завтрашнему дню, и будете есть мясо; так как вы плакали вслух Господа и говорили: кто накормит нас мясом? хорошо нам было в Египте, – то и даст вам Господь мясо, и будете есть [мясо]:"},{num:19,verse:"не один день будете есть, не два дня, не пять дней, не десять дней и не двадцать дней,"},{num:20,verse:"но целый месяц [будете есть], пока не пойдет оно из ноздрей ваших и не сделается для вас отвратительным, за то, что вы презрели Господа, Который среди вас, и плакали пред Ним, говоря: для чего было нам выходить из Египта?"},{num:21,verse:"И сказал Моисей: шестьсот тысяч пеших в народе сем, среди которого я <i>нахожусь</i>; а Ты говоришь: Я дам им мясо, и будут есть целый месяц!"},{num:22,verse:"заколоть ли всех овец и волов, чтобы им было довольно? или вся рыба морская соберется, чтобы удовлетворить их?"},{num:23,verse:"И сказал Господь Моисею: разве рука Господня коротка? ныне ты увидишь, сбудется ли слово Мое тебе, или нет?"},{num:0,verse:""},{num:24,verse:"Моисей вышел и сказал народу слова Господни, и собрал семьдесят мужей из старейшин народа и поставил их около скинии."},{num:25,verse:"И сошел Господь в облаке, и говорил с ним, и взял от Духа, Который на нем, и дал семидесяти мужам старейшинам. И когда почил на них Дух, они стали пророчествовать, но потом перестали."},{num:26,verse:"Двое из мужей оставались в стане, одному имя Елдад, а другому имя Модад; но и на них почил Дух [они были из числа записанных, только не выходили к скинии], и они пророчествовали в стане."},{num:27,verse:"И прибежал отрок и донес Моисею, и сказал: Елдад и Модад пророчествуют в стане."},{num:28,verse:"В ответ на это Иисус, сын Навин, служитель Моисея, один из избранных его, сказал: господин мой Моисей! запрети им."},{num:29,verse:"Но Моисей сказал ему: не ревнуешь ли ты за меня? о, если бы все в народе Господнем были пророками, когда бы Господь послал Духа Своего на них!"},{num:30,verse:"И возвратился Моисей в стан, он и старейшины Израилевы."},{num:31,verse:"И поднялся ветер от Господа, и принес от моря перепелов, и набросал их около стана, на путь дня по одну сторону и на путь дня по другую сторону около стана, на два почти локтя от земли."},{num:32,verse:"И встал народ, и весь тот день, и всю ночь, и весь следующий день собирали перепелов; и кто мало собирал, тот собрал десять хомеров; и разложили их для себя вокруг стана."},{num:33,verse:"Мясо еще было в зубах их и не было еще съедено, как гнев Господень возгорелся на народ, и поразил Господь народ весьма великою язвою."},{num:34,verse:"И нарекли имя месту сему: Киброт-Гаттаава, ибо там похоронили прихотливый народ."},{num:35,verse:"От Киброт-Гаттаавы двинулся народ в Асироф, и остановился в Асирофе."}]},{chapter:"12",verses:[{num:1,verse:"И упрекали Мариам и Аарон Моисея за жену Ефиоплянку, которую он взял, – ибо он взял <i>за</i> <i>себя</i> Ефиоплянку, –"},{num:2,verse:"и сказали: одному ли Моисею говорил Господь? не говорил ли Он и нам? И услышал <i>сие</i> Господь."},{num:3,verse:"Моисей же был человек кротчайший из всех людей на земле."},{num:0,verse:""},{num:4,verse:"И сказал Господь внезапно Моисею и Аарону и Мариами: выйдите вы трое к скинии собрания. И вышли все трое."},{num:5,verse:"И сошел Господь в облачном столпе, и стал у входа скинии, и позвал Аарона и Мариам, и вышли они оба."},{num:6,verse:"И сказал: слушайте слова Мои: если бывает у вас пророк Господень, то Я открываюсь ему в видении, во сне говорю с ним;"},{num:7,verse:"но не так с рабом Моим Моисеем, – он верен во всем дому Моем:"},{num:8,verse:"устами к устам говорю Я с ним, и явно, а не в гаданиях, и образ Господа он видит; как же вы не убоялись упрекать раба Моего, Моисея?"},{num:9,verse:"И воспламенился гнев Господа на них, и Он отошел."},{num:10,verse:"И облако отошло от скинии, и вот, Мариам покрылась проказою, как снегом. Аарон взглянул на Мариам, и вот, она в проказе."},{num:11,verse:"И сказал Аарон Моисею: господин мой! не поставь нам в грех, что мы поступили глупо и согрешили;"},{num:12,verse:"не попусти, чтоб она была, как мертворожденный <i>младенец</i>, у которого, когда он выходит из чрева матери своей, истлела уже половина тела."},{num:13,verse:"И возопил Моисей к Господу, говоря: Боже, исцели ее!"},{num:14,verse:"И сказал Господь Моисею: если бы отец ее плюнул ей в лице, то не должна ли была бы она стыдиться семь дней? итак пусть будет она в заключении семь дней вне стана, а после опять возвратится."},{num:15,verse:"И пробыла Мариам в заключении вне стана семь дней, и народ не отправлялся в путь, доколе не возвратилась Мариам."}]},{chapter:"13",verses:[{num:1,verse:"После сего народ двинулся из Асирофа, и остановился в пустыне Фаран."},{num:2,verse:"И сказал Господь Моисею, говоря:"},{num:3,verse:"пошли от себя людей, чтобы они высмотрели землю Ханаанскую, которую Я даю сынам Израилевым; по одному человеку от колена отцов их пошлите, главных из них."},{num:4,verse:"И послал их Моисей из пустыни Фаран, по повелению Господню, и все они мужи главные у сынов Израилевых."},{num:5,verse:"Вот имена их: из колена Рувимова Саммуа, сын Закхуров,"},{num:6,verse:"из колена Симеонова Сафат, сын Хориев,"},{num:7,verse:"из колена Иудина Халев, сын Иефонниин,"},{num:8,verse:"из колена Иссахарова Игал, сын Иосифов,"},{num:9,verse:"из колена Ефремова Осия, сын Навин,"},{num:10,verse:"из колена Вениаминова Фалтий, сын Рафуев,"},{num:11,verse:"из колена Завулонова Гаддиил, сын Содиев,"},{num:12,verse:"из колена Иосифова от Манассии Гаддий, сын Сусиев,"},{num:13,verse:"из колена Данова Аммиил, сын Гемаллиев,"},{num:14,verse:"из колена Асирова Сефур, сын Михаилев,"},{num:15,verse:"из колена Неффалимова Нахбий, сын Вофсиев,"},{num:16,verse:"из колена Гадова Геуил, сын Махиев."},{num:17,verse:"Вот имена мужей, которых посылал Моисей высмотреть землю. И назвал Моисей Осию, сына Навина, Иисусом."},{num:0,verse:""},{num:18,verse:"И послал их Моисей [из пустыни Фаран] высмотреть землю Ханаанскую и сказал им: пойдите в эту южную страну, и взойдите на гору,"},{num:19,verse:"и осмотрите землю, какова она, и народ живущий на ней, силен ли он или слаб, малочислен ли он или многочислен?"},{num:20,verse:"и какова земля, на которой он живет, хороша ли она или худа? и каковы города, в которых он живет, в шатрах ли он живет или в укреплениях?"},{num:21,verse:"и какова земля, тучна ли она или тоща? есть ли на <i>ней</i> дерева или нет? будьте смелы, и возьмите от плодов земли. Было же это ко времени созревания винограда."},{num:22,verse:"Они пошли и высмотрели землю от пустыни Син даже до Рехова, близ Емафа;"},{num:23,verse:"и пошли в южную страну, и дошли до Хеврона, где жили Ахиман, Сесай и Фалмай, дети Енаковы: Хеврон же построен был семью годами прежде Цоана, [города] Египетского;"},{num:24,verse:"и пришли к долине Есхол, [и осмотрели ее,] и срезали там виноградную ветвь с одною кистью ягод, и понесли ее на шесте двое; <i>взяли</i> также гранатовых яблок и смокв;"},{num:25,verse:"место сие назвали долиною Есхол, по причине виноградной кисти, которую срезали там сыны Израилевы."},{num:26,verse:"И высмотрев землю, возвратились они через сорок дней."},{num:27,verse:"И пошли и пришли к Моисею и Аарону и ко всему обществу сынов Израилевых в пустыню Фаран, в Кадес, и принесли им и всему обществу ответ, и показали им плоды земли;"},{num:28,verse:"и рассказывали ему и говорили: мы ходили в землю, в которую ты посылал нас; в ней подлинно течет молоко и мед, и вот плоды ее;"},{num:29,verse:"но народ, живущий на земле той, силен, и города укрепленные, весьма большие, и сынов Енаковых мы видели там;"},{num:30,verse:"Амалик живет на южной части земли, Хеттеи, [Евеи,] Иевусеи и Аморреи живут на горе, Хананеи же живут при море и на берегу Иордана."},{num:31,verse:"Но Халев успокаивал народ пред Моисеем, говоря: пойдем и завладеем ею, потому что мы можем одолеть ее."},{num:32,verse:"А те, которые ходили с ним, говорили: не можем мы идти против народа сего, ибо он сильнее нас."},{num:33,verse:"И распускали худую молву о земле, которую они осматривали, между сынами Израилевыми, говоря: земля, которую проходили мы для осмотра, есть земля, поедающая живущих на ней, и весь народ, который видели мы среди ее, люди великорослые;"},{num:34,verse:"там видели мы и исполинов, сынов Енаковых, от исполинского рода; и мы были в глазах наших <i>пред</i> <i>ними</i>, как саранча, такими же были мы и в глазах их."}]},{chapter:"14",verses:[{num:1,verse:"И подняло все общество вопль, и плакал народ во [всю] ту ночь;"},{num:2,verse:"и роптали на Моисея и Аарона все сыны Израилевы, и все общество сказало им: о, если бы мы умерли в земле Египетской, или умерли бы в пустыне сей!"},{num:3,verse:"и для чего Господь ведет нас в землю сию, чтобы мы пали от меча? жены наши и дети наши достанутся в добычу <i>врагам</i>; не лучше ли нам возвратиться в Египет?"},{num:4,verse:"И сказали друг другу: поставим себе начальника и возвратимся в Египет."},{num:5,verse:"И пали Моисей и Аарон на лица свои пред всем собранием общества сынов Израилевых."},{num:6,
verse:"И Иисус, сын Навин, и Халев, сын Иефонниин, из осматривавших землю, разодрали одежды свои"},{num:7,verse:"и сказали всему обществу сынов Израилевых: земля, которую мы проходили для осмотра, очень, очень хороша;"},{num:8,verse:"если Господь милостив к нам, то введет нас в землю сию и даст нам ее – эту землю, в которой течет молоко и мед;"},{num:9,verse:"только против Господа не восставайте и не бойтесь народа земли сей; ибо он достанется нам на съедение: защиты у них не стало, а с нами Господь; не бойтесь их."},{num:10,verse:"И сказало все общество: побить их камнями! Но слава Господня явилась [в облаке] в скинии собрания всем сынам Израилевым."},{num:0,verse:""},{num:11,verse:"И сказал Господь Моисею: доколе будет раздражать Меня народ сей? и доколе будет он не верить Мне при всех знамениях, которые делал Я среди его?"},{num:12,verse:"поражу его язвою и истреблю его и произведу от тебя [и от дома отца твоего] народ многочисленнее и сильнее его."},{num:13,verse:"Но Моисей сказал Господу: услышат Египтяне, из среды которых Ты силою Твоею вывел народ сей,"},{num:14,verse:"и скажут жителям земли сей, которые слышали, что Ты, Господь, находишься среди народа сего, и что Ты, Господь, даешь им видеть Себя лицем к лицу, и облако Твое стоит над ними, и Ты идешь пред ними днем в столпе облачном, а ночью в столпе огненном;"},{num:15,verse:"и если Ты истребишь народ сей, как одного человека, то народы, которые слышали славу Твою, скажут:"},{num:16,verse:"Господь не мог ввести народ сей в землю, которую Он с клятвою обещал ему, а потому и погубил его в пустыне."},{num:17,verse:"Итак да возвеличится сила Господня, как Ты сказал, говоря:"},{num:18,verse:"Господь долготерпелив и многомилостив [и истинен], прощающий беззакония и преступления [и грехи], и не оставляющий без наказания, но наказывающий беззаконие отцов в детях до третьего и четвертого рода."},{num:19,verse:"Прости грех народу сему по великой милости Твоей, как Ты прощал народ сей от Египта доселе."},{num:0,verse:""},{num:20,verse:"И сказал Господь [Моисею]: прощаю по слову твоему;"},{num:21,verse:"но жив Я, [и всегда живет имя Мое,] и славы Господней полна вся земля:"},{num:22,verse:"все, которые видели славу Мою и знамения Мои, сделанные Мною в Египте и в пустыне, и искушали Меня уже десять раз, и не слушали гласа Моего,"},{num:23,verse:"не увидят земли, которую Я с клятвою обещал отцам их; [только детям их, которые здесь со Мною, которые не знают, что добро, что зло, всем малолетним, ничего не смыслящим, им дам землю, а] все, раздражавшие Меня, не увидят ее;"},{num:24,verse:"но раба Моего, Халева, за то, что в нем был иной дух, и он совершенно повиновался Мне, введу в землю, в которую он ходил, и семя его наследует ее;"},{num:25,verse:"Амаликитяне и Хананеи живут в долине; завтра обратитесь и идите в пустыню к Чермному морю."},{num:0,verse:""},{num:26,verse:"И сказал Господь Моисею и Аарону, говоря:"},{num:27,verse:"доколе злому обществу сему роптать на Меня? ропот сынов Израилевых, которым они ропщут на Меня, Я слышу."},{num:28,verse:"Скажи им: живу Я, говорит Господь: как говорили вы вслух Мне, так и сделаю вам;"},{num:29,verse:"в пустыне сей падут тела ваши, и все вы исчисленные, сколько вас числом, от двадцати лет и выше, которые роптали на Меня,"},{num:30,verse:"не войдете в землю, на которой Я, подъемля руку Мою, <i>клялся</i> поселить вас, кроме Халева, сына Иефонниина, и Иисуса, сына Навина;"},{num:31,verse:"детей ваших, о которых вы говорили, что они достанутся в добычу <i>врагам</i>, Я введу <i>туда</i>, и они узнают землю, которую вы презрели,"},{num:32,verse:"а ваши трупы падут в пустыне сей;"},{num:33,verse:"а сыны ваши будут кочевать в пустыне сорок лет, и будут нести <i>наказание</i> за блудодейство ваше, доколе не погибнут все тела ваши в пустыне;"},{num:34,verse:"по числу сорока дней, в которые вы осматривали землю, вы понесете наказание за грехи ваши сорок лет, год за день, дабы вы познали, <i>что</i> <i>значит</i> быть оставленным Мною."},{num:35,verse:"Я, Господь, говорю, и так и сделаю со всем сим злым обществом, восставшим против Меня: в пустыне сей все они погибнут и перемрут."},{num:36,verse:"И те, которых посылал Моисей для осмотрения земли, и которые, возвратившись, возмутили против него все сие общество, распуская худую молву о земле,"},{num:37,verse:"сии, распустившие худую молву о земле, умерли, быв поражены пред Господом;"},{num:38,verse:"только Иисус, сын Навин, и Халев, сын Иефонниин, остались живы из тех мужей, которые ходили осматривать землю."},{num:0,verse:""},{num:39,verse:"И сказал Моисей слова сии пред всеми сынами Израилевыми, и народ сильно опечалился."},{num:40,verse:"И, встав рано поутру, пошли на вершину горы, говоря: вот, мы пойдем на то место, о котором сказал Господь, ибо мы согрешили."},{num:41,verse:"Моисей сказал: для чего вы преступаете повеление Господне? это будет безуспешно;"},{num:42,verse:"не ходите, ибо нет среди вас Господа, чтобы не поразили вас враги ваши;"},{num:43,verse:"ибо Амаликитяне и Хананеи там пред вами, и вы падете от меча, потому что вы отступили от Господа, и не будет с вами Господа."},{num:44,verse:"Но они дерзнули подняться на вершину горы; ковчег же завета Господня и Моисей не оставляли стана."},{num:45,verse:"И сошли Амаликитяне и Хананеи, живущие на горе той, и разбили их, и гнали их до Хормы, [и возвратились в стан.]"}]},{chapter:"15",verses:[{num:1,verse:"И сказал Господь Моисею, говоря:"},{num:2,verse:"объяви сынам Израилевым и скажи им: когда вы войдете в землю вашего жительства, которую Я даю вам,"},{num:3,verse:"и будете приносить жертву Господу, всесожжение, или жертву заколаемую, от волов и овец, во исполнение обета, или по усердию, или в праздники ваши, дабы сделать приятное благоухание Господу, –"},{num:4,verse:"тогда приносящий жертву свою Господу должен принести в приношение от хлеба десятую часть [ефы] пшеничной муки, смешанной с четвертою частью гина елея;"},{num:5,verse:"и вина для возлияния приноси четвертую часть гина при всесожжении, или при заколаемой жертве, на каждого агнца [в приятное благоухание Господу]."},{num:6,verse:"А принося овна, приноси в приношение хлебное две десятых части <i>ефы</i> пшеничной муки, смешанной с третьею частью гина елея;"},{num:7,verse:"и вина для возлияния приноси третью часть гина в приятное благоухание Господу."},{num:8,verse:"Если молодого вола приносишь во всесожжение или жертву заколаемую, во исполнение обета или в мирную жертву Господу,"},{num:9,verse:"то вместе с волом должно принести приношения хлебного три десятых части <i>ефы</i> пшеничной муки, смешанной с половиною гина елея;"},{num:10,verse:"и вина для возлияния приноси полгина в жертву, в приятное благоухание Господу."},{num:11,verse:"Так делай при каждом приношении вола и овна и агнца из овец, или коз;"},{num:12,verse:"по числу <i>жертв</i>, которые вы приносите, так делайте при каждой, по числу их."},{num:13,verse:"Всякий туземец так должен делать это, принося жертву в приятное благоухание Господу;"},{num:14,verse:"и если будет между вами жить пришелец, или кто бы ни был среди вас в роды ваши, и принесет жертву в приятное благоухание Господу, то и он должен делать так, как вы делаете;"},{num:15,verse:"для вас, общество [Господне], и для пришельца, живущего [у вас], устав один, устав вечный в роды ваши: что вы, то и пришелец да будет пред Господом;"},{num:16,verse:"закон один и одни права да будут для вас и для пришельца, живущего у вас."},{num:0,verse:""},{num:17,verse:"И сказал Господь Моисею, говоря:"},{num:18,verse:"объяви сынам Израилевым и скажи им: когда вы войдете в землю, в которую Я веду вас,"},{num:19,verse:"и будете есть хлеб той земли, то возносите возношение Господу;"},{num:20,verse:"от начатков теста вашего лепешку возносите в возношение; возносите ее так, как возношение с гумна;"},{num:21,verse:"от начатков теста вашего отдавайте в возношение Господу в роды ваши."},{num:22,verse:"Если же преступите по неведению и не исполните всех сих заповедей, которые изрек Господь Моисею,"},{num:23,verse:"всего, что заповедал вам Господь [Бог] чрез Моисея, от того дня, в который Господь заповедал вам, и впредь в роды ваши, –"},{num:24,verse:"то, если по недосмотру общества сделана ошибка, пусть все общество принесет одного молодого вола [без порока] во всесожжение, в приятное благоухание Господу, с хлебным приношением и возлиянием его, по уставу, и одного козла в жертву за грех;"},{num:25,verse:"и очистит священник все общество сынов Израилевых, и будет прощено им, ибо это была ошибка, и они принесли приношение свое в жертву Господу, и жертву за грех свой пред Господом, за свою ошибку;"},{num:26,verse:"и будет прощено всему обществу сынов Израилевых и пришельцу, живущему между ними, потому что весь народ сделал это по ошибке."},{num:27,verse:"Если же один кто согрешит по неведению, то пусть принесет козу однолетнюю в жертву за грех;"},{num:28,verse:"и очистит священник душу, сделавшую по ошибке грех пред Господом, и очищена будет, и прощено будет ей;"},{num:29,verse:"один закон да будет для вас, как для природного жителя из сынов Израилевых, так и для пришельца, живущего у вас, если кто сделает что по ошибке."},{num:30,verse:"Если же кто из туземцев, или из пришельцев, сделает что дерзкою рукою, то он хулит Господа: истребится душа та из народа своего,"},{num:31,verse:"ибо слово Господне он презрел и заповедь Его нарушил; истребится душа та; грех ее на ней."},{num:0,verse:""},{num:32,verse:"Когда сыны Израилевы были в пустыне, нашли человека, собиравшего дрова в день субботы;"},{num:33,verse:"и привели его нашедшие его собирающим дрова [в день субботы] к Моисею и Аарону и ко всему обществу [сынов Израилевых];"},{num:34,verse:"и посадили его под стражу, потому что не было еще определено, что должно с ним сделать."},{num:35,verse:"И сказал Господь Моисею: должен умереть человек сей; пусть побьет его камнями все общество вне стана."},{num:36,verse:"И вывело его все общество вон из стана, и побили его камнями, и он умер, как повелел Господь Моисею."},{num:0,verse:""},{num:37,verse:"И сказал Господь Моисею, говоря:"},{num:38,verse:"объяви сынам Израилевым и скажи им, чтоб они делали себе кисти на краях одежд своих в роды их, и в кисти, которые на краях, вставляли нити из голубой шерсти;"},{num:39,verse:"и будут они в кистях у вас для того, чтобы вы, смотря на них, вспоминали все заповеди Господни, и исполняли их, и не ходили вслед сердца вашего и очей ваших, которые влекут вас к блудодейству,"},{num:40,verse:"чтобы вы помнили и исполняли все заповеди Мои и были святы пред Богом вашим."},{num:41,verse:"Я Господь, Бог ваш, Который вывел вас из земли Египетской, чтоб быть вашим Богом: Я Господь, Бог ваш."}]},{chapter:"16",verses:[{num:1,verse:"Корей, сын Ицгара, сын Каафов, сын Левиин, и Дафан и Авирон, сыны Елиава, и Авнан, сын Фалефа, сыны Рувимовы,"},{num:2,verse:"восстали на Моисея, и <i>с</i> <i>ними</i> из сынов Израилевых двести пятьдесят мужей, начальники общества, призываемые на собрания, люди именитые."},{num:3,verse:"И собрались против Моисея и Аарона и сказали им: полно вам; все общество, все святы, и среди их Господь! почему же вы ставите себя выше народа Господня?"},{num:4,verse:"Моисей, услышав это, пал на лице свое"},{num:5,verse:"и сказал Корею и всем сообщникам его, говоря: завтра покажет Господь, кто Его, и кто свят, чтобы приблизить его к Себе; и кого Он изберет, того и приблизит к Себе;"},{num:6,verse:"вот что сделайте: Корей и все сообщники его возьмите себе кадильницы"},{num:7,verse:"и завтра положите в них огня и всыпьте в них курения пред Господом; и кого изберет Господь, тот и будет свят. Полно вам, сыны Левиины!"},{num:8,verse:"И сказал Моисей Корею: послушайте, сыны Левия!"},{num:9,verse:"неужели мало вам того, что Бог Израилев отделил вас от общества Израильского и приблизил вас к Себе, чтобы вы исполняли службы при скинии Господней и стояли пред обществом [Господним], служа для них?"},{num:10,verse:"Он приблизил тебя и с тобою всех братьев твоих, сынов Левия, и вы домогаетесь еще и священства."},{num:11,verse:"Итак ты и все твое общество собрались против Господа. Что Аарон, что вы ропщете на него?"},{num:12,verse:"И послал Моисей позвать Дафана и Авирона, сынов Елиава. Но они сказали: не пойдем!"},{num:13,verse:"разве мало того, что ты вывел нас из земли, в которой течет молоко и мед, чтобы погубить нас в пустыне? и ты еще хочешь властвовать над нами!"},{num:14,verse:"привел ли ты нас в землю, где течет молоко и мед, и дал ли нам во владение поля и виноградники? глаза людей сих ты хочешь ослепить? не пойдем!"},{num:15,verse:"Моисей весьма огорчился и сказал Господу: не обращай взора Твоего на приношение их; я не взял ни у одного из них осла и не сделал зла ни одному из них."},{num:16,verse:"И сказал Моисей Корею: завтра ты и все общество твое будьте пред лицем Господа, ты, они и Аарон;"},{num:17,verse:"и возьмите каждый свою кадильницу, и положите в них курения, и принесите пред лице Господне каждый свою кадильницу, двести пятьдесят кадильниц; ты и Аарон, каждый свою кадильницу."},{num:18,verse:"И взял каждый свою кадильницу, и положили в них огня, и всыпали в них курения, и стали при входе в скинию собрания; также и Моисей и Аарон."},{num:19,verse:"И собрал против них Корей все общество ко входу скинии собрания. И явилась слава Господня всему обществу."},{num:0,verse:""},{num:20,verse:"И сказал Господь Моисею и Аарону, говоря:"},{num:21,verse:"отделитесь от общества сего, и Я истреблю их во мгновение."},{num:22,verse:"Они же пали на лица свои и сказали: Боже, Боже духов всякой плоти! один человек согрешил, и Ты гневаешься на все общество?"},{num:23,verse:"и сказал Господь Моисею, говоря:"},{num:24,verse:"скажи обществу: отступите со всех сторон от жилища Корея, Дафана и Авирона."},{num:25,verse:"И встал Моисей, и пошел к Дафану и Авирону, и за ним пошли старейшины Израилевы."},{num:26,verse:"И сказал обществу: отойдите от шатров нечестивых людей сих, и не прикасайтесь ни к чему, что принадлежит им, чтобы не погибнуть вам [вместе] во всех грехах их."},{num:27,verse:"И отошли они со всех сторон от жилища Корея, Дафана и Авирона; а Дафан и Авирон вышли и стояли у дверей шатров своих с женами своими и сыновьями своими и с малыми детьми своими."},{num:28,verse:"И сказал Моисей: из сего узнаете, что Господь послал меня делать все дела сии, а не по своему произволу [я делаю сие]:"},{num:29,verse:"если они умрут, как умирают все люди, и постигнет их такое наказание, какое <i>постигает</i> всех людей, то не Господь послал меня;"},{num:30,verse:"а если Господь сотворит необычайное, и земля разверзет уста свои и поглотит их [и домы их и шатры их] и все, что у них, и они живые сойдут в преисподнюю, то знайте, что люди сии презрели Господа."},{num:31,verse:"Лишь только он сказал слова сии, расселась земля под ними;"},{num:32,verse:"и разверзла земля уста свои, и поглотила их и домы их, и всех людей Кореевых и все имущество;"},{num:33,verse:"и сошли они со всем, что принадлежало им, живые в преисподнюю, и покрыла их земля, и погибли они из среды общества."},{num:34,verse:"И все Израильтяне, которые были вокруг них, побежали при их вопле, дабы, говорили они, и нас не поглотила земля."},{num:35,verse:"И вышел огонь от Господа и пожрал тех двести пятьдесят мужей, которые принесли курение."},{num:0,verse:""},{num:36,verse:"И сказал Господь Моисею, говоря:"},{num:37,verse:"скажи Елеазару, сыну Аарона, священнику, пусть он соберет [медные] кадильницы сожженных и огонь выбросит вон; ибо освятились"},{num:38,verse:"кадильницы грешников сих смертью их, и пусть разобьют их в листы для покрытия жертвенника, ибо они принесли их пред лице Господа, и они сделались освященными; и будут они знамением для сынов Израилевых."},{num:39,verse:"И взял Елеазар священник медные кадильницы, которые принесли сожженные, и разбили их в листы для покрытия жертвенника,"},{num:40,verse:"в память сынам Израилевым, чтобы никто посторонний, который не от семени Аарона, не приступал приносить курение пред лице Господне, и не было с ним, что с Кореем и сообщниками его, как говорил ему Господь чрез Моисея."},{num:0,verse:""},{num:41,verse:"На другой день все общество сынов Израилевых возроптало на Моисея и Аарона и говорило: вы умертвили народ Господень."},{num:42,verse:"И когда собралось общество против Моисея и Аарона, они обратились к скинии собрания, и вот, облако покрыло ее, и явилась слава Господня."},{num:43,verse:"И пришел Моисей и Аарон к скинии собрания."},{num:0,verse:""},{num:44,verse:"И сказал Господь Моисею [и Аарону], говоря:"},{num:45,verse:"отсторонитесь от общества сего, и Я погублю их во мгновение. Но они пали на лица свои."},{num:46,verse:"И сказал Моисей Аарону: возьми кадильницу и положи в нее огня с жертвенника и всыпь курения, и неси скорее к обществу и заступи их, ибо вышел гнев от Господа, [и] началось поражение."},{num:47,verse:"И взял Аарон, как сказал Моисей, и побежал в среду общества, и вот, уже началось поражение в народе. И он положил курения и заступил народ;"},{num:48,verse:"стал он между мертвыми и живыми, и поражение прекратилось."},{num:49,verse:"И умерло от поражения четырнадцать тысяч семьсот человек, кроме умерших по делу Корееву."},{num:50,verse:"И возвратился Аарон к Моисею, ко входу скинии собрания, после того как поражение прекратилось."}]},{chapter:"17",verses:[{num:1,verse:"И сказал Господь Моисею, говоря:"},{num:2,verse:"скажи сынам Израилевым и возьми у них по жезлу от колена, от всех начальников их по коленам, двенадцать жезлов, и каждого имя напиши на жезле его;"},{num:3,verse:"имя Аарона напиши на жезле Левиином, ибо один жезл от начальника колена их [должны они дать];"},{num:4,verse:"и положи их в скинии собрания, пред <i>ковчегом</i> откровения, где являюсь Я вам;"},{num:5,verse:"и кого Я изберу, того жезл расцветет; и так Я успокою ропот сынов Израилевых, которым они ропщут на вас."},{num:0,verse:""},{num:6,verse:"И сказал Моисей сынам Израилевым, и дали ему все начальники их, от каждого начальника по жезлу, по коленам их двенадцать жезлов, и жезл Ааронов был среди жезлов их."},{num:7,verse:"И положил Моисей жезлы пред лицем Господа в скинии откровения."},{num:8,verse:"На другой день вошел Моисей [и Аарон] в скинию откровения, и вот, жезл Ааронов, от дома Левиина, расцвел, пустил почки, дал цвет и принес миндали."},{num:9,verse:"И вынес Моисей все жезлы от лица Господня ко всем сынам Израилевым. И увидели они это и взяли каждый свой жезл."},{num:0,verse:""},{num:10,verse:"И сказал Господь Моисею: положи опять жезл Ааронов пред <i>ковчегом</i> откровения на сохранение, в знамение для непокорных, чтобы прекратился ропот их на Меня, и они не умирали."},{num:11,verse:"Моисей сделал это; как повелел ему Господь, так он и сделал."},{num:12,verse:"И сказали сыны Израилевы Моисею: вот, мы умираем, погибаем, все погибаем!"},{num:13,verse:"всякий, приближающийся к скинии Господней, умирает: не придется ли всем нам умереть?"}]},{chapter:"18",verses:[{num:1,verse:"И сказал Господь Аарону: ты и сыны твои и дом отца твоего с тобою понесете на себе грех за <i>небрежность</i> <i>во</i> святилище; и ты и сыны твои с тобою понесете на себе грех за <i>неисправность</i> в священстве вашем."},{num:2,verse:"Также и братьев твоих, колено Левиино, племя отца твоего, возьми себе: пусть они будут при тебе и служат тебе, а ты и сыны твои с тобою <i>будете</i> при скинии откровения;"},{num:3,verse:"пусть они отправляют службу тебе и службу во всей скинии; только чтобы не приступали к вещам святилища и к жертвеннику, дабы не умереть и им и вам."},{num:4,verse:"Пусть они будут при тебе и отправляют службу в скинии собрания, все работы по скинии; а посторонний не должен приближаться к вам."},{num:5,verse:"Так отправляйте службу во святилище и при жертвеннике, дабы не было впредь гнева на сынов Израилевых;"},{num:6,verse:"ибо братьев ваших, левитов, Я взял от сынов Израилевых и дал их вам, в дар Господу, для отправления службы при скинии собрания;"},{num:7,verse:"и ты и сыны твои с тобою наблюдайте священство ваше во всем, что принадлежит жертвеннику и что внутри за завесою, и служите; вам даю Я в дар службу священства, а посторонний, приступивший, предан будет смерти."},{num:0,verse:""},{num:8,verse:"И сказал Господь Аарону: вот, Я поручаю тебе наблюдать за возношениями Мне; от всего, посвящаемого сынами Израилевыми, Я дал тебе и сынам твоим, ради священства вашего, уставом вечным;"},{num:9,verse:"вот, что принадлежит тебе из святынь великих, от сожигаемого: всякое приношение их хлебное, и всякая жертва их за грех, и всякая жертва их повинности, что они принесут Мне; это великая святыня тебе и сынам твоим."},{num:10,verse:"На святейшем месте ешьте это; все мужеского пола могут есть, [ты и сыны твои]; это святынею да будет для тебя."},{num:11,verse:"И вот, что тебе из возношений даров их: все возношения сынов Израилевых Я дал тебе и сынам твоим и дочерям твоим с тобою, уставом вечным; всякий чистый в доме твоем может есть это."},{num:12,verse:"Все лучшее из елея и все лучшее из винограда и хлеба, начатки их, которые они дают Господу, Я отдал тебе;"},{num:13,verse:"все первые произведения земли их, которые они принесут Господу, да будут твоими; всякий чистый в доме твоем может есть это."},{num:14,verse:"Все заклятое в земле Израилевой да будет твоим."},{num:15,verse:"Все, разверзающее ложесна у всякой плоти, которую приносят Господу, из людей и из скота, да будет твоим; только первенец из людей должен быть выкуплен, и первородное из скота нечистого должно быть выкуплено;"},{num:16,verse:"а выкуп за них: начиная от одного месяца, по оценке твоей, бери выкуп пять сиклей серебра, по сиклю священному, который в двадцать гер;"},{num:17,verse:"но за первородное из волов, и за первородное из овец, и за первородное из коз, не бери выкупа: они святыня; кровью их окропляй жертвенник, и тук их сожигай в жертву, в приятное благоухание Господу;"},{num:18,verse:"мясо же их тебе принадлежит, равно как грудь возношения и правое плечо тебе принадлежит."},{num:19,verse:"Все возносимые святыни, которые возносят сыны Израилевы Господу, отдаю тебе и сынам твоим и дочерям твоим с тобою, уставом вечным; это завет соли вечный пред Господом, данный для тебя и потомства твоего с тобою."},{num:0,verse:""},{num:20,verse:"И сказал Господь Аарону: в земле их не будешь иметь удела и части не будет тебе между ними; Я часть твоя и удел твой среди сынов Израилевых;"},{num:21,verse:"а сынам Левия, вот, Я дал в удел десятину из всего, что у Израиля, за службу их, за то, что они отправляют службы в скинии собрания;"},{num:22,verse:"и сыны Израилевы не должны впредь приступать к скинии собрания, чтобы не понести греха и не умереть:"},{num:23,verse:"пусть левиты исправляют службы в скинии собрания и несут на себе грех их. Это устав вечный в роды ваши; среди же сынов Израилевых они не получат удела;"},{num:24,verse:"так как десятину сынов Израилевых, которую они приносят в возношение Господу, Я отдаю левитам в удел, потому и сказал Я им: между сынами Израилевыми они не получат удела."},{num:0,verse:""},{num:25,verse:"И сказал Господь Моисею, говоря:"},{num:26,verse:"объяви левитам и скажи им: когда вы будете брать от сынов Израилевых десятину, которую Я дал вам от них в удел, то возносите из нее возношение Господу, десятину из десятины, –"},{num:27,verse:"и вменено будет вам это возношение ваше, как хлеб с гумна и как взятое от точила;"},{num:28,verse:"так и вы будете возносить возношение Господу из всех десятин ваших, которые будете брать от сынов Израилевых, и будете давать из них возношение Господне Аарону священнику;"},{num:29,verse:"из всего, даруемого вам, возносите возношение Господу, из всего лучшего освящаемого."},{num:30,verse:"И скажи им: когда вы принесете из сего лучшее, то это вменено будет левитам, как получаемое с гумна и получаемое от точила;"},{num:31,verse:"вы можете есть это на всяком месте, вы и [сыны ваши и] семейства ваши, ибо это вам плата за работы ваши в скинии собрания;"},{num:32,verse:"и не понесете за это греха, когда принесете лучшее из сего; и посвящаемого сынами Израилевыми не оскверните, и не умрете."}]},{chapter:"19",verses:[{num:1,verse:"И сказал Господь Моисею и Аарону, говоря:"},{num:2,verse:"вот устав закона, который заповедал Господь, говоря: скажи сынам Израилевым, пусть приведут тебе рыжую телицу без порока, у которой нет недостатка, [и] на которой не было ярма;"},{num:3,verse:"и отдайте ее Елеазару священнику, и выведет ее вон из стана [на место чистое], и заколют ее при нем;"},{num:4,verse:"и пусть возьмет Елеазар священник перстом своим крови ее и кровью покропит к передней стороне скинии собрания семь раз;"},{num:5,verse:"и сожгут телицу при его глазах: кожу ее и мясо ее и кровь ее с нечистотою ее пусть сожгут;"},{num:6,verse:"и пусть возьмет священник кедрового дерева и иссопа и нить из червленой шерсти и бросит на сожигаемую телицу;"},{num:7,verse:"и пусть вымоет священник одежды свои, и омоет тело свое водою, и потом войдет в стан, и нечист будет священник до вечера."},{num:8,verse:"И сожигавший ее пусть вымоет одежды свои водою, и омоет тело свое водою, и нечист будет до вечера;"},{num:9,verse:"и кто-нибудь чистый пусть соберет пепел телицы и положит вне стана на чистом месте, и будет он сохраняться для общества сынов Израилевых, для воды очистительной: это жертва за грех;"},{num:10,verse:"и собиравший пепел телицы пусть вымоет одежды свои, и нечист будет до вечера. Это для сынов Израилевых и для пришельцев, живущих у них, да будет уставом вечным."},{num:11,verse:"Кто прикоснется к мертвому телу какого-либо человека, нечист будет семь дней:"},{num:12,verse:"он должен очистить себя сею [водою] в третий день и в седьмой день, и будет чист; если же он не очистит себя в третий и седьмой день, то не будет чист;"},{num:13,verse:"всякий, прикоснувшийся к мертвому телу какого-либо человека умершего и не очистивший себя, осквернит жилище Господа: истребится человек тот из среды Израиля, ибо он не окроплен очистительною водою, он нечист, еще нечистота его на нем."},{num:14,verse:"Вот закон: если человек умрет в шатре, то всякий, кто придет в шатер, и все, что в шатре, нечисто будет семь дней;"},{num:15,verse:"всякий открытый сосуд, который не обвязан и не покрыт, нечист."},{num:16,verse:"Всякий, кто прикоснется на поле к убитому мечом, или к умершему, или к кости человеческой, или ко гробу, нечист будет семь дней."},{num:17,verse:"Для нечистого пусть возьмут пепла той сожженной жертвы за грех и нальют на него живой воды в сосуд;"},{num:18,verse:"и пусть кто-нибудь чистый возьмет иссоп, и омочит его в воде, и окропит шатер и все сосуды и людей, которые находятся в нем, и прикоснувшегося к кости [человеческой], или к убитому, или к умершему, или ко гробу;"},{num:19,verse:"и пусть окропит чистый нечистого в третий и седьмой день, и очистит его в седьмой день; и вымоет он одежды свои, и омоет [тело свое] водою, и к вечеру будет чист."},{num:20,verse:"Если же кто будет нечист и не очистит себя, то истребится человек тот из среды народа, ибо он осквернил святилище Господа; очистительною водою он не окроплен, он нечист."},{num:21,verse:"И да будет это для них уставом вечным. И кропивший очистительною водою пусть вымоет одежды свои; и прикоснувшийся к очистительной воде нечист будет до вечера."},{num:22,verse:"И все, к чему прикоснется нечистый, будет нечисто; и прикоснувшийся человек нечист будет до вечера."}]},{chapter:"20",verses:[{num:1,verse:"И пришли сыны Израилевы, все общество, в пустыню Син в первый месяц, и остановился народ в Кадесе, и умерла там Мариам и погребена там."},{num:2,verse:"И не было воды для общества, и собрались они против Моисея и Аарона;"},{num:3,verse:"и возроптал народ на Моисея и сказал: о, если бы умерли тогда и мы, когда умерли братья наши пред Господом!"},{num:4,verse:"зачем вы привели общество Господне в эту пустыню, чтобы умереть здесь нам и скоту нашему?"},{num:5,verse:"и для чего вывели вы нас из Египта, чтобы привести нас на это негодное место, где нельзя сеять, нет ни смоковниц, ни винограда, ни гранатовых яблок, ни даже воды для питья?"},{num:6,verse:"И пошел Моисей и Аарон от народа ко входу скинии собрания, и пали на лица свои, и явилась им слава Господня."},{num:0,verse:""},{num:7,verse:"И сказал Господь Моисею, говоря:"},{num:8,verse:"Возьми жезл и собери общество, ты и Аарон, брат твой, и скажите в глазах их скале, и она даст из себя воду: и так ты изведешь им воду из скалы, и напоишь общество и скот его."},{num:9,verse:"И взял Моисей жезл от лица Господа, как Он повелел ему."},{num:10,verse:"И собрали Моисей и Аарон народ к скале, и сказал он им: послушайте, непокорные, разве нам из этой скалы извести для вас воду?"},{num:11,verse:"И поднял Моисей руку свою и ударил в скалу жезлом своим дважды, и потекло много воды, и пило общество и скот его."},{num:0,verse:""},{num:12,verse:"И сказал Господь Моисею и Аарону: за то, что вы не поверили Мне, чтоб явить святость Мою пред очами сынов Израилевых, не введете вы народа сего в землю, которую Я даю ему."},{num:13,verse:"Это вода Меривы, у которой вошли в распрю сыны Израилевы с Господом, и Он явил им святость Свою."},{num:0,verse:""},{num:14,verse:"И послал Моисей из Кадеса послов к Царю Едомскому [сказать]: так говорит брат твой Израиль: ты знаешь все трудности, которые постигли нас;"},{num:15,verse:"отцы наши перешли в Египет, и мы жили в Египте много времени, и худо поступали Египтяне с нами и отцами нашими;"},{num:16,verse:"и воззвали мы к Господу, и услышал Он голос наш, и послал Ангела, и вывел нас из Египта; и вот, мы в Кадесе, городе у самого предела твоего;"},{num:17,verse:"позволь нам пройти землею твоею: мы не пойдем по полям и по виноградникам и не будем пить воды из колодезей [твоих]; но пойдем дорогою царскою, не своротим ни направо ни налево, доколе не перейдем пределов твоих."},{num:18,verse:"Но Едом сказал ему: не проходи через меня, иначе я с мечом выступлю против тебя."},{num:19,verse:"И сказали ему сыны Израилевы: мы пойдем большою дорогою, и если будем пить твою воду, я и скот мой, то буду платить за нее; только ногами моими пройду, что ничего не стоит."},{num:20,verse:"Но он сказал: не проходи [через меня]. И выступил против него Едом с многочисленным народом и с сильною рукою."},{num:21,verse:"Итак не согласился Едом позволить Израилю пройти чрез его пределы, и Израиль пошел в сторону от него."},{num:22,verse:"И отправились сыны Израилевы из Кадеса, и пришло все общество к горе Ор."},{num:0,verse:""},{num:23,verse:"И сказал Господь Моисею и Аарону на горе Ор, у пределов земли Едомской, говоря:"},{num:24,verse:"пусть приложится Аарон к народу своему; ибо он не войдет в землю, которую Я даю сынам Израилевым, за то, что вы непокорны были повелению Моему у вод Меривы;"},{num:25,verse:"и возьми Аарона [брата твоего] и Елеазара, сына его, и возведи их на гору Ор [пред всем обществом];"},{num:26,verse:"и сними с Аарона одежды его, и облеки в них Елеазара, сына его, и пусть Аарон отойдет и умрет там."},{num:27,verse:"И сделал Моисей так, как повелел Господь. Пошли они на гору Ор в глазах всего общества,"},{num:28,verse:"и снял Моисей с Аарона одежды его, и облек в них Елеазара, сына его; и умер там Аарон на вершине горы. А Моисей и Елеазар сошли с горы."},{num:29,verse:"И увидело все общество, что Аарон умер, и оплакивал Аарона весь дом Израилев тридцать дней."}]},{chapter:"21",verses:[{num:1,verse:"Ханаанский царь Арада, живущий к югу, услышав, что Израиль идет дорогою от Афарима, вступил в сражение с Израильтянами и несколько из них взял в плен."},{num:2,verse:"И дал Израиль обет Господу, и сказал: если предашь народ сей в руки мои, то положу заклятие [на них и] на города их."},{num:3,verse:"Господь услышал голос Израиля и предал Хананеев в руки ему, и он положил заклятие на них и на города их и нарек имя месту тому: Хорма."},{num:0,verse:""},{num:4,verse:"От горы Ор отправились они путем Чермного моря, чтобы миновать землю Едома. И стал малодушествовать народ на пути,"},{num:5,
verse:"и говорил народ против Бога и против Моисея: зачем вывели вы нас из Египта, чтоб умереть [нам] в пустыне, ибо <i>здесь</i> нет ни хлеба, ни воды, и душе нашей опротивела эта негодная пища."},{num:6,verse:"И послал Господь на народ ядовитых змеев, которые жалили народ, и умерло множество народа из [сынов] Израилевых."},{num:7,verse:"И пришел народ к Моисею и сказал: согрешили мы, что говорили против Господа и против тебя; помолись Господу, чтоб Он удалил от нас змеев. И помолился Моисей [Господу] о народе."},{num:0,verse:""},{num:8,verse:"И сказал Господь Моисею: сделай себе [медного] змея и выставь его на знамя, и [если ужалит змей какого-либо человека], ужаленный, взглянув на него, останется жив."},{num:9,verse:"И сделал Моисей медного змея и выставил его на знамя, и когда змей ужалил человека, он, взглянув на медного змея, оставался жив."},{num:0,verse:""},{num:10,verse:"И отправились сыны Израилевы и остановились в Овофе;"},{num:11,verse:"и отправились из Овофа и остановились в Ийе-Авариме, в пустыне, что против Моава, к восходу солнца;"},{num:12,verse:"оттуда отправились, и остановились на долине Заред;"},{num:13,verse:"отправившись отсюда, остановились у той части Арнона в пустыне, которая течет вне пределов Аморрея, ибо Арнон граница Моава, между Моавом и Аморреем."},{num:14,verse:"Потому и сказано в книге браней Господних:"},{num:15,verse:"Вагеб в Суфе и потоки Арнона, и верховье потоков, которое склоняется к Шебет-Ару и прилегает к пределам Моава."},{num:16,verse:"Отсюда [отправились] к Беэр; это тот колодезь, о котором Господь сказал Моисею: собери народ, и дам им воды."},{num:17,verse:"Тогда воспел Израиль песнь сию: наполняйся, колодезь, пойте ему;"},{num:18,verse:"колодезь, который выкопали князья, вырыли вожди народа с законодателем жезлами своими. Из пустыни [отправились] в Матанну,"},{num:19,verse:"из Матанны в Нагалиил, из Нагалиила в Вамоф,"},{num:20,verse:"из Вамофа в Гай, который в земле Моава, на вершине <i>горы</i> Фасги, обращенной лицем к пустыне."},{num:21,verse:"И послал Израиль послов к Сигону, царю Аморрейскому, [с предложением мирным,] чтобы сказать:"},{num:22,verse:"позволь мне пройти землею твоею; [мы пойдем дорогою,] не будем заходить в поля и виноградники, не будем пить воды из колодезей [твоих], а пойдем путем царским, доколе не перейдем пределов твоих."},{num:23,verse:"Но Сигон не позволил Израилю идти через свои пределы; и собрал Сигон весь народ свой и выступил против Израиля в пустыню, и дошел до Иаацы, и сразился с Израилем."},{num:24,verse:"И поразил его Израиль мечом и взял во владение землю его от Арнона до Иавока, до <i>пределов</i> Аммонитских, ибо крепок был предел Аммонитян;"},{num:25,verse:"и взял Израиль все города сии, и жил Израиль во всех городах Аморрейских, в Есевоне и во всех зависящих от него;"},{num:26,verse:"ибо Есевон был город Сигона, царя Аморрейского, и он воевал с прежним царем Моавитским и взял из руки его всю землю его до Арнона."},{num:27,verse:"Потому говорят приточники: идите в Есевон, да устроят и утвердят город Сигона;"},{num:28,verse:"ибо огонь вышел из Есевона, пламень из города Сигонова, и пожрал Ар-Моав и владеющих высотами Арнона."},{num:29,verse:"Горе тебе, Моав! погиб ты, народ Хамоса! Разбежались сыновья его, и дочери его сделались пленницами Аморрейского царя Сигона;"},{num:30,verse:"мы поразили их стрелами; погиб Есевон до Дивона, мы опустошили их до Нофы, которая близ Медевы."},{num:31,verse:"И жил Израиль в земле Аморрейской."},{num:32,verse:"И послал Моисей высмотреть Иазер, и взяли [его и] селения, зависящие от него, и прогнали Аморреев, которые в них были."},{num:33,verse:"И поворотили и пошли к Васану. И выступил против них Ог, царь Васанский, сам и весь народ его, на сражение к Едреи."},{num:0,verse:""},{num:34,verse:"И сказал Господь Моисею: не бойся его, ибо Я предам его и весь народ его и всю землю его в руки твои, и поступишь с ним, как поступил с Сигоном, царем Аморрейским, который жил в Есевоне."},{num:35,verse:"И поразили они его и сынов его и весь народ его, так что ни одного не осталось [живого], и овладели землею его."}]},{chapter:"22",verses:[{num:1,verse:"И отправились сыны Израилевы, и остановились на равнинах Моава, при Иордане, против Иерихона."},{num:0,verse:""},{num:2,verse:"И видел Валак, сын Сепфоров, все, что сделал Израиль Аморреям;"},{num:3,verse:"и весьма боялись Моавитяне народа сего, потому что он был многочислен; и устрашились Моавитяне сынов Израилевых."},{num:4,verse:"И сказали Моавитяне старейшинам Мадиамским: этот народ поедает теперь все вокруг нас, как вол поедает траву полевую. Валак же, сын Сепфоров, был царем Моавитян в то время."},{num:5,verse:"И послал он послов к Валааму, сыну Веорову, в Пефор, который на реке <i>Евфрате</i>, в земле сынов народа его, чтобы позвать его <i>и</i> сказать: вот, народ вышел из Египта и покрыл лице земли, и живет он подле меня;"},{num:6,verse:"итак приди, прокляни мне народ сей, ибо он сильнее меня: может быть, я тогда буду в состоянии поразить его и выгнать его из земли; я знаю, что кого ты благословишь, тот благословен, и кого ты проклянешь, тот проклят."},{num:7,verse:"И пошли старейшины Моавитские и старейшины Мадиамские, с подарками в руках за волхвование, и пришли к Валааму, и пересказали ему слова Валаковы."},{num:8,verse:"И сказал он им: переночуйте здесь ночь, и дам вам ответ, как скажет мне Господь. И остались старейшины Моавитские у Валаама."},{num:9,verse:"И пришел Бог к Валааму и сказал: какие это люди у тебя?"},{num:10,verse:"Валаам сказал Богу: Валак, сын Сепфоров, царь Моавитский, прислал [их] ко мне [сказать]:"},{num:11,verse:"вот, народ вышел из Египта и покрыл лице земли, [и живет подле меня]; итак приди, прокляни мне его; может быть я тогда буду в состоянии сразиться с ним и выгнать его [из земли]."},{num:12,verse:"И сказал Бог Валааму: не ходи с ними, не проклинай народа сего, ибо он благословен."},{num:13,verse:"И встал Валаам поутру и сказал князьям Валаковым: пойдите в землю вашу, ибо не хочет Господь позволить мне идти с вами."},{num:14,verse:"И встали князья Моавитские, и пришли к Валаку, и сказали [ему]: не согласился Валаам идти с нами."},{num:0,verse:""},{num:15,verse:"Валак послал еще князей, более и знаменитее тех."},{num:16,verse:"И пришли они к Валааму и сказали ему: так говорит Валак, сын Сепфоров: не откажись придти ко мне;"},{num:17,verse:"я окажу тебе великую почесть и сделаю [тебе] все, что ни скажешь мне; приди же, прокляни мне народ сей."},{num:18,verse:"И отвечал Валаам и сказал рабам Валаковым: хотя бы Валак давал мне полный свой дом серебра и золота, не могу преступить повеления Господа, Бога моего, и сделать что-либо малое или великое [по своему произволу];"},{num:19,verse:"впрочем, останьтесь здесь и вы на ночь, и я узнаю, что еще скажет мне Господь."},{num:20,verse:"И пришел Бог к Валааму ночью и сказал ему: если люди сии пришли звать тебя, встань, пойди с ними; но только делай то, что Я буду говорить тебе."},{num:21,verse:"Валаам встал поутру, оседлал ослицу свою и пошел с князьями Моавитскими."},{num:22,verse:"И воспылал гнев Божий за то, что он пошел, и стал Ангел Господень на дороге, чтобы воспрепятствовать ему. Он ехал на ослице своей и с ними двое слуг его."},{num:23,verse:"И увидела ослица Ангела Господня, стоящего на дороге с обнаженным мечом в руке, и своротила ослица с дороги, и пошла на поле; а Валаам стал бить ослицу, чтобы возвратить ее на дорогу."},{num:24,verse:"И стал Ангел Господень на узкой дороге, между виноградниками, <i>где</i> с одной стороны стена и с другой стороны стена."},{num:25,verse:"Ослица, увидев Ангела Господня, прижалась к стене и прижала ногу Валаамову к стене; и он опять стал бить ее."},{num:26,verse:"Ангел Господень опять перешел и стал в тесном месте, где некуда своротить, ни направо, ни налево."},{num:27,verse:"Ослица, увидев Ангела Господня, легла под Валаамом. И воспылал гнев Валаама, и стал он бить ослицу палкою."},{num:28,verse:"И отверз Господь уста ослицы, и она сказала Валааму: что я тебе сделала, что ты бьешь меня вот уже третий раз?"},{num:29,verse:"Валаам сказал ослице: за то, что ты поругалась надо мною; если бы у меня в руке был меч, то я теперь же убил бы тебя."},{num:30,verse:"Ослица же сказала Валааму: не я ли твоя ослица, на которой ты ездил сначала до сего дня? имела ли я привычку так поступать с тобою? Он сказал: нет."},{num:31,verse:"И открыл Господь глаза Валааму, и увидел он Ангела Господня, стоящего на дороге с обнаженным мечом в руке, и преклонился, и пал на лице свое."},{num:32,verse:"И сказал ему Ангел Господень: за что ты бил ослицу твою вот уже три раза? Я вышел, чтобы воспрепятствовать [тебе], потому что путь [твой] не прав предо Мною;"},{num:33,verse:"и ослица, видев Меня, своротила от Меня вот уже три раза; если бы она не своротила от Меня, то Я убил бы тебя, а ее оставил бы живою."},{num:34,verse:"И сказал Валаам Ангелу Господню: согрешил я, ибо не знал, что Ты стоишь против меня на дороге; итак, если это неприятно в очах Твоих, то я возвращусь."},{num:35,verse:"И сказал Ангел Господень Валааму: пойди с людьми сими, только говори то, что Я буду говорить тебе. И пошел Валаам с князьями Валаковыми."},{num:0,verse:""},{num:36,verse:"Валак, услышав, что идет Валаам, вышел навстречу ему в город Моавитский, который на границе при Арноне, что у самого предела."},{num:37,verse:"И сказал Валак Валааму: не посылал ли я к тебе, звать тебя? почему ты не шел ко мне? неужели я в самом деле не могу почтить тебя?"},{num:38,verse:"И сказал Валаам Валаку: вот, я и пришел к тебе, но могу ли я что <i>от</i> <i>себя</i> сказать? что вложит Бог в уста мои, то и буду говорить."},{num:39,verse:"И пошел Валаам с Валаком и пришли в Кириаф-Хуцоф."},{num:40,verse:"И заколол Валак волов и овец, и послал к Валааму и князьям, которые были с ним."},{num:41,verse:"На другой день утром Валак взял Валаама и возвел его на высоты Вааловы, чтобы он увидел оттуда часть народа."}]},{chapter:"23",verses:[{num:1,verse:"И сказал Валаам Валаку: построй мне здесь семь жертвенников и приготовь мне семь тельцов и семь овнов."},{num:2,verse:"Валак сделал так, как говорил Валаам, и вознесли Валак и Валаам по тельцу и по овну на каждом жертвеннике."},{num:3,verse:"И сказал Валаам Валаку: постой у всесожжения твоего, а я пойду; может быть, Господь выйдет мне навстречу, и что Он откроет мне, я объявлю тебе. И [остался Валак у всесожжения своего, а Валаам] пошел на возвышенное место [вопросить Бога]."},{num:4,verse:"И встретился Бог с Валаамом, и сказал ему [Валаам]: семь жертвенников устроил я и вознес по тельцу и по овну на каждом жертвеннике."},{num:5,verse:"И вложил Господь слово в уста Валаамовы и сказал: возвратись к Валаку и так говори."},{num:6,verse:"И возвратился к нему, и вот, он стоит у всесожжения своего, он и все князья Моавитские. [И был на нем Дух Божий.]"},{num:7,verse:"И произнес притчу свою и сказал: из Месопотамии привел меня Валак, царь Моава, от гор восточных: приди, прокляни мне Иакова, приди, изреки зло на Израиля!"},{num:8,verse:"Как прокляну я? Бог не проклинает его. Как изреку зло? Господь не изрекает [на него] зла."},{num:9,verse:"С вершины скал вижу я его, и с холмов смотрю на него: вот, народ живет отдельно и между народами не числится."},{num:10,verse:"Кто исчислит песок Иакова и число четвертой части Израиля? Да умрет душа моя смертью праведников, и да будет кончина моя, как их!"},{num:11,verse:"И сказал Валак Валааму: что ты со мною делаешь? я взял тебя, чтобы проклясть врагов моих, а ты, вот, благословляешь?"},{num:12,verse:"И отвечал он и сказал: не должен ли я в точности сказать то, что влагает Господь в уста мои?"},{num:13,verse:"И сказал ему Валак: пойди со мною на другое место, с которого ты увидишь его, но только часть его увидишь, а всего его не увидишь; и прокляни мне его оттуда."},{num:14,verse:"И взял его на место стражей, на вершину <i>горы</i> Фасги, и построил семь жертвенников, и вознес по тельцу и по овну на каждом жертвеннике."},{num:15,verse:"И сказал [Валаам] Валаку: постой здесь у всесожжения твоего, а я [пойду] туда навстречу [Богу]."},{num:16,verse:"И встретился Господь с Валаамом, и вложил слово в уста его, и сказал: возвратись к Валаку и так говори."},{num:17,verse:"И пришел к нему, и вот, он стоит у всесожжения своего, и с ним [все] князья Моавитские. И сказал ему Валак: что говорил Господь?"},{num:18,verse:"Он произнес притчу свою и сказал: встань, Валак, и послушай, внимай мне, сын Сепфоров."},{num:19,verse:"Бог не человек, чтоб Ему лгать, и не сын человеческий, чтоб Ему изменяться. Он ли скажет и не сделает? будет говорить и не исполнит?"},{num:20,verse:"Вот, благословлять начал я, ибо Он благословил, и я не могу изменить сего."},{num:21,verse:"Не видно бедствия в Иакове, и не заметно несчастья в Израиле; Господь, Бог его, с ним, и трубный царский звук у него;"},{num:22,verse:"Бог вывел их из Египта, быстрота единорога у него;"},{num:23,verse:"нет волшебства в Иакове и нет ворожбы в Израиле. В <i>свое</i> время скажут об Иакове и об Израиле: вот что творит Бог!"},{num:24,verse:"Вот, народ как львица встает и как лев поднимается; не ляжет, пока не съест добычи и не напьется крови убитых."},{num:25,verse:"И сказал Валак Валааму: ни клясть не кляни его, ни благословлять не благословляй его."},{num:26,verse:"И отвечал Валаам и сказал Валаку: не говорил ли я тебе, что я буду делать все то, что скажет мне Господь?"},{num:27,verse:"И сказал Валак Валааму: пойди, я возьму тебя на другое место; может быть, угодно будет Богу, и оттуда проклянешь мне его."},{num:28,verse:"И взял Валак Валаама на верх Фегора, обращенного к пустыне."},{num:29,verse:"И сказал Валаам Валаку: построй мне здесь семь жертвенников и приготовь мне здесь семь тельцов и семь овнов."},{num:30,verse:"И сделал Валак, как сказал Валаам, и вознес по тельцу и овну на каждом жертвеннике."}]},{chapter:"24",verses:[{num:1,verse:"Валаам увидел, что Господу угодно благословлять Израиля, и не пошел, как прежде, для волхвования, но обратился лицем своим к пустыне."},{num:2,verse:"И взглянул Валаам и увидел Израиля, стоявшего по коленам своим, и был на нем Дух Божий."},{num:3,verse:"И произнес он притчу свою и сказал: говорит Валаам, сын Веоров, говорит муж с открытым оком,"},{num:4,verse:"говорит слышащий слова Божии, который видит видения Всемогущего; падает, но открыты глаза его:"},{num:5,verse:"как прекрасны шатры твои, Иаков, жилища твои, Израиль!"},{num:6,verse:"расстилаются они как долины, как сады при реке, как алойные дерева, насажденные Господом, как кедры при водах;"},{num:7,verse:"польется вода из ведр его, и семя его <i>будет</i> как великие воды, превзойдет Агага царь его и возвысится царство его."},{num:8,verse:"Бог вывел его из Египта, быстрота единорога у него, пожирает народы, враждебные ему, раздробляет кости их и стрелами своими разит [врага]."},{num:9,verse:"Преклонился, лежит как лев и как львица, кто поднимет его? Благословляющий тебя благословен, и проклинающий тебя проклят!"},{num:0,verse:""},{num:10,verse:"И воспламенился гнев Валака на Валаама, и всплеснул он руками своими, и сказал Валак Валааму: я призвал тебя проклясть врагов моих, а ты благословляешь их вот уже третий раз;"},{num:11,verse:"итак, беги в свое место; я хотел почтить тебя, но вот, Господь лишает тебя чести."},{num:12,verse:"И сказал Валаам Валаку: не говорил ли я послам твоим, которых ты присылал ко мне:"},{num:13,verse:"«хотя бы давал мне Валак полный свой дом серебра и золота, не могу преступить повеления Господня, чтобы сделать что-либо доброе или худое по своему произволу: что скажет Господь, то и буду говорить»?"},{num:14,verse:"Итак, вот, я иду к народу своему; пойди, я возвещу тебе, что сделает народ сей с народом твоим в последствие времени."},{num:15,verse:"И произнес притчу свою и сказал: говорит Валаам, сын Веоров, говорит муж с открытым оком,"},{num:16,verse:"говорит слышащий слова Божии, имеющий ведение от Всевышнего, который видит видения Всемогущего, падает, но открыты очи его."},{num:17,verse:"Вижу Его, но ныне еще нет; зрю Его, но не близко. Восходит звезда от Иакова и восстает жезл от Израиля, и разит князей Моава и сокрушает всех сынов Сифовых."},{num:18,verse:"Едом будет под владением, Сеир будет под владением врагов своих, а Израиль явит силу <i>свою</i>."},{num:19,verse:"<i>Происшедший</i> от Иакова овладеет и погубит оставшееся от города."},{num:20,verse:"И увидел он Амалика, и произнес притчу свою, и сказал: первый из народов Амалик, но конец его – гибель."},{num:21,verse:"И увидел он Кенеев, и произнес притчу свою, и сказал: крепко жилище твое, и на скале положено гнездо твое;"},{num:22,verse:"но разорен будет Каин, и недолго до того, что Ассур уведет тебя в плен."},{num:23,verse:"И [увидев Ога,] произнес притчу свою, и сказал: горе, [горе,] кто уцелеет, когда наведет сие Бог!"},{num:24,verse:"<i>придут</i> корабли от Киттима, и смирят Ассура, и смирят Евера; но и им гибель!"},{num:25,verse:"И встал Валаам и пошел обратно в свое место, а Валак также пошел своею дорогою."}]},{chapter:"25",verses:[{num:1,verse:"И жил Израиль в Ситтиме, и начал народ блудодействовать с дочерями Моава,"},{num:2,verse:"и приглашали они народ к жертвам богов своих, и ел народ [жертвы их] и кланялся богам их."},{num:3,verse:"И прилепился Израиль к Ваал-Фегору. И воспламенился гнев Господень на Израиля."},{num:4,verse:"И сказал Господь Моисею: возьми всех начальников народа и повесь их Господу перед солнцем, и отвратится от Израиля ярость гнева Господня."},{num:5,verse:"И сказал Моисей судьям Израилевым: убейте каждый людей своих, прилепившихся к Ваал-Фегору."},{num:0,verse:""},{num:6,verse:"И вот, некто из сынов Израилевых пришел и привел к братьям своим Мадианитянку, в глазах Моисея и в глазах всего общества сынов Израилевых, когда они плакали у входа скинии собрания."},{num:7,verse:"Финеес, сын Елеазара, сына Аарона священника, увидев это, встал из среды общества и взял в руку свою копье,"},{num:8,verse:"и вошел вслед за Израильтянином в спальню и пронзил обоих их, Израильтянина и женщину в чрево ее: и прекратилось поражение сынов Израилевых."},{num:9,verse:"Умерших же от поражения было двадцать четыре тысячи."},{num:0,verse:""},{num:10,verse:"И сказал Господь Моисею, говоря:"},{num:11,verse:"Финеес, сын Елеазара, сына Аарона священника, отвратил ярость Мою от сынов Израилевых, возревновав по Мне среди их, и Я не истребил сынов Израилевых в ревности Моей;"},{num:12,verse:"посему скажи: вот, Я даю ему Мой завет мира,"},{num:13,verse:"и будет он ему и потомству его по нем заветом священства вечного, за то, что он показал ревность по Боге своем и заступил сынов Израилевых."},{num:14,verse:"Имя убитого Израильтянина, который убит с Мадианитянкою, было Зимри, сын Салу, начальник поколения Симеонова;"},{num:15,verse:"а имя убитой Мадианитянки Хазва; она была дочь Цура, начальника Оммофа, племени Мадиамского."},{num:0,verse:""},{num:16,verse:"И сказал Господь Моисею, говоря:"},{num:17,verse:"враждуйте с Мадианитянами, и поражайте их,"},{num:18,verse:"ибо они враждебно поступили с вами в коварстве своем, прельстив вас Фегором и Хазвою, дочерью начальника Мадиамского, сестрою своею, убитою в день поражения за Фегора."}]},{chapter:"26",verses:[{num:1,verse:"После сего поражения сказал Господь Моисею и Елеазару, сыну Аарона, священнику, говоря:"},{num:2,verse:"исчислите все общество сынов Израилевых от двадцати лет и выше, по семействам их, всех годных для войны у Израиля."},{num:3,verse:"И сказал им Моисей и Елеазар священник на равнинах Моавитских у Иордана, против Иерихона, говоря:"},{num:4,verse:"<i>исчислите</i> <i>всех</i> от двадцати лет и выше, как повелел Господь Моисею и сынам Израилевым, которые вышли из земли Египетской:"},{num:5,verse:"Рувим, первенец Израиля. Сыны Рувима: от Ханоха поколение Ханохово, от Фаллу поколение Фаллуево,"},{num:6,verse:"от Хецрона поколение Хецроново, от Харми поколение Хармиево;"},{num:7,verse:"вот поколения Рувимовы; и исчислено их сорок три тысячи семьсот тридцать."},{num:8,verse:"И сыны Фаллуя: Елиав."},{num:9,verse:"Сыны Елиава: Немуил, Дафан и Авирон. Это те Дафан и Авирон, призываемые в собрание, которые произвели возмущение против Моисея и Аарона вместе с сообщниками Корея, когда сии произвели возмущение против Господа;"},{num:10,verse:"и разверзла земля уста свои, и поглотила их и Корея; вместе с <i>ними</i> умерли и сообщники их, когда огонь пожрал двести пятьдесят человек, и стали они в знамение;"},{num:11,verse:"но сыны Кореевы не умерли."},{num:12,verse:"Сыны Симеона по поколениям их: от Немуила поколение Немуилово, от Ямина поколение Яминово, от Яхина поколение Яхиново,"},{num:13,verse:"от Зары поколение Зарино, от Саула поколение Саулово;"},{num:14,verse:"вот поколения Симеоновы [при исчислении их]: двадцать две тысячи двести."},{num:15,verse:"Сыны Гада по поколениям их: от Цефона поколение Цефоново, от Хаггия поколение Хаггиево, от Шуния поколение Шуниево,"},{num:16,verse:"от Озния поколение Озниево, от Ерия поколение Ериево,"},{num:17,verse:"от Арода поколение Ародово, от Арелия поколение Арелиево;"},{num:18,verse:"вот поколения сынов Гадовых, по исчислению их: сорок тысяч пятьсот."},{num:19,verse:"Сыны Иуды: Ир и Онан, [Шела, Фарес и Зара]; но Ир и Онан умерли в земле Ханаанской;"},{num:20,verse:"и были сыны Иуды по поколениям их: от Шелы поколение Шелино, от Фареса поколение Фаресово, от Зары поколение Зарино;"},{num:21,verse:"и были сыны Фаресовы: от Есрома поколение Есромово, от Хамула поколение Хамулово;"},{num:22,verse:"вот поколения Иудины, по исчислению их: семьдесят шесть тысяч пятьсот."},{num:23,verse:"Сыны Иссахаровы по поколениям их: от Фолы поколение Фолино, от Фувы поколение Фувино,"},{num:24,verse:"от Иашува поколение Иашувово, от Шимрона поколение Шимроново;"},{num:25,verse:"вот поколения Иссахаровы, по исчислению их: шестьдесят четыре тысячи триста."},{num:26,verse:"Сыны Завулона по поколениям их: от Середа поколение Середово, от Елона поколение Елоново, от Иахлеила поколение Иахлеилово;"},{num:27,verse:"вот поколения Завулоновы, по исчислению их: шестьдесят тысяч пятьсот."},{num:28,verse:"Сыны Иосифа по поколениям их: Манассия и Ефрем."},{num:29,verse:"Сыны Манассии: от Махира поколение Махирово; от Махира родился Галаад, от Галаада поколение Галаадово."},{num:30,verse:"Вот сыны Галаадовы: от Иезера поколение Иезерово, от Хелека поколение Хелеково,"},{num:31,verse:"от Асриила поколение Асриилово, от Шехема поколение Шехемово,"},{num:32,verse:"от Шемиды поколение Шемидино, от Хефера поколение Хеферово."},{num:33,verse:"У Салпаада, сына Хеферова, не было сыновей, а только дочери; имя дочерей Салпаадовых: Махла, Ноа, Хогла, Милка и Фирца."},{num:34,verse:"Вот поколения Манассиины; а исчислено их пятьдесят две тысячи семьсот."},{num:35,verse:"Вот сыны Ефремовы по поколениям их: от Шутелы поколение Шутелино, от Бехера поколение Бехерово, от Тахана поколение Таханово;"},{num:36,verse:"и вот сыны Шутелы: от Арана поколение Араново;"},{num:37,verse:"вот поколения сынов Ефремовых, по исчислению их: тридцать две тысячи пятьсот. Вот сыны Иосифовы по поколениям их."},{num:38,verse:"Сыны Вениамина по поколениям их: от Белы поколение Белино, от Ашбела поколение Ашбелово, от Ахирама поколение Ахирамово,"},{num:39,verse:"от Шефуфама поколение Шефуфамово, от Хуфама поколение Хуфамово;"},{num:40,verse:"и были сыны Белы: Ард и Нааман; [от Арда] поколение Ардово, от Наамана поколение Нааманово;"},{num:41,verse:"вот сыны Вениамина по поколениям их; а исчислено их сорок пять тысяч шестьсот."},{num:42,verse:"Вот сыны Дановы по поколениям их: от Шухама поколение Шухамово; вот семейства Дановы по поколениям их."},{num:43,verse:"и всех поколений Шухама, по исчислению их: шестьдесят четыре тысячи четыреста."},{num:44,verse:"Сыны Асировы по поколениям их: от Имны поколение Имнино, от Ишвы поколение Ишвино, от Верии поколение Вериино;"},{num:45,verse:"от сынов Верии, от Хевера поколение Хеверово, от Малхиила поколение Малхиилово;"},{num:46,verse:"имя дочери Асировой Сара;"},{num:47,verse:"вот поколения сынов Асировых, по исчислению их: пятьдесят три тысячи четыреста."},{num:48,verse:"Сыны Неффалима по поколениям их: от Иахцеила поколение Иахцеилово, от Гуния поколение Гуниево,"},{num:49,verse:"от Иецера поколение Иецерово, от Шиллема поколение Шиллемово;"},{num:50,verse:"вот поколения Неффалимовы по поколениям их; исчислено же их сорок пять тысяч четыреста."},{num:51,verse:"Вот <i>число</i> вошедших в исчисление сынов Израилевых: шестьсот одна тысяча семьсот тридцать."},{num:0,verse:""},{num:52,verse:"И сказал Господь Моисею, говоря:"},{num:53,verse:"сим в удел должно разделить землю по числу имен;"},{num:54,verse:"кто многочисленнее, тем дай удел более; а кто малочисленнее, тем дай удел менее: каждому должно дать удел соразмерно с числом вошедших в исчисление;"},{num:55,verse:"по жребию должно разделить землю, по именам колен отцов их должны они получить уделы;"},{num:56,verse:"по жребию должно разделить им уделы их, как многочисленным, так и малочисленным."},{num:0,verse:""},{num:57,verse:"Сии суть вошедшие в исчисление левиты по поколениям их: от Гирсона поколение Гирсоново, от Каафа поколение Каафово, от Мерари поколение Мерарино."},{num:58,verse:"Вот поколения Левиины: поколение Ливниево, поколение Хевроново, поколение Махлиево, поколение Мушиево, поколение Кореево. От Каафа родился Амрам."},{num:59,verse:"Имя жены Амрамовой Иохаведа, дочь Левиина, которую родила <i>жена</i> Левиина в Египте, а она Амраму родила Аарона, Моисея и Мариам, сестру их."},{num:60,verse:"И родились у Аарона Надав и Авиуд, Елеазар и Ифамар;"},{num:61,verse:"но Надав и Авиуд умерли, когда принесли чуждый огонь пред Господа [в пустыне Синайской]."},{num:62,verse:"И было исчислено двадцать три тысячи всех мужеского пола, от одного месяца и выше; ибо они не были исчислены вместе с сынами Израилевыми, потому что не дано им удела среди сынов Израилевых."},{num:0,verse:""},{num:63,verse:"Вот исчисленные Моисеем и Елеазаром священником, которые исчисляли сынов Израилевых на равнинах Моавитских у Иордана, против Иерихона;"},{num:64,verse:"в числе их не было ни одного человека из исчисленных Моисеем и Аароном священником, которые исчисляли сынов Израилевых в пустыне Синайской;"},{num:65,verse:"ибо Господь сказал им, что умрут они в пустыне, – и не осталось из них никого, кроме Халева, сына Иефонниина, и Иисуса, сына Навина."}]},{chapter:"27",verses:[{num:1,verse:"И пришли дочери Салпаада, сына Хеферова, сына Галаадова, сына Махирова, сына Манассиина из поколения Манассии, сына Иосифова, и вот имена дочерей его: Махла, Ноа, Хогла, Милка и Фирца;"},{num:2,verse:"и предстали пред Моисея и пред Елеазара священника, и пред князей и пред все общество, у входа скинии собрания, и сказали:"},{num:3,verse:"отец наш умер в пустыне, и он не был в числе сообщников, собравшихся против Господа со скопищем Кореевым, но за свой грех умер, и сыновей у него не было;"},{num:4,verse:"за что исчезать имени отца нашего из племени его, потому что нет у него сына? дай нам удел среди братьев отца нашего."},{num:5,verse:"И представил Моисей дело их Господу."},{num:0,verse:""},{num:6,verse:"И сказал Господь Моисею:"},{num:7,verse:"правду говорят дочери Салпаадовы; дай им наследственный удел среди братьев отца их и передай им удел отца их;"},{num:8,verse:"и сынам Израилевым объяви и скажи: если кто умрет, не имея у себя сына, то передавайте удел его дочери его;"},{num:9,verse:"если же нет у него дочери, передавайте удел его братьям его;"},{num:10,verse:"если же нет у него братьев, отдайте удел его братьям отца его;"},{num:11,verse:"если же нет братьев отца его, отдайте удел его близкому его родственнику из поколения его, чтоб он наследовал его; и да будет это для сынов Израилевых постановлено в закон, как повелел Господь Моисею."},{num:0,verse:""},{num:12,verse:"И сказал Господь Моисею: взойди на сию гору Аварим, [которая по эту сторону Иордана, на сию гору Ново,] и посмотри на землю [Ханаанскую], которую Я даю сынам Израилевым [во владение];"},{num:13,verse:"и когда посмотришь на нее, приложись к народу своему и ты, как приложился Аарон, брат твой, [на горе Ор];"},{num:14,verse:"потому что вы не послушались повеления Моего в пустыне Син, во время распри общества, чтоб явить пред глазами их святость Мою при водах. [Это воды Меривы при Кадесе в пустыне Син.]"},{num:15,verse:"И сказал Моисей Господу, говоря:"},{num:16,verse:"да поставит Господь, Бог духов всякой плоти, над обществом сим человека,"},{num:17,verse:"который выходил бы пред ними и который входил бы пред ними, который выводил бы их и который приводил бы их, чтобы не осталось общество Господне, как овцы, у которых нет пастыря."},{num:18,verse:"И сказал Господь Моисею: возьми себе Иисуса, сына Навина, человека, в котором есть Дух, и возложи на него руку твою,"},{num:19,verse:"и поставь его пред Елеазаром священником и пред всем обществом, и дай ему наставление пред глазами их,"},{num:20,verse:"и дай ему от славы твоей, чтобы слушало его все общество сынов Израилевых;"},{num:21,verse:"и будет он обращаться к Елеазару священнику и спрашивать его о решении, посредством урима пред Господом; и по его слову должны выходить, и по его слову должны входить он и все сыны Израилевы с ним и все общество."},{num:22,verse:"И сделал Моисей, как повелел ему Господь [Бог], и взял Иисуса, и поставил его пред Елеазаром священником и пред всем обществом;"},{num:23,verse:"и возложил на него руки свои и дал ему наставление, как говорил Господь чрез Моисея."}]},{chapter:"28",verses:[{num:1,verse:"И сказал Господь Моисею, говоря:"},{num:2,verse:"повели сынам Израилевым и скажи им: наблюдайте, чтобы приношение Мое, хлеб Мой в жертву Мне, в приятное благоухание Мне, приносимо было Мне в свое время."},{num:3,verse:"И скажи им: вот жертва, которую вы должны приносить Господу: два агнца однолетних без порока на день, во всесожжение постоянное;"},{num:4,verse:"одного агнца приноси утром, а другого агнца приноси вечером;"},{num:5,verse:"и в приношение хлебное [приноси] десятую часть <i>ефы</i> пшеничной муки, смешанной с четвертью гина выбитого елея;"},{num:6,verse:"это – всесожжение постоянное, какое совершено было при горе Синае, в приятное благоухание, в жертву Господу;"},{num:7,verse:"и возлияния при ней четверть гина на одного агнца: на святом месте возливай возлияние, вино Господу."},{num:8,verse:"Другого агнца приноси вечером, с таким хлебным приношением, как поутру, и с таким же возлиянием при нем приноси его в жертву, в приятное благоухание Господу."},{num:9,verse:"А в субботу [приносите] двух агнцев однолетних без порока, и в приношение хлебное две десятых части <i>ефы</i> пшеничной муки, смешанной с елеем, и возлияние при нем:"},{num:10,verse:"это – субботнее всесожжение в каждую субботу, сверх постоянного всесожжения и возлияния при нем."},{num:11,verse:"И в новомесячия ваши приносите всесожжение Господу: из крупного скота двух тельцов, одного овна и семь однолетних агнцев без порока,"},{num:12,verse:"и три десятых части <i>ефы</i> пшеничной муки, смешанной с елеем, в приношение хлебное на одного тельца, и две десятых части <i>ефы</i> пшеничной муки, смешанной с елеем, в приношение хлебное на овна,"},{num:13,verse:"и по десятой части <i>ефы</i> пшеничной муки, смешанной с елеем, в приношение хлебное на каждого агнца; <i>это</i> – всесожжение, приятное благоухание, жертва Господу;"},{num:14,verse:"и возлияния при них должно быть пол-гина вина на тельца, треть гина на овна и четверть гина на агнца; это всесожжение в каждое новомесячие <i>во</i> <i>все</i> месяцы года."},{num:15,verse:"И одного козла приносите Господу в жертву за грех; сверх всесожжения постоянного должно приносить его с возлиянием его."},{num:0,verse:""},{num:16,verse:"В первый месяц, в четырнадцатый день месяца – Пасха Господня."},{num:17,verse:"И в пятнадцатый день сего месяца праздник; семь дней должно есть опресноки."},{num:18,verse:"В первый день [да будет у вас] священное собрание; никакой работы не работайте;"},{num:19,verse:"и приносите жертву, всесожжение Господу: из крупного скота двух тельцов, одного овна и семь однолетних агнцев; без порока они должны быть у вас;"
},{num:20,verse:"и при них в приношение хлебное приносите пшеничной муки, смешанной с елеем, три десятых части <i>ефы</i> на каждого тельца, и две десятых части <i>ефы</i> на овна,"},{num:21,verse:"и по десятой части <i>ефы</i> приноси на каждого из семи агнцев,"},{num:22,verse:"и одного козла в жертву за грех, для очищения вас;"},{num:23,verse:"сверх утреннего всесожжения, которое есть всесожжение постоянное, приносите сие."},{num:24,verse:"Так приносите и в каждый из семи дней; <i>это</i> <i>хлеб</i>, жертва, приятное благоухание Господу; сверх всесожжения постоянного и возлияния его, должно приносить <i>сие</i>."},{num:25,verse:"И в седьмой день да будет у вас священное собрание; никакой работы не работайте."},{num:26,verse:"И в день первых плодов, когда приносите Господу новое приношение хлебное в седмицы ваши, да будет у вас священное собрание; никакой работы не работайте;"},{num:27,verse:"и приносите всесожжение в приятное благоухание Господу: из крупного скота двух тельцов, одного овна и семь однолетних агнцев [без порока],"},{num:28,verse:"и при них в приношение хлебное пшеничной муки, смешанной с елеем, три десятых части <i>ефы</i> на каждого тельца, две десятых части <i>ефы</i> на овна,"},{num:29,verse:"и по десятой части <i>ефы</i> на каждого из семи агнцев,"},{num:30,verse:"и одного козла [в жертву за грех], для очищения вас;"},{num:31,verse:"сверх постоянного всесожжения и хлебного приношения при нем, приносите [сие Мне] с возлиянием их; без порока должны быть они у вас."}]},{chapter:"29",verses:[{num:1,verse:"И в седьмой месяц, в первый [день] месяца, да будет у вас священное собрание; никакой работы не работайте; пусть будет <i>это</i> у вас день трубного звука;"},{num:2,verse:"и приносите всесожжение в приятное благоухание Господу: одного тельца, одного овна, семь однолетних агнцев, без порока,"},{num:3,verse:"и при них в приношение хлебное пшеничной муки, смешанной с елеем, три десятых части <i>ефы</i> на тельца, две десятых части <i>ефы</i> на овна,"},{num:4,verse:"и одну десятую часть <i>ефы</i> на каждого из семи агнцев,"},{num:5,verse:"и одного козла в жертву за грех, для очищения вас,"},{num:6,verse:"сверх новомесячного всесожжения и хлебного приношения его, и <i>сверх</i> постоянного всесожжения и хлебного приношения его, и возлияний их, по уставу, в приятное благоухание Господу."},{num:7,verse:"И в десятый [день] сего седьмого месяца пусть будет у вас священное собрание: смиряйте <i>тогда</i> души ваши и никакого дела не делайте;"},{num:8,verse:"и приносите всесожжение Господу в приятное благоухание: одного тельца, одного овна, семь однолетних агнцев; без порока пусть будут они у вас;"},{num:9,verse:"и при них в приношение хлебное пшеничной муки, смешанной с елеем, три десятых части <i>ефы</i> на тельца, две десятых части <i>ефы</i> на овна,"},{num:10,verse:"и по десятой части <i>ефы</i> на каждого из семи агнцев,"},{num:11,verse:"и одного козла в жертву за грех, [для очищения вас,] сверх жертвы за грех, <i>приносимой</i> <i>в</i> <i>день</i> очищения, и <i>сверх</i> всесожжения постоянного и хлебного приношения его, и возлияния их, [по уставу <i>приносимых</i> в приятное благоухание, в жертву Господу]."},{num:12,verse:"И в пятнадцатый день седьмого месяца пусть будет у вас священное собрание; никакой работы не работайте и празднуйте праздник Господень семь дней;"},{num:13,verse:"и приносите всесожжение, жертву, приятное благоухание Господу: тринадцать тельцов, двух овнов, четырнадцать однолетних агнцев; без порока пусть будут они;"},{num:14,verse:"и при них в приношение хлебное пшеничной муки, смешанной с елеем, три десятых части <i>ефы</i> на каждого из тринадцати тельцов, две десятых части <i>ефы</i> на каждого из двух овнов,"},{num:15,verse:"и по десятой части <i>ефы</i> на каждого из четырнадцати агнцев,"},{num:16,verse:"и одного козла в жертву за грех, сверх всесожжения постоянного и хлебного приношения его и возлияния его."},{num:17,verse:"И во второй день двенадцать тельцов, двух овнов, четырнадцать однолетних агнцев, без порока,"},{num:18,verse:"и при них приношение хлебное и возлияние для тельцов, овнов и агнцев, по числу их, по уставу,"},{num:19,verse:"и одного козла в жертву за грех, сверх всесожжения постоянного и хлебного приношения и возлияния их."},{num:20,verse:"И в третий день одиннадцать тельцов, двух овнов, четырнадцать однолетних агнцев, без порока,"},{num:21,verse:"и при них приношение хлебное и возлияние для тельцов, овнов и агнцев, по числу их, по уставу,"},{num:22,verse:"и одного козла в жертву за грех, сверх всесожжения постоянного и хлебного приношения и возлияния его."},{num:23,verse:"И в четвертый день десять тельцов, двух овнов, четырнадцать однолетних агнцев, без порока,"},{num:24,verse:"и при них приношение хлебное и возлияние для тельцов, овнов и агнцев, по числу их, по уставу,"},{num:25,verse:"и одного козла в жертву за грех, сверх всесожжения постоянного и хлебного приношения и возлияния его."},{num:26,verse:"И в пятый день девять тельцов, двух овнов, четырнадцать однолетних агнцев, без порока,"},{num:27,verse:"и при них приношение хлебное и возлияние для тельцов, овнов и агнцев, по числу их, по уставу,"},{num:28,verse:"и одного козла в жертву за грех, сверх всесожжения постоянного и хлебного приношения и возлияния его."},{num:29,verse:"И в шестой день восемь тельцов, двух овнов, четырнадцать однолетних агнцев, без порока,"},{num:30,verse:"и при них приношение хлебное и возлияние для тельцов, овнов и агнцев, по числу их, по уставу,"},{num:31,verse:"и одного козла в жертву за грех, сверх всесожжения постоянного и хлебного приношения и возлияния его."},{num:32,verse:"И в седьмой день семь тельцов, двух овнов, четырнадцать однолетних агнцев, без порока,"},{num:33,verse:"и при них приношение хлебное и возлияние для тельцов, овнов и агнцев, по числу их, по уставу,"},{num:34,verse:"и одного козла в жертву за грех, сверх всесожжения постоянного и хлебного приношения и возлияния его."},{num:35,verse:"В восьмой день пусть будет у вас отдание праздника; никакой работы не работайте;"},{num:36,verse:"и приносите всесожжение, жертву, приятное благоухание Господу: одного тельца, одного овна, семь однолетних агнцев, без порока,"},{num:37,verse:"и при них приношение хлебное и возлияние для тельца, овна и агнцев по числу их, по уставу,"},{num:38,verse:"и одного козла в жертву за грех, сверх всесожжения постоянного и приношения хлебного и возлияния его."},{num:39,verse:"Приносите это Господу в праздники ваши, сверх <i>приносимых</i> вами, по обету или по усердию, всесожжений ваших и хлебных приношений ваших, и возлияний ваших и мирных жертв ваших."}]},{chapter:"30",verses:[{num:1,verse:"И пересказал Моисей сынам Израилевым все, что повелел Господь Моисею."},{num:2,verse:"И сказал Моисей начальникам колен сынов Израилевых, говоря: вот что повелел Господь:"},{num:3,verse:"если кто даст обет Господу, или поклянется клятвою, положив зарок на душу свою, то он не должен нарушать слова своего, но должен исполнить все, что вышло из уст его."},{num:4,verse:"Если женщина даст обет Господу и положит <i>на</i> <i>себя</i> зарок в доме отца своего, в юности своей,"},{num:5,verse:"и услышит отец обет ее и зарок, который она положила на душу свою, и промолчит о том отец ее, то все обеты ее состоятся, и всякий зарок ее, который она положила на душу свою, состоится;"},{num:6,verse:"если же отец ее, услышав, запретит ей, то все обеты ее и зароки, которые она возложила на душу свою, не состоятся, и Господь простит ей, потому что запретил ей отец ее."},{num:7,verse:"Если она выйдет в замужество, а на ней обет ее, или слово уст ее, которым она связала себя,"},{num:8,verse:"и услышит муж ее и, услышав, промолчит: то обеты ее состоятся, и зароки ее, которые она возложила на душу свою, состоятся;"},{num:9,verse:"если же муж ее, услышав, запретит ей и отвергнет обет ее, который на ней, и слово уст ее, которым она связала себя, [то они не состоятся, потому что запретил ей муж ее,] и Господь простит ей."},{num:10,verse:"Обет же вдовы и разведенной, какой бы она ни возложила зарок на душу свою, состоится."},{num:11,verse:"Если <i>жена</i> в доме мужа своего дала обет, или возложила зарок на душу свою с клятвою,"},{num:12,verse:"и муж ее слышал, и промолчал о том, и не запретил ей, то все обеты ее состоятся, и всякий зарок, который она возложила на душу свою, состоится;"},{num:13,verse:"если же муж ее, услышав, отвергнул их, то все вышедшие из уст ее обеты ее и зароки души ее не состоятся: муж ее уничтожил их, и Господь простит ей."},{num:14,verse:"Всякий обет и всякий клятвенный зарок, чтобы смирить душу, муж ее может утвердить, и муж ее может отвергнуть;"},{num:15,verse:"если же муж ее молчал о том день за день, то он <i>тем</i> утвердил все обеты ее и все зароки ее, которые на ней, утвердил, потому что он, услышав, молчал о том;"},{num:16,verse:"а если [муж] отвергнул их, после того как услышал, то он взял на себя грех ее."},{num:0,verse:""},{num:17,verse:"Вот уставы, которые Господь заповедал Моисею об отношении между мужем и женою его, между отцом и дочерью его в юности ее, в доме отца ее."}]},{chapter:"31",verses:[{num:1,verse:"И сказал Господь Моисею, говоря:"},{num:2,verse:"отмсти Мадианитянам за сынов Израилевых, и после отойдешь к народу твоему."},{num:3,verse:"И сказал Моисей народу, говоря: вооружите из себя людей на войну, чтобы они пошли против Мадианитян, совершить мщение Господне над Мадианитянами;"},{num:4,verse:"по тысяче из колена, от всех колен [сынов] Израилевых пошлите на войну."},{num:5,verse:"И выделено из тысяч Израилевых, по тысяче из колена, двенадцать тысяч вооруженных на войну."},{num:6,verse:"И послал их Моисей на войну, по тысяче из колена, их и Финееса, сына Елеазара, [сына Аарона,] священника, на войну, и в руке его священные сосуды и трубы для тревоги."},{num:7,verse:"И пошли войною на Мадиама, как повелел Господь Моисею, и убили всех мужеского пола;"},{num:8,verse:"и вместе с убитыми их убили царей Мадиамских: Евия, Рекема, Цура, Хура и Реву, пять царей Мадиамских, и Валаама, сына Веорова, убили мечом [вместе с убитыми их];"},{num:9,verse:"а жен Мадиамских и детей их сыны Израилевы взяли в плен, и весь скот их, и все стада их и все имение их взяли в добычу,"},{num:10,verse:"и все города их во владениях их и все селения их сожгли огнем;"},{num:11,verse:"и взяли все захваченное и всю добычу, от человека до скота;"},{num:12,verse:"и доставили пленных и добычу и захваченное к Моисею и к Елеазару священнику и к обществу сынов Израилевых, к стану, на равнины Моавитские, что у Иордана, против Иерихона."},{num:13,verse:"И вышли Моисей и Елеазар священник и все князья общества навстречу им из стана."},{num:14,verse:"И прогневался Моисей на военачальников, тысяченачальников и стоначальников, пришедших с войны,"},{num:15,verse:"и сказал им Моисей: [для чего] вы оставили в живых всех женщин?"},{num:16,verse:"вот они, по совету Валаамову, были для сынов Израилевых поводом к отступлению от Господа в угождение Фегору, <i>за</i> <i>что</i> и поражение было в обществе Господнем;"},{num:17,verse:"итак убейте всех детей мужеского пола, и всех женщин, познавших мужа на мужеском ложе, убейте;"},{num:18,verse:"а всех детей женского пола, которые не познали мужеского ложа, оставьте в живых для себя;"},{num:19,verse:"и пробудьте вне стана семь дней; всякий, убивший человека и прикоснувшийся к убитому, очиститесь в третий день и в седьмой день, вы и пленные ваши;"},{num:20,verse:"и все одежды, и все кожаные вещи, и все сделанное из козьей <i>шерсти</i>, и все деревянные сосуды очистите."},{num:21,verse:"И сказал Елеазар священник воинам, ходившим на войну: вот постановление закона, который заповедал Господь Моисею:"},{num:22,verse:"золото, серебро, медь, железо, олово и свинец,"},{num:23,verse:"и все, что проходит через огонь, проведите через огонь, чтоб оно очистилось, а кроме того и очистительною водою должно очистить; все же, что не проходит через огонь, проведите через воду;"},{num:24,verse:"и одежды ваши вымойте в седьмой день, и очиститесь, и после того входите в стан."},{num:0,verse:""},{num:25,verse:"И сказал Господь Моисею, говоря:"},{num:26,verse:"сочти добычу плена, от человека до скота, ты и Елеазар священник и начальники племен общества;"},{num:27,verse:"и раздели добычу пополам между воевавшими, ходившими на войну, и между всем обществом;"},{num:28,verse:"и от воинов, ходивших на войну, возьми дань Господу, по одной душе из пятисот, из людей и из крупного скота, и из ослов и из мелкого скота;"},{num:29,verse:"возьми это из половины их и отдай Елеазару священнику в возношение Господу;"},{num:30,verse:"и из половины сынов Израилевых возьми по одной доле из пятидесяти, из людей, из крупного скота, из ослов и из мелкого скота, и отдай это левитам, служащим при скинии Господней."},{num:31,verse:"И сделал Моисей и Елеазар священник, как повелел Господь Моисею."},{num:32,verse:"И было добычи, оставшейся от захваченного, что захватили бывшие на войне: мелкого скота шестьсот семьдесят пять тысяч,"},{num:33,verse:"крупного скота семьдесят две тысячи,"},{num:34,verse:"ослов шестьдесят одна тысяча,"},{num:35,verse:"людей, женщин, которые не знали мужеского ложа, всех душ тридцать две тысячи."},{num:36,verse:"Половина, доля ходивших на войну, по расчислению была: мелкого скота триста тридцать семь тысяч пятьсот,"},{num:37,verse:"и дань Господу из мелкого скота шестьсот семьдесят пять;"},{num:38,verse:"крупного скота тридцать шесть тысяч, и дань из них Господу семьдесят два;"},{num:39,verse:"ослов тридцать тысяч пятьсот, и дань из них Господу шестьдесят один;"},{num:40,verse:"людей шестнадцать тысяч, и дань из них Господу тридцать две души."},{num:41,verse:"И отдал Моисей дань, возношение Господу, Елеазару священнику, как повелел Господь Моисею."},{num:42,verse:"И из половины сынов Израилевых, которую отделил Моисей у бывших на войне;"},{num:43,verse:"половина же <i>на</i> <i>долю</i> общества была: мелкого скота триста тридцать семь тысяч пятьсот,"},{num:44,verse:"крупного скота тридцать шесть тысяч,"},{num:45,verse:"ослов тридцать тысяч пятьсот,"},{num:46,verse:"людей шестнадцать тысяч."},{num:47,verse:"Из половины сынов Израилевых взял Моисей одну пятидесятую часть из людей и из скота и отдал это левитам, исполняющим службу при скинии Господней, как повелел Господь Моисею."},{num:48,verse:"И пришли к Моисею начальники над тысячами войска, тысяченачальники и стоначальники,"},{num:49,verse:"и сказали Моисею: рабы твои сосчитали воинов, которые нам поручены, и не убыло ни одного из них;"},{num:50,verse:"и <i>вот</i>, мы принесли приношение Господу, кто что достал из золотых вещей: цепочки, запястья, перстни, серьги и привески, для очищения душ наших пред Господом."},{num:51,verse:"И взял у них Моисей и Елеазар священник золото во всех этих изделиях;"},{num:52,verse:"и было всего золота, которое принесено в возношение Господу, шестнадцать тысяч семьсот пятьдесят сиклей, от тысяченачальников и стоначальников."},{num:53,verse:"Воины грабили каждый для себя."},{num:54,verse:"И взял Моисей и Елеазар священник золото от тысяченачальников и стоначальников, и принесли его в скинию собрания, в память сынов Израилевых пред Господом."}]},{chapter:"32",verses:[{num:1,verse:"У сынов Рувимовых и у сынов Гадовых стад было весьма много; и увидели они, что земля Иазер и земля Галаад есть место <i>годное</i> для стад;"},{num:2,verse:"и пришли сыны Гадовы и сыны Рувимовы и сказали Моисею и Елеазару священнику и князьям общества, говоря:"},{num:3,verse:"Атароф и Дивон, и Иазер, и Нимра, и Есевон, и Елеале, и Севам, и Нево, и Веон,"},{num:4,verse:"земля, которую Господь поразил пред обществом Израилевым, есть земля <i>годная</i> для стад, а у рабов твоих есть стада."},{num:5,verse:"И сказали: если мы нашли благоволение в глазах твоих, отдай землю сию рабам твоим во владение; не переводи нас чрез Иордан."},{num:6,verse:"И сказал Моисей сынам Гадовым и сынам Рувимовым: братья ваши пойдут на войну, а вы останетесь здесь?"},{num:7,verse:"для чего вы отвращаете сердце сынов Израилевых от перехода в землю, которую дает им Господь?"},{num:8,verse:"так поступили отцы ваши, когда я посылал их из Кадес-Варни для обозрения земли:"},{num:9,verse:"они доходили до долины Есхол, и видели землю, и отвратили сердце сынов Израилевых, чтобы не шли они в землю, которую Господь дает им;"},{num:10,verse:"и воспылал в тот день гнев Господа, и поклялся Он, говоря:"},{num:11,verse:"люди сии, вышедшие из Египта, от двадцати лет и выше [знающие добро и зло,] не увидят земли, о которой Я клялся Аврааму, Исааку и Иакову, потому что они не повиновались Мне,"},{num:12,verse:"кроме Халева, сына Иефонниина, Кенезеянина, и Иисуса, сына Навина, потому что они повиновались Господу."},{num:13,verse:"И воспылал гнев Господа на Израиля, и водил Он их по пустыне сорок лет, доколе не кончился весь род, сделавший зло в очах Господних."},{num:14,verse:"И вот, вместо отцов ваших восстали вы, отродье грешников, чтоб усилить еще ярость гнева Господня на Израиля."},{num:15,verse:"Если вы отвратитесь от Него, то Он опять оставит его в пустыне, и вы погубите весь народ сей."},{num:16,verse:"И подошли они к нему и сказали: мы построим здесь овчие дворы для стад наших и города для детей наших;"},{num:17,verse:"сами же мы первые вооружимся и пойдем пред сынами Израилевыми, доколе не приведем их в места их; а дети наши пусть останутся в укрепленных городах, <i>для</i> <i>безопасности</i> от жителей земли;"},{num:18,verse:"не возвратимся в домы наши, доколе не вступят сыны Израилевы каждый в удел свой;"},{num:19,verse:"ибо мы не возьмем с ними удела по ту сторону Иордана и далее, если удел нам достанется по эту сторону Иордана, к востоку."},{num:20,verse:"И сказал им Моисей: если вы это сделаете, если вооруженные пойдете на войну пред Господом,"},{num:21,verse:"и пойдет каждый из вас вооруженный за Иордан пред Господом, доколе не истребит Он врагов Своих пред Собою,"},{num:22,verse:"и покорена будет земля пред Господом, то после возвратитесь и будете неповинны пред Господом и пред Израилем, и будет земля сия у вас во владении пред Господом;"},{num:23,verse:"если же не сделаете так, то согрешите пред Господом, и испытаете <i>наказание</i> за грех ваш, которое постигнет вас;"},{num:24,verse:"стройте себе города для детей ваших и дворы для овец ваших и делайте, что произнесено устами вашими."},{num:25,verse:"И сказали сыны Гадовы и сыны Рувимовы Моисею: рабы твои сделают, как повелевает господин наш;"},{num:26,verse:"дети наши, жены наши, стада наши и весь скот наш останутся тут в городах Галаада,"},{num:27,verse:"а рабы твои, все, вооружившись, как воины, пойдут пред Господом на войну, как говорит господин наш."},{num:28,verse:"И дал Моисей о них повеление Елеазару священнику и Иисусу, сыну Навину, и начальникам племен сынов Израилевых,"},{num:29,verse:"и сказал им Моисей: если сыны Гадовы и сыны Рувимовы перейдут с вами за Иордан, все вооружившись на войну пред Господом, и покорена будет пред вами земля, то отдайте им землю Галаад во владение;"},{num:30,verse:"если же не пойдут они с вами вооруженные [на войну пред Господом, то пошлите пред собою имение их, жен их и скот их в землю Ханаанскую], и они получат владение вместе с вами в земле Ханаанской."},{num:31,verse:"И отвечали сыны Гадовы и сыны Рувимовы и сказали: как сказал Господь рабам твоим, так и сделаем;"},{num:32,verse:"мы пойдем вооруженные пред Господом в землю Ханаанскую, а удел владения нашего пусть будет по эту сторону Иордана."},{num:33,verse:"И отдал Моисей им, сынам Гадовым и сынам Рувимовым, и половине колена Манассии, сына Иосифова, царство Сигона, царя Аморрейского, и царство Ога, царя Васанского, землю с городами ее и окрестностями, – города земли во все стороны."},{num:34,verse:"И построили сыны Гадовы Дивон и Атароф, и Ароер,"},{num:35,verse:"и Атароф-Шофан, и Иазер, и Иогбегу,"},{num:36,verse:"и Беф-Нимру и Беф-Гаран, города укрепленные и дворы для овец."},{num:37,verse:"И сыны Рувимовы построили Есевон, Елеале, Кириафаим,"},{num:38,verse:"и Нево, и Ваал-Меон, которых имена переменены, и Сивму, и дали имена городам, которые они построили."},{num:39,verse:"И пошли сыны Махира, сына Манассиина, в Галаад, и взяли его, и выгнали Аморреев, которые были в нем;"},{num:40,verse:"и отдал Моисей Галаад Махиру, сыну Манассии, и он поселился в нем."},{num:41,verse:"И Иаир, сын Манассии, пошел и взял селения их, и назвал их: селения Иаировы."},{num:42,verse:"И Новах пошел и взял Кенаф и зависящие от него города, и назвал его своим именем: Новах."}]},{chapter:"33",verses:[{num:1,verse:"Вот станы сынов Израилевых, которые вышли из земли Египетской по ополчениям своим, под начальством Моисея и Аарона."},{num:2,verse:"Моисей, по повелению Господню, описал путешествие их по станам их, и вот станы путешествия их:"},{num:3,verse:"из Раамсеса отправились они в первый месяц, в пятнадцатый день первого месяца; на другой день Пасхи вышли сыны Израилевы под рукою высокою в глазах всего Египта;"},{num:4,verse:"между тем Египтяне хоронили всех первенцев, которых поразил у них Господь, и над богами их Господь совершил суд."},{num:5,verse:"Так отправились сыны Израилевы из Раамсеса и расположились станом в Сокхофе."},{num:6,verse:"И отправились из Сокхофа и расположились станом в Ефаме, что на краю пустыни."},{num:7,verse:"И отправились из Ефама и обратились к Пи-Гахирофу, что пред Ваал-Цефоном, и расположились станом пред Мигдолом."},{num:8,verse:"Отправившись от Гахирофа, прошли среди моря в пустыню, и шли три дня пути пустынею Ефам, и расположились станом в Мерре."},{num:9,verse:"И отправились из Мерры и пришли в Елим; в Елиме же [было] двенадцать источников воды и семьдесят финиковых дерев, и расположились там станом."},{num:10,verse:"И отправились из Елима и расположились станом у Чермного моря."},{num:11,verse:"И отправились от Чермного моря и расположились станом в пустыне Син."},{num:12,verse:"И отправились из пустыни Син и расположились станом в Дофке."},{num:13,verse:"И отправились из Дофки и расположились станом в Алуше."},{num:14,verse:"И отправились из Алуша и расположились станом в Рефидиме, и не было там воды, чтобы пить народу."},{num:15,verse:"И отправились из Рефидима и расположились станом в пустыне Синайской."},{num:16,verse:"И отправились из пустыни Синайской и расположились станом в Киброт-Гаттааве."},{num:17,verse:"И отправились из Киброт-Гаттаавы и расположились станом в Асирофе."},{num:18,verse:"И отправились из Асирофа и расположились станом в Рифме."},{num:19,verse:"И отправились из Рифмы и расположились станом в Римнон-Фареце."},{num:20,verse:"И отправились из Римнон-Фареца и расположились станом в Ливне."},{num:21,verse:"И отправились из Ливны и расположились станом в Риссе."},{num:22,verse:"И отправились из Риссы и расположились станом в Кегелафе."},{num:23,verse:"И отправились из Кегелафы и расположились станом на горе Шафер."},{num:24,verse:"И отправились от горы Шафер и расположились станом в Хараде."},{num:25,verse:"И отправились из Харады и расположились станом в Макелофе."},{num:26,verse:"И отправились из Макелофа и расположились станом в Тахафе."},{num:27,verse:"И отправились из Тахафа и расположились станом в Тарахе."},{num:28,verse:"И отправились из Тараха и расположились станом в Мифке."},{num:29,verse:"И отправились из Мифки и расположились станом в Хашмоне."},{num:30,verse:"И отправились из Хашмоны и расположились станом в Мосерофе."},{num:31,verse:"И отправились из Мосерофа и расположились станом в Бене-Яакане."},{num:32,verse:"И отправились из Бене-Яакана и расположились станом в Хор-Агидгаде."},{num:33,verse:"И отправились из Хор-Агидгада и расположились станом в Иотвафе."},{num:34,verse:"И отправились от Иотвафы и расположились станом в Авроне."},{num:35,verse:"И отправились из Аврона и расположились станом в Ецион-Гавере."},{num:36,verse:"И отправились из Ецион-Гавера и расположились станом в пустыне Син. [Отправившись из пустыни Син, расположились станом в пустыне Фаран,] она же Кадес."},{num:37,verse:"И отправились из Кадеса и расположились станом на горе Ор, у пределов земли Едомской."},{num:38,verse:"И взошел Аарон священник на гору Ор по повелению Господню и умер там в сороковой год по исшествии сынов Израилевых из земли Египетской, в пятый месяц, в первый день месяца;"},{num:39,verse:"Аарон был ста двадцати трех лет, когда умер на горе Ор."},{num:0,verse:""},{num:40,verse:"Ханаанский царь Арада, который жил к югу земли Ханаанской, услышал тогда, что идут сыны Израилевы."},{num:41,verse:"И отправились они от горы Ор и расположились станом в Салмоне."},{num:42,verse:"И отправились из Салмона и расположились станом в Пуноне."},{num:43,verse:"И отправились из Пунона и расположились станом в Овофе."},{num:44,verse:"И отправились из Овофа и расположились станом в Ийм-Авариме, на пределах Моава."},{num:45,verse:"И отправились из Ийма и расположились станом в Дивон-Гаде."},{num:46,verse:"И отправились из Дивон-Гада и расположились станом в Алмон-Дивлафаиме."},{num:47,verse:"И отправились из Алмон-Дивлафаима и расположились станом на горах Аваримских пред Нево."},{num:48,verse:"И отправились от гор Аваримских и расположились станом на равнинах Моавитских у Иордана, против Иерихона;"},{num:49,verse:"они расположились станом у Иордана от Беф-Иешимофа до Аве-Ситтима на равнинах Моавитских."},{num:0,verse:""},{num:50,verse:"И сказал Господь Моисею на равнинах Моавитских у Иордана, против Иерихона, говоря:"},{num:51,verse:"объяви сынам Израилевым и скажи им: когда перейдете через Иордан в землю Ханаанскую,"},{num:52,verse:"то прогоните от себя всех жителей земли и истребите все изображения их, и всех литых идолов их истребите и все высоты их разорите;"},{num:53,verse:"и возьмите во владение землю и поселитесь на ней, ибо Я вам даю землю сию во владение;"},{num:54,verse:"и разделите землю по жребию на уделы племенам вашим: многочисленному дайте удел более, а малочисленному дай удел менее; кому где выйдет жребий, там ему и будет <i>удел</i>; по коленам отцов ваших возьмите себе уделы;"},{num:55,verse:"если же вы не прогоните от себя жителей земли, то оставшиеся из них будут тернами для глаз ваших и иглами для боков ваших и будут теснить вас на земле, в которой вы будете жить,"},{num:56,verse:"и тогда, что Я вознамерился сделать им, сделаю вам."}]},{chapter:"34",verses:[{num:1,verse:"И сказал Господь Моисею, говоря:"},{num:2,verse:"дай повеление сынам Израилевым и скажи им: когда войдете в землю Ханаанскую, то вот земля, которая достанется вам в удел, земля Ханаанская с ее границами:"},{num:3,verse:"южная сторона будет у вас от пустыни Син, подле Едома, и пойдет у вас южная граница от конца Соленого моря с востока,"},{num:4,verse:"и направится граница на юг к возвышенности Акравима и пойдет через Син, и будут выступы ее на юг к Кадес-Варни, оттуда пойдет к Гацар-Аддару и пройдет через Ацмон;"},{num:5,verse:"от Ацмона направится граница к потоку Египетскому, и будут выступы ее к морю;"},{num:6,verse:"а границею западною будет у вас великое море: это будет у вас граница к западу;"},{num:7,verse:"к северу же будет у вас граница: от великого моря проведите ее к горе Ор,"},{num:8,verse:"от горы Ор проведите к Емафу, и будут выступы границы к Цедаду;"},{num:9,verse:"оттуда пойдет граница к Цифрону, и выступы ее будут к Гацар-Енану: это будет у вас граница северная;"},{num:10,verse:"границу восточную проведите себе от Гацар-Енана к Шефаму,"},{num:11,verse:"от Шефама пойдет граница к Рибле, с восточной стороны Аина, потом пойдет граница и коснется берегов моря Киннереф с восточной стороны;"},{num:12,verse:"и пойдет граница к Иордану, и будут выступы ее к Соленому морю. Это будет земля ваша по границам ее со всех сторон."},{num:13,verse:"И дал повеление Моисей сынам Израилевым и сказал: вот земля, которую вы разделите на уделы по жребию, которую повелел Господь дать девяти коленам и половине колена [Манассиина];"},{num:14,verse:"ибо колено сынов Рувимовых по семействам их, и колено сынов Гадовых по семействам их, и половина колена Манассиина получили удел свой:"},{num:15,verse:"два колена и половина колена получили удел свой за Иорданом против Иерихона к востоку."},{num:0,verse:""},{num:16,verse:"И сказал Господь Моисею, говоря:"},{num:17,verse:"вот имена мужей, которые будут делить вам землю: Елеазар священник и Иисус, сын Навин;"},{num:18,verse:"и по одному князю от колена возьмите для раздела земли."},{num:19,verse:"И вот имена сих мужей: для колена Иудина Халев, сын Иефонниин;"},{num:20,verse:"для колена сынов Симеоновых Самуил, сын Аммиуда;"},{num:21,verse:"для колена Вениаминова Елидад, сын Кислона;"},{num:22,verse:"для колена сынов Дановых князь Буккий, сын Иоглии;"},{num:23,verse:"для сынов Иосифовых, для колена сынов Манассииных князь Ханниил, сын Ефода;"},{num:24,verse:"для колена сынов Ефремовых князь Кемуил, сын Шифтана;"},{num:25,verse:"для колена сынов Завулоновых князь Елицафан, сын Фарнака;"},{num:26,verse:"для колена сынов Иссахаровых князь Фалтиил, сын Аззана;"},{num:27,verse:"для колена сынов Асировых князь Ахиуд, сын Шеломия;"},{num:28,verse:"для колена сынов Неффалимовых князь Педаил, сын Аммиуда;"},{num:29,verse:"вот те, которым повелел Господь разделить уделы сынам Израилевым в земле Ханаанской."}]},{chapter:"35",verses:[{num:1,verse:"И сказал Господь Моисею на равнинах Моавитских у Иордана против Иерихона, говоря:"},{num:2,verse:"повели сынам Израилевым, чтоб они из уделов владения своего дали левитам города для жительства, и поля при городах со всех сторон дайте левитам:"},{num:3,verse:"города будут им для жительства, а поля будут для скота их и для имения их и для всех житейских потребностей их;"},{num:4,verse:"поля при городах, которые вы должны дать левитам, от стены города <i>должны</i> <i>простираться</i> на [две] тысячи локтей, во все стороны;"},{num:5,verse:"и отмерьте за городом к восточной стороне две тысячи локтей, и к южной стороне две тысячи локтей, и к западу две тысячи локтей, и к северной стороне две тысячи локтей, а посредине город: таковы будут у них поля при городах."},{num:6,verse:"Из городов, которые вы дадите левитам, [будут] шесть городов для убежища, в которые вы позволите убегать убийце; и сверх их дайте сорок два города:"},{num:7,verse:"всех городов, которые вы должны дать левитам, <i>будет</i> сорок восемь городов, с полями при них."},{num:8,verse:"И когда будете давать города из владения сынов Израилевых, тогда из большего дайте более, из меньшего менее; каждое колено, смотря по уделу, какой получит, должно дать из городов своих левитам."},{num:0,verse:""},{num:9,verse:"И сказал Господь Моисею, говоря:"},{num:10,verse:"объяви сынам Израилевым и скажи им: когда вы перейдете чрез Иордан в землю Ханаанскую,"},{num:11,verse:"выберите себе города, которые были бы у вас городами для убежища, куда мог бы убежать убийца, убивший человека неумышленно;"},{num:12,verse:"и будут у вас города сии убежищем от мстителя [за кровь], чтобы не был умерщвлен убивший, прежде нежели он предстанет пред общество на суд."},{num:13,verse:"Городов же, которые должны вы дать, городов для убежища, должно быть у вас шесть:"},{num:14,verse:"три города дайте по эту сторону Иордана и три города дайте в земле Ханаанской; городами убежища должны быть они;"},{num:15,verse:"для сынов Израилевых и для пришельца и для поселенца между вами будут сии шесть городов убежищем, чтобы убегать туда всякому, убившему человека неумышленно."},{num:16,verse:"Если кто ударит кого железным орудием так, что тот умрет, то он убийца: убийцу должно предать смерти;"},{num:17,verse:"и если кто ударит кого из руки камнем, от которого можно умереть, так что тот умрет, то он убийца: убийцу должно предать смерти;"},{num:18,verse:"или если деревянным орудием, от которого можно умереть, ударит из руки так, что тот умрет, то он убийца: убийцу должно предать смерти;"},{num:19,verse:"мститель за кровь сам может умертвить убийцу: лишь только встретит его, сам может умертвить его;"},{num:20,verse:"если кто толкнет кого по ненависти, или с умыслом бросит на него <i>что-нибудь</i> так, что тот умрет,"},{num:21,
verse:"или по вражде ударит его рукою так, что тот умрет, то ударившего должно предать смерти: он убийца; мститель за кровь может умертвить убийцу, лишь только встретит его."},{num:22,verse:"Если же он толкнет его нечаянно, без вражды, или бросит на него что-нибудь без умысла,"},{num:23,verse:"или какой-нибудь камень, от которого можно умереть, не видя уронит на него так, что тот умрет, но он не был врагом его и не желал ему зла,"},{num:24,verse:"то общество должно рассудить между убийцею и мстителем за кровь по сим постановлениям;"},{num:25,verse:"и должно общество спасти убийцу от руки мстителя за кровь, и должно возвратить его общество в город убежища его, куда он убежал, чтоб он жил там до смерти великого священника, который помазан священным елеем;"},{num:26,verse:"если же убийца выйдет за предел города убежища, в который он убежал,"},{num:27,verse:"и найдет его мститель за кровь вне пределов города убежища его, и убьет убийцу сего мститель за кровь, то не будет на нем <i>вины</i> кровопролития,"},{num:28,verse:"ибо тот должен был жить в городе убежища своего до смерти великого священника, а по смерти великого священника должен был возвратиться убийца в землю владения своего."},{num:29,verse:"Да будет это у вас постановлением законным в роды ваши, во всех жилищах ваших."},{num:30,verse:"Если кто убьет человека, то убийцу должно убить по словам свидетелей; но одного свидетеля недостаточно, <i>чтобы</i> <i>осудить</i> на смерть."},{num:31,verse:"И не берите выкупа за душу убийцы, который повинен смерти, но его должно предать смерти;"},{num:32,verse:"и не берите выкупа за убежавшего в город убежища, чтоб ему <i>позволить</i> жить в земле <i>своей</i> прежде смерти [великого] священника."},{num:33,verse:"Не оскверняйте земли, на которой вы [будете жить]; ибо кровь оскверняет землю, и земля не иначе очищается от пролитой на ней крови, как кровью пролившего ее."},{num:34,verse:"Не должно осквернять землю, на которой вы живете, среди которой обитаю Я; ибо Я Господь обитаю среди сынов Израилевых."}]},{chapter:"36",verses:[{num:1,verse:"Пришли главы семейств от племени сынов Галаада, сына Махирова, сына Манассиина из племен сынов Иосифовых, и говорили пред Моисеем [и пред Елеазаром священником] и пред князьями, главами поколений сынов Израилевых,"},{num:2,verse:"и сказали: Господь повелел господину нашему дать землю в удел сынам Израилевым по жребию, и господину нашему повелено от Господа дать удел Салпаада, брата нашего, дочерям его;"},{num:3,verse:"если же они будут женами сынов которого-нибудь <i>другого</i> колена сынов Израилевых, то удел их отнимется от удела отцов наших и прибавится к уделу того колена, в котором они будут [женами], и отнимется от доставшегося по жребию удела нашего;"},{num:4,verse:"и даже когда будет у сынов Израилевых юбилей, тогда удел их прибавится к уделу того колена, в котором они будут [женами], и от удела колена отцов наших отнимется удел их."},{num:5,verse:"И дал Моисей повеление сынам Израилевым, по слову Господню, и сказал: правду говорит колено сынов Иосифовых;"},{num:6,verse:"вот что заповедует Господь о дочерях Салпаадовых: они могут быть женами тех, кто понравится глазам их, только должны быть женами в племени колена отца своего,"},{num:7,verse:"чтобы удел сынов Израилевых не переходил из колена в колено; ибо каждый из сынов Израилевых должен быть привязан к уделу колена отцов своих;"},{num:8,verse:"и всякая дочь, наследующая удел в коленах сынов Израилевых, должна быть женою кого-нибудь из племени колена отца своего, чтобы сыны Израилевы наследовали каждый удел отцов своих,"},{num:9,verse:"и чтобы не переходил удел из колена в другое колено; ибо каждое из колен сынов Израилевых должно быть привязано к своему уделу."},{num:10,verse:"Как повелел Господь Моисею, так и сделали дочери Салпаадовы."},{num:11,verse:"И вышли дочери Салпаадовы Махла, Фирца, Хогла, Милка и Ноа в замужество за сыновей дядей своих;"},{num:12,verse:"в племени сынов Манассии, сына Иосифова, они были женами, и остался удел их в колене племени отца их."},{num:0,verse:""},{num:13,verse:"Сии суть заповеди и постановления, которые дал Господь сынам Израилевым чрез Моисея на равнинах Моавитских, у Иордана, против Иерихона."}]}]};
