export const prit={treat:"Ветхий завет",title:"Притчи Соломона",chapters:[{chapter:"1",verses:[{num:1,verse:"Притчи Соломона, сына Давидова, царя Израильского,"},{num:2,verse:"чтобы познать мудрость и наставление, понять изречения разума;"},{num:3,verse:"усвоить правила благоразумия, правосудия, суда и правоты;"},{num:4,verse:"простым дать смышленость, юноше – знание и рассудительность;"},{num:5,verse:"послушает мудрый – и умножит познания, и разумный найдет мудрые советы;"},{num:6,verse:"чтобы разуметь притчу и замысловатую речь, слова мудрецов и загадки их."},{num:0,verse:""},{num:7,verse:"Начало мудрости – страх Господень; [доброе разумение у всех, водящихся им; а благоговение к Богу – начало разумения;] глупцы только презирают мудрость и наставление."},{num:8,verse:"Слушай, сын мой, наставление отца твоего и не отвергай завета матери твоей,"},{num:9,verse:"потому что это – прекрасный венок для головы твоей и украшение для шеи твоей."},{num:10,verse:"Сын мой! если будут склонять тебя грешники, не соглашайся;"},{num:11,verse:'если будут говорить: "иди с нами, сделаем засаду для убийства, подстережем непорочного без вины,'},{num:12,verse:"живых проглотим их, как преисподняя, и – целых, как нисходящих в могилу;"},{num:13,verse:"наберем всякого драгоценного имущества, наполним домы наши добычею;"},{num:14,verse:'жребий твой ты будешь бросать вместе с нами, склад один будет у всех нас", -'},{num:15,verse:"сын мой! не ходи в путь с ними, удержи ногу твою от стези их,"},{num:16,verse:"потому что ноги их бегут ко злу и спешат на пролитие крови."},{num:17,verse:"В глазах всех птиц напрасно расставляется сеть,"},{num:18,verse:"а делают засаду для их крови и подстерегают их души."},{num:19,verse:"Таковы пути всякого, кто алчет чужого добра: оно отнимает жизнь у завладевшего им."},{num:0,verse:""},{num:20,verse:"Премудрость возглашает на улице, на площадях возвышает голос свой,"},{num:21,verse:"в главных местах собраний проповедует, при входах в городские ворота говорит речь свою:"},{num:22,verse:'"доколе, невежды, будете любить невежество? <i>доколе</i> буйные будут услаждаться буйством? доколе глупцы будут ненавидеть знание?'},{num:23,verse:"Обратитесь к моему обличению: вот, я изолью на вас дух мой, возвещу вам слова мои."},{num:24,verse:"Я звала, и вы не послушались; простирала руку мою, и не было внимающего;"},{num:25,verse:"и вы отвергли все мои советы, и обличений моих не приняли."},{num:26,verse:"За то и я посмеюсь вашей погибели; порадуюсь, когда придет на вас ужас;"},{num:27,verse:"когда придет на вас ужас, как буря, и беда, как вихрь, принесется на вас; когда постигнет вас скорбь и теснота."},{num:28,verse:"Тогда будут звать меня, и я не услышу; с утра будут искать меня, и не найдут меня."},{num:29,verse:"За то, что они возненавидели знание и не избрали <i>для себя</i> страха Господня,"},{num:30,verse:"не приняли совета моего, презрели все обличения мои;"},{num:31,verse:"за то и будут они вкушать от плодов путей своих и насыщаться от помыслов их."},{num:32,verse:"Потому что упорство невежд убьет их, и беспечность глупцов погубит их,"},{num:33,verse:'а слушающий меня будет жить безопасно и спокойно, не страшась зла".'}]},{chapter:"2",verses:[{num:1,verse:"Сын мой! если ты примешь слова мои и сохранишь при себе заповеди мои,"},{num:2,verse:"так что ухо твое сделаешь внимательным к мудрости и наклонишь сердце твое к размышлению;"},{num:3,verse:"если будешь призывать знание и взывать к разуму;"},{num:4,verse:"если будешь искать его, как серебра, и отыскивать его, как сокровище,"},{num:5,verse:"то уразумеешь страх Господень и найдешь познание о Боге."},{num:6,verse:"Ибо Господь дает мудрость; из уст Его – знание и разум;"},{num:7,verse:"Он сохраняет для праведных спасение; Он – щит для ходящих непорочно;"},{num:8,verse:"Он охраняет пути правды и оберегает стезю святых Своих."},{num:9,verse:"Тогда ты уразумеешь правду и правосудие и прямоту, всякую добрую стезю."},{num:10,verse:"Когда мудрость войдет в сердце твое, и знание будет приятно душе твоей,"},{num:11,verse:"тогда рассудительность будет оберегать тебя, разум будет охранять тебя,"},{num:12,verse:"дабы спасти тебя от пути злого, от человека, говорящего ложь,"},{num:13,verse:"от тех, которые оставляют стези прямые, чтобы ходить путями тьмы;"},{num:14,verse:"от тех, которые радуются, делая зло, восхищаются злым развратом,"},{num:15,verse:"которых пути кривы, и которые блуждают на стезях своих;"},{num:16,verse:"дабы спасти тебя от жены другого, от чужой, которая умягчает речи свои,"},{num:17,verse:"которая оставила руководителя юности своей и забыла завет Бога своего."},{num:18,verse:"Дом ее ведет к смерти, и стези ее – к мертвецам;"},{num:19,verse:"никто из вошедших к ней не возвращается и не вступает на путь жизни."},{num:20,verse:"Посему ходи путем добрых и держись стезей праведников,"},{num:21,verse:"потому что праведные будут жить на земле, и непорочные пребудут на ней;"},{num:22,verse:"а беззаконные будут истреблены с земли, и вероломные искоренены из нее."}]},{chapter:"3",verses:[{num:1,verse:"Сын мой! наставления моего не забывай, и заповеди мои да хранит сердце твое;"},{num:2,verse:"ибо долготы дней, лет жизни и мира они приложат тебе."},{num:3,verse:"Милость и истина да не оставляют тебя: обвяжи ими шею твою, напиши их на скрижали сердца твоего,"},{num:4,verse:"и обретешь милость и благоволение в очах Бога и людей."},{num:5,verse:"Надейся на Господа всем сердцем твоим, и не полагайся на разум твой."},{num:6,verse:"Во всех путях твоих познавай Его, и Он направит стези твои."},{num:7,verse:"Не будь мудрецом в глазах твоих; бойся Господа и удаляйся от зла:"},{num:8,verse:"это будет здравием для тела твоего и питанием для костей твоих."},{num:9,verse:"Чти Господа от имения твоего и от начатков всех прибытков твоих,"},{num:10,verse:"и наполнятся житницы твои до избытка, и точила твои будут переливаться новым вином."},{num:0,verse:""},{num:11,verse:"Наказания Господня, сын мой, не отвергай, и не тяготись обличением Его;"},{num:12,verse:"ибо кого любит Господь, того наказывает и благоволит к тому, как отец к сыну своему."},{num:13,verse:"Блажен человек, который снискал мудрость, и человек, который приобрел разум, -"},{num:14,verse:"потому что приобретение ее лучше приобретения серебра, и прибыли от нее больше, нежели от золота:"},{num:15,verse:"она дороже драгоценных камней; [никакое зло не может противиться ей; она хорошо известна всем, приближающимся к ней,] и ничто из желаемого тобою не сравнится с нею."},{num:16,verse:"Долгоденствие – в правой руке ее, а в левой у нее – богатство и слава; [из уст ее выходит правда; закон и милость она на языке носит;]"},{num:17,verse:"пути ее – пути приятные, и все стези ее – мирные."},{num:18,verse:"Она – древо жизни для тех, которые приобретают ее, – и блаженны, которые сохраняют ее!"},{num:0,verse:""},{num:19,verse:"Господь премудростью основал землю, небеса утвердил разумом;"},{num:20,verse:"Его премудростью разверзлись бездны, и облака кропят росою."},{num:21,verse:"Сын мой! не упускай их из глаз твоих; храни здравомыслие и рассудительность,"},{num:22,verse:"и они будут жизнью для души твоей и украшением для шеи твоей."},{num:23,verse:"Тогда безопасно пойдешь по пути твоему, и нога твоя не споткнется."},{num:24,verse:"Когда ляжешь спать, – не будешь бояться; и когда уснешь, – сон твой приятен будет."},{num:25,verse:"Не убоишься внезапного страха и пагубы от нечестивых, когда она придет;"},{num:26,verse:"потому что Господь будет упованием твоим и сохранит ногу твою от уловления."},{num:0,verse:""},{num:27,verse:"Не отказывай в благодеянии нуждающемуся, когда рука твоя в силе сделать его."},{num:28,verse:'Не говори другу твоему: "пойди и приди опять, и завтра я дам", когда ты имеешь при себе. [Ибо ты не знаешь, что родит грядущий день.]'},{num:29,verse:"Не замышляй против ближнего твоего зла, когда он без опасения живет с тобою."},{num:30,verse:"Не ссорься с человеком без причины, когда он не сделал зла тебе."},{num:31,verse:"Не соревнуй человеку, поступающему насильственно, и не избирай ни одного из путей его;"},{num:32,verse:"потому что мерзость пред Господом развратный, а с праведными у Него общение."},{num:33,verse:"Проклятие Господне на доме нечестивого, а жилище благочестивых Он благословляет."},{num:34,verse:"Если над кощунниками Он посмевается, то смиренным дает благодать."},{num:35,verse:"Мудрые наследуют славу, а глупые – бесславие."}]},{chapter:"4",verses:[{num:1,verse:"Слушайте, дети, наставление отца, и внимайте, чтобы научиться разуму,"},{num:2,verse:"потому что я преподал вам доброе учение. Не оставляйте заповеди моей."},{num:3,verse:"Ибо и я был сын у отца моего, нежно любимый и единственный у матери моей,"},{num:4,verse:"и он учил меня и говорил мне: да удержит сердце твое слова мои; храни заповеди мои, и живи."},{num:5,verse:"Приобретай мудрость, приобретай разум: не забывай этого и не уклоняйся от слов уст моих."},{num:6,verse:"Не оставляй ее, и она будет охранять тебя; люби ее, и она будет оберегать тебя."},{num:7,verse:"Главное – мудрость: приобретай мудрость, и всем имением твоим приобретай разум."},{num:8,verse:"Высоко цени ее, и она возвысит тебя; она прославит тебя, если ты прилепишься к ней;"},{num:9,verse:"возложит на голову твою прекрасный венок, доставит тебе великолепный венец."},{num:0,verse:""},{num:10,verse:"Слушай, сын мой, и прими слова мои, – и умножатся тебе лета жизни."},{num:11,verse:"Я указываю тебе путь мудрости, веду тебя по стезям прямым."},{num:12,verse:"Когда пойдешь, не будет стеснен ход твой, и когда побежишь, не споткнешься."},{num:13,verse:"Крепко держись наставления, не оставляй, храни его, потому что оно – жизнь твоя."},{num:14,verse:"Не вступай на стезю нечестивых и не ходи по пути злых;"},{num:15,verse:"оставь его, не ходи по нему, уклонись от него и пройди мимо;"},{num:16,verse:"потому что они не заснут, если не сделают зла; пропадает сон у них, если они не доведут кого до падения;"},{num:17,verse:"ибо они едят хлеб беззакония и пьют вино хищения."},{num:18,verse:"Стезя праведных – как светило лучезарное, которое более и более светлеет до полного дня."},{num:19,verse:"Путь же беззаконных – как тьма; они не знают, обо что споткнутся."},{num:0,verse:""},{num:20,verse:"Сын мой! словам моим внимай, и к речам моим приклони ухо твое;"},{num:21,verse:"да не отходят они от глаз твоих; храни их внутри сердца твоего:"},{num:22,verse:"потому что они жизнь для того, кто нашел их, и здравие для всего тела его."},{num:23,verse:"Больше всего хранимого храни сердце твое, потому что из него источники жизни."},{num:24,verse:"Отвергни от себя лживость уст, и лукавство языка удали от себя."},{num:25,verse:"Глаза твои пусть прямо смотрят, и ресницы твои да направлены будут прямо пред тобою."},{num:26,verse:"Обдумай стезю для ноги твоей, и все пути твои да будут тверды."},{num:27,verse:"Не уклоняйся ни направо, ни налево; удали ногу твою от зла,"},{num:28,verse:"[потому что пути правые наблюдает Господь, а левые – испорчены."},{num:29,verse:"Он же прямыми сделает пути твои, и шествия твои в мире устроит.]"}]},{chapter:"5",verses:[{num:1,verse:"Сын мой! внимай мудрости моей, и приклони ухо твое к разуму моему,"},{num:2,verse:"чтобы соблюсти рассудительность, и чтобы уста твои сохранили знание. [Не внимай льстивой женщине;]"},{num:3,verse:"ибо мед источают уста чужой жены, и мягче елея речь ее;"},{num:4,verse:"но последствия от нее горьки, как полынь, остры, как меч обоюдоострый;"},{num:5,verse:"ноги ее нисходят к смерти, стопы ее достигают преисподней."},{num:6,verse:"Если бы ты захотел постигнуть стезю жизни ее, то пути ее непостоянны, и ты не узнаешь их."},{num:7,verse:"Итак, дети, слушайте меня и не отступайте от слов уст моих."},{num:8,verse:"Держи дальше от нее путь твой и не подходи близко к дверям дома ее,"},{num:9,verse:"чтобы здоровья твоего не отдать другим и лет твоих мучителю;"},{num:10,verse:"чтобы не насыщались силою твоею чужие, и труды твои не были для чужого дома."},{num:11,verse:"И ты будешь стонать после, когда плоть твоя и тело твое будут истощены, -"},{num:12,verse:'и скажешь: "зачем я ненавидел наставление, и сердце мое пренебрегало обличением,'},{num:13,verse:"и я не слушал голоса учителей моих, не приклонял уха моего к наставникам моим:"},{num:14,verse:'едва не впал я во всякое зло среди собрания и общества!"'},{num:0,verse:""},{num:15,verse:"Пей воду из твоего водоема и текущую из твоего колодезя."},{num:16,verse:"Пусть [не] разливаются источники твои по улице, потоки вод – по площадям;"},{num:17,verse:"пусть они будут принадлежать тебе одному, а не чужим с тобою."},{num:18,verse:"Источник твой да будет благословен; и утешайся женою юности твоей,"},{num:19,verse:"любезною ланью и прекрасною серною: груди ее да упоявают тебя во всякое время, любовью ее услаждайся постоянно."},{num:20,verse:"И для чего тебе, сын мой, увлекаться постороннею и обнимать груди чужой?"},{num:21,verse:"Ибо пред очами Господа пути человека, и Он измеряет все стези его."},{num:22,verse:"Беззаконного уловляют собственные беззакония его, и в узах греха своего он содержится:"},{num:23,verse:"он умирает без наставления, и от множества безумия своего теряется."}]},{chapter:"6",verses:[{num:1,verse:"Сын мой! если ты поручился за ближнего твоего и дал руку твою за другого, -"},{num:2,verse:"ты опутал себя словами уст твоих, пойман словами уст твоих."},{num:3,verse:"Сделай же, сын мой, вот что, и избавь себя, так как ты попался в руки ближнего твоего: пойди, пади к ногам и умоляй ближнего твоего;"},{num:4,verse:"не давай сна глазам твоим и дремания веждам твоим;"},{num:5,verse:"спасайся, как серна из руки и как птица из руки птицелова."},{num:0,verse:""},{num:6,verse:"Пойди к муравью, ленивец, посмотри на действия его, и будь мудрым."},{num:7,verse:"Нет у него ни начальника, ни приставника, ни повелителя;"},{num:8,verse:"но он заготовляет летом хлеб свой, собирает во время жатвы пищу свою. [Или пойди к пчеле и познай, как она трудолюбива, какую почтенную работу она производит; ее труды употребляют во здравие и цари и простолюдины; любима же она всеми и славна; хотя силою она слаба, но мудростью почтена.]"},{num:9,verse:"Доколе ты, ленивец, будешь спать? когда ты встанешь от сна твоего?"},{num:10,verse:"Немного поспишь, немного подремлешь, немного, сложив руки, полежишь:"},{num:11,verse:"и придет, как прохожий, бедность твоя, и нужда твоя, как разбойник. [Если же будешь не ленив, то, как источник, придет жатва твоя; скудость же далеко убежит от тебя.]"},{num:0,verse:""},{num:12,verse:"Человек лукавый, человек нечестивый ходит со лживыми устами,"},{num:13,verse:"мигает глазами своими, говорит ногами своими, дает знаки пальцами своими;"},{num:14,verse:"коварство в сердце его: он умышляет зло во всякое время, сеет раздоры."},{num:15,verse:"Зато внезапно придет погибель его, вдруг будет разбит – без исцеления."},{num:16,verse:"Вот шесть, что ненавидит Господь, даже семь, что мерзость душе Его:"},{num:17,verse:"глаза гордые, язык лживый и руки, проливающие кровь невинную,"},{num:18,verse:"сердце, кующее злые замыслы, ноги, быстро бегущие к злодейству,"},{num:19,verse:"лжесвидетель, наговаривающий ложь и сеющий раздор между братьями."},{num:0,verse:""},{num:20,verse:"Сын мой! храни заповедь отца твоего и не отвергай наставления матери твоей;"},{num:21,verse:"навяжи их навсегда на сердце твое, обвяжи ими шею твою."},{num:22,verse:"Когда ты пойдешь, они будут руководить тебя; когда ляжешь спать, будут охранять тебя; когда пробудишься, будут беседовать с тобою:"},{num:23,verse:"ибо заповедь есть светильник, и наставление – свет, и назидательные поучения – путь к жизни,"},{num:24,verse:"чтобы остерегать тебя от негодной женщины, от льстивого языка чужой."},{num:25,verse:"Не пожелай красоты ее в сердце твоем, [да не уловлен будешь очами твоими,] и да не увлечет она тебя ресницами своими;"},{num:26,verse:"потому что из-за жены блудной <i>обнищевают</i> до куска хлеба, а замужняя жена уловляет дорогую душу."},{num:27,verse:"Может ли кто взять себе огонь в пазуху, чтобы не прогорело платье его?"},{num:28,verse:"Может ли кто ходить по горящим угольям, чтобы не обжечь ног своих?"},{num:29,verse:"То же бывает и с тем, кто входит к жене ближнего своего: кто прикоснется к ней, не останется без вины."},{num:30,verse:"Не спускают вору, если он крадет, чтобы насытить душу свою, когда он голоден;"},{num:31,verse:"но, будучи пойман, он заплатит всемеро, отдаст все имущество дома своего."},{num:32,verse:"Кто же прелюбодействует с женщиною, у того нет ума; тот губит душу свою, кто делает это:"},{num:33,verse:"побои и позор найдет он, и бесчестие его не изгладится,"},{num:34,verse:"потому что ревность – ярость мужа, и не пощадит он в день мщения,"},{num:35,verse:"не примет никакого выкупа и не удовольствуется, сколько бы ты ни умножал даров."}]},{chapter:"7",verses:[{num:1,verse:"Сын мой! храни слова мои и заповеди мои сокрой у себя. [Сын мой! чти Господа, – и укрепишься, и кроме Его не бойся никого.]"},{num:2,verse:"Храни заповеди мои и живи, и учение мое, как зрачок глаз твоих."},{num:3,verse:"Навяжи их на персты твои, напиши их на скрижали сердца твоего."},{num:4,verse:'Скажи мудрости: "Ты сестра моя!" и разум назови родным твоим,'},{num:5,verse:"чтобы они охраняли тебя от жены другого, от чужой, которая умягчает слова свои."},{num:6,verse:"Вот, однажды смотрел я в окно дома моего, сквозь решетку мою,"},{num:7,verse:"и увидел среди неопытных, заметил между молодыми людьми неразумного юношу,"},{num:8,verse:"переходившего площадь близ угла ее и шедшего по дороге к дому ее,"},{num:9,verse:"в сумерки в вечер дня, в ночной темноте и во мраке."},{num:10,verse:"И вот – навстречу к нему женщина, в наряде блудницы, с коварным сердцем,"},{num:11,verse:"шумливая и необузданная; ноги ее не живут в доме ее:"},{num:12,verse:"то на улице, то на площадях, и у каждого угла строит она ковы."},{num:13,verse:"Она схватила его, целовала его, и с бесстыдным лицом говорила ему:"},{num:14,verse:'"мирная жертва у меня: сегодня я совершила обеты мои;'},{num:15,verse:"поэтому и вышла навстречу тебе, чтобы отыскать тебя, и – нашла тебя;"},{num:16,verse:"коврами я убрала постель мою, разноцветными тканями Египетскими;"},{num:17,verse:"спальню мою надушила смирною, алоем и корицею;"},{num:18,verse:"зайди, будем упиваться нежностями до утра, насладимся любовью,"},{num:19,verse:"потому что мужа нет дома: он отправился в дальнюю дорогу;"},{num:20,verse:'кошелек серебра взял с собою; придет домой ко дню полнолуния".'},{num:21,verse:"Множеством ласковых слов она увлекла его, мягкостью уст своих овладела им."},{num:22,verse:"Тотчас он пошел за нею, как вол идет на убой, [и как пес – на цепь,] и как олень – на выстрел,"},{num:23,verse:"доколе стрела не пронзит печени его; как птичка кидается в силки, и не знает, что они – на погибель ее."},{num:24,verse:"Итак, дети, слушайте меня и внимайте словам уст моих."},{num:25,verse:"Да не уклоняется сердце твое на пути ее, не блуждай по стезям ее,"},{num:26,verse:"потому что многих повергла она ранеными, и много сильных убиты ею:"},{num:27,verse:"дом ее – пути в преисподнюю, нисходящие во внутренние жилища смерти."}]},{chapter:"8",verses:[{num:1,verse:"Не премудрость ли взывает? и не разум ли возвышает голос свой?"},{num:2,verse:"Она становится на возвышенных местах, при дороге, на распутиях;"},{num:3,verse:"она взывает у ворот при входе в город, при входе в двери:"},{num:4,verse:'"к вам, люди, взываю я, и к сынам человеческим голос мой!'},{num:5,verse:"Научитесь, неразумные, благоразумию, и глупые – разуму."},{num:6,verse:"Слушайте, потому что я буду говорить важное, и изречение уст моих – правда;"},{num:7,verse:"ибо истину произнесет язык мой, и нечестие – мерзость для уст моих;"},{num:8,verse:"все слова уст моих справедливы; нет в них коварства и лукавства;"},{num:9,verse:"все они ясны для разумного и справедливы для приобретших знание."},{num:10,verse:"Примите учение мое, а не серебро; лучше знание, нежели отборное золото;"},{num:11,verse:"потому что мудрость лучше жемчуга, и ничто из желаемого не сравнится с нею."},{num:12,verse:"Я, премудрость, обитаю с разумом и ищу рассудительного знания."},{num:13,verse:"Страх Господень – ненавидеть зло; гордость и высокомерие и злой путь и коварные уста я ненавижу."},{num:14,verse:"У меня совет и правда; я разум, у меня сила."},{num:15,verse:"Мною цари царствуют и повелители узаконяют правду;"},{num:16,verse:"мною начальствуют начальники и вельможи и все судьи земли."},{num:17,verse:"Любящих меня я люблю, и ищущие меня найдут меня;"},{num:18,verse:"богатство и слава у меня, сокровище непогибающее и правда;"},{num:19,verse:"плоды мои лучше золота, и золота самого чистого, и пользы от меня больше, нежели от отборного серебра."},{num:20,verse:"Я хожу по пути правды, по стезям правосудия,"},{num:21,verse:"чтобы доставить любящим меня существенное благо, и сокровищницы их я наполняю. [Когда я возвещу то, что бывает ежедневно, то не забуду исчислить то, что от века.]"},{num:0,verse:""},{num:22,verse:"Господь имел меня началом пути Своего, прежде созданий Своих, искони;"},{num:23,verse:"от века я помазана, от начала, прежде бытия земли."},{num:24,verse:"Я родилась, когда еще не существовали бездны, когда еще не было источников, обильных водою."},{num:25,verse:"Я родилась прежде, нежели водружены были горы, прежде холмов,"},{num:26,verse:"когда еще Он не сотворил ни земли, ни полей, ни начальных пылинок вселенной."},{num:27,verse:"Когда Он уготовлял небеса, <i>я была</i> там. Когда Он проводил круговую черту по лицу бездны,"},{num:28,verse:"когда утверждал вверху облака, когда укреплял источники бездны,"},{num:29,verse:"когда давал морю устав, чтобы воды не переступали пределов его, когда полагал основания земли:"},{num:30,verse:"тогда я была при Нем художницею, и была радостью всякий день, веселясь пред лицем Его во все время,"},{num:31,verse:"веселясь на земном кругу Его, и радость моя <i>была</i> с сынами человеческими."},{num:0,verse:""},{num:32,verse:"Итак, дети, послушайте меня; и блаженны те, которые хранят пути мои!"},{num:33,verse:"Послушайте наставления и будьте мудры, и не отступайте <i>от него</i>."},{num:34,verse:"Блажен человек, который слушает меня, бодрствуя каждый день у ворот моих и стоя на страже у дверей моих!"},{num:35,verse:"потому что, кто нашел меня, тот нашел жизнь, и получит благодать от Господа;"},{num:36,verse:'а согрешающий против меня наносит вред душе своей: все ненавидящие меня любят смерть".'}]},{chapter:"9",verses:[{num:1,verse:"Премудрость построила себе дом, вытесала семь столбов его,"},{num:2,verse:"заколола жертву, растворила вино свое и приготовила у себя трапезу;"},{num:3,verse:"послала слуг своих провозгласить с возвышенностей городских:"},{num:4,verse:'"кто неразумен, обратись сюда!" И скудоумному она сказала:'},{num:5,verse:'"идите, ешьте хлеб мой и пейте вино, мною растворенное;'},{num:6,verse:'оставьте неразумие, и живите, и ходите путем разума".'},{num:7,verse:"Поучающий кощунника наживет себе бесславие, и обличающий нечестивого – пятно себе."},{num:8,verse:"Не обличай кощунника, чтобы он не возненавидел тебя; обличай мудрого, и он возлюбит тебя;"},{num:9,verse:"дай <i>наставление</i> мудрому, и он будет еще мудрее; научи правдивого, и он приумножит знание."},{num:10,verse:"Начало мудрости – страх Господень, и познание Святаго – разум;"},{num:11,verse:"потому что чрез меня умножатся дни твои, и прибавится тебе лет жизни."},{num:12,verse:"[Сын мой!] если ты мудр, то мудр для себя [и для ближних твоих]; и если буен, то один потерпишь. [Кто утверждается на лжи, тот пасет ветры, тот гоняется за птицами летающими: ибо он оставил пути своего виноградника и блуждает по тропинкам поля своего; проходит чрез безводную пустыню и землю, обреченную на жажду; собирает руками бесплодие.]"},{num:0,verse:""},{num:13,verse:"Женщина безрассудная, шумливая, глупая и ничего не знающая"},{num:14,verse:"садится у дверей дома своего на стуле, на возвышенных местах города,"},{num:15,verse:"чтобы звать проходящих дорогою, идущих прямо своими путями:"},{num:16,verse:'"кто глуп, обратись сюда!" и скудоумному сказала она:'},{num:17,verse:'"воды краденые сладки, и утаенный хлеб приятен".'},{num:18,verse:"И он не знает, что мертвецы там, и что в глубине преисподней зазванные ею. [Но ты отскочи, не медли на месте, не останавливай взгляда твоего на ней; ибо таким образом ты пройдешь воду чужую. От воды чужой удаляйся, и из источника чужого не пей, чтобы пожить многое время, и чтобы прибавились тебе лета жизни.]"}]},{chapter:"10",verses:[{num:1,verse:"Притчи Соломона. Сын мудрый радует отца, а сын глупый – огорчение для его матери."},{num:2,verse:"Не доставляют пользы сокровища неправедные, правда же избавляет от смерти."},{num:3,verse:"Не допустит Господь терпеть голод душе праведного, стяжание же нечестивых исторгнет."},{num:4,verse:"Ленивая рука делает бедным, а рука прилежных обогащает."},{num:5,verse:"Собирающий во время лета – сын разумный, спящий же во время жатвы – сын беспутный."},{num:6,verse:"Благословения – на голове праведника, уста же беззаконных заградит насилие."},{num:7,verse:"Память праведника пребудет благословенна, а имя нечестивых омерзеет."},{num:8,verse:"Мудрый сердцем принимает заповеди, а глупый устами преткнется."},{num:9,verse:"Кто ходит в непорочности, тот ходит безопасно; а кто превращает пути свои, тот будет наказан."},{num:10,verse:"Кто мигает глазами, тот причиняет досаду, а глупый устами преткнется."},{num:11,verse:"Уста праведника – источник жизни, уста же беззаконных заградит насилие."},{num:12,verse:"Ненависть возбуждает раздоры, но любовь покрывает все грехи."},{num:13,verse:"В устах разумного находится мудрость, но на теле глупого – розга."},{num:14,verse:"Мудрые сберегают знание, но уста глупого – близкая погибель."},{num:15,verse:"Имущество богатого – крепкий город его, беда для бедных – скудость их."},{num:16,verse:"Труды праведного – к жизни, успех нечестивого – ко греху."},{num:17,verse:"Кто хранит наставление, тот на пути к жизни; а отвергающий обличение – блуждает."},{num:18,verse:"Кто скрывает ненависть, у того уста лживые; и кто разглашает клевету, тот глуп."},{num:19,verse:"При многословии не миновать греха, а сдерживающий уста свои – разумен."},{num:20,verse:"Отборное серебро – язык праведного, сердце же нечестивых – ничтожество."},{num:21,verse:"Уста праведного пасут многих, а глупые умирают от недостатка разума."},{num:22,verse:"Благословение Господне – оно обогащает и печали с собою не приносит."},{num:23,verse:"Для глупого преступное деяние как бы забава, а человеку разумному свойственна мудрость."},{num:24,verse:"Чего страшится нечестивый, то и постигнет его, а желание праведников исполнится."},{num:25,verse:"Как проносится вихрь, <i>так</i> нет более нечестивого; а праведник – на вечном основании."},{num:26,verse:"Что уксус для зубов и дым для глаз, то ленивый для посылающих его."},{num:27,verse:"Страх Господень прибавляет дней, лета же нечестивых сократятся."},{num:28,verse:"Ожидание праведников – радость, а надежда нечестивых погибнет."},{num:29,verse:"Путь Господень – твердыня для непорочного и страх для делающих беззаконие."},{num:30,verse:"Праведник во веки не поколеблется, нечестивые же не поживут на земле."},{num:31,verse:"Уста праведника источают мудрость, а язык зловредный отсечется."},{num:32,verse:"Уста праведного знают благоприятное, а уста нечестивых – развращенное."}]},{chapter:"11",verses:[{num:1,verse:"Неверные весы – мерзость пред Господом, но правильный вес угоден Ему."},{num:2,verse:"Придет гордость, придет и посрамление; но со смиренными – мудрость. [Праведник, умирая, оставляет сожаление; но внезапна и радостна бывает погибель нечестивых.]"},{num:3,verse:"Непорочность прямодушных будет руководить их, а лукавство коварных погубит их."},{num:4,verse:"Не поможет богатство в день гнева, правда же спасет от смерти."},{num:5,verse:"Правда непорочного уравнивает путь его, а нечестивый падет от нечестия своего."},{num:6,verse:"Правда прямодушных спасет их, а беззаконники будут уловлены беззаконием своим."},{num:7,verse:"Со смертью человека нечестивого исчезает надежда, и ожидание беззаконных погибает."},{num:8,verse:"Праведник спасается от беды, а вместо него попадает <i>в нее</i> нечестивый."},{num:9,verse:"Устами лицемер губит ближнего своего, но праведники прозорливостью спасаются."},{num:10,verse:"При благоденствии праведников веселится город, и при погибели нечестивых <i>бывает</i> торжество."},{num:11,verse:"Благословением праведных возвышается город, а устами нечестивых разрушается."},{num:12,verse:"Скудоумный высказывает презрение к ближнему своему; но разумный человек молчит."},{num:13,verse:"Кто ходит переносчиком, тот открывает тайну; но верный человек таит дело."},{num:14,verse:"При недостатке попечения падает народ, а при многих советниках благоденствует."},{num:15,verse:"Зло причиняет себе, кто ручается за постороннего; а кто ненавидит ручательство, тот безопасен."},{num:16,verse:"Благонравная жена приобретает славу [мужу, а жена, ненавидящая правду, есть верх бесчестия. Ленивцы бывают скудны], а трудолюбивые приобретают богатство."},{num:17,verse:"Человек милосердый благотворит душе своей, а жестокосердый разрушает плоть свою."},{num:18,verse:"Нечестивый делает дело ненадежное, а сеющему правду – награда верная."},{num:19,verse:"Праведность <i>ведет</i> к жизни, а стремящийся к злу <i>стремится</i> к смерти своей."},{num:20,verse:"Мерзость пред Господом – коварные сердцем; но благоугодны Ему непорочные в пути."},{num:21,verse:"Можно поручиться, что порочный не останется ненаказанным; семя же праведных спасется."},{num:22,verse:"Что золотое кольцо в носу у свиньи, то женщина красивая и – безрассудная."},{num:23,verse:"Желание праведных <i>есть</i> одно добро, ожидание нечестивых – гнев."},{num:24,verse:"Иной сыплет щедро, и <i>ему</i> еще прибавляется; а другой сверх меры бережлив, и однако же беднеет."},{num:25,verse:"Благотворительная душа будет насыщена, и кто напояет <i>других</i>, тот и сам напоен будет."},{num:26,verse:"Кто удерживает у себя хлеб, того клянет народ; а на голове продающего – благословение."},{num:27,verse:"Кто стремится к добру, тот ищет благоволения; а кто ищет зла, к тому оно и приходит."},{num:28,verse:"Надеющийся на богатство свое упадет; а праведники, как лист, будут зеленеть."},{num:29,verse:"Расстроивающий дом свой получит в удел ветер, и глупый будет рабом мудрого сердцем."},{num:30,verse:"Плод праведника – древо жизни, и мудрый привлекает души."},{num:31,verse:"Так праведнику воздается на земле, тем паче нечестивому и грешнику."}]},{chapter:"12",verses:[{num:1,verse:"Кто любит наставление, тот любит знание; а кто ненавидит обличение, тот невежда."},{num:2,verse:"Добрый приобретает благоволение от Господа; а человека коварного Он осудит."},{num:3,verse:"Не утвердит себя человек беззаконием; корень же праведников неподвижен."},{num:4,verse:"Добродетельная жена – венец для мужа своего; а позорная – как гниль в костях его."},{num:5,verse:"Промышления праведных – правда, а замыслы нечестивых – коварство."},{num:6,verse:"Речи нечестивых – засада для пролития крови, уста же праведных спасают их."},{num:7,verse:"Коснись нечестивых несчастие – и нет их, а дом праведных стоит."},{num:8,verse:"Хвалят человека по мере разума его, а развращенный сердцем будет в презрении."},{num:9,verse:"Лучше простой, но работающий на себя, нежели выдающий себя за знатного, но нуждающийся в хлебе."},{num:10,verse:"Праведный печется и о жизни скота своего, сердце же нечестивых жестоко."},{num:11,verse:"Кто возделывает землю свою, тот будет насыщаться хлебом; а кто идет по следам празднолюбцев, тот скудоумен. [Кто находит удовольствие в трате времени за вином, тот в своем доме оставит бесславие.]"},{num:12,verse:"Нечестивый желает уловить в сеть зла; но корень праведных тверд."},{num:13,verse:"Нечестивый уловляется грехами уст своих; но праведник выйдет из беды. [Смотрящий кротко помилован будет, а встречающийся в воротах стеснит других.]"},{num:14,verse:"От плода уст <i>своих</i> человек насыщается добром, и воздаяние человеку – по делам рук его."},{
num:15,verse:"Путь глупого прямой в его глазах; но кто слушает совета, тот мудр."},{num:16,verse:"У глупого тотчас же выкажется гнев его, а благоразумный скрывает оскорбление."},{num:17,verse:"Кто говорит то, что знает, тот говорит правду; а у свидетеля ложного – обман."},{num:18,verse:"Иной пустослов уязвляет как мечом, а язык мудрых – врачует."},{num:19,verse:"Уста правдивые вечно пребывают, а лживый язык – только на мгновение."},{num:20,verse:"Коварство – в сердце злоумышленников, радость – у миротворцев."},{num:21,verse:"Не приключится праведнику никакого зла, нечестивые же будут преисполнены зол."},{num:22,verse:"Мерзость пред Господом – уста лживые, а говорящие истину благоугодны Ему."},{num:23,verse:"Человек рассудительный скрывает знание, а сердце глупых высказывает глупость."},{num:24,verse:"Рука прилежных будет господствовать, а ленивая будет под данью."},{num:25,verse:"Тоска на сердце человека подавляет его, а доброе слово развеселяет его."},{num:26,verse:"Праведник указывает ближнему своему путь, а путь нечестивых вводит их в заблуждение."},{num:27,verse:"Ленивый не жарит своей дичи; а имущество человека прилежного многоценно."},{num:28,verse:"На пути правды – жизнь, и на стезе ее нет смерти."}]},{chapter:"13",verses:[{num:1,verse:"Мудрый сын <i>слушает</i> наставление отца, а буйный не слушает обличения."},{num:2,verse:"От плода уст <i>своих</i> человек вкусит добро, душа же законопреступников – зло."},{num:3,verse:"Кто хранит уста свои, тот бережет душу свою; а кто широко раскрывает свой рот, тому беда."},{num:4,verse:"Душа ленивого желает, но тщетно; а душа прилежных насытится."},{num:5,verse:"Праведник ненавидит ложное слово, а нечестивый срамит и бесчестит <i>себя</i>."},{num:6,verse:"Правда хранит непорочного в пути, а нечестие губит грешника."},{num:7,verse:"Иной выдает себя за богатого, а у него ничего нет; другой выдает себя за бедного, а у него богатства много."},{num:8,verse:"Богатством своим человек выкупает жизнь <i>свою</i>, а бедный и угрозы не слышит."},{num:9,verse:"Свет праведных весело горит, светильник же нечестивых угасает. [Души коварные блуждают в грехах, а праведники сострадают и милуют.]"},{num:10,verse:"От высокомерия происходит раздор, а у советующихся – мудрость."},{num:11,verse:"Богатство от суетности истощается, а собирающий трудами умножает его."},{num:12,verse:"Надежда, долго не сбывающаяся, томит сердце, а исполнившееся желание – <i>как</i> древо жизни."},{num:13,verse:"Кто пренебрегает словом, тот причиняет вред себе; а кто боится заповеди, тому воздается."},{num:14,verse:"[У сына лукавого ничего нет доброго, а у разумного раба дела благоуспешны, и путь его прямой.]"},{num:15,verse:"Учение мудрого – источник жизни, удаляющий от сетей смерти."},{num:16,verse:"Добрый разум доставляет приятность, путь же беззаконных жесток."},{num:17,verse:"Всякий благоразумный действует с знанием, а глупый выставляет напоказ глупость."},{num:18,verse:"Худой посол попадает в беду, а верный посланник – спасение."},{num:19,verse:"Нищета и посрамление отвергающему учение; а кто соблюдает наставление, будет в чести."},{num:20,verse:"Желание исполнившееся – приятно для души; но несносно для глупых уклоняться от зла."},{num:21,verse:"Общающийся с мудрыми будет мудр, а кто дружит с глупыми, развратится."},{num:22,verse:"Грешников преследует зло, а праведникам воздается добром."},{num:23,verse:"Добрый оставляет наследство [и] внукам, а богатство грешника сберегается для праведного."},{num:24,verse:"Много хлеба <i>бывает</i> и на ниве бедных; но некоторые гибнут от беспорядка."},{num:25,verse:"Кто жалеет розги своей, тот ненавидит сына; а кто любит, тот с детства наказывает его."},{num:26,verse:"Праведник ест до сытости, а чрево беззаконных терпит лишение."}]},{chapter:"14",verses:[{num:1,verse:"Мудрая жена устроит дом свой, а глупая разрушит его своими руками."},{num:2,verse:"Идущий прямым путем боится Господа; но чьи пути кривы, тот небрежет о Нем."},{num:3,verse:"В устах глупого – бич гордости; уста же мудрых охраняют их."},{num:4,verse:"Где нет волов, <i>там</i> ясли пусты; а много прибыли от силы волов."},{num:5,verse:"Верный свидетель не лжет, а свидетель ложный наговорит много лжи."},{num:6,verse:"Распутный ищет мудрости, и не находит; а для разумного знание легко."},{num:7,verse:"Отойди от человека глупого, у которого ты не замечаешь разумных уст."},{num:8,verse:"Мудрость разумного – знание пути своего, глупость же безрассудных – заблуждение."},{num:9,verse:"Глупые смеются над грехом, а посреди праведных – благоволение."},{num:10,verse:"Сердце знает горе души своей, и в радость его не вмешается чужой."},{num:11,verse:"Дом беззаконных разорится, а жилище праведных процветет."},{num:12,verse:"Есть пути, которые кажутся человеку прямыми; но конец их – путь к смерти."},{num:13,verse:"И при смехе <i>иногда</i> болит сердце, и концом радости бывает печаль."},{num:14,verse:"Человек с развращенным сердцем насытится от путей своих, и добрый – от своих."},{num:15,verse:"Глупый верит всякому слову, благоразумный же внимателен к путям своим."},{num:16,verse:"Мудрый боится и удаляется от зла, а глупый раздражителен и самонадеян."},{num:17,verse:"Вспыльчивый может сделать глупость; но человек, умышленно делающий зло, ненавистен."},{num:18,verse:"Невежды получают в удел себе глупость, а благоразумные увенчаются знанием."},{num:19,verse:"Преклонятся злые пред добрыми и нечестивые – у ворот праведника."},{num:20,verse:"Бедный ненавидим бывает даже близким своим, а у богатого много друзей."},{num:21,verse:"Кто презирает ближнего своего, тот грешит; а кто милосерд к бедным, тот блажен."},{num:22,verse:"Не заблуждаются ли умышляющие зло? [не знают милости и верности делающие зло;] но милость и верность у благомыслящих."},{num:23,verse:"От всякого труда есть прибыль, а от пустословия только ущерб."},{num:24,verse:"Венец мудрых – богатство их, а глупость невежд глупость <i>и есть</i>."},{num:25,verse:"Верный свидетель спасает души, а лживый наговорит много лжи."},{num:26,verse:"В страхе пред Господом – надежда твердая, и сынам Своим Он прибежище."},{num:27,verse:"Страх Господень – источник жизни, удаляющий от сетей смерти."},{num:28,verse:"Во множестве народа – величие царя, а при малолюдстве народа беда государю."},{num:29,verse:"У терпеливого человека много разума, а раздражительный выказывает глупость."},{num:30,verse:"Кроткое сердце – жизнь для тела, а зависть – гниль для костей."},{num:31,verse:"Кто теснит бедного, тот хулит Творца его; чтущий же Его благотворит нуждающемуся."},{num:32,verse:"За зло свое нечестивый будет отвергнут, а праведный и при смерти своей имеет надежду."},{num:33,verse:"Мудрость почиет в сердце разумного, и среди глупых дает знать о себе."},{num:34,verse:"Праведность возвышает народ, а беззаконие – бесчестие народов."},{num:35,verse:"Благоволение царя – к рабу разумному, а гнев его – против того, кто позорит его."}]},{chapter:"15",verses:[{num:1,verse:"[Гнев губит и разумных.] Кроткий ответ отвращает гнев, а оскорбительное слово возбуждает ярость."},{num:2,verse:"Язык мудрых сообщает добрые знания, а уста глупых изрыгают глупость."},{num:3,verse:"На всяком месте очи Господни: они видят злых и добрых."},{num:4,verse:"Кроткий язык – древо жизни, но необузданный – сокрушение духа."},{num:5,verse:"Глупый пренебрегает наставлением отца своего; а кто внимает обличениям, тот благоразумен. [В обилии правды великая сила, а нечестивые искоренятся из земли.]"},{num:6,verse:"В доме праведника – обилие сокровищ, а в прибытке нечестивого – расстройство."},{num:7,verse:"Уста мудрых распространяют знание, а сердце глупых не так."},{num:8,verse:"Жертва нечестивых – мерзость пред Господом, а молитва праведных благоугодна Ему."},{num:9,verse:"Мерзость пред Господом – путь нечестивого, а идущего путем правды Он любит."},{num:10,verse:"Злое наказание – уклоняющемуся от пути, и ненавидящий обличение погибнет."},{num:11,verse:"Преисподняя и Аваддон [открыты] пред Господом, тем более сердца сынов человеческих."},{num:12,verse:"Не любит распутный обличающих его, и к мудрым не пойдет."},{num:13,verse:"Веселое сердце делает лице веселым, а при сердечной скорби дух унывает."},{num:14,verse:"Сердце разумного ищет знания, уста же глупых питаются глупостью."},{num:15,verse:"Все дни несчастного печальны; а у кого сердце весело, у того всегда пир."},{num:16,verse:"Лучше немногое при страхе Господнем, нежели большое сокровище, и при нем тревога."},{num:17,verse:"Лучше блюдо зелени, и при нем любовь, нежели откормленный бык, и при нем ненависть."},{num:18,verse:"Вспыльчивый человек возбуждает раздор, а терпеливый утишает распрю."},{num:19,verse:"Путь ленивого – как терновый плетень, а путь праведных – гладкий."},{num:20,verse:"Мудрый сын радует отца, а глупый человек пренебрегает мать свою."},{num:21,verse:"Глупость – радость для малоумного, а человек разумный идет прямою дорогою."},{num:22,verse:"Без совета предприятия расстроятся, а при множестве советников они состоятся."},{num:23,verse:"Радость человеку в ответе уст его, и как хорошо слово вовремя!"},{num:24,verse:"Путь жизни мудрого вверх, чтобы уклониться от преисподней внизу."},{num:25,verse:"Дом надменных разорит Господь, а межу вдовы укрепит."},{num:26,verse:"Мерзость пред Господом – помышления злых, слова же непорочных угодны Ему."},{num:27,verse:"Корыстолюбивый расстроит дом свой, а ненавидящий подарки будет жить."},{num:28,verse:"Сердце праведного обдумывает ответ, а уста нечестивых изрыгают зло. [Приятны пред Господом пути праведных; чрез них и враги делаются друзьями.]"},{num:29,verse:"Далек Господь от нечестивых, а молитву праведников слышит."},{num:30,verse:"Светлый взгляд радует сердце, добрая весть утучняет кости."},{num:31,verse:"Ухо, внимательное к учению жизни, пребывает между мудрыми."},{num:32,verse:"Отвергающий наставление не радеет о своей душе; а кто внимает обличению, тот приобретает разум."},{num:33,verse:"Страх Господень научает мудрости, и славе предшествует смирение."}]},{chapter:"16",verses:[{num:1,verse:"Человеку <i>принадлежат</i> предположения сердца, но от Господа ответ языка."},{num:2,verse:"Все пути человека чисты в его глазах, но Господь взвешивает души."},{num:3,verse:"Предай Господу дела твои, и предприятия твои совершатся."},{num:4,verse:"Все сделал Господь ради Себя; и даже нечестивого <i>блюдет</i> на день бедствия."},{num:5,verse:"Мерзость пред Господом всякий надменный сердцем; можно поручиться, что он не останется ненаказанным. [Начало доброго пути – делать правду; это угоднее пред Богом, нежели приносить жертвы. Ищущий Господа найдет знание с правдою; истинно ищущие Его найдут мир.]"},{num:6,verse:"Милосердием и правдою очищается грех, и страх Господень отводит от зла."},{num:7,verse:"Когда Господу угодны пути человека, Он и врагов его примиряет с ним."},{num:8,verse:"Лучше немногое с правдою, нежели множество прибытков с неправдою."},{num:9,verse:"Сердце человека обдумывает свой путь, но Господь управляет шествием его."},{num:10,verse:"В устах царя – слово вдохновенное; уста его не должны погрешать на суде."},{num:11,verse:"Верные весы и весовые чаши – от Господа; от Него же все гири в суме."},{num:12,verse:"Мерзость для царей – дело беззаконное, потому что правдою утверждается престол."},{num:13,verse:"Приятны царю уста правдивые, и говорящего истину он любит."},{num:14,verse:"Царский гнев – вестник смерти; но мудрый человек умилостивит его."},{num:15,verse:"В светлом взоре царя – жизнь, и благоволение его – как облако с поздним дождем."},{num:16,verse:"Приобретение мудрости гораздо лучше золота, и приобретение разума предпочтительнее отборного серебра."},{num:17,verse:"Путь праведных – уклонение от зла: тот бережет душу свою, кто хранит путь свой."},{num:18,verse:"Погибели предшествует гордость, и падению – надменность."},{num:19,verse:"Лучше смиряться духом с кроткими, нежели разделять добычу с гордыми."},{num:20,verse:"Кто ведет дело разумно, тот найдет благо, и кто надеется на Господа, тот блажен."},{num:21,verse:"Мудрый сердцем прозовется благоразумным, и сладкая речь прибавит к учению."},{num:22,verse:"Разум для имеющих его – источник жизни, а ученость глупых – глупость."},{num:23,verse:"Сердце мудрого делает язык его мудрым и умножает знание в устах его."},{num:24,verse:"Приятная речь – сотовый мед, сладка для души и целебна для костей."},{num:25,verse:"Есть пути, которые кажутся человеку прямыми, но конец их путь к смерти."},{num:26,verse:"Трудящийся трудится для себя, потому что понуждает его <i>к тому</i> рот его."},{num:27,verse:"Человек лукавый замышляет зло, и на устах его как бы огонь палящий."},{num:28,verse:"Человек коварный сеет раздор, и наушник разлучает друзей."},{num:29,verse:"Человек неблагонамеренный развращает ближнего своего и ведет его на путь недобрый;"},{num:30,verse:"прищуривает глаза свои, чтобы придумать коварство; закусывая себе губы, совершает злодейство; [он – печь злобы]."},{num:31,verse:"Венец славы – седина, которая находится на пути правды."},{num:32,verse:"Долготерпеливый лучше храброго, и владеющий собою <i>лучше</i> завоевателя города."},{num:33,verse:"В полу бросается жребий, но все решение его – от Господа."}]},{chapter:"17",verses:[{num:1,verse:"Лучше кусок сухого хлеба, и с ним мир, нежели дом, полный заколотого скота, с раздором."},{num:2,verse:"Разумный раб господствует над беспутным сыном и между братьями разделит наследство."},{num:3,verse:"Плавильня – для серебра, и горнило – для золота, а сердца испытывает Господь."},{num:4,verse:"Злодей внимает устам беззаконным, лжец слушается языка пагубного."},{num:5,verse:"Кто ругается над нищим, тот хулит Творца его; кто радуется несчастью, тот не останется ненаказанным [а милосердый помилован будет]."},{num:6,verse:"Венец стариков – сыновья сыновей, и слава детей – родители их. [У верного целый мир богатства, а у неверного – ни обола.]"},{num:7,verse:"Неприлична глупому важная речь, тем паче знатному – уста лживые."},{num:8,verse:"Подарок – драгоценный камень в глазах владеющего им: куда ни обратится он, успеет."},{num:9,verse:"Прикрывающий проступок ищет любви; а кто снова напоминает о нем, тот удаляет друга."},{num:10,verse:"На разумного сильнее действует выговор, нежели на глупого сто ударов."},{num:11,verse:"Возмутитель ищет только зла; поэтому жестокий ангел будет послан против него."},{num:12,verse:"Лучше встретить человеку медведицу, лишенную детей, нежели глупца с его глупостью."},{num:13,verse:"Кто за добро воздает злом, от дома того не отойдет зло."},{num:14,verse:"Начало ссоры – как прорыв воды; оставь ссору прежде, нежели разгорелась она."},{num:15,verse:"Оправдывающий нечестивого и обвиняющий праведного – оба мерзость пред Господом."},{num:16,verse:"К чему сокровище в руках глупца? Для приобретения мудрости <i>у него</i> нет разума. [Кто высоким делает свой дом, тот ищет разбиться; а уклоняющийся от учения впадет в беды.]"},{num:17,verse:"Друг любит во всякое время и, как брат, явится во время несчастья."},{num:18,verse:"Человек малоумный дает руку и ручается за ближнего своего."},{num:19,verse:"Кто любит ссоры, любит грех, и кто высоко поднимает ворота свои, тот ищет падения."},{num:20,verse:"Коварное сердце не найдет добра, и лукавый язык попадет в беду."},{num:21,verse:"Родил кто глупого, – себе на горе, и отец глупого не порадуется."},{num:22,verse:"Веселое сердце благотворно, как врачевство, а унылый дух сушит кости."},{num:23,verse:"Нечестивый берет подарок из пазухи, чтобы извратить пути правосудия."},{num:24,verse:"Мудрость – пред лицем у разумного, а глаза глупца – на конце земли."},{num:25,verse:"Глупый сын – досада отцу своему и огорчение для матери своей."},{num:26,verse:"Нехорошо и обвинять правого, <i>и</i> бить вельмож за правду."},{num:27,verse:"Разумный воздержан в словах своих, и благоразумный хладнокровен."},{num:28,verse:"И глупец, когда молчит, может показаться мудрым, и затворяющий уста свои – благоразумным."}]},{chapter:"18",verses:[{num:1,verse:"Прихоти ищет своенравный, восстает против всего умного."},{num:2,verse:"Глупый не любит знания, а только бы выказать свой ум."},{num:3,verse:"С приходом нечестивого приходит и презрение, а с бесславием – поношение."},{num:4,verse:"Слова уст человеческих – глубокие воды; источник мудрости – струящийся поток."},{num:5,verse:"Нехорошо быть лицеприятным к нечестивому, чтобы ниспровергнуть праведного на суде."},{num:6,verse:"Уста глупого идут в ссору, и слова его вызывают побои."},{num:7,verse:"Язык глупого – гибель для него, и уста его – сеть для души его."},{num:8,verse:"[Ленивого низлагает страх, а души женоподобные будут голодать.]"},{num:9,verse:"Слова наушника – как лакомства, и они входят во внутренность чрева."},{num:10,verse:"Нерадивый в работе своей – брат расточителю."},{num:11,verse:"Имя Господа – крепкая башня: убегает в нее праведник – и безопасен."},{num:12,verse:"Имение богатого – крепкий город его, и как высокая ограда в его воображении."},{num:13,verse:"Перед падением возносится сердце человека, а смирение предшествует славе."},{num:14,verse:"Кто дает ответ не выслушав, тот глуп, и стыд ему."},{num:15,verse:"Дух человека переносит его немощи; а пораженный дух – кто может подкрепить его?"},{num:16,verse:"Сердце разумного приобретает знание, и ухо мудрых ищет знания."},{num:17,verse:"Подарок у человека дает ему простор и до вельмож доведет его."},{num:18,verse:"Первый в тяжбе своей прав, но приходит соперник его и исследывает его."},{num:19,verse:"Жребий прекращает споры и решает между сильными."},{num:20,verse:"Озлобившийся брат <i>неприступнее</i> крепкого города, и ссоры подобны запорам замка."},{num:21,verse:"От плода уст человека наполняется чрево его; произведением уст своих он насыщается."},{num:22,verse:"Смерть и жизнь – во власти языка, и любящие его вкусят от плодов его."},{num:23,verse:"Кто нашел [добрую] жену, тот нашел благо и получил благодать от Господа. [Кто изгоняет добрую жену, тот изгоняет счастье, а содержащий прелюбодейку – безумен и нечестив.]"},{num:24,verse:"С мольбою говорит нищий, а богатый отвечает грубо."},{num:25,verse:"Кто хочет иметь друзей, тот и сам должен быть дружелюбным; и бывает друг, более привязанный, нежели брат."}]},{chapter:"19",verses:[{num:1,verse:"Лучше бедный, ходящий в своей непорочности, нежели [богатый] со лживыми устами, и притом глупый."},{num:2,verse:"Нехорошо душе без знания, и торопливый ногами оступится."},{num:3,verse:"Глупость человека извращает путь его, а сердце его негодует на Господа."},{num:4,verse:"Богатство прибавляет много друзей, а бедный оставляется и другом своим."},{num:5,verse:"Лжесвидетель не останется ненаказанным, и кто говорит ложь, не спасется."},{num:6,verse:"Многие заискивают у знатных, и всякий – друг человеку, делающему подарки."},{num:7,verse:"Бедного ненавидят все братья его, тем паче друзья его удаляются от него: гонится за ними, чтобы поговорить, но и этого нет."},{num:8,verse:"Кто приобретает разум, тот любит душу свою; кто наблюдает благоразумие, тот находит благо."},{num:9,verse:"Лжесвидетель не останется ненаказанным, и кто говорит ложь, погибнет."},{num:10,verse:"Неприлична глупцу пышность, тем паче рабу господство над князьями."},{num:11,verse:"Благоразумие делает человека медленным на гнев, и слава для него – быть снисходительным к проступкам."},{num:12,verse:"Гнев царя – как рев льва, а благоволение его – как роса на траву."},{num:13,verse:"Глупый сын – сокрушение для отца своего, и сварливая жена – сточная труба."},{num:14,verse:"Дом и имение – наследство от родителей, а разумная жена – от Господа."},{num:15,verse:"Леность погружает в сонливость, и нерадивая душа будет терпеть голод."},{num:16,verse:"Хранящий заповедь хранит душу свою, а нерадящий о путях своих погибнет."},{num:17,verse:"Благотворящий бедному дает взаймы Господу, и Он воздаст ему за благодеяние его."},{num:18,verse:"Наказывай сына своего, доколе есть надежда, и не возмущайся криком его."},{num:19,verse:"Гневливый пусть терпит наказание, потому что, если пощадишь <i>его</i>, придется тебе еще больше наказывать его."},{num:20,verse:"Слушайся совета и принимай обличение, чтобы сделаться тебе впоследствии мудрым."},{num:21,verse:"Много замыслов в сердце человека, но состоится только определенное Господом."},{num:22,verse:"Радость человеку – благотворительность его, и бедный человек лучше, нежели лживый."},{num:23,verse:"Страх Господень <i>ведет</i> к жизни, и <i>кто имеет его</i>, всегда будет доволен, и зло не постигнет его."},{num:24,verse:"Ленивый опускает руку свою в чашу, и не хочет донести ее до рта своего."},{num:25,verse:"Если ты накажешь кощунника, то и простой сделается благоразумным; и <i>если</i> обличишь разумного, то он поймет наставление."},{num:26,verse:"Разоряющий отца и выгоняющий мать – сын срамной и бесчестный."},{num:27,verse:"Перестань, сын мой, слушать внушения об уклонении от изречений разума."},{num:28,verse:"Лукавый свидетель издевается над судом, и уста беззаконных глотают неправду."},{num:29,verse:"Готовы для кощунствующих суды, и побои – на тело глупых."}]},{chapter:"20",verses:[{num:1,verse:"Вино – глумливо, сикера – буйна; и всякий, увлекающийся ими, неразумен."},{num:2,verse:"Гроза царя – как бы рев льва: кто раздражает его, тот грешит против самого себя."},{num:3,verse:"Честь для человека – отстать от ссоры; а всякий глупец задорен."},{num:4,verse:"Ленивец зимою не пашет: поищет летом – и нет ничего."},{num:5,verse:"Помыслы в сердце человека – глубокие воды, но человек разумный вычерпывает их."},{num:6,verse:"Многие хвалят человека за милосердие, но правдивого человека кто находит?"},{num:7,verse:"Праведник ходит в своей непорочности: блаженны дети его после него!"},{num:8,verse:"Царь, сидящий на престоле суда, разгоняет очами своими все злое."},{num:9,verse:'Кто может сказать: "я очистил мое сердце, я чист от греха моего?"'},{num:10,verse:"Неодинаковые весы, неодинаковая мера, то и другое – мерзость пред Господом."},{num:11,verse:"Можно узнать даже отрока по занятиям его, чисто ли и правильно ли будет поведение его."},{num:12,verse:"Ухо слышащее и глаз видящий – и то и другое создал Господь."},{num:13,verse:"Не люби спать, чтобы тебе не обеднеть; держи открытыми глаза твои, и будешь досыта есть хлеб."},{num:14,verse:'"Дурно, дурно", говорит покупатель, а когда отойдет, хвалится.'},{num:15,verse:"Есть золото и много жемчуга, но драгоценная утварь – уста разумные."},{num:16,verse:"Возьми платье его, так как он поручился за чужого; и за стороннего возьми от него залог."},{num:17,verse:"Сладок для человека хлеб, <i>приобретенный</i> неправдою; но после рот его наполнится дресвою."},{num:18,verse:"Предприятия получают твердость чрез совещание, и по совещании веди войну."},{num:19,verse:"Кто ходит переносчиком, тот открывает тайну; и кто широко раскрывает рот, с тем не сообщайся."},{num:20,verse:"Кто злословит отца своего и свою мать, того светильник погаснет среди глубокой тьмы."},{num:21,verse:"Наследство, поспешно захваченное вначале, не благословится впоследствии."},{num:22,verse:'Не говори: "я отплачу за зло"; предоставь Господу, и Он сохранит тебя.'},{num:23,verse:"Мерзость пред Господом – неодинаковые гири, и неверные весы – не добро."},{num:24,verse:"От Господа направляются шаги человека; человеку же как узнать путь свой?"},{num:25,verse:"Сеть для человека – поспешно давать обет, и после обета обдумывать."},{num:26,verse:"Мудрый царь вывеет нечестивых и обратит на них колесо."},{num:27,verse:"Светильник Господень – дух человека, испытывающий все глубины сердца."},{num:28,verse:"Милость и истина охраняют царя, и милостью он поддерживает престол свой."},{num:29,verse:"Слава юношей – сила их, а украшение стариков – седина."},{num:30,verse:"Раны от побоев – врачевство против зла, и удары, проникающие во внутренности чрева."}]},{chapter:"21",verses:[{num:1,verse:"Сердце царя – в руке Господа, как потоки вод: куда захочет, Он направляет его."},{num:2,verse:"Всякий путь человека прям в глазах его; но Господь взвешивает сердца."},{num:3,verse:"Соблюдение правды и правосудия более угодно Господу, нежели жертва."},{num:4,verse:"Гордость очей и надменность сердца, отличающие нечестивых, – грех."},{num:5,verse:"Помышления прилежного стремятся к изобилию, а всякий торопливый терпит лишение."},{num:6,verse:"Приобретение сокровища лживым языком – мимолетное дуновение ищущих смерти."},{num:7,verse:"Насилие нечестивых обрушится на них, потому что они отреклись соблюдать правду."},{num:8,verse:"Превратен путь человека развращенного; а кто чист, того действие прямо."},{num:9,verse:"Лучше жить в углу на кровле, нежели со сварливою женою в пространном доме."},{num:10,verse:"Душа нечестивого желает зла: не найдет милости в глазах его и друг его."},{num:11,verse:"Когда наказывается кощунник, простой делается мудрым; и когда вразумляется мудрый, то он приобретает знание."},{num:12,verse:"Праведник наблюдает за домом нечестивого: как повергаются нечестивые в несчастие."},{num:13,verse:"Кто затыкает ухо свое от вопля бедного, тот и сам будет вопить, – и не будет услышан."},{num:14,verse:"Подарок тайный тушит гнев, и дар в пазуху – сильную ярость."},{num:15,verse:"Соблюдение правосудия – радость для праведника и страх для делающих зло."},{num:16,verse:"Человек, сбившийся с пути разума, водворится в собрании мертвецов."},{num:17,verse:"Кто любит веселье, обеднеет; а кто любит вино и тук, не разбогатеет."},{num:18,verse:"Выкупом будет за праведного нечестивый и за прямодушного – лукавый."},{num:19,verse:"Лучше жить в земле пустынной, нежели с женою сварливою и сердитою."},{num:20,verse:"Вожделенное сокровище и тук – в доме мудрого; а глупый человек расточает их."},{num:21,verse:"Соблюдающий правду и милость найдет жизнь, правду и славу."},{num:22,verse:"Мудрый входит в город сильных и ниспровергает крепость, на которую они надеялись."},{num:23,verse:"Кто хранит уста свои и язык свой, тот хранит от бед душу свою."},{num:24,verse:"Надменный злодей – кощунник имя ему – действует в пылу гордости."},{num:25,verse:"Алчба ленивца убьет его, потому что руки его отказываются работать;"},{num:26,verse:"всякий день он сильно алчет, а праведник дает и не жалеет."},{num:27,verse:"Жертва нечестивых – мерзость, особенно когда с лукавством приносят ее."},{num:28,verse:"Лжесвидетель погибнет; а человек, который говорит, что знает, будет говорить всегда."},{num:29,verse:"Человек нечестивый дерзок лицом своим, а праведный держит прямо путь свой."},{num:30,verse:"Нет мудрости, и нет разума, и нет совета вопреки Господу."},{num:31,verse:"Коня приготовляют на день битвы, но победа – от Господа."}]},{chapter:"22",verses:[{num:1,verse:"Доброе имя лучше большого богатства, и добрая слава лучше серебра и золота."},{num:2,verse:"Богатый и бедный встречаются друг с другом: того и другого создал Господь."},{num:3,verse:"Благоразумный видит беду, и укрывается; а неопытные идут вперед, и наказываются."},{num:4,verse:"За смирением следует страх Господень, богатство и слава и жизнь."},{num:5,verse:"Терны и сети на пути коварного; кто бережет душу свою, удались от них."},{num:6,verse:"Наставь юношу при начале пути его: он не уклонится от него, когда и состарится."},{num:7,verse:"Богатый господствует над бедным, и должник <i>делается</i> рабом заимодавца."},{num:8,verse:"Сеющий неправду пожнет беду, и трости гнева его не станет. [Человека, доброхотно дающего, любит Бог, и недостаток дел его восполнит.]"},{num:9,verse:"Милосердый будет благословляем, потому что дает бедному от хлеба своего. [Победу и честь приобретает дающий дары, и даже овладевает душею получающих оные.]"},{num:10,verse:"Прогони кощунника, и удалится раздор, и прекратятся ссора и брань."},{num:11,verse:"Кто любит чистоту сердца, у того приятность на устах, тому царь – друг."},{num:12,verse:"Очи Господа охраняют знание, а слова законопреступника Он ниспровергает."},{num:13,verse:'Ленивец говорит: "лев на улице! посреди площади убьют меня!"'},{num:14,verse:"Глубокая пропасть – уста блудниц: на кого прогневается Господь, тот упадет туда."},{num:15,verse:"Глупость привязалась к сердцу юноши, но исправительная розга удалит ее от него."},{num:16,verse:"Кто обижает бедного, чтобы умножить свое богатство, и кто дает богатому, тот обеднеет."},{num:0,verse:""},{num:17,verse:"Приклони ухо твое, и слушай слова мудрых, и сердце твое обрати к моему знанию;"},{num:18,verse:"потому что утешительно будет, если ты будешь хранить их в сердце твоем, и они будут также в устах твоих."},{num:19,verse:"Чтобы упование твое было на Господа, я учу тебя и сегодня, и ты <i>помни</i>."},{num:20,verse:"Не писал ли я тебе трижды в советах и наставлении,"},{num:21,verse:"чтобы научить тебя точным словам истины, дабы ты мог передавать слова истины посылающим тебя?"},{num:0,verse:""},{num:22,verse:"Не будь грабителем бедного, потому что он беден, и не притесняй несчастного у ворот,"},{num:23,verse:"потому что Господь вступится в дело их и исхитит душу у грабителей их."},{num:24,verse:"Не дружись с гневливым и не сообщайся с человеком вспыльчивым,"},{num:25,verse:"чтобы не научиться путям его и не навлечь петли на душу твою."},{num:26,verse:"Не будь из тех, которые дают руки и поручаются за долги:"},{num:27,verse:"если тебе нечем заплатить, то для чего доводить себя, чтобы взяли постель твою из-под тебя?"},{num:28,verse:"Не передвигай межи давней, которую провели отцы твои."},{num:29,verse:"Видел ли ты человека проворного в своем деле? Он будет стоять перед царями, он не будет стоять перед простыми."}]},{chapter:"23",verses:[{num:1,verse:"Когда сядешь вкушать пищу с властелином, то тщательно наблюдай, что перед тобою,"},{num:2,verse:"и поставь преграду в гортани твоей, если ты алчен."},{num:3,verse:"Не прельщайся лакомыми яствами его; это – обманчивая пища."},{num:4,verse:"Не заботься о том, чтобы нажить богатство; оставь такие мысли твои."},{num:5,verse:"Устремишь глаза твои на него, и – его уже нет; потому что оно сделает себе крылья и, как орел, улетит к небу."},{num:6,verse:"Не вкушай пищи у человека завистливого и не прельщайся лакомыми яствами его;"},{num:7,verse:'потому что, каковы мысли в душе его, таков и он; "ешь и пей", говорит он тебе, а сердце его не с тобою.'},{num:8,verse:"Кусок, который ты съел, изблюешь, и добрые слова твои ты потратишь напрасно."},{num:9,verse:"В уши глупого не говори, потому что он презрит разумные слова твои."},{num:10,verse:"Не передвигай межи давней и на поля сирот не заходи,"},{num:11,verse:"потому что Защитник их силен; Он вступится в дело их с тобою."},{num:12,verse:"Приложи сердце твое к учению и уши твои – к умным словам."},{num:13,verse:"Не оставляй юноши без наказания: если накажешь его розгою, он не умрет;"},{num:14,verse:"ты накажешь его розгою и спасешь душу его от преисподней."},{num:0,verse:""},{num:15,verse:"Сын мой! если сердце твое будет мудро, то порадуется и мое сердце;"},{num:16,verse:"и внутренности мои будут радоваться, когда уста твои будут говорить правое."},{num:17,verse:"Да не завидует сердце твое грешникам, но да пребудет оно во все дни в страхе Господнем;"},{num:18,verse:"потому что есть будущность, и надежда твоя не потеряна."},{num:19,verse:"Слушай, сын мой, и будь мудр, и направляй сердце твое на прямой путь."},{num:20,verse:"Не будь между упивающимися вином, между пресыщающимися мясом:"},{num:21,verse:"потому что пьяница и пресыщающийся обеднеют, и сонливость оденет в рубище."},{num:22,verse:"Слушайся отца твоего: он родил тебя; и не пренебрегай матери твоей, когда она и состарится."},{num:23,verse:"Купи истину и не продавай мудрости и учения и разума."},{num:24,verse:"Торжествует отец праведника, и родивший мудрого радуется о нем."},{num:25,verse:"Да веселится отец твой и да торжествует мать твоя, родившая тебя."},{num:0,verse:""},{num:26,verse:"Сын мой! отдай сердце твое мне, и глаза твои да наблюдают пути мои,"},{num:27,verse:"потому что блудница – глубокая пропасть, и чужая жена – тесный колодезь;"},{num:28,verse:"она, как разбойник, сидит в засаде и умножает между людьми законопреступников."},{num:29,verse:"У кого вой? у кого стон? у кого ссоры? у кого горе? у кого раны без причины? у кого багровые глаза?"},{num:30,
verse:"У тех, которые долго сидят за вином, которые приходят отыскивать <i>вина</i> приправленного."},{num:31,verse:"Не смотри на вино, как оно краснеет, как оно искрится в чаше, как оно ухаживается ровно:"},{num:32,verse:"впоследствии, как змей, оно укусит, и ужалит, как аспид;"},{num:33,verse:"глаза твои будут смотреть на чужих жен, и сердце твое заговорит развратное,"},{num:34,verse:"и ты будешь, как спящий среди моря и как спящий на верху мачты."},{num:35,verse:'[И скажешь:] "били меня, мне не было больно; толкали меня, я не чувствовал. Когда проснусь, опять буду искать того же".'}]},{chapter:"24",verses:[{num:1,verse:"Не ревнуй злым людям и не желай быть с ними,"},{num:2,verse:"потому что о насилии помышляет сердце их, и о злом говорят уста их."},{num:3,verse:"Мудростью устрояется дом и разумом утверждается,"},{num:4,verse:"и с уменьем внутренности его наполняются всяким драгоценным и прекрасным имуществом."},{num:5,verse:"Человек мудрый силен, и человек разумный укрепляет силу свою."},{num:6,verse:"Поэтому с обдуманностью веди войну твою, и успех <i>будет</i> при множестве совещаний."},{num:7,verse:"Для глупого слишком высока мудрость; у ворот не откроет он уст своих."},{num:8,verse:"Кто замышляет сделать зло, того называют злоумышленником."},{num:9,verse:"Помысл глупости – грех, и кощунник – мерзость для людей."},{num:10,verse:"Если ты в день бедствия оказался слабым, то бедна сила твоя."},{num:11,verse:"Спасай взятых на смерть, и неужели откажешься от обреченных на убиение?"},{num:12,verse:'Скажешь ли: "вот, мы не знали этого"? А Испытующий сердца разве не знает? Наблюдающий над душею твоею знает это, и воздаст человеку по делам его.'},{num:13,verse:"Ешь, сын мой, мед, потому что он приятен, и сот, который сладок для гортани твоей:"},{num:14,verse:"таково и познание мудрости для души твоей. Если ты нашел <i>ее</i>, то есть будущность, и надежда твоя не потеряна."},{num:15,verse:"Не злоумышляй, нечестивый, против жилища праведника, не опустошай места покоя его,"},{num:16,verse:"ибо семь раз упадет праведник, и встанет; а нечестивые впадут в погибель."},{num:17,verse:"Не радуйся, когда упадет враг твой, и да не веселится сердце твое, когда он споткнется."},{num:18,verse:"Иначе, увидит Господь, и неугодно будет это в очах Его, и Он отвратит от него гнев Свой."},{num:19,verse:"Не негодуй на злодеев и не завидуй нечестивым,"},{num:20,verse:"потому что злой не имеет будущности, – светильник нечестивых угаснет."},{num:21,verse:"Бойся, сын мой, Господа и царя; с мятежниками не сообщайся,"},{num:22,verse:"потому что внезапно придет погибель от них, и беду от них обоих кто предузнает?"},{num:23,verse:"Сказано также мудрыми: иметь лицеприятие на суде – нехорошо."},{num:24,verse:'Кто говорит виновному: "ты прав", того будут проклинать народы, того будут ненавидеть племена;'},{num:25,verse:"а обличающие будут любимы, и на них придет благословение."},{num:26,verse:"В уста целует, кто отвечает словами верными."},{num:27,verse:"Соверши дела твои вне дома, окончи их на поле твоем, и потом устрояй и дом твой."},{num:28,verse:"Не будь лжесвидетелем на ближнего твоего: к чему тебе обманывать устами твоими?"},{num:29,verse:'Не говори: "как он поступил со мною, так и я поступлю с ним, воздам человеку по делам его".'},{num:30,verse:"Проходил я мимо поля человека ленивого и мимо виноградника человека скудоумного:"},{num:31,verse:"и вот, все это заросло терном, поверхность его покрылась крапивою, и каменная ограда его обрушилась."},{num:32,verse:"И посмотрел я, и обратил сердце мое, и посмотрел и получил урок:"},{num:33,verse:'"немного поспишь, немного подремлешь, немного, сложив руки, полежишь, -'},{num:34,verse:'и придет, <i>как</i> прохожий, бедность твоя, и нужда твоя – как человек вооруженный".'}]},{chapter:"25",verses:[{num:1,verse:"И это притчи Соломона, которые собрали мужи Езекии, царя Иудейского."},{num:2,verse:"Слава Божия – облекать тайною дело, а слава царей – исследывать дело."},{num:3,verse:"Как небо в высоте и земля в глубине, так сердце царей – неисследимо."},{num:4,verse:"Отдели примесь от серебра, и выйдет у серебряника сосуд:"},{num:5,verse:"удали неправедного от царя, и престол его утвердится правдою."},{num:6,verse:"Не величайся пред лицем царя, и на месте великих не становись;"},{num:7,verse:'потому что лучше, когда скажут тебе: "пойди сюда повыше", нежели когда понизят тебя пред знатным, которого видели глаза твои.'},{num:8,verse:"Не вступай поспешно в тяжбу: иначе что будешь делать при окончании, когда соперник твой осрамит тебя?"},{num:9,verse:"Веди тяжбу с соперником твоим, но тайны другого не открывай,"},{num:10,verse:"дабы не укорил тебя услышавший это, и тогда бесчестие твое не отойдет от тебя. [Любовь и дружба освобождают: сбереги их для себя, чтобы не сделаться тебе достойным поношения; сохрани пути твои благоустроенными.]"},{num:11,verse:"Золотые яблоки в серебряных прозрачных сосудах – слово, сказанное прилично."},{num:12,verse:"Золотая серьга и украшение из чистого золота – мудрый обличитель для внимательного уха."},{num:13,verse:"Что прохлада от снега во время жатвы, то верный посол для посылающего его: он доставляет душе господина своего отраду."},{num:14,verse:"Что тучи и ветры без дождя, то человек, хвастающий ложными подарками."},{num:15,verse:"Кротостью склоняется к милости вельможа, и мягкий язык переламывает кость."},{num:16,verse:"Нашел ты мед, – ешь, сколько тебе потребно, чтобы не пресытиться им и не изблевать его."},{num:17,verse:"Не учащай входить в дом друга твоего, чтобы он не наскучил тобою и не возненавидел тебя."},{num:18,verse:"Что молот и меч и острая стрела, то человек, произносящий ложное свидетельство против ближнего своего."},{num:19,verse:"Что сломанный зуб и расслабленная нога, то надежда на ненадежного [человека] в день бедствия."},{num:20,verse:"Что снимающий с себя одежду в холодный день, что уксус на рану, то поющий песни печальному сердцу. [Как моль одежде и червь дереву, так печаль вредит сердцу человека.]"},{num:21,verse:"Если голоден враг твой, накорми его хлебом; и если он жаждет, напой его водою:"},{num:22,verse:"ибо, [делая сие,] ты собираешь горящие угли на голову его, и Господь воздаст тебе."},{num:23,verse:"Северный ветер производит дождь, а тайный язык – недовольные лица."},{num:24,verse:"Лучше жить в углу на кровле, нежели со сварливою женою в пространном доме."},{num:25,verse:"Что холодная вода для истомленной жаждой души, то добрая весть из дальней страны."},{num:26,verse:"Что возмущенный источник и поврежденный родник, то праведник, падающий пред нечестивым."},{num:27,verse:"Как нехорошо есть много меду, так домогаться славы не есть слава."},{num:28,verse:"Что город разрушенный, без стен, то человек, не владеющий духом своим."}]},{chapter:"26",verses:[{num:1,verse:"Как снег летом и дождь во время жатвы, так честь неприлична глупому."},{num:2,verse:"Как воробей вспорхнет, как ласточка улетит, так незаслуженное проклятие не сбудется."},{num:3,verse:"Бич для коня, узда для осла, а палка для глупых."},{num:4,verse:"Не отвечай глупому по глупости его, чтобы и тебе не сделаться подобным ему;"},{num:5,verse:"но отвечай глупому по глупости его, чтобы он не стал мудрецом в глазах своих."},{num:6,verse:"Подрезывает себе ноги, терпит неприятность тот, кто дает словесное поручение глупцу."},{num:7,verse:"Неровно поднимаются ноги у хромого, – и притча в устах глупцов."},{num:8,verse:"Что влагающий драгоценный камень в пращу, то воздающий глупому честь."},{num:9,verse:"Что <i>колючий</i> терн в руке пьяного, то притча в устах глупцов."},{num:10,verse:"Сильный делает все произвольно: и глупого награждает, и всякого прохожего награждает."},{num:11,verse:"Как пес возвращается на блевотину свою, так глупый повторяет глупость свою."},{num:12,verse:"Видал ли ты человека, мудрого в глазах его? На глупого больше надежды, нежели на него."},{num:13,verse:'Ленивец говорит: "лев на дороге! лев на площадях!"'},{num:14,verse:"Дверь ворочается на крючьях своих, а ленивец на постели своей."},{num:15,verse:"Ленивец опускает руку свою в чашу, и ему тяжело донести ее до рта своего."},{num:16,verse:"Ленивец в глазах своих мудрее семерых, отвечающих обдуманно."},{num:17,verse:"Хватает пса за уши, кто, проходя мимо, вмешивается в чужую ссору."},{num:18,verse:"Как притворяющийся помешанным бросает огонь, стрелы и смерть,"},{num:19,verse:'так – человек, который коварно вредит другу своему и потом говорит: "я только пошутил".'},{num:20,verse:"Где нет больше дров, огонь погасает, и где нет наушника, раздор утихает."},{num:21,verse:"Уголь – для жара и дрова – для огня, а человек сварливый – для разжжения ссоры."},{num:22,verse:"Слова наушника – как лакомства, и они входят во внутренность чрева."},{num:23,verse:"Что нечистым серебром обложенный глиняный сосуд, то пламенные уста и сердце злобное."},{num:24,verse:"Устами своими притворяется враг, а в сердце своем замышляет коварство."},{num:25,verse:"Если он говорит и нежным голосом, не верь ему, потому что семь мерзостей в сердце его."},{num:26,verse:"Если ненависть прикрывается наедине, то откроется злоба его в народном собрании."},{num:27,verse:"Кто роет яму, тот упадет в нее, и кто покатит вверх камень, к тому он воротится."},{num:28,verse:"Лживый язык ненавидит уязвляемых им, и льстивые уста готовят падение."}]},{chapter:"27",verses:[{num:1,verse:"Не хвались завтрашним днем, потому что не знаешь, что родит тот день."},{num:2,verse:"Пусть хвалит тебя другой, а не уста твои, – чужой, а не язык твой."},{num:3,verse:"Тяжел камень, весок и песок; но гнев глупца тяжелее их обоих."},{num:4,verse:"Жесток гнев, неукротима ярость; но кто устоит против ревности?"},{num:5,verse:"Лучше открытое обличение, нежели скрытая любовь."},{num:6,verse:"Искренни укоризны от любящего, и лживы поцелуи ненавидящего."},{num:7,verse:"Сытая душа попирает и сот, а голодной душе все горькое сладко."},{num:8,verse:"Как птица, покинувшая гнездо свое, так человек, покинувший место свое."},{num:9,verse:"Масть и курение радуют сердце; так сладок <i>всякому</i> друг сердечным советом своим."},{num:10,verse:"Не покидай друга твоего и друга отца твоего, и в дом брата твоего не ходи в день несчастья твоего: лучше сосед вблизи, нежели брат вдали."},{num:0,verse:""},{num:11,verse:"Будь мудр, сын мой, и радуй сердце мое; и я буду иметь, что отвечать злословящему меня."},{num:12,verse:"Благоразумный видит беду и укрывается; а неопытные идут вперед <i>и</i> наказываются."},{num:13,verse:"Возьми у него платье его, потому что он поручился за чужого, и за стороннего возьми от него залог."},{num:14,verse:"Кто громко хвалит друга своего с раннего утра, того сочтут за злословящего."},{num:15,verse:"Непрестанная капель в дождливый день и сварливая жена – равны:"},{num:16,verse:"кто хочет скрыть ее, тот хочет скрыть ветер и масть в правой руке своей, дающую знать о себе."},{num:17,verse:"Железо железо острит, и человек изощряет взгляд друга своего."},{num:18,verse:"Кто стережет смоковницу, тот будет есть плоды ее; и кто бережет господина своего, тот будет в чести."},{num:19,verse:"Как в воде лицо – к лицу, так сердце человека – к человеку."},{num:20,verse:"Преисподняя и Аваддон – ненасытимы; так ненасытимы и глаза человеческие. [Мерзость пред Господом дерзко поднимающий глаза, и неразумны невоздержанные языком.]"},{num:21,verse:"Что плавильня – для серебра, горнило – для золота, то для человека уста, которые хвалят его. [Сердце беззаконника ищет зла, сердце же правое ищет знания.]"},{num:22,verse:"Толки глупого в ступе пестом вместе с зерном, не отделится от него глупость его."},{num:23,verse:"Хорошо наблюдай за скотом твоим, имей попечение о стадах;"},{num:24,verse:"потому что <i>богатство</i> не навек, да и власть разве из рода в род?"},{num:25,verse:"Прозябает трава, и является зелень, и собирают горные травы."},{num:26,verse:"Овцы – на одежду тебе, и козлы – на покупку поля."},{num:27,verse:"И довольно козьего молока в пищу тебе, в пищу домашним твоим и на продовольствие служанкам твоим."}]},{chapter:"28",verses:[{num:1,verse:"Нечестивый бежит, когда никто не гонится <i>за</i> <i>ним</i>; а праведник смел, как лев."},{num:2,verse:"Когда страна отступит от закона, тогда много в ней начальников; а при разумном и знающем муже она долговечна."},{num:3,verse:"Человек бедный и притесняющий слабых <i>то же, что</i> проливной дождь, смывающий хлеб."},{num:4,verse:"Отступники от закона хвалят нечестивых, а соблюдающие закон негодуют на них."},{num:5,verse:"Злые люди не разумеют справедливости, а ищущие Господа разумеют все."},{num:6,verse:"Лучше бедный, ходящий в своей непорочности, нежели тот, кто извращает пути свои, хотя он и богат."},{num:7,verse:"Хранящий закон – сын разумный, а знающийся с расточителями срамит отца своего."},{num:8,verse:"Умножающий имение свое ростом и лихвою соберет его для благотворителя бедных."},{num:9,verse:"Кто отклоняет ухо свое от слушания закона, того и молитва – мерзость."},{num:10,verse:"Совращающий праведных на путь зла сам упадет в свою яму, а непорочные наследуют добро."},{num:11,verse:"Человек богатый – мудрец в глазах своих, но умный бедняк обличит его."},{num:12,verse:"Когда торжествуют праведники, великая слава, но когда возвышаются нечестивые, люди укрываются."},{num:13,verse:"Скрывающий свои преступления не будет иметь успеха; а кто сознается и оставляет их, тот будет помилован."},{num:0,verse:""},{num:14,verse:"Блажен человек, который всегда пребывает в благоговении; а кто ожесточает сердце свое, тот попадет в беду."},{num:15,verse:"Как рыкающий лев и голодный медведь, так нечестивый властелин над бедным народом."},{num:16,verse:"Неразумный правитель много делает притеснений, а ненавидящий корысть продолжит дни."},{num:17,verse:"Человек, виновный в пролитии человеческой крови, будет бегать до могилы, чтобы кто не схватил его."},{num:18,verse:"Кто ходит непорочно, то будет невредим; а ходящий кривыми путями упадет на одном из них."},{num:19,verse:"Кто возделывает землю свою, тот будет насыщаться хлебом, а кто подражает праздным, тот насытится нищетою."},{num:20,verse:"Верный человек богат благословениями, а кто спешит разбогатеть, тот не останется ненаказанным."},{num:21,verse:"Быть лицеприятным – нехорошо: такой человек и за кусок хлеба сделает неправду."},{num:22,verse:"Спешит к богатству завистливый человек, и не думает, что нищета постигнет его."},{num:23,verse:"Обличающий человека найдет после большую приязнь, нежели тот, кто льстит языком."},{num:24,verse:'Кто обкрадывает отца своего и мать свою и говорит: "это не грех", тот – сообщник грабителям.'},{num:25,verse:"Надменный разжигает ссору, а надеющийся на Господа будет благоденствовать."},{num:26,verse:"Кто надеется на себя, тот глуп; а кто ходит в мудрости, тот будет цел."},{num:27,verse:"Дающий нищему не обеднеет; а кто закрывает глаза свои от него, на том много проклятий."},{num:28,verse:"Когда возвышаются нечестивые, люди укрываются, а когда они падают, умножаются праведники."}]},{chapter:"29",verses:[{num:1,verse:"Человек, который, будучи обличаем, ожесточает выю свою, внезапно сокрушится, и не будет <i>ему</i> исцеления."},{num:2,verse:"Когда умножаются праведники, веселится народ, а когда господствует нечестивый, народ стенает."},{num:3,verse:"Человек, любящий мудрость, радует отца своего; а кто знается с блудницами, тот расточает имение."},{num:4,verse:"Царь правосудием утверждает землю, а любящий подарки разоряет ее."},{num:5,verse:"Человек, льстящий другу своему, расстилает сеть ногам его."},{num:6,verse:"В грехе злого человека – сеть <i>для [него</i>, а праведник веселится и радуется."},{num:7,verse:"Праведник тщательно вникает в тяжбу бедных, а нечестивый не разбирает дела."},{num:8,verse:"Люди развратные возмущают город, а мудрые утишают мятеж."},{num:9,verse:"Умный человек, судясь с человеком глупым, сердится ли, смеется ли, – не имеет покоя."},{num:10,verse:"Кровожадные люди ненавидят непорочного, а праведные заботятся о его жизни."},{num:11,verse:"Глупый весь гнев свой изливает, а мудрый сдерживает его."},{num:12,verse:"Если правитель слушает ложные речи, то и все служащие у него нечестивы."},{num:13,verse:"Бедный и лихоимец встречаются друг с другом; но свет глазам того и другого дает Господь."},{num:14,verse:"Если царь судит бедных по правде, то престол его навсегда утвердится."},{num:15,verse:"Розга и обличение дают мудрость; но отрок, оставленный в небрежении, делает стыд своей матери."},{num:16,verse:"При умножении нечестивых умножается беззаконие; но праведники увидят падение их."},{num:17,verse:"Наказывай сына твоего, и он даст тебе покой, и доставит радость душе твоей."},{num:0,verse:""},{num:18,verse:"Без откровения свыше народ необуздан, а соблюдающий закон блажен."},{num:19,verse:"Словами не научится раб, потому что, хотя он понимает <i>их</i>, но не слушается."},{num:20,verse:"Видал ли ты человека опрометчивого в словах своих? на глупого больше надежды, нежели на него."},{num:21,verse:"Если с детства воспитывать раба в неге, то впоследствии он захочет быть сыном."},{num:22,verse:"Человек гневливый заводит ссору, и вспыльчивый много грешит."},{num:23,verse:"Гордость человека унижает его, а смиренный духом приобретает честь."},{num:24,verse:"Кто делится с вором, тот ненавидит душу свою; слышит он проклятие, но не объявляет о том."},{num:25,verse:"Боязнь пред людьми ставит сеть; а надеющийся на Господа будет безопасен."},{num:26,verse:"Многие ищут <i>благосклонного</i> лица правителя, но судьба человека – от Господа."},{num:27,verse:"Мерзость для праведников – человек неправедный, и мерзость для нечестивого – идущий прямым путем."}]},{chapter:"30",verses:[{num:1,verse:"Слова Агура, сына Иакеева. Вдохновенные изречения, <i>которые</i> сказал этот человек Ифиилу, Ифиилу и Укалу:"},{num:2,verse:"подлинно, я более невежда, нежели кто-либо из людей, и разума человеческого нет у меня,"},{num:3,verse:"и не научился я мудрости, и познания святых не имею."},{num:4,verse:"Кто восходил на небо и нисходил? кто собрал ветер в пригоршни свои? кто завязал воду в одежду? кто поставил все пределы земли? какое имя ему? и какое имя сыну его? знаешь ли?"},{num:0,verse:""},{num:5,verse:"Всякое слово Бога чисто; Он – щит уповающим на Него."},{num:6,verse:"Не прибавляй к словам Его, чтобы Он не обличил тебя, и ты не оказался лжецом."},{num:0,verse:""},{num:7,verse:"Двух вещей я прошу у Тебя, не откажи мне, прежде нежели я умру:"},{num:8,verse:"суету и ложь удали от меня, нищеты и богатства не давай мне, питай меня насущным хлебом,"},{num:9,verse:'дабы, пресытившись, я не отрекся <i>Тебя</i> и не сказал: "кто Господь?" и чтобы, обеднев, не стал красть и употреблять имя Бога моего всуе.'},{num:10,verse:"Не злословь раба пред господином его, чтобы он не проклял тебя, и ты не остался виноватым."},{num:11,verse:"Есть род, который проклинает отца своего и не благословляет матери своей."},{num:12,verse:"Есть род, который чист в глазах своих, тогда как не омыт от нечистот своих."},{num:13,verse:"Есть род – о, как высокомерны глаза его, и как подняты ресницы его!"},{num:14,verse:"Есть род, у которого зубы – мечи, и челюсти – ножи, чтобы пожирать бедных на земле и нищих между людьми."},{num:15,verse:'У ненасытимости две дочери: "давай, давай!" Вот три ненасытимых, и четыре, которые не скажут: "довольно!"'},{num:16,verse:'Преисподняя и утроба бесплодная, земля, которая не насыщается водою, и огонь, который не говорит: "довольно!"'},{num:17,verse:"Глаз, насмехающийся над отцом и пренебрегающий покорностью к матери, выклюют вороны дольные, и сожрут птенцы орлиные!"},{num:18,verse:"Три вещи непостижимы для меня, и четырех я не понимаю:"},{num:19,verse:"пути орла на небе, пути змея на скале, пути корабля среди моря и пути мужчины к девице."},{num:20,verse:'Таков путь и жены прелюбодейной; поела и обтерла рот свой, и говорит: "я ничего худого не сделала".'},{num:21,verse:"От трех трясется земля, четырех она не может носить:"},{num:22,verse:"раба, когда он делается царем; глупого, когда он досыта ест хлеб;"},{num:23,verse:"позорную женщину, когда она выходит замуж, и служанку, когда она занимает место госпожи своей."},{num:24,verse:"Вот четыре малых на земле, но они мудрее мудрых:"},{num:25,verse:"муравьи – народ не сильный, но летом заготовляют пищу свою;"},{num:26,verse:"горные мыши – народ слабый, но ставят домы свои на скале;"},{num:27,verse:"у саранчи нет царя, но выступает вся она стройно;"},{num:28,verse:"паук лапками цепляется, но бывает в царских чертогах."},{num:29,verse:"Вот трое имеют стройную походку, и четверо стройно выступают:"},{num:30,verse:"лев, силач между зверями, не посторонится ни перед кем;"},{num:31,verse:"конь и козел, [предводитель стада,] и царь среди народа своего."},{num:32,verse:"Если ты в заносчивости своей сделал глупость и помыслил злое, то <i>положи</i> руку на уста;"},{num:33,verse:"потому что, как сбивание молока производит масло, толчок в нос производит кровь, так и возбуждение гнева производит ссору."}]},{chapter:"31",verses:[{num:1,verse:"Слова Лемуила царя. Наставление, которое преподала ему мать его:"},{num:2,verse:"что, сын мой? что, сын чрева моего? что, сын обетов моих?"},{num:3,verse:"Не отдавай женщинам сил твоих, ни путей твоих губительницам царей."},{num:4,verse:"Не царям, Лемуил, не царям пить вино, и не князьям – сикеру,"},{num:5,verse:"чтобы, напившись, они не забыли закона и не превратили суда всех угнетаемых."},{num:6,verse:"Дайте сикеру погибающему и вино огорченному душею;"},{num:7,verse:"пусть он выпьет и забудет бедность свою и не вспомнит больше о своем страдании."},{num:8,verse:"Открывай уста твои за безгласного и для защиты всех сирот."},{num:9,verse:"Открывай уста твои для правосудия и для дела бедного и нищего."},{num:0,verse:""},{num:10,verse:"Кто найдет добродетельную жену? цена ее выше жемчугов;"},{num:11,verse:"уверено в ней сердце мужа ее, и он не останется без прибытка;"},{num:12,verse:"она воздает ему добром, а не злом, во все дни жизни своей."},{num:13,verse:"Добывает шерсть и лен, и с охотою работает своими руками."},{num:14,verse:"Она, как купеческие корабли, издалека добывает хлеб свой."},{num:15,verse:"Она встает еще ночью и раздает пищу в доме своем и урочное служанкам своим."},{num:16,verse:"Задумает она о поле, и приобретает его; от плодов рук своих насаждает виноградник."},{num:17,verse:"Препоясывает силою чресла свои и укрепляет мышцы свои."},{num:18,verse:"Она чувствует, что занятие ее хорошо, и – светильник ее не гаснет и ночью."},{num:19,verse:"Протягивает руки свои к прялке, и персты ее берутся за веретено."},{num:20,verse:"Длань свою она открывает бедному, и руку свою подает нуждающемуся."},{num:21,verse:"Не боится стужи для семьи своей, потому что вся семья ее одета в двойные одежды."},{num:22,verse:"Она делает себе ковры; виссон и пурпур – одежда ее."},{num:23,verse:"Муж ее известен у ворот, когда сидит со старейшинами земли."},{num:24,verse:"Она делает покрывала и продает, и поясы доставляет купцам Финикийским."},{num:25,verse:"Крепость и красота – одежда ее, и весело смотрит она на будущее."},{num:26,verse:"Уста свои открывает с мудростью, и кроткое наставление на языке ее."},{num:27,verse:"Она наблюдает за хозяйством в доме своем и не ест хлеба праздности."},{num:28,verse:"Встают дети и ублажают ее, – муж, и хвалит ее:"},{num:29,verse:'"много было жен добродетельных, но ты превзошла всех их".'},{num:30,verse:"Миловидность обманчива и красота суетна; но жена, боящаяся Господа, достойна хвалы."},{num:31,verse:"Дайте ей от плода рук ее, и да прославят ее у ворот дела ее!"}]}]};
