export const ps={treat:"Ветхий завет",title:"Псалтирь",chapters:[{chapter:"1",verses:[{num:0,verse:"Псалом Давида."},{num:0,verse:""},{num:1,verse:"Блажен муж, который не ходит на совет нечестивых и не стоит на пути грешных и не сидит в собрании развратителей,"},{num:2,verse:"но в законе Господа воля его, и о законе Его размышляет он день и ночь!"},{num:3,verse:"И будет он как дерево, посаженное при потоках вод, которое приносит плод свой во время свое, и лист которого не вянет; и во всем, что он ни делает, успеет."},{num:4,verse:"Не так – нечестивые, [не так]: но они – как прах, возметаемый ветром [с лица земли]."},{num:5,verse:"Потому не устоят нечестивые на суде, и грешники – в собрании праведных."},{num:6,verse:"Ибо знает Господь путь праведных, а путь нечестивых погибнет."}]},{chapter:"2",verses:[{num:0,verse:"Псалом Давида."},{num:0,verse:""},{num:1,verse:"Зачем мятутся народы, и племена замышляют тщетное?"},{num:2,verse:"Восстают цари земли, и князья совещаются вместе против Господа и против Помазанника Его."},{num:3,verse:'"Расторгнем узы их, и свергнем с себя оковы их".'},{num:4,verse:"Живущий на небесах посмеется, Господь поругается им."},{num:5,verse:"Тогда скажет им во гневе Своем и яростью Своею приведет их в смятение:"},{num:6,verse:'"Я помазал Царя Моего над Сионом, святою горою Моею;'},{num:7,verse:"возвещу определение: Господь сказал Мне: Ты Сын Мой; Я ныне родил Тебя;"},{num:8,verse:"проси у Меня, и дам народы в наследие Тебе и пределы земли во владение Тебе;"},{num:9,verse:'Ты поразишь их жезлом железным; сокрушишь их, как сосуд горшечника".'},{num:10,verse:"Итак вразумитесь, цари; научитесь, судьи земли!"},{num:11,verse:"Служите Господу со страхом и радуйтесь [пред Ним] с трепетом."},{num:12,verse:"Почтите Сына, чтобы Он не прогневался, и чтобы вам не погибнуть в пути <i>вашем</i>, ибо гнев Его возгорится вскоре. Блаженны все, уповающие на Него."}]},{chapter:"3",verses:[{num:1,verse:"Псалом Давида, когда он бежал от Авессалома, сына своего."},{num:0,verse:""},{num:2,verse:"Господи! как умножились враги мои! Многие восстают на меня"},{num:3,verse:'многие говорят душе моей: "нет ему спасения в Боге".'},{num:4,verse:"Но Ты, Господи, щит предо мною, слава моя, и Ты возносишь голову мою."},{num:5,verse:"Гласом моим взываю к Господу, и Он слышит меня со святой горы Своей."},{num:6,verse:"Ложусь я, сплю и встаю, ибо Господь защищает меня."},{num:7,verse:"Не убоюсь тем народа, которые со всех сторон ополчились на меня."},{num:8,verse:"Восстань, Господи! спаси меня, Боже мой! ибо Ты поражаешь в ланиту всех врагов моих; сокрушаешь зубы нечестивых."},{num:9,verse:"От Господа спасение. Над народом Твоим благословение Твое."}]},{chapter:"4",verses:[{num:1,verse:"Начальнику хора. На струнных <i>орудиях</i>. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Когда я взываю, услышь меня, Боже правды моей! В тесноте Ты давал мне простор. Помилуй меня и услышь молитву мою."},{num:3,verse:"Сыны мужей! доколе слава моя будет в поругании? доколе будете любить суету и искать лжи?"},{num:4,verse:"Знайте, что Господь отделил для Себя святаго Своего; Господь слышит, когда я призываю Его."},{num:5,verse:"Гневаясь, не согрешайте: размыслите в сердцах ваших на ложах ваших, и утишитесь;"},{num:6,verse:"приносите жертвы правды и уповайте на Господа."},{num:7,verse:'Многие говорят: "кто покажет нам благо?" Яви нам свет лица Твоего, Господи!'},{num:8,verse:"Ты исполнил сердце мое веселием с того времени, как у них хлеб и вино [и елей] умножились."},{num:9,verse:"Спокойно ложусь я и сплю, ибо Ты, Господи, един даешь мне жить в безопасности."}]},{chapter:"5",verses:[{num:1,verse:"Начальнику хора. На духовых <i>орудиях</i>. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Услышь, Господи, слова мои, уразумей помышления мои."},{num:3,verse:"Внемли гласу вопля моего, Царь мой и Бог мой! ибо я к Тебе молюсь."},{num:4,verse:"Господи! рано услышь голос мой, – рано предстану пред Тобою, и буду ожидать,"},{num:5,verse:"ибо Ты Бог, не любящий беззакония; у Тебя не водворится злой;"},{num:6,verse:"нечестивые не пребудут пред очами Твоими: Ты ненавидишь всех, делающих беззаконие."},{num:7,verse:"Ты погубишь говорящих ложь; кровожадного и коварного гнушается Господь."},{num:8,verse:"А я, по множеству милости Твоей, войду в дом Твой, поклонюсь святому храму Твоему в страхе Твоем."},{num:9,verse:"Господи! путеводи меня в правде Твоей, ради врагов моих; уровняй предо мною путь Твой."},{num:10,verse:"Ибо нет в устах их истины: сердце их – пагуба, гортань их – открытый гроб, языком своим льстят."},{num:11,verse:"Осуди их, Боже, да падут они от замыслов своих; по множеству нечестия их, отвергни их, ибо они возмутились против Тебя."},{num:12,verse:"И возрадуются все уповающие на Тебя, вечно будут ликовать, и Ты будешь покровительствовать им; и будут хвалиться Тобою любящие имя Твое."},{num:13,verse:"Ибо Ты благословляешь праведника, Господи; благоволением, как щитом, венчаешь его."}]},{chapter:"6",verses:[{num:1,verse:"Начальнику хора. На восьмиструнном. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Господи! не в ярости Твоей обличай меня и не во гневе Твоем наказывай меня."},{num:3,verse:"Помилуй меня, Господи, ибо я немощен; исцели меня, Господи, ибо кости мои потрясены;"},{num:4,verse:"и душа моя сильно потрясена; Ты же, Господи, доколе?"},{num:5,verse:"Обратись, Господи, избавь душу мою, спаси меня ради милости Твоей,"},{num:6,verse:"ибо в смерти нет памятования о Тебе: во гробе кто будет славить Тебя?"},{num:7,verse:"Утомлен я воздыханиями моими: каждую ночь омываю ложе мое, слезами моими омочаю постель мою."},{num:8,verse:"Иссохло от печали око мое, обветшало от всех врагов моих."},{num:9,verse:"Удалитесь от меня все, делающие беззаконие, ибо услышал Господь голос плача моего,"},{num:10,verse:"услышал Господь моление мое; Господь примет молитву мою."},{num:11,verse:"Да будут постыжены и жестоко поражены все враги мои; да возвратятся и постыдятся мгновенно."}]},{chapter:"7",verses:[{num:1,verse:"Плачевная песнь, которую Давид воспел Господу по делу Хуса, из племени Вениаминова."},{num:0,verse:""},{num:2,verse:"Господи, Боже мой! на Тебя я уповаю; спаси меня от всех гонителей моих и избавь меня;"},{num:3,verse:"да не исторгнет он, подобно льву, души моей, терзая, когда нет избавляющего [и спасающего]."},{num:4,verse:"Господи, Боже мой! если я что сделал, если есть неправда в руках моих,"},{num:5,verse:"если я платил злом тому, кто был со мною в мире, – я, который спасал даже того, кто без причины стал моим врагом, -"},{num:6,verse:"то пусть враг преследует душу мою и настигнет, пусть втопчет в землю жизнь мою, и славу мою повергнет в прах."},{num:7,verse:"Восстань, Господи, во гневе Твоем; подвигнись против неистовства врагов моих, пробудись для меня на суд, который Ты заповедал, -"},{num:8,verse:"сонм людей станет вокруг Тебя; над ним поднимись на высоту."},{num:9,verse:"Господь судит народы. Суди меня, Господи, по правде моей и по непорочности моей во мне."},{num:10,verse:"Да прекратится злоба нечестивых, а праведника подкрепи, ибо Ты испытуешь сердца и утробы, праведный Боже!"},{num:11,verse:"Щит мой в Боге, спасающем правых сердцем."},{num:12,verse:"Бог – судия праведный, [крепкий и долготерпеливый,] и Бог, всякий день строго взыскивающий,"},{num:13,verse:"если <i>кто</i> не обращается. Он изощряет Свой меч, напрягает лук Свой и направляет его,"},{num:14,verse:"приготовляет для него сосуды смерти, стрелы Свои делает палящими."},{num:15,verse:"Вот, <i>нечестивый</i> зачал неправду, был чреват злобою и родил себе ложь;"},{num:16,verse:"рыл ров, и выкопал его, и упал в яму, которую приготовил:"},{num:17,verse:"злоба его обратится на его голову, и злодейство его упадет на его темя."},{num:18,verse:"Славлю Господа по правде Его и пою имени Господа Всевышнего."}]},{chapter:"8",verses:[{num:1,verse:"Начальнику хора. На Гефском <i>орудии</i>. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Господи, Боже наш! как величественно имя Твое по всей земле! Слава Твоя простирается превыше небес!"},{num:3,verse:"Из уст младенцев и грудных детей Ты устроил хвалу, ради врагов Твоих, дабы сделать безмолвным врага и мстителя."},{num:4,verse:"Когда взираю я на небеса Твои – дело Твоих перстов, на луну и звезды, которые Ты поставил,"},{num:5,verse:"то что <i>есть</i> человек, что Ты помнишь его, и сын человеческий, что Ты посещаешь его?"},{num:6,verse:"Не много Ты умалил его пред Ангелами: славою и честью увенчал его;"},{num:7,verse:"поставил его владыкою над делами рук Твоих; все положил под ноги его:"},{num:8,verse:"овец и волов всех, и также полевых зверей,"},{num:9,verse:"птиц небесных и рыб морских, все, преходящее морскими стезями."},{num:10,verse:"Господи, Боже наш! Как величественно имя Твое по всей земле!"}]},{chapter:"9",verses:[{num:1,verse:"Начальнику хора. По смерти Лабена. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Буду славить [Тебя], Господи, всем сердцем моим, возвещать все чудеса Твои."},{num:3,verse:"Буду радоваться и торжествовать о Тебе, петь имени Твоему, Всевышний."},{num:4,verse:"Когда враги мои обращены назад, то преткнутся и погибнут пред лицем Твоим,"},{num:5,verse:"ибо Ты производил мой суд и мою тяжбу; Ты воссел на престоле, Судия праведный."},{num:6,verse:"Ты вознегодовал на народы, погубил нечестивого, имя их изгладил на веки и веки."},{num:7,verse:"У врага совсем не стало оружия, и города Ты разрушил; погибла память их с ними."},{num:8,verse:"Но Господь пребывает вовек; Он приготовил для суда престол Свой,"},{num:9,verse:"и Он будет судить вселенную по правде, совершит суд над народами по правоте."},{num:10,verse:"И будет Господь прибежищем угнетенному, прибежищем во времена скорби;"},{num:11,verse:"и будут уповать на Тебя знающие имя Твое, потому что Ты не оставляешь ищущих Тебя, Господи."},{num:12,verse:"Пойте Господу, живущему на Сионе, возвещайте между народами дела Его,"},{num:13,verse:"ибо Он взыскивает за кровь; помнит их, не забывает вопля угнетенных."},{num:14,verse:"Помилуй меня, Господи; воззри на страдание мое от ненавидящих меня, – Ты, Который возносишь меня от врат смерти,"},{num:15,verse:"чтобы я возвещал все хвалы Твои во вратах дщери Сионовой: буду радоваться о спасении Твоем."},{num:16,verse:"Обрушились народы в яму, которую выкопали; в сети, которую скрыли они, запуталась нога их."},{num:17,verse:"Познан был Господь по суду, который Он совершил; нечестивый уловлен делами рук своих."},{num:18,verse:"Да обратятся нечестивые в ад, – все народы, забывающие Бога."},{num:19,verse:"Ибо не навсегда забыт будет нищий, и надежда бедных не до конца погибнет."},{num:20,verse:"Восстань, Господи, да не преобладает человек, да судятся народы пред лицем Твоим."},{num:21,verse:"Наведи, Господи, страх на них; да знают народы, что человеки они."},{num:22,verse:"Для чего, Господи, стоишь вдали, скрываешь Себя во время скорби?"},{num:23,verse:"По гордости своей нечестивый преследует бедного: да уловятся они ухищрениями, которые сами вымышляют."},{num:24,verse:"Ибо нечестивый хвалится похотью души своей; корыстолюбец ублажает себя."},{num:25,verse:'В надмении своем нечестивый пренебрегает Господа: "не взыщет"; во всех помыслах его: "нет Бога!"'},{num:26,verse:"Во всякое время пути его гибельны; суды Твои далеки для него; на всех врагов своих он смотрит с пренебрежением;"},{num:27,verse:'говорит в сердце своем: "не поколеблюсь; в род и род не приключится <i>мне</i> зла";'},{num:28,verse:"уста его полны проклятия, коварства и лжи; под языком – его мучение и пагуба;"},{num:29,verse:"сидит в засаде за двором, в потаенных местах убивает невинного; глаза его подсматривают за бедным;"},{num:30,verse:"подстерегает в потаенном месте, как лев в логовище; подстерегает в засаде, чтобы схватить бедного; хватает бедного, увлекая в сети свои;"},{num:31,verse:"сгибается, прилегает, – и бедные падают в сильные когти его;"},{num:32,verse:"говорит в сердце своем: [забыл Бог, закрыл лице Свое, не увидит никогда]."},{num:33,verse:"Восстань, Господи, Боже [мой], вознеси руку Твою, не забудь угнетенных [Твоих до конца]."},{num:34,verse:'Зачем нечестивый пренебрегает Бога, говоря в сердце своем: "Ты не взыщешь"?'},{num:35,verse:"Ты видишь, ибо Ты взираешь на обиды и притеснения, чтобы воздать Твоею рукою. Тебе предает себя бедный; сироте Ты помощник."},{num:36,verse:"Сокруши мышцу нечестивому и злому, так чтобы искать и не найти его нечестия."},{num:37,verse:"Господь – царь на веки, навсегда; исчезнут язычники с земли Его."},{num:38,verse:"Господи! Ты слышишь желания смиренных; укрепи сердце их; открой ухо Твое,"},{num:39,verse:"чтобы дать суд сироте и угнетенному, да не устрашает более человек на земле."}]},{chapter:"10",verses:[{num:0,verse:"Начальнику хора. Псалом Давида."},{num:0,verse:""},{num:1,verse:'На Господа уповаю; как же вы говорите душе моей: "улетай на гору вашу, <i>как</i> птица"?'},{num:2,verse:"Ибо вот, нечестивые натянули лук, стрелу свою приложили к тетиве, чтобы во тьме стрелять в правых сердцем."},{num:3,verse:"Когда разрушены основания, что сделает праведник?"},{num:4,verse:"Господь во святом храме Своем, Господь, – престол Его на небесах, очи Его зрят [на нищего]; вежды Его испытывают сынов человеческих."},{num:5,verse:"Господь испытывает праведного, а нечестивого и любящего насилие ненавидит душа Его."},{num:6,verse:"Дождем прольет Он на нечестивых горящие угли, огонь и серу; и палящий ветер – их доля из чаши;"},{num:7,verse:"ибо Господь праведен, любит правду; лице Его видит праведника."}]},{chapter:"11",verses:[{num:1,verse:"Начальнику хора. На восьмиструнном. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Спаси [меня], Господи, ибо не стало праведного, ибо нет верных между сынами человеческими."},{num:3,verse:"Ложь говорит каждый своему ближнему; уста льстивы, говорят от сердца притворного."},{num:4,verse:"Истребит Господь все уста льстивые, язык велеречивый,"},{num:5,verse:'<i>тех</i>, которые говорят: "языком нашим пересилим, уста наши с нами; кто нам господин"?'},{num:6,verse:"Ради страдания нищих и воздыхания бедных ныне восстану, говорит Господь, поставлю в безопасности того, кого уловить хотят."},{num:7,verse:"Слова Господни – слова чистые, серебро, очищенное от земли в горниле, семь раз переплавленное."},{num:8,verse:"Ты, Господи, сохранишь их, соблюдешь от рода сего вовек."},{num:9,verse:"Повсюду ходят нечестивые, когда ничтожные из сынов человеческих возвысились."}]},{chapter:"12",verses:[{num:1,verse:"Начальнику хора. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Доколе, Господи, будешь забывать меня вконец, доколе будешь скрывать лице Твое от меня?"},{num:3,verse:"Доколе мне слагать советы в душе моей, скорбь в сердце моем день [и ночь]? Доколе врагу моему возноситься надо мною?"},{num:4,verse:"Призри, услышь меня, Господи Боже мой! Просвети очи мои, да не усну я <i>сном</i> смертным;"},{num:5,verse:"да не скажет враг мой: [я одолел его]. Да не возрадуются гонители мои, если я поколеблюсь."},{num:6,verse:"Я же уповаю на милость Твою; сердце мое возрадуется о спасении Твоем; воспою Господу, облагодетельствовавшему меня, [и буду петь имени Господа Всевышнего]."}]},{chapter:"13",verses:[{num:0,verse:"Начальнику хора. Псалом Давида."},{num:0,verse:""},{num:1,verse:"Сказал безумец в сердце своем: [нет Бога]. Они развратились, совершили гнусные дела; нет делающего добро."},{num:2,verse:"Господь с небес призрел на сынов человеческих, чтобы видеть, есть ли разумеющий, ищущий Бога."},{num:3,verse:"Все уклонились, сделались равно непотребными; нет делающего добро, нет ни одного."},{num:4,verse:"Неужели не вразумятся все, делающие беззаконие, съедающие народ мой, <i>как</i> едят хлеб, и не призывающие Господа?"},{num:5,verse:"Там убоятся они страха, [где нет страха,] ибо Бог в роде праведных."},{num:6,verse:"Вы посмеялись над мыслью нищего, что Господь упование его."},{num:7,verse:"[Кто даст с Сиона спасение Израилю!] Когда Господь возвратит пленение народа Своего, тогда возрадуется Иаков и возвеселится Израиль."}]},{chapter:"14",verses:[{num:0,verse:"Псалом Давида."},{num:0,verse:""},{num:1,verse:"Господи! кто может пребывать в жилище Твоем? кто может обитать на святой горе Твоей?"},{num:2,verse:"Тот, кто ходит непорочно и делает правду, и говорит истину в сердце своем;"},{num:3,verse:"кто не клевещет языком своим, не делает искреннему своему зла и не принимает поношения на ближнего своего4"},{num:4,verse:"тот, в глазах которого презрен отверженный, но который боящихся Господа славит; кто клянется, <i>хотя бы</i> злому, и не изменяет;"},{num:5,verse:"кто серебра своего не отдает в рост и не принимает даров против невинного. Поступающий так не поколеблется вовек."}]},{chapter:"15",verses:[{num:0,verse:"Песнь Давида."},{num:0,verse:""},{num:1,verse:"Храни меня, Боже, ибо я на Тебя уповаю."},{num:2,verse:"Я сказал Господу: Ты – Господь мой; блага мои Тебе не нужны."},{num:3,verse:"К святым, которые на земле, и к дивным <i>Твоим</i> – к ним все желание мое."},{num:4,verse:"Пусть умножаются скорби у тех, которые текут к <i>богу</i> чужому; я не возлию кровавых возлияний их и не помяну имен их устами моими."},{num:5,verse:"Господь есть часть наследия моего и чаши моей. Ты держишь жребий мой."},{num:6,verse:"Межи мои прошли по прекрасным <i>местам</i>, и наследие мое приятно для меня."},{num:7,verse:"Благословлю Господа, вразумившего меня; даже и ночью учит меня внутренность моя."},{num:8,verse:"Всегда видел я пред собою Господа, ибо Он одесную меня; не поколеблюсь."},{num:9,verse:"Оттого возрадовалось сердце мое и возвеселился язык мой; даже и плоть моя успокоится в уповании,"},{num:10,verse:"ибо Ты не оставишь души моей в аде и не дашь святому Твоему увидеть тление,"},{num:11,verse:"Ты укажешь мне путь жизни: полнота радостей пред лицем Твоим, блаженство в деснице Твоей вовек."}]},{chapter:"16",verses:[{num:0,verse:"Молитва Давида."},{num:0,verse:""},{num:1,verse:"Услышь, Господи, правду [мою], внемли воплю моему, прими мольбу из уст нелживых."},{num:2,verse:"От Твоего лица суд мне да изыдет; да воззрят очи Твои на правоту."},{num:3,verse:"Ты испытал сердце мое, посетил меня ночью, искусил меня и ничего не нашел; от мыслей моих не отступают уста мои."},{num:4,verse:"В делах человеческих, по слову уст Твоих, я охранял себя от путей притеснителя."},{num:5,verse:"Утверди шаги мои на путях Твоих, да не колеблются стопы мои."},{num:6,verse:"К Тебе взываю я, ибо Ты услышишь меня, Боже; приклони ухо Твое ко мне, услышь слова мои."},{num:7,verse:"Яви дивную милость Твою, Спаситель уповающих [на Тебя] от противящихся деснице Твоей."},{num:8,verse:"Храни меня, как зеницу ока; в тени крыл Твоих укрой меня"},{num:9,verse:"от лица нечестивых, нападающих на меня, – от врагов души моей, окружающих меня:"},{num:10,verse:"они заключились в туке своем, надменно говорят устами своими."},{num:11,verse:"На всяком шагу нашем ныне окружают нас; они устремили глаза свои, чтобы низложить <i>меня</i> на землю;"},{num:12,verse:"они подобны льву, жаждущему добычи, подобны скимну, сидящему в местах скрытных."},{num:13,verse:"Восстань, Господи, предупреди их, низложи их. Избавь душу мою от нечестивого мечом Твоим,"},{num:14,verse:"от людей – рукою Твоею, Господи, от людей мира, которых удел в <i>этой</i> жизни, которых чрево Ты наполняешь из сокровищниц Твоих; сыновья их сыты и оставят остаток детям своим."},{num:15,verse:"А я в правде буду взирать на лице Твое; пробудившись, буду насыщаться образом Твоим."}]},{chapter:"17",verses:[{num:1,verse:"Начальнику хора. Раба Господня Давида, который произнес слова песни сей к Господу, когда Господь избавил его от рук всех врагов его и от руки Саула. И он сказал:"},{num:0,verse:""},{num:2,verse:"Возлюблю Тебя, Господи, крепость моя!"},{num:3,verse:"Господь – твердыня моя и прибежище мое, Избавитель мой, Бог мой, – скала моя; на Него я уповаю; щит мой, рог спасения моего и убежище мое."},{num:4,verse:"Призову достопоклоняемого Господа и от врагов моих спасусь."},{num:5,verse:"Объяли меня муки смертные, и потоки беззакония устрашили меня;"},{num:6,verse:"цепи ада облегли меня, и сети смерти опутали меня."},{num:7,verse:"В тесноте моей я призвал Господа и к Богу моему воззвал. И Он услышал от [святаго] чертога Своего голос мой, и вопль мой дошел до слуха Его."},{num:8,verse:"Потряслась и всколебалась земля, дрогнули и подвиглись основания гор, ибо разгневался [Бог];"},{num:9,verse:"поднялся дым от гнева Его и из уст Его огонь поядающий; горячие угли <i>сыпались</i> от Него."},{num:10,verse:"Наклонил Он небеса и сошел, – и мрак под ногами Его."},{num:11,verse:"И воссел на Херувимов и полетел, и понесся на крыльях ветра."},{num:12,verse:"И мрак сделал покровом Своим, сению вокруг Себя мрак вод, облаков воздушных."},{num:13,verse:"От блистания пред Ним бежали облака Его, град и угли огненные."},{num:14,verse:"Возгремел на небесах Господь, и Всевышний дал глас Свой, град и угли огненные."},{num:15,verse:"Пустил стрелы Свои и рассеял их, множество молний, и рассыпал их."},{num:16,verse:"И явились источники вод, и открылись основания вселенной от грозного <i>гласа</i> Твоего, Господи, от дуновения духа гнева Твоего."},{num:17,verse:"Он простер <i>руку</i> с высоты и взял меня, и извлек меня из вод многих;"},{num:18,verse:"избавил меня от врага моего сильного и от ненавидящих меня, которые были сильнее меня."},{num:19,verse:"Они восстали на меня в день бедствия моего, но Господь был мне опорою."},{num:20,verse:"Он вывел меня на пространное место и избавил меня, ибо Он благоволит ко мне."},{num:21,verse:"Воздал мне Господь по правде моей, по чистоте рук моих вознаградил меня,"},{num:22,verse:"ибо я хранил пути Господни и не был нечестивым пред Богом моим;"},{num:23,verse:"ибо все заповеди Его предо мною, и от уставов Его я не отступал."},{num:24,verse:"Я был непорочен пред Ним и остерегался, чтобы не согрешить мне;"},{num:25,verse:"и воздал мне Господь по правде моей, по чистоте рук моих пред очами Его."},{num:26,verse:"С милостивым Ты поступаешь милостиво, с мужем искренним – искренно,"},{num:27,verse:"с чистым – чисто, а с лукавым – по лукавству его,"},{num:28,verse:"ибо Ты людей угнетенных спасаешь, а очи надменные унижаешь."},{num:29,verse:"Ты возжигаешь светильник мой, Господи; Бог мой просвещает тьму мою."},{num:30,verse:"С Тобою я поражаю войско, с Богом моим восхожу на стену."},{num:31,verse:"Бог! – Непорочен путь Его, чисто слово Господа; щит Он для всех, уповающих на Него."},{num:32,verse:"Ибо кто Бог, кроме Господа, и кто защита, кроме Бога нашего?"},{num:33,verse:"Бог препоясывает меня силою и устрояет мне верный путь;"},{num:34,verse:"делает ноги мои, как оленьи, и на высотах моих поставляет меня;"},{num:35,verse:"научает руки мои брани, и мышцы мои сокрушают медный лук."},{num:36,verse:"Ты дал мне щит спасения Твоего, и десница Твоя поддерживает меня, и милость Твоя возвеличивает меня."},{num:37,verse:"Ты расширяешь шаг мой подо мною, и не колеблются ноги мои."},{num:38,verse:"Я преследую врагов моих и настигаю их, и не возвращаюсь, доколе не истреблю их;"},{num:39,verse:"поражаю их, и они не могут встать, падают под ноги мои,"},{num:40,verse:"ибо Ты препоясал меня силою для войны и низложил под ноги мои восставших на меня;"},{num:41,verse:"Ты обратил ко мне тыл врагов моих, и я истребляю ненавидящих меня:"},{num:42,verse:"они вопиют, но нет спасающего; ко Господу, – но Он не внемлет им;"},{num:43,verse:"я рассеваю их, как прах пред лицем ветра, как уличную грязь попираю их."},{num:44,verse:"Ты избавил меня от мятежа народа, поставил меня главою иноплеменников; народ, которого я не знал, служит мне;"},{num:45,verse:"по одному слуху о мне повинуются мне; иноплеменники ласкательствуют предо мною;"},{num:46,verse:"иноплеменники бледнеют и трепещут в укреплениях своих."},{num:47,verse:"Жив Господь и благословен защитник мой! Да будет превознесен Бог спасения моего,"},{num:48,verse:"Бог, мстящий за меня и покоряющий мне народы,"},{num:49,verse:"и избавляющий меня от врагов моих! Ты вознес меня над восстающими против меня и от человека жестокого избавил меня."},{num:50,verse:"За то буду славить Тебя, Господи, между иноплеменниками и буду петь имени Твоему,"},{num:51,verse:"величественно спасающий царя и творящий милость помазаннику Твоему Давиду и потомству его во веки."}]},{chapter:"18",verses:[{num:1,verse:"Начальнику хора. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Небеса проповедуют славу Божию, и о делах рук Его вещает твердь."},{num:3,verse:"День дню передает речь, и ночь ночи открывает знание."},{num:4,verse:"Нет языка, и нет наречия, где не слышался бы голос их."},{num:5,verse:"По всей земле проходит звук их, и до пределов вселенной слова их. Он поставил в них жилище солнцу,"},{num:6,verse:"и оно выходит, как жених из брачного чертога своего, радуется, как исполин, пробежать поприще:"},{num:7,verse:"от края небес исход его, и шествие его до края их, и ничто не укрыто от теплоты его."},{num:8,verse:"Закон Господа совершен, укрепляет душу; откровение Господа верно, умудряет простых."},{num:9,verse:"Повеления Господа праведны, веселят сердце; заповедь Господа светла, просвещает очи."},{num:10,verse:"Страх Господень чист, пребывает вовек. Суды Господни истина, все праведны;"},{num:11,verse:"они вожделеннее золота и даже множества золота чистого, слаще меда и капель сота;"},{num:12,verse:"и раб Твой охраняется ими, в соблюдении их великая награда."},{num:13,verse:"Кто усмотрит погрешности свои? От тайных <i>моих</i> очисти меня"},{num:14,verse:"и от умышленных удержи раба Твоего, чтобы не возобладали мною. Тогда я буду непорочен и чист от великого развращения."},{num:15,verse:"Да будут слова уст моих и помышление сердца моего благоугодны пред Тобою, Господи, твердыня моя и Избавитель мой!"}]},{chapter:"19",verses:[{num:1,verse:"Начальнику хора. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Да услышит тебя Господь в день печали, да защитит тебя имя Бога Иаковлева."},{num:3,verse:"Да пошлет тебе помощь из Святилища и с Сиона да подкрепит тебя."},{num:4,verse:"Да воспомянет все жертвоприношения твои и всесожжение твое да соделает тучным."},{num:5,verse:"Да даст тебе [Господь] по сердцу твоему и все намерения твои да исполнит."},{num:6,verse:"Мы возрадуемся о спасении твоем и во имя Бога нашего поднимем знамя. Да исполнит Господь все прошения твои."},{num:7,verse:"Ныне познал я, что Господь спасает помазанника Своего, отвечает ему со святых небес Своих могуществом спасающей десницы Своей."},{num:8,verse:"Иные колесницами, иные конями, а мы именем Господа Бога нашего хвалимся:"},{num:9,verse:"они поколебались и пали, а мы встали и стоим прямо."},{num:10,verse:"Господи! спаси царя и услышь нас, когда будем взывать [к Тебе]."}]},{chapter:"20",verses:[{num:1,verse:"Начальнику хора. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Господи! силою Твоею веселится царь и о спасении Твоем безмерно радуется."},{num:3,verse:"Ты дал ему, чего желало сердце его, и прошения уст его не отринул,"},{num:4,verse:"ибо Ты встретил его благословениями благости, возложил на голову его венец из чистого золота."},{num:5,verse:"Он просил у Тебя жизни; Ты дал ему долгоденствие на век и век."},{num:6,verse:"Велика слава его в спасении Твоем; Ты возложил на него честь и величие."},{num:7,verse:"Ты положил на него благословения на веки, возвеселил его радостью лица Твоего,"},{num:8,verse:"ибо царь уповает на Господа, и во благости Всевышнего не поколеблется."},{num:9,verse:"Рука Твоя найдет всех врагов Твоих, десница Твоя найдет [всех] ненавидящих Тебя."},{num:10,verse:"Во время гнева Твоего Ты сделаешь их, как печь огненную; во гневе Своем Господь погубит их, и пожрет их огонь."},{num:11,verse:"Ты истребишь плод их с земли и семя их – из среды сынов человеческих,"},{num:12,verse:"ибо они предприняли против Тебя злое, составили замыслы, но не могли [выполнить их]."},{num:13,verse:"Ты поставишь их целью, из луков Твоих пустишь стрелы в лице их."},{num:14,verse:"Вознесись, Господи, силою Твоею: мы будем воспевать и прославлять Твое могущество."}]},{chapter:"21",verses:[{num:1,verse:"Начальнику хора. При появлении зари. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Боже мой! Боже мой! [внемли мне] для чего Ты оставил меня? Далеки от спасения моего слова вопля моего."},{num:3,verse:"Боже мой! я вопию днем, – и Ты не внемлешь мне, ночью, – и нет мне успокоения."},{num:4,verse:"Но Ты, Святый, живешь среди славословий Израиля."},{num:5,verse:"На Тебя уповали отцы наши; уповали, и Ты избавлял их;"},{num:6,verse:"к Тебе взывали они, и были спасаемы; на Тебя уповали, и не оставались в стыде."},{num:7,verse:"Я же червь, а не человек, поношение у людей и презрение в народе."},{num:8,verse:"Все, видящие меня, ругаются надо мною, говорят устами, кивая головою:"},{num:9,verse:'"он уповал на Господа; пусть избавит его, пусть спасет, если он угоден Ему".'},{num:10,verse:"Но Ты извел меня из чрева, вложил в меня упование у грудей матери моей."},{num:11,verse:"На Тебя оставлен я от утробы; от чрева матери моей Ты – Бог мой."},{num:12,verse:"Не удаляйся от меня, ибо скорбь близка, а помощника нет."},{num:13,verse:"Множество тельцов обступили меня; тучные Васанские окружили меня,"},{num:14,verse:"раскрыли на меня пасть свою, как лев, алчущий добычи и рыкающий."},{num:15,verse:"Я пролился, как вода; все кости мои рассыпались; сердце мое сделалось, как воск, растаяло посреди внутренности моей."},{num:16,verse:"Сила моя иссохла, как черепок; язык мой прильпнул к гортани моей, и Ты свел меня к персти смертной."},{num:17,verse:"Ибо псы окружили меня, скопище злых обступило меня, пронзили руки мои и ноги мои."},{num:18,verse:"Можно было бы перечесть все кости мои; а они смотрят и делают из меня зрелище;"},{num:19,verse:"делят ризы мои между собою и об одежде моей бросают жребий."},{num:20,verse:"Но Ты, Господи, не удаляйся от меня; сила моя! поспеши на помощь мне;"},{num:21,verse:"избавь от меча душу мою и от псов одинокую мою;"},{num:22,verse:"спаси меня от пасти льва и от рогов единорогов, услышав, <i>избавь</i> меня."},{num:23,verse:"Буду возвещать имя Твое братьям моим, посреди собрания восхвалять Тебя."},{num:24,verse:"Боящиеся Господа! восхвалите Его. Все семя Иакова! прославь Его. Да благоговеет пред Ним все семя Израиля,"},{num:25,verse:"ибо Он не презрел и не пренебрег скорби страждущего, не скрыл от него лица Своего, но услышал его, когда сей воззвал к Нему."},{num:26,verse:"О Тебе хвала моя в собрании великом; воздам обеты мои пред боящимися Его."},{num:27,verse:"Да едят бедные и насыщаются; да восхвалят Господа ищущие Его; да живут сердца ваши во веки!"},{num:28,verse:"Вспомнят, и обратятся к Господу все концы земли, и поклонятся пред Тобою все племена язычников,"},{num:29,verse:"ибо Господне есть царство, и Он – Владыка над народами."},{num:30,verse:"Будут есть и поклоняться все тучные земли; преклонятся пред Ним все нисходящие в персть и не могущие сохранить жизни своей."},{num:31,verse:"Потомство [мое] будет служить Ему, и будет называться Господним вовек:"},{num:32,verse:"придут и будут возвещать правду Его людям, которые родятся, что сотворил Господь."}]},{chapter:"22",verses:[{num:0,verse:"Псалом Давида."},{num:0,verse:""},{num:1,verse:"Господь – Пастырь мой; я ни в чем не буду нуждаться:"},{num:2,verse:"Он покоит меня на злачных пажитях и водит меня к водам тихим,"},{num:3,verse:"подкрепляет душу мою, направляет меня на стези правды ради имени Своего."},{num:4,verse:"Если я пойду и долиною смертной тени, не убоюсь зла, потому что Ты со мной; Твой жезл и Твой посох – они успокаивают меня."},{num:5,verse:"Ты приготовил предо мною трапезу в виду врагов моих; умастил елеем голову мою; чаша моя преисполнена."},{num:6,verse:"Так, благость и милость [Твоя] да сопровождают меня во все дни жизни моей, и я пребуду в доме Господнем многие дни."}]},{chapter:"23",verses:[{num:0,verse:"Псалом Давида. [В первый день недели]."},{num:0,verse:""},{num:1,verse:"Господня земля и что наполняет ее, вселенная и все живущее в ней,"},{num:2,verse:"ибо Он основал ее на морях и на реках утвердил ее."},{num:3,verse:"Кто взойдет на гору Господню, или кто станет на святом месте Его?"},{num:4,
verse:"Тот, у которого руки неповинны и сердце чисто, кто не клялся душею своею напрасно и не божился ложно [ближнему своему], -"},{num:5,verse:"<i>тот</i> получит благословение от Господа и милость от Бога, Спасителя своего."},{num:6,verse:"Таков род ищущих Его, ищущих лица Твоего, Боже Иакова!"},{num:7,verse:"Поднимите, врата, верхи ваши, и поднимитесь, двери вечные, и войдет Царь славы!"},{num:8,verse:"Кто сей Царь славы? – Господь крепкий и сильный, Господь, сильный в брани."},{num:9,verse:"Поднимите, врата, верхи ваши, и поднимитесь, двери вечные, и войдет Царь славы!"},{num:10,verse:"Кто сей Царь славы? – Господь сил, Он – царь славы."}]},{chapter:"24",verses:[{num:0,verse:"Псалом Давида."},{num:0,verse:""},{num:1,verse:"К Тебе, Господи, возношу душу мою."},{num:2,verse:"Боже мой! на Тебя уповаю, да не постыжусь [вовек], да не восторжествуют надо мною враги мои,"},{num:3,verse:"да не постыдятся и все надеющиеся на Тебя: да постыдятся беззаконнующие втуне."},{num:4,verse:"Укажи мне, Господи, пути Твои и научи меня стезям Твоим."},{num:5,verse:"Направь меня на истину Твою и научи меня, ибо Ты Бог спасения моего; на Тебя надеюсь всякий день."},{num:6,verse:"Вспомни щедроты Твои, Господи, и милости Твои, ибо они от века."},{num:7,verse:"Грехов юности моей и преступлений моих не вспоминай; по милости Твоей вспомни меня Ты, ради благости Твоей, Господи!"},{num:8,verse:"Благ и праведен Господь, посему наставляет грешников на путь,"},{num:9,verse:"направляет кротких к правде, и научает кротких путям Своим."},{num:10,verse:"Все пути Господни – милость и истина к хранящим завет Его и откровения Его."},{num:11,verse:"Ради имени Твоего, Господи, прости согрешение мое, ибо велико оно."},{num:12,verse:"Кто есть человек, боящийся Господа? Ему укажет Он путь, который избрать."},{num:13,verse:"Душа его пребудет во благе, и семя его наследует землю."},{num:14,verse:"Тайна Господня – боящимся Его, и завет Свой Он открывает им."},{num:15,verse:"Очи мои всегда к Господу, ибо Он извлекает из сети ноги мои."},{num:16,verse:"Призри на меня и помилуй меня, ибо я одинок и угнетен."},{num:17,verse:"Скорби сердца моего умножились; выведи меня из бед моих,"},{num:18,verse:"призри на страдание мое и на изнеможение мое и прости все грехи мои."},{num:19,verse:"Посмотри на врагов моих, как много их, и <i>какою</i> лютою ненавистью они ненавидят меня."},{num:20,verse:"Сохрани душу мою и избавь меня, да не постыжусь, что я на Тебя уповаю."},{num:21,verse:"Непорочность и правота да охраняют меня, ибо я на Тебя надеюсь."},{num:22,verse:"Избавь, Боже, Израиля от всех скорбей его."}]},{chapter:"25",verses:[{num:0,verse:"Псалом Давида."},{num:0,verse:""},{num:1,verse:"Рассуди меня, Господи, ибо я ходил в непорочности моей, и, уповая на Господа, не поколеблюсь."},{num:2,verse:"Искуси меня, Господи, и испытай меня; расплавь внутренности мои и сердце мое,"},{num:3,verse:"ибо милость Твоя пред моими очами, и я ходил в истине Твоей,"},{num:4,verse:"не сидел я с людьми лживыми, и с коварными не пойду;"},{num:5,verse:"возненавидел я сборище злонамеренных, и с нечестивыми не сяду;"},{num:6,verse:"буду омывать в невинности руки мои и обходить жертвенник Твой, Господи,"},{num:7,verse:"чтобы возвещать гласом хвалы и поведать все чудеса Твои."},{num:8,verse:"Господи! возлюбил я обитель дома Твоего и место жилища славы Твоей."},{num:9,verse:"Не погуби души моей с грешниками и жизни моей с кровожадными,"},{num:10,verse:"у которых в руках злодейство, и которых правая рука полна мздоимства."},{num:11,verse:"А я хожу в моей непорочности; избавь меня, [Господи,] и помилуй меня."},{num:12,verse:"Моя нога стоит на прямом <i>пути</i>; в собраниях благословлю Господа."}]},{chapter:"26",verses:[{num:0,verse:"Псалом Давида. [Прежде помазания]."},{num:0,verse:""},{num:1,verse:"Господь – свет мой и спасение мое: кого мне бояться? Господь крепость жизни моей: кого мне страшиться?"},{num:2,verse:"Если будут наступать на меня злодеи, противники и враги мои, чтобы пожрать плоть мою, то они сами преткнутся и падут."},{num:3,verse:"Если ополчится против меня полк, не убоится сердце мое; если восстанет на меня война, и тогда буду надеяться."},{num:4,verse:"Одного просил я у Господа, того только ищу, чтобы пребывать мне в доме Господнем во все дни жизни моей, созерцать красоту Господню и посещать [святый] храм Его,"},{num:5,verse:"ибо Он укрыл бы меня в скинии Своей в день бедствия, скрыл бы меня в потаенном месте селения Своего, вознес бы меня на скалу."},{num:6,verse:"Тогда вознеслась бы голова моя над врагами, окружающими меня; и я принес бы в Его скинии жертвы славословия, стал бы петь и воспевать пред Господом."},{num:7,verse:"Услышь, Господи, голос мой, которым я взываю, помилуй меня и внемли мне."},{num:8,verse:"Сердце мое говорит от Тебя: [ищите лица Моего]; и я буду искать лица Твоего, Господи."},{num:9,verse:"Не скрой от меня лица Твоего; не отринь во гневе раба Твоего. Ты был помощником моим; не отвергни меня и не оставь меня, Боже, Спаситель мой!"},{num:10,verse:"ибо отец мой и мать моя оставили меня, но Господь примет меня."},{num:11,verse:"Научи меня, Господи, пути Твоему и наставь меня на стезю правды, ради врагов моих;"},{num:12,verse:"не предавай меня на произвол врагам моим, ибо восстали на меня свидетели лживые и дышат злобою."},{num:13,verse:"Но я верую, что увижу благость Господа на земле живых."},{num:14,verse:"Надейся на Господа, мужайся, и да укрепляется сердце твое, и надейся на Господа."}]},{chapter:"27",verses:[{num:0,verse:"Псалом Давида."},{num:0,verse:""},{num:1,verse:"К Тебе, Господи, взываю: твердыня моя! не будь безмолвен для меня, чтобы при безмолвии Твоем я не уподобился нисходящим в могилу."},{num:2,verse:"Услышь голос молений моих, когда я взываю к Тебе, когда поднимаю руки мои к святому храму Твоему."},{num:3,verse:"Не погуби меня с нечестивыми и с делающими неправду, которые с ближними своими говорят о мире, а в сердце у них зло."},{num:4,verse:"Воздай им по делам их, по злым поступкам их; по делам рук их воздай им, отдай им заслуженное ими."},{num:5,verse:"За то, что они невнимательны к действиям Господа и к делу рук Его, Он разрушит их и не созиждет их."},{num:6,verse:"Благословен Господь, ибо Он услышал голос молений моих."},{num:7,verse:"Господь – крепость моя и щит мой; на Него уповало сердце мое, и Он помог мне, и возрадовалось сердце мое; и я прославлю Его песнью моею."},{num:8,verse:"Господь – крепость народа Своего и спасительная защита помазанника Своего."},{num:9,verse:"Спаси народ Твой и благослови наследие Твое; паси их и возвышай их во веки!"}]},{chapter:"28",verses:[{num:0,verse:"Псалом Давида. [При окончании праздника кущей.]"},{num:0,verse:""},{num:1,verse:"Воздайте Господу, сыны Божии, воздайте Господу славу и честь,"},{num:2,verse:"воздайте Господу славу имени Его; поклонитесь Господу в благолепном святилище <i>Его</i>."},{num:3,verse:"Глас Господень над водами; Бог славы возгремел, Господь над водами многими."},{num:4,verse:"Глас Господа силен, глас Господа величествен."},{num:5,verse:"Глас Господа сокрушает кедры; Господь сокрушает кедры Ливанские"},{num:6,verse:"и заставляет их скакать подобно тельцу, Ливан и Сирион, подобно молодому единорогу."},{num:7,verse:"Глас Господа высекает пламень огня."},{num:8,verse:"Глас Господа потрясает пустыню; потрясает Господь пустыню Кадес."},{num:9,verse:"Глас Господа разрешает от бремени ланей и обнажает леса; и во храме Его все возвещает о <i>Его</i> славе."},{num:10,verse:"Господь восседал над потопом, и будет восседать Господь царем вовек."},{num:11,verse:"Господь даст силу народу Своему, Господь благословит народ Свой миром."}]},{chapter:"29",verses:[{num:1,verse:"Псалом Давида; песнь при обновлении дома."},{num:0,verse:""},{num:2,verse:"Превознесу Тебя, Господи, что Ты поднял меня и не дал моим врагам восторжествовать надо мною."},{num:3,verse:"Господи, Боже мой! я воззвал к Тебе, и Ты исцелил меня."},{num:4,verse:"Господи! Ты вывел из ада душу мою и оживил меня, чтобы я не сошел в могилу."},{num:5,verse:"Пойте Господу, святые Его, славьте память святыни Его,"},{num:6,verse:"ибо на мгновение гнев Его, на <i>всю</i> жизнь благоволение Его: вечером водворяется плач, а на утро радость."},{num:7,verse:'И я говорил в благоденствии моем: "не поколеблюсь вовек".'},{num:8,verse:"По благоволению Твоему, Господи, Ты укрепил гору мою; но Ты сокрыл лице Твое, <i>и</i> я смутился."},{num:9,verse:"<i>Тогда</i> к Тебе, Господи, взывал я, и Господа [моего] умолял:"},{num:10,verse:'"что пользы в крови моей, когда я сойду в могилу? будет ли прах славить Тебя? будет ли возвещать истину Твою?'},{num:11,verse:'услышь, Господи, и помилуй меня; Господи! будь мне помощником".'},{num:12,verse:"И Ты обратил сетование мое в ликование, снял с меня вретище и препоясал меня веселием,"},{num:13,verse:"да славит Тебя душа моя и да не умолкает. Господи, Боже мой! буду славить Тебя вечно."}]},{chapter:"30",verses:[{num:1,verse:"Начальнику хора. Псалом Давида. [Во время смятения]."},{num:0,verse:""},{num:2,verse:"На Тебя, Господи, уповаю, да не постыжусь вовек; по правде Твоей избавь меня;"},{num:3,verse:"приклони ко мне ухо Твое, поспеши избавить меня. Будь мне каменною твердынею, домом прибежища, чтобы спасти меня,"},{num:4,verse:"ибо Ты каменная гора моя и ограда моя; ради имени Твоего води меня и управляй мною."},{num:5,verse:"Выведи меня из сети, которую тайно поставили мне, ибо Ты крепость моя."},{num:6,verse:"В Твою руку предаю дух мой; Ты избавлял меня, Господи, Боже истины."},{num:7,verse:"Ненавижу почитателей суетных идолов, но на Господа уповаю."},{num:8,verse:"Буду радоваться и веселиться о милости Твоей, потому что Ты призрел на бедствие мое, узнал горесть души моей"},{num:9,verse:"и не предал меня в руки врага; поставил ноги мои на пространном месте."},{num:10,verse:"Помилуй меня, Господи, ибо тесно мне; иссохло от горести око мое, душа моя и утроба моя."},{num:11,verse:"Истощилась в печали жизнь моя и лета мои в стенаниях; изнемогла от грехов моих сила моя, и кости мои иссохли."},{num:12,verse:"От всех врагов моих я сделался поношением даже у соседей моих и страшилищем для знакомых моих; видящие меня на улице бегут от меня."},{num:13,verse:"Я забыт в сердцах, как мертвый; я – как сосуд разбитый,"},{num:14,verse:"ибо слышу злоречие многих; отвсюду ужас, когда они сговариваются против меня, умышляют исторгнуть душу мою."},{num:15,verse:"А я на Тебя, Господи, уповаю; я говорю: Ты – мой Бог."},{num:16,verse:"В Твоей руке дни мои; избавь меня от руки врагов моих и от гонителей моих."},{num:17,verse:"Яви светлое лице Твое рабу Твоему; спаси меня милостью Твоею."},{num:18,verse:"Господи! да не постыжусь, что я к Тебе взываю; нечестивые же да посрамятся, да умолкнут в аде."},{num:19,verse:"Да онемеют уста лживые, которые против праведника говорят злое с гордостью и презреньем."},{num:20,verse:"Как много у Тебя благ, которые Ты хранишь для боящихся Тебя и которые приготовил уповающим на Тебя пред сынами человеческими!"},{num:21,verse:"Ты укрываешь их под покровом лица Твоего от мятежей людских, скрываешь их под сенью от пререкания языков."},{num:22,verse:"Благословен Господь, что явил мне дивную милость Свою в укрепленном городе!"},{num:23,verse:'В смятении моем я думал: "отвержен я от очей Твоих"; но Ты услышал голос молитвы моей, когда я воззвал к Тебе.'},{num:24,verse:"Любите Господа, все праведные Его; Господь хранит верных и поступающим надменно воздает с избытком."},{num:25,verse:"Мужайтесь, и да укрепляется сердце ваше, все надеющиеся на Господа!"}]},{chapter:"31",verses:[{num:0,verse:"Псалом Давида. Учение."},{num:0,verse:""},{num:1,verse:"Блажен, кому отпущены беззакония, и чьи грехи покрыты!"},{num:2,verse:"Блажен человек, которому Господь не вменит греха, и в чьем духе нет лукавства!"},{num:3,verse:"Когда я молчал, обветшали кости мои от вседневного стенания моего,"},{num:4,verse:"ибо день и ночь тяготела надо мною рука Твоя; свежесть моя исчезла, как в летнюю засуху."},{num:5,verse:'Но я открыл Тебе грех мой и не скрыл беззакония моего; я сказал: "исповедаю Господу преступления мои", и Ты снял с меня вину греха моего.'},{num:6,verse:"За то помолится Тебе каждый праведник во время благопотребное, и тогда разлитие многих вод не достигнет его."},{num:7,verse:"Ты покров мой: Ты охраняешь меня от скорби, окружаешь меня радостями избавления."},{num:8,verse:'"Вразумлю тебя, наставлю тебя на путь, по которому тебе идти; буду руководить тебя, око Мое над тобою".'},{num:9,verse:'"Не будьте как конь, как лошак несмысленный, которых челюсти нужно обуздывать уздою и удилами, чтобы они покорялись тебе".'},{num:10,verse:"Много скорбей нечестивому, а уповающего на Господа окружает милость."},{num:11,verse:"Веселитесь о Господе и радуйтесь, праведные; торжествуйте, все правые сердцем."}]},{chapter:"32",verses:[{num:0,verse:"[Псалом Давида.]"},{num:0,verse:""},{num:1,verse:"Радуйтесь, праведные, о Господе: правым прилично славословить."},{num:2,verse:"Славьте Господа на гуслях, пойте Ему на десятиструнной псалтири;"},{num:3,verse:"пойте Ему новую песнь; пойте Ему стройно, с восклицанием,"},{num:4,verse:"ибо слово Господне право и все дела Его верны."},{num:5,verse:"Он любит правду и суд; милости Господней полна земля."},{num:6,verse:"Словом Господа сотворены небеса, и духом уст Его – все воинство их:"},{num:7,verse:"Он собрал, будто груды, морские воды, положил бездны в хранилищах."},{num:8,verse:"Да боится Господа вся земля; да трепещут пред Ним все живущие во вселенной,"},{num:9,verse:"ибо Он сказал, – и сделалось; Он повелел, – и явилось."},{num:10,verse:"Господь разрушает советы язычников, уничтожает замыслы народов, [уничтожает советы князей]."},{num:11,verse:"Совет же Господень стоит вовек; помышления сердца Его – в род и род."},{num:12,verse:"Блажен народ, у которого Господь есть Бог, – племя, которое Он избрал в наследие Себе."},{num:13,verse:"С небес призирает Господь, видит всех сынов человеческих;"},{num:14,verse:"с престола, на котором восседает, Он призирает на всех, живущих на земле:"},{num:15,verse:"Он создал сердца всех их и вникает во все дела их."},{num:16,verse:"Не спасется царь множеством воинства; исполина не защитит великая сила."},{num:17,verse:"Ненадежен конь для спасения, не избавит великою силою своею."},{num:18,verse:"Вот, око Господне над боящимися Его и уповающими на милость Его,"},{num:19,verse:"что Он душу их спасет от смерти и во время голода пропитает их."},{num:20,verse:"Душа наша уповает на Господа: Он – помощь наша и защита наша;"},{num:21,verse:"о Нем веселится сердце наше, ибо на святое имя Его мы уповали."},{num:22,verse:"Да будет милость Твоя, Господи, над нами, как мы уповаем на Тебя."}]},{chapter:"33",verses:[{num:1,verse:"Псалом Давида, когда он притворился безумным пред Авимелехом и был изгнан от него и удалился."},{num:0,verse:""},{num:2,verse:"Благословлю Господа во всякое время; хвала Ему непрестанно в устах моих."},{num:3,verse:"Господом будет хвалиться душа моя; услышат кроткие и возвеселятся."},{num:4,verse:"Величайте Господа со мною, и превознесем имя Его вместе."},{num:5,verse:"Я взыскал Господа, и Он услышал меня, и от всех опасностей моих избавил меня."},{num:6,verse:"Кто обращал взор к Нему, те просвещались, и лица их не постыдятся."},{num:7,verse:"Сей нищий воззвал, – и Господь услышал и спас его от всех бед его."},{num:8,verse:"Ангел Господень ополчается вокруг боящихся Его и избавляет их."},{num:9,verse:"Вкусите, и увидите, как благ Господь! Блажен человек, который уповает на Него!"},{num:10,verse:"Бойтесь Господа, [все] святые Его, ибо нет скудости у боящихся Его."},{num:11,verse:"Скимны бедствуют и терпят голод, а ищущие Господа не терпят нужды ни в каком благе."},{num:12,verse:"Придите, дети, послушайте меня: страху Господню научу вас."},{num:13,verse:"Хочет ли человек жить и любит ли долгоденствие, чтобы видеть благо?"},{num:14,verse:"Удерживай язык свой от зла и уста свои от коварных слов."},{num:15,verse:"Уклоняйся от зла и делай добро; ищи мира и следуй за ним."},{num:16,verse:"Очи Господни <i>обращены</i> на праведников, и уши Его – к воплю их."},{num:17,verse:"Но лице Господне против делающих зло, чтобы истребить с земли память о них."},{num:18,verse:"Взывают [праведные], и Господь слышит, и от всех скорбей их избавляет их."},{num:19,verse:"Близок Господь к сокрушенным сердцем и смиренных духом спасет."},{num:20,verse:"Много скорбей у праведного, и от всех их избавит его Господь."},{num:21,verse:"Он хранит все кости его; ни одна из них не сокрушится."},{num:22,verse:"Убьет грешника зло, и ненавидящие праведного погибнут."},{num:23,verse:"Избавит Господь душу рабов Своих, и никто из уповающих на Него не погибнет."}]},{chapter:"34",verses:[{num:0,verse:"Псалом Давида."},{num:0,verse:""},{num:1,verse:"Вступись, Господи, в тяжбу с тяжущимися со мною, побори борющихся со мною;"},{num:2,verse:"возьми щит и латы и восстань на помощь мне;"},{num:3,verse:'обнажи мечь и прегради <i>путь</i> преследующим меня; скажи душе моей: "Я – спасение твое!"'},{num:4,verse:"Да постыдятся и посрамятся ищущие души моей; да обратятся назад и покроются бесчестием умышляющие мне зло;"},{num:5,verse:"да будут они, как прах пред лицем ветра, и Ангел Господень да прогоняет <i>их</i>;"},{num:6,verse:"да будет путь их темен и скользок, и Ангел Господень да преследует их,"},{num:7,verse:"ибо они без вины скрыли для меня яму – сеть свою, без вины выкопали <i>ее</i> для души моей."},{num:8,verse:"Да придет на него гибель неожиданная, и сеть его, которую он скрыл <i>для меня</i>, да уловит его самого; да впадет в нее на погибель."},{num:9,verse:"А моя душа будет радоваться о Господе, будет веселиться о спасении от Него."},{num:10,verse:'Все кости мои скажут: "Господи! кто подобен Тебе, избавляющему слабого от сильного, бедного и нищего от грабителя его?"'},{num:11,verse:"Восстали на меня свидетели неправедные: чего я не знаю, о том допрашивают меня;"},{num:12,verse:"воздают мне злом за добро, сиротством душе моей."},{num:13,verse:"Я во время болезни их одевался во вретище, изнурял постом душу мою, и молитва моя возвращалась в недро мое."},{num:14,verse:"Я поступал, как бы это был друг мой, брат мой; я ходил скорбный, с поникшею головою, как бы оплакивающий мать."},{num:15,verse:"А когда я претыкался, они радовались и собирались; собирались ругатели против меня, не знаю за что, поносили и не переставали;"},{num:16,verse:"с лицемерными насмешниками скрежетали на меня зубами своими."},{num:17,verse:"Господи! долго ли будешь смотреть <i>на это</i>? Отведи душу мою от злодейств их, от львов – одинокую мою."},{num:18,verse:"Я прославлю Тебя в собрании великом, среди народа многочисленного восхвалю Тебя,"},{num:19,verse:"чтобы не торжествовали надо мною враждующие против меня неправедно, и не перемигивались глазами ненавидящие меня безвинно;"},{num:20,verse:"ибо не о мире говорят они, но против мирных земли составляют лукавые замыслы;"},{num:21,verse:'расширяют на меня уста свои; говорят: "хорошо! хорошо! видел глаз наш".'},{num:22,verse:"Ты видел, Господи, не умолчи; Господи! не удаляйся от меня."},{num:23,verse:"Подвигнись, пробудись для суда моего, для тяжбы моей, Боже мой и Господи мой!"},{num:24,verse:"Суди меня по правде Твоей, Господи, Боже мой, и да не торжествуют они надо мною;"},{num:25,verse:'да не говорят в сердце своем: "хорошо! [хорошо!] по душе нашей!" Да не говорят: "мы поглотили его".'},{num:26,verse:"Да постыдятся и посрамятся все, радующиеся моему несчастью; да облекутся в стыд и позор величающиеся надо мною."},{num:27,verse:'Да радуются и веселятся желающие правоты моей и говорят непрестанно: "да возвеличится Господь, желающий мира рабу Своему!"'},{num:28,verse:"И язык мой будет проповедывать правду Твою и хвалу Твою всякий день."}]},{chapter:"35",verses:[{num:1,verse:"Начальнику хора. Раба Господня Давида."},{num:0,verse:""},{num:2,verse:"Нечестие беззаконного говорит в сердце моем: нет страха Божия пред глазами его,"},{num:3,verse:"ибо он льстит себе в глазах своих, будто отыскивает беззаконие свое, чтобы возненавидеть его;"},{num:4,verse:"слова уст его – неправда и лукавство; не хочет он вразумиться, чтобы делать добро;"},{num:5,verse:"на ложе своем замышляет беззаконие, становится на путь недобрый, не гнушается злом."},{num:6,verse:"Господи! милость Твоя до небес, истина Твоя до облаков!"},{num:7,verse:"Правда Твоя, как горы Божии, и судьбы Твои – бездна великая! Человеков и скотов хранишь Ты, Господи!"},{num:8,verse:"Как драгоценна милость Твоя, Боже! Сыны человеческие в тени крыл Твоих покойны:"},{num:9,verse:"насыщаются от тука дома Твоего, и из потока сладостей Твоих Ты напояешь их,"},{num:10,verse:"ибо у Тебя источник жизни; во свете Твоем мы видим свет."},{num:11,verse:"Продли милость Твою к знающим Тебя и правду Твою к правым сердцем,"},{num:12,verse:"да не наступит на меня нога гордыни, и рука грешника да не изгонит меня:"},{num:13,verse:"там пали делающие беззаконие, низринуты и не могут встать."}]},{chapter:"36",verses:[{num:0,verse:"Псалом Давида."},{num:0,verse:""},{num:1,verse:"Не ревнуй злодеям, не завидуй делающим беззаконие,"},{num:2,verse:"ибо они, как трава, скоро будут подкошены и, как зеленеющий злак, увянут."},{num:3,verse:"Уповай на Господа и делай добро; живи на земле и храни истину."},{num:4,verse:"Утешайся Господом, и Он исполнит желания сердца твоего."},{num:5,verse:"Предай Господу путь твой и уповай на Него, и Он совершит,"},{num:6,verse:"и выведет, как свет, правду твою и справедливость твою, как полдень."},{num:7,verse:"Покорись Господу и надейся на Него. Не ревнуй успевающему в пути своем, человеку лукавствующему."},{num:8,verse:"Перестань гневаться и оставь ярость; не ревнуй до того, чтобы делать зло,"},{num:9,verse:"ибо делающие зло истребятся, уповающие же на Господа наследуют землю."},{num:10,verse:"Еще немного, и не станет нечестивого; посмотришь на его место, и нет его."},{num:11,verse:"А кроткие наследуют землю и насладятся множеством мира."},{num:12,verse:"Нечестивый злоумышляет против праведника и скрежещет на него зубами своими:"},{num:13,verse:"Господь же посмевается над ним, ибо видит, что приходит день его."},{num:14,verse:"Нечестивые обнажают меч и натягивают лук свой, чтобы низложить бедного и нищего, чтобы пронзить <i>идущих</i> прямым путем:"},{num:15,verse:"меч их войдет в их же сердце, и луки их сокрушатся."},{num:16,verse:"Малое у праведника – лучше богатства многих нечестивых,"},{num:17,verse:"ибо мышцы нечестивых сокрушатся, а праведников подкрепляет Господь."},{num:18,verse:"Господь знает дни непорочных, и достояние их пребудет вовек:"},{num:19,verse:"не будут они постыжены во время лютое и во дни голода будут сыты;"},{num:20,verse:"а нечестивые погибнут, и враги Господни, как тук агнцев, исчезнут, в дыме исчезнут."},{num:21,verse:"Нечестивый берет взаймы и не отдает, а праведник милует и дает,"},{num:22,verse:"ибо благословенные Им наследуют землю, а проклятые Им истребятся."},{num:23,verse:"Господом утверждаются стопы <i>такого</i> человека, и Он благоволит к пути его:"},{num:24,verse:"когда он будет падать, не упадет, ибо Господь поддерживает его за руку."},{num:25,verse:"Я был молод и состарился, и не видал праведника оставленным и потомков его просящими хлеба:"},{num:26,verse:"он всякий день милует и взаймы дает, и потомство его в благословение будет."},{num:27,verse:"Уклоняйся от зла, и делай добро, и будешь жить вовек:"},{num:28,verse:"ибо Господь любит правду и не оставляет святых Своих; вовек сохранятся они; [а беззаконные будут извержены] и потомство нечестивых истребится."},{num:29,verse:"Праведники наследуют землю и будут жить на ней вовек."},{num:30,verse:"Уста праведника изрекают премудрость, и язык его произносит правду."},{num:31,verse:"Закон Бога его в сердце у него; не поколеблются стопы его."},{num:32,verse:"Нечестивый подсматривает за праведником и ищет умертвить его;"},{num:33,verse:"но Господь не отдаст его в руки его и не допустит обвинить его, когда он будет судим."},{num:34,verse:"Уповай на Господа и держись пути Его: и Он вознесет тебя, чтобы ты наследовал землю; и когда будут истребляемы нечестивые, ты увидишь."},{num:35,verse:"Видел я нечестивца грозного, расширявшегося, подобно укоренившемуся многоветвистому дереву;"},{num:36,verse:"но он прошел, и вот нет его; ищу его и не нахожу."},{num:37,verse:"Наблюдай за непорочным и смотри на праведного, ибо будущность <i>такого</i> человека есть мир;"},{num:38,verse:"а беззаконники все истребятся; будущность нечестивых погибнет."},{num:39,verse:"От Господа спасение праведникам, Он – защита их во время скорби;"},{num:40,verse:"и поможет им Господь и избавит их; избавит их от нечестивых и спасет их, ибо они на Него уповают."}]},{chapter:"37",verses:[{num:1,verse:"Псалом Давида. В воспоминание [о субботе]."},{num:0,verse:""},{num:2,verse:"Господи! не в ярости Твоей обличай меня и не во гневе Твоем наказывай меня,"},{num:3,verse:"ибо стрелы Твои вонзились в меня, и рука Твоя тяготеет на мне."},{num:4,verse:"Нет целого места в плоти моей от гнева Твоего; нет мира в костях моих от грехов моих,"},{num:5,verse:"ибо беззакония мои превысили голову мою, как тяжелое бремя отяготели на мне,"},{num:6,verse:"смердят, гноятся раны мои от безумия моего."},{num:7,verse:"Я согбен и совсем поник, весь день сетуя хожу,"},{num:8,verse:"ибо чресла мои полны воспалениями, и нет целого места в плоти моей."},{num:9,verse:"Я изнемог и сокрушен чрезмерно; кричу от терзания сердца моего."},{num:10,verse:"Господи! пред Тобою все желания мои, и воздыхание мое не сокрыто от Тебя."},{num:11,verse:"Сердце мое трепещет; оставила меня сила моя, и свет очей моих, – и того нет у меня."},{num:12,verse:"Друзья мои и искренние отступили от язвы моей, и ближние мои стоят вдали."},{num:13,verse:"Ищущие же души моей ставят сети, и желающие мне зла говорят о погибели <i>моей</i> и замышляют всякий день козни;"},{num:14,verse:"а я, как глухой, не слышу, и как немой, который не открывает уст своих;"},{num:15,verse:"и стал я, как человек, который не слышит и не имеет в устах своих ответа,"},{num:16,verse:"ибо на Тебя, Господи, уповаю я; Ты услышишь, Господи, Боже мой."},{num:17,verse:"И я сказал: да не восторжествуют надо мною [враги мои]; когда колеблется нога моя, они величаются надо мною."},{num:18,verse:"Я близок к падению, и скорбь моя всегда предо мною."},{num:19,verse:"Беззаконие мое я сознаю, сокрушаюсь о грехе моем."},{num:20,verse:"А враги мои живут и укрепляются, и умножаются ненавидящие меня безвинно;"},{num:21,verse:"и воздающие мне злом за добро враждуют против меня за то, что я следую добру."},{num:22,verse:"Не оставь меня, Господи, Боже мой! Не удаляйся от меня;"},{num:23,verse:"поспеши на помощь мне, Господи, Спаситель мой!"}]},{chapter:"38",verses:[{num:1,verse:"Начальнику хора, Идифуму. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Я сказал: буду я наблюдать за путями моими, чтобы не согрешать мне языком моим; буду обуздывать уста мои, доколе нечестивый предо мною."},{num:3,verse:"Я был нем и безгласен, и молчал <i>даже</i> о добром; и скорбь моя подвиглась."},{num:4,verse:"Воспламенилось сердце мое во мне; в мыслях моих возгорелся огонь; я стал говорить языком моим:"},{num:5,verse:"скажи мне, Господи, кончину мою и число дней моих, какое оно, дабы я знал, какой век мой."},{num:6,verse:"Вот, Ты дал мне дни, <i>как</i> пяди, и век мой как ничто пред Тобою. Подлинно, совершенная суета – всякий человек живущий."},{num:7,verse:"Подлинно, человек ходит подобно призраку; напрасно он суетится, собирает и не знает, кому достанется то."},{num:8,verse:"И ныне чего ожидать мне, Господи? надежда моя – на Тебя."},{num:9,verse:"От всех беззаконий моих избавь меня, не предавай меня на поругание безумному."},{num:10,verse:"Я стал нем, не открываю уст моих; потому что Ты соделал это."},{num:11,verse:"Отклони от меня удары Твои; я исчезаю от поражающей руки Твоей."},{num:12,verse:"Если Ты обличениями будешь наказывать человека за преступления, то рассыплется, как от моли, краса его. Так, суетен всякий человек!"},{num:13,verse:"Услышь, Господи, молитву мою и внемли воплю моему; не будь безмолвен к слезам моим, ибо странник я у Тебя <i>и</i> пришлец, как и все отцы мои."},{num:14,verse:"Отступи от меня, чтобы я мог подкрепиться, прежде нежели отойду и не будет меня."}]},{chapter:"39",verses:[{num:1,verse:"Начальнику хора. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Твердо уповал я на Господа, и Он приклонился ко мне и услышал вопль мой;"},{num:3,verse:"извлек меня из страшного рва, из тинистого болота, и поставил на камне ноги мои и утвердил стопы мои;"},{num:4,verse:"и вложил в уста мои новую песнь – хвалу Богу нашему. Увидят многие и убоятся и будут уповать на Господа."},{num:5,verse:"Блажен человек, который на Господа возлагает надежду свою и не обращается к гордым и к уклоняющимся ко лжи."},{num:6,verse:"Много соделал Ты, Господи, Боже мой: о чудесах и помышлениях Твоих о нас – кто уподобится Тебе! – хотел бы я проповедывать и говорить, но они превышают число."},{num:7,verse:"Жертвы и приношения Ты не восхотел; Ты открыл мне уши; всесожжения и жертвы за грех Ты не потребовал."},{num:8,verse:"Тогда я сказал: вот, иду; в свитке книжном написано о мне:"},{num:9,verse:"я желаю исполнить волю Твою, Боже мой, и закон Твой у меня в сердце."},{num:10,verse:"Я возвещал правду Твою в собрании великом; я не возбранял устам моим: Ты, Господи, знаешь."},{num:11,verse:"Правды Твоей не скрывал в сердце моем, возвещал верность Твою и спасение Твое, не утаивал милости Твоей и истины Твоей пред собранием великим."},{num:12,verse:"Не удерживай, Господи, щедрот Твоих от меня; милость Твоя и истина Твоя да охраняют меня непрестанно,"},{num:13,verse:"ибо окружили меня беды неисчислимые; постигли меня беззакония мои, так что видеть не могу: их более, нежели волос на голове моей; сердце мое оставило меня."},{num:14,verse:"Благоволи, Господи, избавить меня; Господи! поспеши на помощь мне."},{num:15,verse:"Да постыдятся и посрамятся все, ищущие погибели душе моей! Да будут обращены назад и преданы посмеянию желающие мне зла!"},{num:16,verse:'Да смятутся от посрамления своего говорящие мне: "хорошо! хорошо!"'},{num:17,verse:'Да радуются и веселятся Тобою все ищущие Тебя, и любящие спасение Твое да говорят непрестанно: "велик Господь!"'},{num:18,verse:"Я же беден и нищ, но Господь печется о мне. Ты – помощь моя и избавитель мой, Боже мой! не замедли."}]},{chapter:"40",verses:[{num:1,verse:"Начальнику хора. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Блажен, кто помышляет о бедном [и нищем]! В день бедствия избавит его Господь."},{num:3,verse:"Господь сохранит его и сбережет ему жизнь; блажен будет он на земле. И Ты не отдашь его на волю врагов его."},{num:4,verse:"Господь укрепит его на одре болезни его. Ты изменишь все ложе его в болезни его."},{num:5,verse:"Я сказал: Господи! помилуй меня, исцели душу мою, ибо согрешил я пред Тобою."},{num:6,verse:'Враги мои говорят обо мне злое: "когда он умрет и погибнет имя его?"'},{num:7,verse:"И если приходит кто видеть меня, говорит ложь; сердце его слагает в себе неправду, и он, выйдя вон, толкует."},{num:8,verse:"Все ненавидящие меня шепчут между собою против меня, замышляют на меня зло:"},{num:9,verse:'"слово велиала пришло на него; он слег; не встать ему более".'},{num:10,verse:"Даже человек мирный со мною, на которого я полагался, который ел хлеб мой, поднял на меня пяту."},{num:11,verse:"Ты же, Господи, помилуй меня и восставь меня, и я воздам им."},{num:12,verse:"Из того узнаю, что Ты благоволишь ко мне, если враг мой не восторжествует надо мною,"},{num:13,verse:"а меня сохранишь в целости моей и поставишь пред лицем Твоим на веки."},{num:14,verse:"Благословен Господь Бог Израилев от века и до века! Аминь, аминь!"}]},{chapter:"41",verses:[{num:1,verse:"Начальнику хора. Учение. Сынов Кореевых."},{num:0,verse:""},{num:2,verse:"Как лань желает к потокам воды, так желает душа моя к Тебе, Боже!"},{num:3,verse:"Жаждет душа моя к Богу крепкому, живому: когда приду и явлюсь пред лице Божие!"},{num:4,verse:'Слезы мои были для меня хлебом день и ночь, когда говорили мне всякий день: "где Бог твой?"'},{num:5,
verse:"Вспоминая об этом, изливаю душу мою, потому что я ходил в многолюдстве, вступал с ними в дом Божий со гласом радости и славословия празднующего сонма."},{num:6,verse:"Что унываешь ты, душа моя, и что смущаешься? Уповай на Бога, ибо я буду еще славить Его, Спасителя моего и Бога моего."},{num:7,verse:"Унывает во мне душа моя; посему я воспоминаю о Тебе с земли Иорданской, с Ермона, с горы Цоар."},{num:8,verse:"Бездна бездну призывает голосом водопадов Твоих; все воды Твои и волны Твои прошли надо мною."},{num:9,verse:"Днем явит Господь милость Свою, и ночью песнь Ему у меня, молитва к Богу жизни моей."},{num:10,verse:"Скажу Богу, заступнику моему: для чего Ты забыл меня? Для чего я сетуя хожу от оскорблений врага?"},{num:11,verse:'Как бы поражая кости мои, ругаются надо мною враги мои, когда говорят мне всякий день: "где Бог твой?"'},{num:12,verse:"Что унываешь ты, душа моя, и что смущаешься? Уповай на Бога, ибо я буду еще славить Его, Спасителя моего и Бога моего."}]},{chapter:"42",verses:[{num:1,verse:"Суди меня, Боже, и вступись в тяжбу мою с народом недобрым. От человека лукавого и несправедливого избавь меня,"},{num:2,verse:"ибо Ты Бог крепости моей. Для чего Ты отринул меня? для чего я сетуя хожу от оскорблений врага?"},{num:3,verse:"Пошли свет Твой и истину Твою; да ведут они меня и приведут на святую гору Твою и в обители Твои."},{num:4,verse:"И подойду я к жертвеннику Божию, к Богу радости и веселия моего, и на гуслях буду славить Тебя, Боже, Боже мой!"},{num:5,verse:"Что унываешь ты, душа моя, и что смущаешься? Уповай на Бога; ибо я буду еще славить Его, Спасителя моего и Бога моего."}]},{chapter:"43",verses:[{num:1,verse:"Начальнику хора. Учение. Сынов Кореевых."},{num:0,verse:""},{num:2,verse:"Боже, мы слышали ушами своими, отцы наши рассказывали нам о деле, какое Ты соделал во дни их, во дни древние:"},{num:3,verse:"Ты рукою Твоею истребил народы, а их насадил; поразил племена и изгнал их;"},{num:4,verse:"ибо они не мечом своим приобрели землю, и не их мышца спасла их, но Твоя десница и Твоя мышца и свет лица Твоего, ибо Ты благоволил к ним."},{num:5,verse:"Боже, Царь мой! Ты – тот же; даруй спасение Иакову."},{num:6,verse:"С Тобою избодаем рогами врагов наших; во имя Твое попрем ногами восстающих на нас:"},{num:7,verse:"ибо не на лук мой уповаю, и не меч мой спасет меня;"},{num:8,verse:"но Ты спасешь нас от врагов наших, и посрамишь ненавидящих нас."},{num:9,verse:"О Боге похвалимся всякий день, и имя Твое будем прославлять вовек."},{num:10,verse:"Но ныне Ты отринул и посрамил нас, и не выходишь с войсками нашими;"},{num:11,verse:"обратил нас в бегство от врага, и ненавидящие нас грабят нас;"},{num:12,verse:"Ты отдал нас, как овец, на съедение и рассеял нас между народами;"},{num:13,verse:"без выгоды Ты продал народ Твой и не возвысил цены его;"},{num:14,verse:"отдал нас на поношение соседям нашим, на посмеяние и поругание живущим вокруг нас;"},{num:15,verse:"Ты сделал нас притчею между народами, покиванием головы между иноплеменниками."},{num:16,verse:"Всякий день посрамление мое предо мною, и стыд покрывает лице мое"},{num:17,verse:"от голоса поносителя и клеветника, от взоров врага и мстителя:"},{num:18,verse:"все это пришло на нас, но мы не забыли Тебя и не нарушили завета Твоего."},{num:19,verse:"Не отступило назад сердце наше, и стопы наши не уклонились от пути Твоего,"},{num:20,verse:"когда Ты сокрушил нас в земле драконов и покрыл нас тенью смертною."},{num:21,verse:"Если бы мы забыли имя Бога нашего и простерли руки наши к богу чужому,"},{num:22,verse:"то не взыскал ли бы сего Бог? Ибо Он знает тайны сердца."},{num:23,verse:"Но за Тебя умерщвляют нас всякий день, считают нас за овец, <i>обреченных</i> на заклание."},{num:24,verse:"Восстань, что спишь, Господи! пробудись, не отринь навсегда."},{num:25,verse:"Для чего скрываешь лице Твое, забываешь скорбь нашу и угнетение наше?"},{num:26,verse:"ибо душа наша унижена до праха, утроба наша прильнула к земле."},{num:27,verse:"Восстань на помощь нам и избавь нас ради милости Твоей."}]},{chapter:"44",verses:[{num:1,verse:"Начальнику хора. На <i>музыкальном орудии</i> Шошан. Учение. Сынов Кореевых. Песнь любви."},{num:0,verse:""},{num:2,verse:"Излилось из сердца моего слово благое; я говорю: песнь моя о Царе; язык мой – трость скорописца."},{num:3,verse:"Ты прекраснее сынов человеческих; благодать излилась из уст Твоих; посему благословил Тебя Бог на веки."},{num:4,verse:"Препояшь Себя по бедру мечом Твоим, Сильный, славою Твоею и красотою Твоею,"},{num:5,verse:"и в сем украшении Твоем поспеши, воссядь на колесницу ради истины и кротости и правды, и десница Твоя покажет Тебе дивные дела."},{num:6,verse:"Остры стрелы Твои, [Сильный], – народы падут пред Тобою, – они – в сердце врагов Царя."},{num:7,verse:"Престол Твой, Боже, вовек; жезл правоты – жезл царства Твоего."},{num:8,verse:"Ты возлюбил правду и возненавидел беззаконие, посему помазал Тебя, Боже, Бог Твой елеем радости более соучастников Твоих."},{num:9,verse:"Все одежды Твои, как смирна и алой и касия; из чертогов слоновой кости увеселяют Тебя."},{num:10,verse:"Дочери царей между почетными у Тебя; стала царица одесную Тебя в Офирском золоте."},{num:11,verse:"Слыши, дщерь, и смотри, и приклони ухо твое, и забудь народ твой и дом отца твоего."},{num:12,verse:"И возжелает Царь красоты твоей; ибо Он Господь твой, и ты поклонись Ему."},{num:13,verse:"И дочь Тира с дарами, и богатейшие из народа будут умолять лице Твое."},{num:14,verse:"Вся слава дщери Царя внутри; одежда ее шита золотом;"},{num:15,verse:"в испещренной одежде ведется она к Царю; за нею ведутся к Тебе девы, подруги ее,"},{num:16,verse:"приводятся с весельем и ликованьем, входят в чертог Царя."},{num:17,verse:"Вместо отцов Твоих, будут сыновья Твои; Ты поставишь их князьями по всей земле."},{num:18,verse:"Сделаю имя Твое памятным в род и род; посему народы будут славить Тебя во веки и веки."}]},{chapter:"45",verses:[{num:1,verse:"Начальнику хора. Сынов Кореевых. На <i>музыкальном орудии</i> Аламоф. Песнь."},{num:0,verse:""},{num:2,verse:"Бог нам прибежище и сила, скорый помощник в бедах,"},{num:3,verse:"посему не убоимся, хотя бы поколебалась земля, и горы двинулись в сердце морей."},{num:4,verse:"Пусть шумят, вздымаются воды их, трясутся горы от волнения их."},{num:5,verse:"Речные потоки веселят град Божий, святое жилище Всевышнего."},{num:6,verse:"Бог посреди его; он не поколеблется: Бог поможет ему с раннего утра."},{num:7,verse:"Восшумели народы; двинулись царства: [Всевышний] дал глас Свой, и растаяла земля."},{num:8,verse:"Господь сил с нами, Бог Иакова заступник наш."},{num:9,verse:"Придите и видите дела Господа, – какие произвел Он опустошения на земле:"},{num:10,verse:"прекращая брани до края земли, сокрушил лук и переломил копье, колесницы сжег огнем."},{num:11,verse:"Остановитесь и познайте, что Я – Бог: буду превознесен в народах, превознесен на земле."},{num:12,verse:"Господь сил с нами, заступник наш Бог Иакова."}]},{chapter:"46",verses:[{num:1,verse:"Начальнику хора. Сынов Кореевых. Псалом."},{num:0,verse:""},{num:2,verse:"Восплещите руками все народы, воскликните Богу гласом радости;"},{num:3,verse:"ибо Господь Всевышний страшен, – великий Царь над всею землею;"},{num:4,verse:"покорил нам народы и племена под ноги наши;"},{num:5,verse:"избрал нам наследие наше, красу Иакова, которого возлюбил."},{num:6,verse:"Восшел Бог при восклицаниях, Господь при звуке трубном."},{num:7,verse:"Пойте Богу нашему, пойте; пойте Царю нашему, пойте,"},{num:8,verse:"ибо Бог – Царь всей земли; пойте все разумно."},{num:9,verse:"Бог воцарился над народами, Бог воссел на святом престоле Своем;"},{num:10,verse:"князья народов собрались к народу Бога Авраамова, ибо щиты земли – Божии; Он превознесен <i>над ними</i>."}]},{chapter:"47",verses:[{num:1,verse:"Песнь. Псалом. Сынов Кореевых."},{num:0,verse:""},{num:2,verse:"Велик Господь и всехвален во граде Бога нашего, на святой горе Его."},{num:3,verse:"Прекрасная возвышенность, радость всей земли гора Сион; на северной стороне <i>ее</i> город великого Царя."},{num:4,verse:"Бог в жилищах его ведом, как заступник:"},{num:5,verse:"ибо вот, сошлись цари и прошли все мимо;"},{num:6,verse:"увидели и изумились, смутились и обратились в бегство;"},{num:7,verse:"страх объял их там и мука, как у женщин в родах;"},{num:8,verse:"восточным ветром Ты сокрушил Фарсийские корабли."},{num:9,verse:"Как слышали мы, так и увидели во граде Господа сил, во граде Бога нашего: Бог утвердит его на веки."},{num:10,verse:"Мы размышляли, Боже, о благости Твоей посреди храма Твоего."},{num:11,verse:"Как имя Твое, Боже, так и хвала Твоя до концов земли; десница Твоя полна правды."},{num:12,verse:"Да веселится гора Сион, [и] да радуются дщери Иудейские ради судов Твоих, [Господи]."},{num:13,verse:"Пойдите вокруг Сиона и обойдите его, пересчитайте башни его;"},{num:14,verse:"обратите сердце ваше к укреплениям его, рассмотрите домы его, чтобы пересказать грядущему роду,"},{num:15,verse:"ибо сей Бог есть Бог наш на веки и веки: Он будет вождем нашим до самой смерти."}]},{chapter:"48",verses:[{num:1,verse:"Начальнику хора. Сынов Кореевых. Псалом."},{num:0,verse:""},{num:2,verse:"Слушайте сие, все народы; внимайте сему, все живущие во вселенной, -"},{num:3,verse:"и простые и знатные, богатый, равно как бедный."},{num:4,verse:"Уста мои изрекут премудрость, и размышления сердца моего – знание."},{num:5,verse:"Приклоню ухо мое к притче, на гуслях открою загадку мою:"},{num:6,verse:'"для чего бояться мне во дни бедствия, <i>когда</i> беззаконие путей моих окружит меня?"'},{num:7,verse:"Надеющиеся на силы свои и хвалящиеся множеством богатства своего!"},{num:8,verse:"человек никак не искупит брата своего и не даст Богу выкупа за него:"},{num:9,verse:"дорога цена искупления души их, и не будет того вовек,"},{num:10,verse:"чтобы остался <i>кто</i> жить навсегда и не увидел могилы."},{num:11,verse:"Каждый видит, что и мудрые умирают, равно как и невежды и бессмысленные погибают и оставляют имущество свое другим."},{num:12,verse:"В мыслях у них, что домы их вечны, и что жилища их в род и род, и земли свои они называют своими именами."},{num:13,verse:"Но человек в чести не пребудет; он уподобится животным, которые погибают."},{num:14,verse:"Этот путь их есть безумие их, хотя последующие за ними одобряют мнение их."},{num:15,verse:"Как овец, заключат их в преисподнюю; смерть будет пасти их, и наутро праведники будут владычествовать над ними; сила их истощится; могила – жилище их."},{num:16,verse:"Но Бог избавит душу мою от власти преисподней, когда примет меня."},{num:17,verse:"Не бойся, когда богатеет человек, когда слава дома его умножается:"},{num:18,verse:"ибо умирая не возьмет ничего; не пойдет за ним слава его;"},{num:19,verse:"хотя при жизни он ублажает душу свою, и прославляют тебя, что ты удовлетворяешь себе,"},{num:20,verse:"но он пойдет к роду отцов своих, которые никогда не увидят света."},{num:21,verse:"Человек, который в чести и неразумен, подобен животным, которые погибают."}]},{chapter:"49",verses:[{num:0,verse:"Псалом Асафа."},{num:0,verse:""},{num:1,verse:"Бог Богов, Господь возглаголал и призывает землю, от восхода солнца до запада."},{num:2,verse:"С Сиона, который есть верх красоты, является Бог,"},{num:3,verse:"грядет Бог наш, и не в безмолвии: пред Ним огонь поядающий, и вокруг Его сильная буря."},{num:4,verse:"Он призывает свыше небо и землю, судить народ Свой:"},{num:5,verse:'"соберите ко Мне святых Моих, вступивших в завет со Мною при жертве".'},{num:6,verse:"И небеса провозгласят правду Его, ибо судия сей есть Бог."},{num:7,verse:'"Слушай, народ Мой, Я буду говорить; Израиль! Я буду свидетельствовать против тебя: Я Бог, твой Бог.'},{num:8,verse:"Не за жертвы твои Я буду укорять тебя; всесожжения твои всегда предо Мною;"},{num:9,verse:"не приму тельца из дома твоего, ни козлов из дворов твоих,"},{num:10,verse:"ибо Мои все звери в лесу, и скот на тысяче гор,"},{num:11,verse:"знаю всех птиц на горах, и животные на полях предо Мною."},{num:12,verse:"Если бы Я взалкал, то не сказал бы тебе, ибо Моя вселенная и все, что наполняет ее."},{num:13,verse:"Ем ли Я мясо волов и пью ли кровь козлов?"},{num:14,verse:"Принеси в жертву Богу хвалу и воздай Всевышнему обеты твои,"},{num:15,verse:'и призови Меня в день скорби; Я избавлю тебя, и ты прославишь Меня".'},{num:16,verse:'Грешнику же говорит Бог: "что ты проповедуешь уставы Мои и берешь завет Мой в уста твои,'},{num:17,verse:"а сам ненавидишь наставление Мое и слова Мои бросаешь за себя?"},{num:18,verse:"когда видишь вора, сходишься с ним, и с прелюбодеями сообщаешься;"},{num:19,verse:"уста твои открываешь на злословие, и язык твой сплетает коварство;"},{num:20,verse:"сидишь и говоришь на брата твоего, на сына матери твоей клевещешь;"},{num:21,verse:"ты это делал, и Я молчал; ты подумал, что Я такой же, как ты. Изобличу тебя и представлю пред глаза твои [грехи твои]."},{num:22,verse:"Уразумейте это, забывающие Бога, дабы Я не восхитил, – и не будет избавляющего."},{num:23,verse:'Кто приносит в жертву хвалу, тот чтит Меня, и кто наблюдает за путем своим, тому явлю Я спасение Божие".'}]},{chapter:"50",verses:[{num:1,verse:"Начальнику хора. Псалом Давида,"},{num:2,verse:"когда приходил к нему пророк Нафан, после того, как Давид вошел к Вирсавии."},{num:0,verse:""},{num:3,verse:"Помилуй меня, Боже, по великой милости Твоей, и по множеству щедрот Твоих изгладь беззакония мои."},{num:4,verse:"Многократно омой меня от беззакония моего, и от греха моего очисти меня,"},{num:5,verse:"ибо беззакония мои я сознаю, и грех мой всегда предо мною."},{num:6,verse:"Тебе, Тебе единому согрешил я и лукавое пред очами Твоими сделал, так что Ты праведен в приговоре Твоем и чист в суде Твоем."},{num:7,verse:"Вот, я в беззаконии зачат, и во грехе родила меня мать моя."},{num:8,verse:"Вот, Ты возлюбил истину в сердце и внутрь меня явил мне мудрость [Твою]."},{num:9,verse:"Окропи меня иссопом, и буду чист; омой меня, и буду белее снега."},{num:10,verse:"Дай мне услышать радость и веселие, и возрадуются кости, Тобою сокрушенные."},{num:11,verse:"Отврати лице Твое от грехов моих и изгладь все беззакония мои."},{num:12,verse:"Сердце чистое сотвори во мне, Боже, и дух правый обнови внутри меня."},{num:13,verse:"Не отвергни меня от лица Твоего и Духа Твоего Святаго не отними от меня."},{num:14,verse:"Возврати мне радость спасения Твоего и Духом владычественным утверди меня."},{num:15,verse:"Научу беззаконных путям Твоим, и нечестивые к Тебе обратятся."},{num:16,verse:"Избавь меня от кровей, Боже, Боже спасения моего, и язык мой восхвалит правду Твою."},{num:17,verse:"Господи! отверзи уста мои, и уста мои возвестят хвалу Твою:"},{num:18,verse:"ибо жертвы Ты не желаешь, – я дал бы ее; к всесожжению не благоволишь."},{num:19,verse:"Жертва Богу – дух сокрушенный; сердца сокрушенного и смиренного Ты не презришь, Боже."},{num:20,verse:"Облагодетельствуй, [Господи,] по благоволению Твоему Сион; воздвигни стены Иерусалима:"},{num:21,verse:"тогда благоугодны будут Тебе жертвы правды, возношение и всесожжение; тогда возложат на алтарь Твой тельцов."}]},{chapter:"51",verses:[{num:1,verse:"Начальнику хора. Учение Давида,"},{num:2,verse:"после того, как приходил Доик Идумеянин и донес Саулу и сказал ему, что Давид пришел в дом Ахимелеха."},{num:0,verse:""},{num:3,verse:"Что хвалишься злодейством, сильный? милость Божия всегда <i>со мною</i>;"},{num:4,verse:"гибель вымышляет язык твой; как изощренная бритва, он <i>у тебя</i>, коварный!"},{num:5,verse:"ты любишь больше зло, нежели добро, больше ложь, нежели говорить правду;"},{num:6,verse:"ты любишь всякие гибельные речи, язык коварный:"},{num:7,verse:"за то Бог сокрушит тебя вконец, изринет тебя и исторгнет тебя из жилища [твоего] и корень твой из земли живых."},{num:8,verse:"Увидят праведники и убоятся, посмеются над ним [и скажут]:"},{num:9,verse:'"вот человек, который не в Боге полагал крепость свою, а надеялся на множество богатства своего, укреплялся в злодействе своем".'},{num:10,verse:"А я, как зеленеющая маслина, в доме Божием, и уповаю на милость Божию во веки веков,"},{num:11,verse:"вечно буду славить Тебя за то, что Ты соделал, и уповать на имя Твое, ибо оно благо пред святыми Твоими."}]},{chapter:"52",verses:[{num:1,verse:"Начальнику хора. На духовом <i>орудии</i>. Учение Давида."},{num:0,verse:""},{num:2,verse:'Сказал безумец в сердце своем: "нет Бога". Развратились они и совершили гнусные преступления; нет делающего добро.'},{num:3,verse:"Бог с небес призрел на сынов человеческих, чтобы видеть, есть ли разумеющий, ищущий Бога."},{num:4,verse:"Все уклонились, сделались равно непотребными; нет делающего добро, нет ни одного."},{num:5,verse:"Неужели не вразумятся делающие беззаконие, съедающие народ мой, <i>как</i> едят хлеб, и не призывающие Бога?"},{num:6,verse:"Там убоятся они страха, где нет страха, ибо рассыплет Бог кости ополчающихся против тебя. Ты постыдишь их, потому что Бог отверг их."},{num:7,verse:"Кто даст с Сиона спасение Израилю! Когда Бог возвратит пленение народа Своего, тогда возрадуется Иаков и возвеселится Израиль."}]},{chapter:"53",verses:[{num:1,verse:"Начальнику хора. На струнных <i>орудиях</i>. Учение Давида,"},{num:2,verse:'когда пришли Зифеи и сказали Саулу: "не у нас ли скрывается Давид?"'},{num:0,verse:""},{num:3,verse:"Боже! именем Твоим спаси меня, и силою Твоею суди меня."},{num:4,verse:"Боже! услышь молитву мою, внемли словам уст моих,"},{num:5,verse:"ибо чужие восстали на меня, и сильные ищут души моей; они не имеют Бога пред собою."},{num:6,verse:"Вот, Бог помощник мой; Господь подкрепляет душу мою."},{num:7,verse:"Он воздаст за зло врагам моим; истиною Твоею истреби их."},{num:8,verse:"Я усердно принесу Тебе жертву, прославлю имя Твое, Господи, ибо оно благо,"},{num:9,verse:"ибо Ты избавил меня от всех бед, и на врагов моих смотрело око мое."}]},{chapter:"54",verses:[{num:1,verse:"Начальнику хора. На струнных <i>орудиях</i>. Учение Давида."},{num:0,verse:""},{num:2,verse:"Услышь, Боже, молитву мою и не скрывайся от моления моего;"},{num:3,verse:"внемли мне и услышь меня; я стенаю в горести моей, и смущаюсь"},{num:4,verse:"от голоса врага, от притеснения нечестивого, ибо они возводят на меня беззаконие и в гневе враждуют против меня."},{num:5,verse:"Сердце мое трепещет во мне, и смертные ужасы напали на меня;"},{num:6,verse:"страх и трепет нашел на меня, и ужас объял меня."},{num:7,verse:'И я сказал: "кто дал бы мне крылья, как у голубя? я улетел бы и успокоился бы;'},{num:8,verse:"далеко удалился бы я, и оставался бы в пустыне;"},{num:9,verse:'поспешил бы укрыться от вихря, от бури".'},{num:10,verse:"Расстрой, Господи, и раздели языки их, ибо я вижу насилие и распри в городе;"},{num:11,verse:"днем и ночью ходят они кругом по стенам его; злодеяния и бедствие посреди его;"},{num:12,verse:"посреди его пагуба; обман и коварство не сходят с улиц его:"},{num:13,verse:"ибо не враг поносит меня, – это я перенес бы; не ненавистник мой величается надо мною, – от него я укрылся бы;"},{num:14,verse:"но ты, который был для меня то же, что я, друг мой и близкий мой,"},{num:15,verse:"с которым мы разделяли искренние беседы и ходили вместе в дом Божий."},{num:16,verse:"Да найдет на них смерть; да сойдут они живыми в ад, ибо злодейство в жилищах их, посреди их."},{num:17,verse:"Я же воззову к Богу, и Господь спасет меня."},{num:18,verse:"Вечером и утром и в полдень буду умолять и вопиять, и Он услышит голос мой,"},{num:19,verse:"избавит в мире душу мою от восстающих на меня, ибо их много у меня;"},{num:20,verse:"услышит Бог, и смирит их от века Живущий, потому что нет в них перемены; они не боятся Бога,"},{num:21,verse:"простерли руки свои на тех, которые с ними в мире, нарушили союз свой;"},{num:22,verse:"уста их мягче масла, а в сердце их вражда; слова их нежнее елея, но они суть обнаженные мечи."},{num:23,verse:"Возложи на Господа заботы твои, и Он поддержит тебя. Никогда не даст Он поколебаться праведнику."},{num:24,verse:"Ты, Боже, низведешь их в ров погибели; кровожадные и коварные не доживут и до половины дней своих. А я на Тебя, [Господи,] уповаю."}]},{chapter:"55",verses:[{num:1,verse:"Начальнику хора. О голубице, безмолвствующей в удалении. Писание Давида, когда Филистимляне захватили его в Гефе."},{num:0,verse:""},{num:2,verse:"Помилуй меня, Боже! ибо человек хочет поглотить меня; нападая всякий день, теснит меня."},{num:3,verse:"Враги мои всякий день ищут поглотить меня, ибо много восстающих на меня, о, Всевышний!"},{num:4,verse:"Когда я в страхе, на Тебя я уповаю."},{num:5,verse:"В Боге восхвалю я слово Его; на Бога уповаю, не боюсь; что сделает мне плоть?"},{num:6,verse:"Всякий день извращают слова мои; все помышления их обо мне – на зло:"},{num:7,verse:"собираются, притаиваются, наблюдают за моими пятами, чтобы уловить душу мою."},{num:8,verse:"Неужели они избегнут воздаяния за неправду <i>свою</i>? Во гневе низложи, Боже, народы."},{num:9,verse:"У Тебя исчислены мои скитания; положи слезы мои в сосуд у Тебя, – не в книге ли они Твоей?"},{num:10,verse:"Враги мои обращаются назад, когда я взываю к Тебе, из этого я узнаю, что Бог за меня."},{num:11,verse:"В Боге восхвалю я слово <i>Его</i>, в Господе восхвалю слово <i>Его</i>."},{num:12,verse:"На Бога уповаю, не боюсь; что сделает мне человек?"},{num:13,verse:"На мне, Боже, обеты Тебе; Тебе воздам хвалы,"},{num:14,verse:"ибо Ты избавил душу мою от смерти, [очи мои от слез,] да и ноги мои от преткновения, чтобы я ходил пред лицем Божиим во свете живых."}]},{chapter:"56",verses:[{num:1,verse:"Начальнику хора. Не погуби. Писание Давида, когда он убежал от Саула в пещеру."},{num:0,verse:""},{num:2,verse:"Помилуй меня, Боже, помилуй меня, ибо на Тебя уповает душа моя, и в тени крыл Твоих я укроюсь, доколе не пройдут беды."},{num:3,verse:"Воззову к Богу Всевышнему, Богу, благодетельствующему мне;"},{num:4,verse:"Он пошлет с небес и спасет меня; посрамит ищущего поглотить меня; пошлет Бог милость Свою и истину Свою."},{num:5,verse:"Душа моя среди львов; я лежу среди дышущих пламенем, среди сынов человеческих, у которых зубы – копья и стрелы, и у которых язык – острый меч."},{num:6,verse:"Будь превознесен выше небес, Боже, и над всею землею да будет слава Твоя!"},{num:7,verse:"Приготовили сеть ногам моим; душа моя поникла; выкопали предо мною яму, и <i>сами</i> упали в нее."},{num:8,verse:"Готово сердце мое, Боже, готово сердце мое: буду петь и славить."},{num:9,verse:"Воспрянь, слава моя, воспрянь, псалтирь и гусли! Я встану рано."},{num:10,verse:"Буду славить Тебя, Господи, между народами; буду воспевать Тебя среди племен,"},{num:11,verse:"ибо до небес велика милость Твоя и до облаков истина Твоя."},{num:12,verse:"Будь превознесен выше небес, Боже, и над всею землею да будет слава Твоя!"}]},{chapter:"57",verses:[{num:1,verse:"Начальнику хора. Не погуби. Писание Давида."},{num:0,verse:""},{num:2,verse:"Подлинно ли правду говорите вы, судьи, и справедливо судите, сыны человеческие?"},{num:3,verse:"Беззаконие составляете в сердце, кладете на весы злодеяния рук ваших на земле."},{num:4,verse:"С самого рождения отступили нечестивые, от утробы <i>матери</i> заблуждаются, говоря ложь."},{num:5,verse:"Яд у них – как яд змеи, как глухого аспида, который затыкает уши свои"},{num:6,verse:"и не слышит голоса заклинателя, самого искусного в заклинаниях."},{num:7,verse:"Боже! сокруши зубы их в устах их; разбей, Господи, челюсти львов!"},{num:8,verse:"Да исчезнут, как вода протекающая; когда напрягут стрелы, пусть они будут как переломленные."},{num:9,verse:"Да исчезнут, как распускающаяся улитка; да не видят солнца, как выкидыш женщины."},{num:10,verse:"Прежде нежели котлы ваши ощутят горящий терн, и свежее и обгоревшее да разнесет вихрь."},{num:11,verse:"Возрадуется праведник, когда увидит отмщение; омоет стопы свои в крови нечестивого."},{num:12,verse:"И скажет человек: [подлинно есть плод праведнику! итак есть Бог, судящий на земле!]"}]},{chapter:"58",verses:[{num:1,verse:"Начальнику хора. Не погуби. Писание Давида, когда Саул послал стеречь дом его, чтобы умертвить его."},{num:0,verse:""},{num:2,verse:"Избавь меня от врагов моих, Боже мой! защити меня от восстающих на меня;"},{num:3,verse:"избавь меня от делающих беззаконие; спаси от кровожадных,"},{num:4,verse:"ибо вот, они подстерегают душу мою; собираются на меня сильные не за преступление мое и не за грех мой, Господи;"},{num:5,verse:"без вины <i>моей</i> сбегаются и вооружаются; подвигнись на помощь мне и воззри."},{num:6,verse:"Ты, Господи, Боже сил, Боже Израилев, восстань посетить все народы, не пощади ни одного из нечестивых беззаконников:"},{num:7,verse:"вечером возвращаются они, воют, как псы, и ходят вокруг города;"},{num:8,verse:'вот они изрыгают хулу языком своим; в устах их мечи: "ибо", <i>думают они</i>, "кто слышит?"'},{num:9,verse:"Но Ты, Господи, посмеешься над ними; Ты посрамишь все народы."},{num:10,verse:"Сила – у них, но я к Тебе прибегаю, ибо Бог – заступник мой."},{num:11,verse:"Бой мой, милующий меня, предварит меня; Бог даст мне смотреть на врагов моих."},{num:12,verse:"Не умерщвляй их, чтобы не забыл народ мой; расточи их силою Твоею и низложи их, Господи, защитник наш."},{num:13,verse:"Слово языка их есть грех уст их, да уловятся они в гордости своей за клятву и ложь, которую произносят."},{num:14,verse:"Расточи их во гневе, расточи, чтобы их не было; и да познают, что Бог владычествует над Иаковом до пределов земли."},{num:15,verse:"Пусть возвращаются вечером, воют, как псы, и ходят вокруг города;"},{num:16,verse:"пусть бродят, чтобы найти пищу, и несытые проводят ночи."},{num:17,verse:"А я буду воспевать силу Твою и с раннего утра провозглашать милость Твою, ибо Ты был мне защитою и убежищем в день бедствия моего."},{num:18,verse:"Сила моя! Тебя буду воспевать я, ибо Бог – заступник мой, Бог мой, милующий меня."}]},{chapter:"59",verses:[{num:1,verse:"Начальнику хора. На <i>музыкальном орудии</i> Шушан-Эдуф. Писание Давида для изучения,"},{num:2,verse:"когда он воевал с Сириею Месопотамскою и с Сириею Цованскою, и когда Иоав, возвращаясь, поразил двенадцать тысяч Идумеев в долине Соляной."},{num:0,verse:""},{num:3,verse:"Боже! Ты отринул нас, Ты сокрушил нас, Ты прогневался: обратись к нам."},{num:4,verse:"Ты потряс землю, разбил ее: исцели повреждения ее, ибо она колеблется."},{num:5,verse:"Ты дал испытать народу Твоему жестокое, напоил нас вином изумления."},{num:6,verse:"Даруй боящимся Тебя знамя, чтобы они подняли его ради истины,"},{num:7,verse:"чтобы избавились возлюбленные Твои; спаси десницею Твоею и услышь меня."},{num:8,verse:'Бог сказал во святилище Своем: "восторжествую, разделю Сихем и долину Сокхоф размерю:'},{num:9,verse:"Мой Галаад, Мой Манассия, Ефрем крепость главы Моей, Иуда скипетр Мой,"},{num:10,verse:'Моав умывальная чаша Моя; на Едома простру сапог Мой. Восклицай Мне, земля Филистимская!"'},{num:11,verse:"Кто введет меня в укрепленный город? Кто доведет меня до Едома?"},{num:12,verse:"Не Ты ли, Боже, <i>Который</i> отринул нас, и не выходишь, Боже, с войсками нашими?"},{num:13,verse:"Подай нам помощь в тесноте, ибо защита человеческая суетна."},{num:14,verse:"С Богом мы окажем силу, Он низложит врагов наших."}]},{chapter:"60",verses:[{num:1,verse:"Начальнику хора. На струнном <i>орудии</i>. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Услышь, Боже, вопль мой, внемли молитве моей!"},{num:3,verse:"От конца земли взываю к Тебе в унынии сердца моего; возведи меня на скалу, для меня недосягаемую,"},{num:4,verse:"ибо Ты прибежище мое, Ты крепкая защита от врага."},{num:5,verse:"Да живу я вечно в жилище Твоем и покоюсь под кровом крыл Твоих,"},{num:6,verse:"ибо Ты, Боже, услышал обеты мои и дал <i>мне</i> наследие боящихся имени Твоего."},{num:7,verse:"Приложи дни ко дням царя, лета его <i>продли</i> в род и род,"},{num:8,verse:"да пребудет он вечно пред Богом; заповедуй милости и истине охранять его."},{num:9,verse:"И я буду петь имени Твоему вовек, исполняя обеты мои всякий день."}]},{chapter:"61",verses:[{num:1,verse:"Начальнику хора Идифумова. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Только в Боге успокаивается душа моя: от Него спасение мое."},{num:3,verse:"Только Он – твердыня моя, спасение мое, убежище мое: не поколеблюсь более."},{num:4,verse:"Доколе вы будете налегать на человека? Вы будете низринуты, все вы, как наклонившаяся стена, как ограда пошатнувшаяся."},{num:5,verse:"Они задумали свергнуть его с высоты, прибегли ко лжи; устами благословляют, а в сердце своем клянут."},{num:6,verse:"Только в Боге успокаивайся, душа моя! ибо на Него надежда моя."},{num:7,verse:"Только Он – твердыня моя и спасение мое, убежище мое: не поколеблюсь."},{num:8,verse:"В Боге спасение мое и слава моя; крепость силы моей и упование мое в Боге."},{num:9,verse:"Народ! надейтесь на Него во всякое время; изливайте пред Ним сердце ваше: Бог нам прибежище."},{num:10,verse:"Сыны человеческие – только суета; сыны мужей – ложь; если положить их на весы, все они вместе легче пустоты."},{num:11,verse:"Не надейтесь на грабительство и не тщеславьтесь хищением; когда богатство умножается, не прилагайте <i>к нему</i> сердца."},{num:12,verse:"Однажды сказал Бог, и дважды слышал я это, что сила у Бога,"},{num:13,verse:"и у Тебя, Господи, милость, ибо Ты воздаешь каждому по делам его."}]},{chapter:"62",verses:[{num:1,verse:"Псалом Давида, когда он был в пустыне Иудейской."},{num:0,verse:""},{num:2,verse:"Боже! Ты Бог мой, Тебя от ранней зари ищу я; Тебя жаждет душа моя, по Тебе томится плоть моя в земле пустой, иссохшей и безводной,"},{num:3,verse:"чтобы видеть силу Твою и славу Твою, как я видел Тебя во святилище:"},{num:4,verse:"ибо милость Твоя лучше, нежели жизнь. Уста мои восхвалят Тебя."},{num:5,verse:"Так благословлю Тебя в жизни моей; во имя Твое вознесу руки мои."},{num:6,verse:"Как туком и елеем насыщается душа моя, и радостным гласом восхваляют Тебя уста мои,"},{num:7,verse:"когда я вспоминаю о Тебе на постели моей, размышляю о Тебе в <i>ночные</i> стражи,"},{num:8,verse:"ибо Ты помощь моя, и в тени крыл Твоих я возрадуюсь;"},{num:9,verse:"к Тебе прилепилась душа моя; десница Твоя поддерживает меня."},{num:10,verse:"А те, которые ищут погибели душе моей, сойдут в преисподнюю земли;"},{num:11,verse:"сразят их силою меча; достанутся они в добычу лисицам."},{num:12,verse:"Царь же возвеселится о Боге, восхвален будет всякий, клянущийся Им, ибо заградятся уста говорящих неправду."}]},{chapter:"63",verses:[{num:1,verse:"Начальнику хора. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Услышь, Боже, голос мой в молитве моей, сохрани жизнь мою от страха врага;"},{num:3,verse:"укрой меня от замысла коварных, от мятежа злодеев,"},{num:4,verse:"которые изострили язык свой, как меч; напрягли лук свой – язвительное слово,"},{num:5,verse:"чтобы втайне стрелять в непорочного; они внезапно стреляют в него и не боятся."},{num:6,verse:"Они утвердились в злом намерении, совещались скрыть сеть, говорили: кто их увидит?"},{num:7,verse:"Изыскивают неправду, делают расследование за расследованием даже до внутренней жизни человека и до глубины сердца."},{num:8,verse:"Но поразит их Бог стрелою: внезапно будут они уязвлены;"},{num:9,verse:"языком своим они поразят самих себя; все, видящие их, удалятся <i>от них</i>."},{num:10,verse:"И убоятся все человеки, и возвестят дело Божие, и уразумеют, что это Его дело."},{num:11,verse:"А праведник возвеселится о Господе и будет уповать на Него; и похвалятся все правые сердцем."}]},{chapter:"64",verses:[{num:1,verse:"Начальнику хора. Псалом Давида для пения."},{num:0,verse:""},{num:2,verse:"Тебе, Боже, принадлежит хвала на Сионе, и Тебе воздастся обет [в Иерусалиме]."},{num:3,verse:"Ты слышишь молитву; к Тебе прибегает всякая плоть."},{num:4,verse:"Дела беззаконий превозмогают меня; Ты очистишь преступления наши."},{num:5,verse:"Блажен, кого Ты избрал и приблизил, чтобы он жил во дворах Твоих. Насытимся благами дома Твоего, святаго храма Твоего."},{num:6,verse:"Страшный в правосудии, услышь нас, Боже, Спаситель наш, упование всех концов земли и находящихся в море далеко,"},{num:7,verse:"поставивший горы силою Своею, препоясанный могуществом,"},{
num:8,verse:"укрощающий шум морей, шум волн их и мятеж народов!"},{num:9,verse:"И убоятся знамений Твоих живущие на пределах <i>земли</i>. Утро и вечер возбудишь к славе <i>Твоей</i>."},{num:10,verse:"Ты посещаешь землю и утоляешь жажду ее, обильно обогащаешь ее: поток Божий полон воды; Ты приготовляешь хлеб, ибо так устроил ее;"},{num:11,verse:"напояешь борозды ее, уравниваешь глыбы ее, размягчаешь ее каплями дождя, благословляешь произрастания ее;"},{num:12,verse:"венчаешь лето благости Твоей, и стези Твои источают тук,"},{num:13,verse:"источают на пустынные пажити, и холмы препоясываются радостью;"},{num:14,verse:"луга одеваются стадами, и долины покрываются хлебом, восклицают и поют."}]},{chapter:"65",verses:[{num:0,verse:"Начальнику хора. Песнь."},{num:0,verse:""},{num:1,verse:"Воскликните Богу, вся земля."},{num:2,verse:"Пойте славу имени Его, воздайте славу, хвалу Ему."},{num:3,verse:"Скажите Богу: как страшен Ты в делах Твоих! По множеству силы Твоей, покорятся Тебе враги Твои."},{num:4,verse:"Вся земля да поклонится Тебе и поет Тебе, да поет имени Твоему, [Вышний]!"},{num:5,verse:"Придите и воззрите на дела Бога, страшного в делах над сынами человеческими."},{num:6,verse:"Он превратил море в сушу; через реку перешли стопами, там веселились мы о Нем."},{num:7,verse:"Могуществом Своим владычествует Он вечно; очи Его зрят на народы, да не возносятся мятежники."},{num:8,verse:"Благословите, народы, Бога нашего и провозгласите хвалу Ему."},{num:9,verse:"Он сохранил душе нашей жизнь и ноге нашей не дал поколебаться."},{num:10,verse:"Ты испытал нас, Боже, переплавил нас, как переплавляют серебро."},{num:11,verse:"Ты ввел нас в сеть, положил оковы на чресла наши,"},{num:12,verse:"посадил человека на главу нашу. Мы вошли в огонь и в воду, и Ты вывел нас на свободу."},{num:13,verse:"Войду в дом Твой со всесожжениями, воздам Тебе обеты мои,"},{num:14,verse:"которые произнесли уста мои и изрек язык мой в скорби моей."},{num:15,verse:"Всесожжения тучные вознесу Тебе с воскурением тука овнов, принесу в жертву волов и козлов."},{num:16,verse:"Придите, послушайте, все боящиеся Бога, и я возвещу <i>вам</i>, что сотворил Он для души моей."},{num:17,verse:"Я воззвал к Нему устами моими и превознес Его языком моим."},{num:18,verse:"Если бы я видел беззаконие в сердце моем, то не услышал бы меня Господь."},{num:19,verse:"Но Бог услышал, внял гласу моления моего."},{num:20,verse:"Благословен Бог, Который не отверг молитвы моей и не отвратил от меня милости Своей."}]},{chapter:"66",verses:[{num:1,verse:"Начальнику хора. На струнных <i>орудиях</i>. Псалом. Песнь."},{num:0,verse:""},{num:2,verse:"Боже! будь милостив к нам и благослови нас, освети нас лицем Твоим,"},{num:3,verse:"дабы познали на земле путь Твой, во всех народах спасение Твое."},{num:4,verse:"Да восхвалят Тебя народы, Боже; да восхвалят Тебя народы все."},{num:5,verse:"Да веселятся и радуются племена, ибо Ты судишь народы праведно и управляешь на земле племенами."},{num:6,verse:"Да восхвалят Тебя народы, Боже, да восхвалят Тебя народы все."},{num:7,verse:"Земля дала плод свой; да благословит нас Бог, Бог наш."},{num:8,verse:"Да благословит нас Бог, и да убоятся Его все пределы земли."}]},{chapter:"67",verses:[{num:1,verse:"Начальнику хора. Псалом Давида. Песнь."},{num:0,verse:""},{num:2,verse:"Да восстанет Бог, и расточатся враги Его, и да бегут от лица Его ненавидящие Его."},{num:3,verse:"Как рассеивается дым, Ты рассей их; как тает воск от огня, так нечестивые да погибнут от лица Божия."},{num:4,verse:"А праведники да возвеселятся, да возрадуются пред Богом и восторжествуют в радости."},{num:5,verse:"Пойте Богу нашему, пойте имени Его, превозносите Шествующего на небесах; имя Ему: Господь, и радуйтесь пред лицем Его."},{num:6,verse:"Отец сирот и судья вдов Бог во святом Своем жилище."},{num:7,verse:"Бог одиноких вводит в дом, освобождает узников от оков, а непокорные остаются в знойной пустыне."},{num:8,verse:"Боже! когда Ты выходил пред народом Твоим, когда Ты шествовал пустынею,"},{num:9,verse:"земля тряслась, даже небеса таяли от лица Божия, и этот Синай – от лица Бога, Бога Израилева."},{num:10,verse:"Обильный дождь проливал Ты, Боже, на наследие Твое, и когда оно изнемогало от труда, Ты подкреплял его."},{num:11,verse:"Народ Твой обитал там; по благости Твоей, Боже, Ты готовил <i>необходимое</i> для бедного."},{num:12,verse:"Господь даст слово: провозвестниц великое множество."},{num:13,verse:"Цари воинств бегут, бегут, а сидящая дома делит добычу."},{num:14,verse:"Расположившись в уделах [своих], вы стали, как голубица, которой крылья покрыты серебром, а перья чистым золотом:"},{num:15,verse:"когда Всемогущий рассеял царей на сей <i>земле</i>, она забелела, как снег на Селмоне."},{num:16,verse:"Гора Божия – гора Васанская! гора высокая – гора Васанская!"},{num:17,verse:"что вы завистливо смотрите, горы высокие, на гору, на которой Бог благоволит обитать и будет Господь обитать вечно?"},{num:18,verse:"Колесниц Божиих тьмы, тысячи тысяч; среди их Господь на Синае, во святилище."},{num:19,verse:"Ты восшел на высоту, пленил плен, принял дары для человеков, так чтоб и из противящихся могли обитать у Господа Бога."},{num:20,verse:"Благословен Господь всякий день. Бог возлагает на нас бремя, но Он же и спасает нас."},{num:21,verse:"Бог для нас – Бог во спасение; во власти Господа Вседержителя врата смерти."},{num:22,verse:"Но Бог сокрушит голову врагов Своих, волосатое темя закоснелого в своих беззакониях."},{num:23,verse:"Господь сказал: [от Васана возвращу, выведу из глубины морской,"},{num:24,verse:"чтобы ты погрузил ногу твою, как и псы твои язык свой, в крови врагов]."},{num:25,verse:"Видели шествие Твое, Боже, шествие Бога моего, Царя моего во святыне:"},{num:26,verse:"впереди шли поющие, позади играющие на орудиях, в средине девы с тимпанами:"},{num:27,verse:'"в собраниях благословите <i>Бога Господа</i>, вы – от семени Израилева!"'},{num:28,verse:"Там Вениамин младший – князь их; князья Иудины – владыки их, князья Завулоновы, князья Неффалимовы."},{num:29,verse:"Бог твой предназначил тебе силу. Утверди, Боже, то, что Ты соделал для нас!"},{num:30,verse:"Ради храма Твоего в Иерусалиме цари принесут Тебе дары."},{num:31,verse:"Укроти зверя в тростнике, стадо волов среди тельцов народов, хвалящихся слитками серебра; рассыпь народы, желающие браней."},{num:32,verse:"Придут вельможи из Египта; Ефиопия прострет руки свои к Богу."},{num:33,verse:"Царства земные! пойте Богу, воспевайте Господа,"},{num:34,verse:"шествующего на небесах небес от века. Вот, Он дает гласу Своему глас силы."},{num:35,verse:"Воздайте славу Богу! величие Его – над Израилем, и могущество Его – на облаках."},{num:36,verse:"Страшен Ты, Боже, во святилище Твоем. Бог Израилев – Он дает силу и крепость народу [Своему]. Благословен Бог!"}]},{chapter:"68",verses:[{num:1,verse:"Начальнику хора. На Шошанниме. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Спаси меня, Боже, ибо воды дошли до души [моей]."},{num:3,verse:"Я погряз в глубоком болоте, и не на чем стать; вошел во глубину вод, и быстрое течение их увлекает меня."},{num:4,verse:"Я изнемог от вопля, засохла гортань моя, истомились глаза мои от ожидания Бога [моего]."},{num:5,verse:"Ненавидящих меня без вины больше, нежели волос на голове моей; враги мои, преследующие меня несправедливо, усилились; чего я не отнимал, то должен отдать."},{num:6,verse:"Боже! Ты знаешь безумие мое, и грехи мои не сокрыты от Тебя."},{num:7,verse:"Да не постыдятся во мне все, надеющиеся на Тебя, Господи, Боже сил. Да не посрамятся во мне ищущие Тебя, Боже Израилев,"},{num:8,verse:"ибо ради Тебя несу я поношение, и бесчестием покрывают лице мое."},{num:9,verse:"Чужим стал я для братьев моих и посторонним для сынов матери моей,"},{num:10,verse:"ибо ревность по доме Твоем снедает меня, и злословия злословящих Тебя падают на меня;"},{num:11,verse:"и плачу, постясь душею моею, и это ставят в поношение мне;"},{num:12,verse:"и возлагаю на себя вместо одежды вретище, – и делаюсь для них притчею;"},{num:13,verse:"о мне толкуют сидящие у ворот, и поют в песнях пьющие вино."},{num:14,verse:"А я с молитвою моею к Тебе, Господи; во время благоугодное, Боже, по великой благости Твоей услышь меня в истине спасения Твоего;"},{num:15,verse:"извлеки меня из тины, чтобы не погрязнуть мне; да избавлюсь от ненавидящих меня и от глубоких вод;"},{num:16,verse:"да не увлечет меня стремление вод, да не поглотит меня пучина, да не затворит надо мною пропасть зева своего."},{num:17,verse:"Услышь меня, Господи, ибо блага милость Твоя; по множеству щедрот Твоих призри на меня;"},{num:18,verse:"не скрывай лица Твоего от раба Твоего, ибо я скорблю; скоро услышь меня;"},{num:19,verse:"приблизься к душе моей, избавь ее; ради врагов моих спаси меня."},{num:20,verse:"Ты знаешь поношение мое, стыд мой и посрамление мое: враги мои все пред Тобою."},{num:21,verse:"Поношение сокрушило сердце мое, и я изнемог, ждал сострадания, но нет его, – утешителей, но не нахожу."},{num:22,verse:"И дали мне в пищу желчь, и в жажде моей напоили меня уксусом."},{num:23,verse:"Да будет трапеза их сетью им, и мирное пиршество их – западнею;"},{num:24,verse:"да помрачатся глаза их, чтоб им не видеть, и чресла их расслабь навсегда;"},{num:25,verse:"излей на них ярость Твою, и пламень гнева Твоего да обымет их;"},{num:26,verse:"жилище их да будет пусто, и в шатрах их да не будет живущих,"},{num:27,verse:"ибо, кого Ты поразил, они <i>еще</i> преследуют, и страдания уязвленных Тобою умножают."},{num:28,verse:"Приложи беззаконие к беззаконию их, и да не войдут они в правду Твою;"},{num:29,verse:"да изгладятся они из книги живых и с праведниками да не напишутся."},{num:30,verse:"А я беден и страдаю; помощь Твоя, Боже, да восставит меня."},{num:31,verse:"Я буду славить имя Бога [моего] в песни, буду превозносить Его в славословии,"},{num:32,verse:"и будет это благоугоднее Господу, нежели вол, нежели телец с рогами и с копытами."},{num:33,verse:"Увидят <i>это</i> страждущие и возрадуются. И оживет сердце ваше, ищущие Бога,"},{num:34,verse:"ибо Господь внемлет нищим и не пренебрегает узников Своих."},{num:35,verse:"Да восхвалят Его небеса и земля, моря и все движущееся в них;"},{num:36,verse:"ибо спасет Бог Сион, создаст города Иудины, и поселятся там и наследуют его,"},{num:37,verse:"и потомство рабов Его утвердится в нем, и любящие имя Его будут поселяться на нем."}]},{chapter:"69",verses:[{num:1,verse:"Начальнику хора. Псалом Давида. В воспоминание."},{num:0,verse:""},{num:2,verse:"Поспеши, Боже, избавить меня, <i>поспеши</i>, Господи, на помощь мне."},{num:3,verse:"Да постыдятся и посрамятся ищущие души моей! Да будут обращены назад и преданы посмеянию желающие мне зла!"},{num:4,verse:'Да будут обращены назад за поношение меня говорящие [мне]: "хорошо! хорошо!"'},{num:5,verse:'Да возрадуются и возвеселятся о Тебе все, ищущие Тебя, и любящие спасение Твое да говорят непрестанно: "велик Бог!"'},{num:6,verse:"Я же беден и нищ; Боже, поспеши ко мне! Ты помощь моя и Избавитель мой; Господи! не замедли."}]},{chapter:"70",verses:[{num:1,verse:"На Тебя, Господи, уповаю, да не постыжусь вовек."},{num:2,verse:"По правде Твоей избавь меня и освободи меня; приклони ухо Твое ко мне и спаси меня."},{num:3,verse:"Будь мне твердым прибежищем, куда я всегда мог бы укрываться; Ты заповедал спасти меня, ибо твердыня моя и крепость моя – Ты."},{num:4,verse:"Боже мой! избавь меня из руки нечестивого, из руки беззаконника и притеснителя,"},{num:5,verse:"ибо Ты – надежда моя, Господи Боже, упование мое от юности моей."},{num:6,verse:"На Тебе утверждался я от утробы; Ты извел меня из чрева матери моей; Тебе хвала моя не престанет."},{num:7,verse:"Для многих я был как бы дивом, но Ты твердая моя надежда."},{num:8,verse:"Да наполнятся уста мои хвалою, [чтобы мне воспевать славу Твою,] всякий день великолепие Твое."},{num:9,verse:"Не отвергни меня во время старости; когда будет оскудевать сила моя, не оставь меня,"},{num:10,verse:"ибо враги мои говорят против меня, и подстерегающие душу мою советуются между собою,"},{num:11,verse:'говоря: "Бог оставил его; преследуйте и схватите его, ибо нет избавляющего".'},{num:12,verse:"Боже! не удаляйся от меня; Боже мой! поспеши на помощь мне."},{num:13,verse:"Да постыдятся и исчезнут враждующие против души моей, да покроются стыдом и бесчестием ищущие мне зла!"},{num:14,verse:"А я всегда буду уповать [на Тебя] и умножать всякую хвалу Тебе."},{num:15,verse:"Уста мои будут возвещать правду Твою, всякий день благодеяния Твои; ибо я не знаю им числа."},{num:16,verse:"Войду в <i>размышление</i> о силах Господа Бога; воспомяну правду Твою – единственно Твою."},{num:17,verse:"Боже! Ты наставлял меня от юности моей, и доныне я возвещаю чудеса Твои."},{num:18,verse:"И до старости, и до седины не оставь меня, Боже, доколе не возвещу силы Твоей роду сему и всем грядущим могущества Твоего."},{num:19,verse:"Правда Твоя, Боже, до превыспренних; великие дела соделал Ты; Боже, кто подобен Тебе?"},{num:20,verse:"Ты посылал на меня многие и лютые беды, но и опять оживлял меня и из бездн земли опять выводил меня."},{num:21,verse:"Ты возвышал меня и утешал меня, [и из бездн земли выводил меня]."},{num:22,verse:"И я буду славить Тебя на псалтири, Твою истину, Боже мой; буду воспевать Тебя на гуслях, Святый Израилев!"},{num:23,verse:"Радуются уста мои, когда я пою Тебе, и душа моя, которую Ты избавил;"},{num:24,verse:"и язык мой всякий день будет возвещать правду Твою, ибо постыжены и посрамлены ищущие мне зла."}]},{chapter:"71",verses:[{num:0,verse:"О Соломоне. [Псалом Давида.]"},{num:0,verse:""},{num:1,verse:"Боже! даруй царю Твой суд и сыну царя Твою правду,"},{num:2,verse:"да судит праведно людей Твоих и нищих Твоих на суде;"},{num:3,verse:"да принесут горы мир людям и холмы правду;"},{num:4,verse:"да судит нищих народа, да спасет сынов убогого и смирит притеснителя, -"},{num:5,verse:"и будут бояться Тебя, доколе пребудут солнце и луна, в роды родов."},{num:6,verse:"Он сойдет, как дождь на скошенный луг, как капли, орошающие землю;"},{num:7,verse:"во дни его процветет праведник, и будет обилие мира, доколе не престанет луна;"},{num:8,verse:"он будет обладать от моря до моря и от реки до концов земли;"},{num:9,verse:"падут пред ним жители пустынь, и враги его будут лизать прах;"},{num:10,verse:"цари Фарсиса и островов поднесут ему дань; цари Аравии и Савы принесут дары;"},{num:11,verse:"и поклонятся ему все цари; все народы будут служить ему;"},{num:12,verse:"ибо он избавит нищего, вопиющего и угнетенного, у которого нет помощника."},{num:13,verse:"Будет милосерд к нищему и убогому, и души убогих спасет;"},{num:14,verse:"от коварства и насилия избавит души их, и драгоценна будет кровь их пред очами его;"},{num:15,verse:"и будет жить, и будут давать ему от золота Аравии, и будут молиться о нем непрестанно, всякий день благословлять его;"},{num:16,verse:"будет обилие хлеба на земле, наверху гор; плоды его будут волноваться, как <i>лес</i> на Ливане, и в городах размножатся люди, как трава на земле;"},{num:17,verse:"будет имя его [благословенно] вовек; доколе пребывает солнце, будет передаваться имя его; и благословятся в нем [все племена земные], все народы ублажат его."},{num:18,verse:"Благословен Господь Бог, Бог Израилев, един творящий чудеса,"},{num:19,verse:"и благословенно имя славы Его вовек, и наполнится славою Его вся земля. Аминь и аминь."},{num:20,verse:"Кончились молитвы Давида, сына Иесеева."}]},{chapter:"72",verses:[{num:0,verse:"Псалом Асафа."},{num:0,verse:""},{num:1,verse:"Как благ Бог к Израилю, к чистым сердцем!"},{num:2,verse:"А я – едва не пошатнулись ноги мои, едва не поскользнулись стопы мои, -"},{num:3,verse:"я позавидовал безумным, видя благоденствие нечестивых,"},{num:4,verse:"ибо им нет страданий до смерти их, и крепки силы их;"},{num:5,verse:"на работе человеческой нет их, и с <i>прочими</i> людьми не подвергаются ударам."},{num:6,verse:"Оттого гордость, как ожерелье, обложила их, и дерзость, <i>как</i> наряд, одевает их;"},{num:7,verse:"выкатились от жира глаза их, бродят помыслы в сердце;"},{num:8,verse:"над всем издеваются, злобно разглашают клевету, говорят свысока;"},{num:9,verse:"поднимают к небесам уста свои, и язык их расхаживает по земле."},{num:10,verse:"Потому туда же обращается народ Его, и пьют воду полною чашею,"},{num:11,verse:'и говорят: "как узнает Бог? и есть ли ведение у Вышнего?"'},{num:12,verse:"И вот, эти нечестивые благоденствуют в веке сем, умножают богатство."},{num:13,verse:"[И я сказал:] так не напрасно ли я очищал сердце мое и омывал в невинности руки мои,"},{num:14,verse:"и подвергал себя ранам всякий день и обличениям всякое утро?"},{num:15,verse:'<i>Но</i> если бы я сказал: "буду рассуждать так", – то я виновен был бы пред родом сынов Твоих.'},{num:16,verse:"И думал я, как бы уразуметь это, но это трудно было в глазах моих,"},{num:17,verse:"доколе не вошел я во святилище Божие и не уразумел конца их."},{num:18,verse:"Так! на скользких путях поставил Ты их и низвергаешь их в пропасти."},{num:19,verse:"Как нечаянно пришли они в разорение, исчезли, погибли от ужасов!"},{num:20,verse:"Как сновидение по пробуждении, так Ты, Господи, пробудив <i>их</i>, уничтожишь мечты их."},{num:21,verse:"Когда кипело сердце мое, и терзалась внутренность моя,"},{num:22,verse:"тогда я был невежда и не разумел; как скот был я пред Тобою."},{num:23,verse:"Но я всегда с Тобою: Ты держишь меня за правую руку;"},{num:24,verse:"Ты руководишь меня советом Твоим и потом примешь меня в славу."},{num:25,verse:"Кто мне на небе? и с Тобою ничего не хочу на земле."},{num:26,verse:"Изнемогает плоть моя и сердце мое: Бог твердыня сердца моего и часть моя вовек."},{num:27,verse:"Ибо вот, удаляющие себя от Тебя гибнут; Ты истребляешь всякого отступающего от Тебя."},{num:28,verse:"А мне благо приближаться к Богу! На Господа Бога я возложил упование мое, чтобы возвещать все дела Твои [во вратах дщери Сионовой]."}]},{chapter:"73",verses:[{num:0,verse:"Учение Асафа."},{num:0,verse:""},{num:1,verse:"Для чего, Боже, отринул нас навсегда? возгорелся гнев Твой на овец пажити Твоей?"},{num:2,verse:"Вспомни сонм Твой, <i>который</i> Ты стяжал издревле, искупил в жезл достояния Твоего, – эту гору Сион, на которой Ты веселился."},{num:3,verse:"Подвигни стопы Твои к вековым развалинам: все разрушил враг во святилище."},{num:4,verse:"Рыкают враги Твои среди собраний Твоих; поставили знаки свои вместо знамений <i>наших</i>;"},{num:5,verse:"показывали себя подобными поднимающему вверх секиру на сплетшиеся ветви дерева;"},{num:6,verse:"и ныне все резьбы в нем в один раз разрушили секирами и бердышами;"},{num:7,verse:"предали огню святилище Твое; совсем осквернили жилище имени Твоего;"},{num:8,verse:'сказали в сердце своем: "разорим их совсем", – и сожгли все места собраний Божиих на земле.'},{num:9,verse:"Знамений наших мы не видим, нет уже пророка, и нет с нами, кто знал бы, доколе <i>это будет</i>."},{num:10,verse:"Доколе, Боже, будет поносить враг? вечно ли будет хулить противник имя Твое?"},{num:11,verse:"Для чего отклоняешь руку Твою и десницу Твою? Из среды недра Твоего порази <i>их</i>."},{num:12,verse:"Боже, Царь мой от века, устрояющий спасение посреди земли!"},{num:13,verse:"Ты расторг силою Твоею море, Ты сокрушил головы змиев в воде;"},{num:14,verse:"Ты сокрушил голову левиафана, отдал его в пищу людям пустыни, [Ефиопским];"},{num:15,verse:"Ты иссек источник и поток, Ты иссушил сильные реки."},{num:16,verse:"Твой день и Твоя ночь: Ты уготовал светила и солнце;"},{num:17,verse:"Ты установил все пределы земли, лето и зиму Ты учредил."},{num:18,verse:"Вспомни же: враг поносит Господа, и люди безумные хулят имя Твое."},{num:19,verse:"Не предай зверям душу горлицы Твоей; собрания убогих Твоих не забудь навсегда."},{num:20,verse:"Призри на завет Твой; ибо наполнились все мрачные места земли жилищами насилия."},{num:21,verse:"Да не возвратится угнетенный посрамленным; нищий и убогий да восхвалят имя Твое."},{num:22,verse:"Восстань, Боже, защити дело Твое, вспомни вседневное поношение Твое от безумного;"},{num:23,verse:"не забудь крика врагов Твоих; шум восстающих против Тебя непрестанно поднимается."}]},{chapter:"74",verses:[{num:1,verse:"Начальнику хора. Не погуби. Псалом Асафа. Песнь."},{num:0,verse:""},{num:2,verse:"Славим Тебя, Боже, славим, ибо близко имя Твое; возвещают чудеса Твои."},{num:3,verse:'"Когда изберу время, Я произведу суд по правде.'},{num:4,verse:'Колеблется земля и все живущие на ней: Я утвержу столпы ее".'},{num:5,verse:'Говорю безумствующим: "не безумствуйте", и нечестивым: "не поднимайте рога,'},{num:6,verse:'не поднимайте высоко рога вашего, [не] говорите [на Бога] жестоковыйно",'},{num:7,verse:"ибо не от востока и не от запада и не от пустыни возвышение,"},{num:8,verse:"но Бог есть судия: одного унижает, а другого возносит;"},{num:9,verse:"ибо чаша в руке Господа, вино кипит в ней, полное смешения, и Он наливает из нее. Даже дрожжи ее будут выжимать и пить все нечестивые земли."},{num:10,verse:"А я буду возвещать вечно, буду воспевать Бога Иаковлева,"},{num:11,verse:"все роги нечестивых сломлю, и вознесутся роги праведника."}]},{chapter:"75",verses:[{num:1,verse:"Начальнику хора. На струнных <i>орудиях</i>. Псалом Асафа. Песнь."},{num:0,verse:""},{num:2,verse:"Ведом в Иудее Бог; у Израиля велико имя Его."},{num:3,verse:"И было в Салиме жилище Его и пребывание Его на Сионе."},{num:4,verse:"Там сокрушил Он стрелы лука, щит и меч и брань."},{num:5,verse:"Ты славен, могущественнее гор хищнических."},{num:6,verse:"Крепкие сердцем стали добычею, уснули сном своим, и не нашли все мужи силы рук своих."},{num:7,verse:"От прещения Твоего, Боже Иакова, вздремали и колесница и конь."},{num:8,verse:"Ты страшен, и кто устоит пред лицем Твоим во время гнева Твоего?"},{num:9,verse:"С небес Ты возвестил суд; земля убоялась и утихла,"},{num:10,verse:"когда восстал Бог на суд, чтобы спасти всех угнетенных земли."},{num:11,verse:"И гнев человеческий обратится во славу Тебе: остаток гнева Ты укротишь."},{num:12,verse:"Делайте и воздавайте обеты Господу, Богу вашему; все, которые вокруг Него, да принесут дары Страшному:"},{num:13,verse:"Он укрощает дух князей, Он страшен для царей земных."}]},{chapter:"76",verses:[{num:1,verse:"Начальнику хора Идифумова. Псалом Асафа."},{num:0,verse:""},{num:2,verse:"Глас мой к Богу, и я буду взывать; глас мой к Богу, и Он услышит меня."},{num:3,verse:"В день скорби моей ищу Господа; рука моя простерта ночью и не опускается; душа моя отказывается от утешения."},{num:4,verse:"Вспоминаю о Боге и трепещу; помышляю, и изнемогает дух мой."},{num:5,verse:"Ты не даешь мне сомкнуть очей моих; я потрясен и не могу говорить."},{num:6,verse:"Размышляю о днях древних, о летах веков <i>минувших</i>;"},{num:7,verse:"припоминаю песни мои в ночи, беседую с сердцем моим, и дух мой испытывает:"},{num:8,verse:"неужели навсегда отринул Господь, и не будет более благоволить?"},{num:9,verse:"неужели навсегда престала милость Его, и пресеклось слово Его в род и род?"},{num:10,verse:"неужели Бог забыл миловать? Неужели во гневе затворил щедроты Свои?"},{num:11,verse:'И сказал я: "вот мое горе – изменение десницы Всевышнего".'},{num:12,verse:"Буду вспоминать о делах Господа; буду вспоминать о чудесах Твоих древних;"},{num:13,verse:"буду вникать во все дела Твои, размышлять о великих Твоих деяниях."},{num:14,verse:"Боже! свят путь Твой. Кто Бог так великий, как Бог [наш]!"},{num:15,verse:"Ты – Бог, творящий чудеса; Ты явил могущество Свое среди народов;"},{num:16,verse:"Ты избавил мышцею народ Твой, сынов Иакова и Иосифа."},{num:17,verse:"Видели Тебя, Боже, воды, видели Тебя воды и убоялись, и вострепетали бездны."},{num:18,verse:"Облака изливали воды, тучи издавали гром, и стрелы Твои летали."},{num:19,verse:"Глас грома Твоего в круге небесном; молнии освещали вселенную; земля содрогалась и тряслась."},{num:20,verse:"Путь Твой в море, и стезя Твоя в водах великих, и следы Твои неведомы."},{num:21,verse:"Как стадо, вел Ты народ Твой рукою Моисея и Аарона."}]},{chapter:"77",verses:[{num:0,verse:"Учение Асафа."},{num:0,verse:""},{num:1,verse:"Внимай, народ мой, закону моему, приклоните ухо ваше к словам уст моих."},{num:2,verse:"Открою уста мои в притче и произнесу гадания из древности."},{num:3,verse:"Что слышали мы и узнали, и отцы наши рассказали нам,"},{num:4,verse:"не скроем от детей их, возвещая роду грядущему славу Господа, и силу Его, и чудеса Его, которые Он сотворил."},{num:5,verse:"Он постановил устав в Иакове и положил закон в Израиле, который заповедал отцам нашим возвещать детям их,"},{num:6,verse:"чтобы знал грядущий род, дети, которые родятся, и чтобы они в свое время возвещали своим детям, -"},{num:7,verse:"возлагать надежду свою на Бога и не забывать дел Божиих, и хранить заповеди Его,"},{num:8,verse:"и не быть подобными отцам их, роду упорному и мятежному, неустроенному сердцем и неверному Богу духом своим."},{num:9,verse:"Сыны Ефремовы, вооруженные, стреляющие из луков, обратились назад в день брани:"},{num:10,verse:"они не сохранили завета Божия и отреклись ходить в законе Его;"},{num:11,verse:"забыли дела Его и чудеса, которые Он явил им."},{num:12,verse:"Он пред глазами отцов их сотворил чудеса в земле Египетской, на поле Цоан:"},{num:13,verse:"разделил море, и провел их чрез него, и поставил воды стеною;"},{num:14,verse:"и днем вел их облаком, а во всю ночь светом огня;"},{num:15,verse:"рассек камень в пустыне и напоил их, как из великой бездны;"},{num:16,verse:"из скалы извел потоки, и воды потекли, как реки."},{num:17,verse:"Но они продолжали грешить пред Ним и раздражать Всевышнего в пустыне:"},{num:18,verse:"искушали Бога в сердце своем, требуя пищи по душе своей,"},{num:19,verse:'и говорили против Бога и сказали: "может ли Бог приготовить трапезу в пустыне?"'},{num:20,verse:'Вот, Он ударил в камень, и потекли воды, и полились ручьи. "Может ли Он дать и хлеб, может ли приготовлять мясо народу Своему?"'},{num:21,verse:"Господь услышал и воспламенился гневом, и огонь возгорелся на Иакова, и гнев подвигнулся на Израиля"},{num:22,verse:"за то, что не веровали в Бога и не уповали на спасение Его."},{num:23,verse:"Он повелел облакам свыше и отверз двери неба,"},{num:24,verse:"и одождил на них манну в пищу, и хлеб небесный дал им."},{num:25,verse:"Хлеб ангельский ел человек; послал Он им пищу до сытости."},{num:26,verse:"Он возбудил на небе восточный ветер и навел южный силою Своею"},{num:27,verse:"и, как пыль, одождил на них мясо и, как песок морской, птиц пернатых:"},{num:28,verse:"поверг их среди стана их, около жилищ их, -"},{num:29,verse:"и они ели и пресытились; и желаемое ими дал им."},{num:30,verse:"Но еще не прошла прихоть их, еще пища была в устах их,"},{num:31,verse:"гнев Божий пришел на них, убил тучных их и юношей Израилевых низложил."},{num:32,verse:"При всем этом они продолжали грешить и не верили чудесам Его."},{num:33,verse:"И погубил дни их в суете и лета их в смятении."},{num:34,verse:"Когда Он убивал их, они искали Его и обращались, и с раннего утра прибегали к Богу,"},{num:35,verse:"и вспоминали, что Бог – их прибежище, и Бог Всевышний – Избавитель их,"},{num:36,verse:"и льстили Ему устами своими и языком своим лгали пред Ним;"},{num:37,verse:"сердце же их было неправо пред Ним, и они не были верны завету Его."},{num:38,verse:"Но Он, Милостивый, прощал грех и не истреблял их, многократно отвращал гнев Свой и не возбуждал всей ярости Своей:"},{num:39,verse:"Он помнил, что они плоть, дыхание, которое уходит и не возвращается."},{num:40,verse:"Сколько раз они раздражали Его в пустыне и прогневляли Его в <i>стране</i> необитаемой!"},{num:41,verse:"и снова искушали Бога и оскорбляли Святаго Израилева,"},{num:42,verse:"не помнили руки Его, дня, когда Он избавил их от угнетения,"},{num:43,verse:"когда сотворил в Египте знамения Свои и чудеса Свои на поле Цоан;"},{num:44,verse:"и превратил реки их и потоки их в кровь, чтобы они не могли пить;"},{num:45,verse:"послал на них насекомых, чтобы жалили их, и жаб, чтобы губили их;"},{num:46,verse:"земные произрастения их отдал гусенице и труд их – саранче;"},{num:47,verse:"виноград их побил градом и сикоморы их – льдом;"},{num:48,verse:"скот их предал граду и стада их – молниям;"},{num:49,verse:"послал на них пламень гнева Своего, и негодование, и ярость и бедствие, посольство злых ангелов;"},{num:50,verse:"уравнял стезю гневу Своему, не охранял души их от смерти, и скот их предал моровой язве;"},{num:51,verse:"поразил всякого первенца в Египте, начатки сил в шатрах Хамовых;"},{num:52,verse:"и повел народ Свой, как овец, и вел их, как стадо, пустынею;"},{num:53,verse:"вел их безопасно, и они не страшились, а врагов их покрыло море;"},{num:54,verse:"и привел их в область святую Свою, на гору сию, которую стяжала десница Его;"},{num:55,verse:"прогнал от лица их народы и землю их разделил в наследие им, и колена Израилевы поселил в шатрах их."},{num:56,verse:"Но они еще искушали и огорчали Бога Всевышнего, и уставов Его не сохраняли;"},{num:57,verse:"отступали и изменяли, как отцы их, обращались назад, как неверный лук;"},{num:58,verse:"огорчали Его высотами своими и истуканами своими возбуждали ревность Его."},{num:59,verse:"Услышал Бог и воспламенился гневом и сильно вознегодовал на Израиля;"},{num:60,verse:"отринул жилище в Силоме, скинию, в которой обитал Он между человеками;"},{num:61,verse:"и отдал в плен крепость Свою и славу Свою в руки врага,"},{num:62,verse:"и предал мечу народ Свой и прогневался на наследие Свое."},{num:63,verse:"Юношей его поедал огонь, и девицам его не пели брачных песен;"},{num:64,verse:"священники его падали от меча, и вдовы его не плакали."},{num:65,verse:"Но, как бы от сна, воспрянул Господь, как бы исполин, побежденный вином,"},{num:66,verse:"и поразил врагов его в тыл, вечному сраму предал их;"},{num:67,verse:"и отверг шатер Иосифов и колена Ефремова не избрал,"},{num:68,verse:"а избрал колено Иудино, гору Сион, которую возлюбил."},{num:69,verse:"И устроил, как небо, святилище Свое и, как землю, утвердил его навек,"},{num:70,verse:"и избрал Давида, раба Своего, и взял его от дворов овчих"},{num:71,verse:"и от доящих привел его пасти народ Свой, Иакова, и наследие Свое, Израиля."},{num:72,verse:"И он пас их в чистоте сердца своего и руками мудрыми водил их."}]},{chapter:"78",verses:[{num:0,verse:"Псалом Асафа."},{num:0,verse:""},{num:1,verse:"Боже! язычники пришли в наследие Твое, осквернили святый храм Твой, Иерусалим превратили в развалины;"},{num:2,verse:"трупы рабов Твоих отдали на съедение птицам небесным, тела святых Твоих – зверям земным;"},{num:3,verse:"пролили кровь их, как воду, вокруг Иерусалима, и некому было похоронить их."},{num:4,verse:"Мы сделались посмешищем у соседей наших, поруганием и посрамлением у окружающих нас."},{num:5,verse:"Доколе, Господи, будешь гневаться непрестанно, будет пылать ревность Твоя, как огонь?"},{num:6,verse:"Пролей гнев Твой на народы, которые не знают Тебя, и на царства, которые имени Твоего не призывают,"},{num:7,verse:"ибо они пожрали Иакова и жилище его опустошили."},{num:8,verse:"Не помяни нам грехов <i>наших</i> предков; скоро да предварят нас щедроты Твои, ибо мы весьма истощены."},{num:9,verse:"Помоги нам, Боже, Спаситель наш, ради славы имени Твоего; избавь нас и прости нам грехи наши ради имени Твоего."},{num:10,verse:'Для чего язычникам говорить: "где Бог их?" Да сделается известным между язычниками пред глазами нашими отмщение за пролитую кровь рабов Твоих.'},{num:11,verse:"Да придет пред лице Твое стенание узника; могуществом мышцы Твоей сохрани обреченных на смерть."},{num:12,verse:"Семикратно возврати соседям нашим в недро их поношение, которым они Тебя, Господи, поносили."},{num:13,verse:"А мы, народ Твой и Твоей пажити овцы, вечно будем славить Тебя и в род и род возвещать хвалу Тебе."}]},{chapter:"79",verses:[{num:1,verse:"Начальнику хора. На музыкальном <i>орудии</i> Шошанним-Эдуф. Псалом Асафа."},{num:0,verse:""},{num:2,verse:"Пастырь Израиля! внемли; водящий, как овец, Иосифа, восседающий на Херувимах, яви Себя."},{num:3,verse:"Пред Ефремом и Вениамином и Манассиею воздвигни силу Твою, и приди спасти нас."
},{num:4,verse:"Боже! восстанови нас; да воссияет лице Твое, и спасемся!"},{num:5,verse:"Господи, Боже сил! доколе будешь гневен к молитвам народа Твоего?"},{num:6,verse:"Ты напитал их хлебом слезным, и напоил их слезами в большой мере,"},{num:7,verse:"положил нас в пререкание соседям нашим, и враги наши издеваются <i>над нами</i>."},{num:8,verse:"Боже сил! восстанови нас; да воссияет лице Твое, и спасемся!"},{num:9,verse:"Из Египта перенес Ты виноградную лозу, выгнал народы и посадил ее;"},{num:10,verse:"очистил для нее место, и утвердил корни ее, и она наполнила землю."},{num:11,verse:"Горы покрылись тенью ее, и ветви ее как кедры Божии;"},{num:12,verse:"она пустила ветви свои до моря и отрасли свои до реки."},{num:13,verse:"Для чего разрушил Ты ограды ее, так что обрывают ее все, проходящие по пути?"},{num:14,verse:"Лесной вепрь подрывает ее, и полевой зверь объедает ее."},{num:15,verse:"Боже сил! обратись же, призри с неба, и воззри, и посети виноград сей;"},{num:16,verse:"охрани то, что насадила десница Твоя, и отрасли, которые Ты укрепил Себе."},{num:17,verse:"Он пожжен огнем, обсечен; от прещения лица Твоего погибнут."},{num:18,verse:"Да будет рука Твоя над мужем десницы Твоей, над сыном человеческим, которого Ты укрепил Себе,"},{num:19,verse:"и мы не отступим от Тебя; оживи нас, и мы будем призывать имя Твое."},{num:20,verse:"Господи, Боже сил! восстанови нас; да воссияет лице Твое, и спасемся!"}]},{chapter:"80",verses:[{num:1,verse:"Начальнику хора. На Гефском орудии. Псалом Асафа."},{num:0,verse:""},{num:2,verse:"Радостно пойте Богу, твердыне нашей; восклицайте Богу Иакова;"},{num:3,verse:"возьмите псалом, дайте тимпан, сладкозвучные гусли с псалтирью;"},{num:4,verse:"трубите в новомесячие трубою, в определенное время, в день праздника нашего;"},{num:5,verse:"ибо это закон для Израиля, устав от Бога Иаковлева."},{num:6,verse:"Он установил это во свидетельство для Иосифа, когда он вышел из земли Египетской, где услышал звуки языка, которого не знал:"},{num:7,verse:'"Я снял с рамен его тяжести, и руки его освободились от корзин.'},{num:8,verse:"В бедствии ты призвал Меня, и Я избавил тебя; из среды грома Я услышал тебя, при водах Меривы испытал тебя."},{num:9,verse:"Слушай, народ Мой, и Я буду свидетельствовать тебе: Израиль! о, если бы ты послушал Меня!"},{num:10,verse:"Да не будет у тебя иного бога, и не поклоняйся богу чужеземному."},{num:11,verse:'Я Господь, Бог твой, изведший тебя из земли Египетской; открой уста твои, и Я наполню их".'},{num:12,verse:"Но народ Мой не слушал гласа Моего, и Израиль не покорялся Мне;"},{num:13,verse:"потому Я оставил их упорству сердца их, пусть ходят по своим помыслам."},{num:14,verse:"О, если бы народ Мой слушал Меня и Израиль ходил Моими путями!"},{num:15,verse:"Я скоро смирил бы врагов их и обратил бы руку Мою на притеснителей их:"},{num:16,verse:"ненавидящие Господа раболепствовали бы им, а их благоденствие продолжалось бы навсегда;"},{num:17,verse:"Я питал бы их туком пшеницы и насыщал бы их медом из скалы."}]},{chapter:"81",verses:[{num:0,verse:"Псалом Асафа."},{num:0,verse:""},{num:1,verse:"Бог стал в сонме богов; среди богов произнес суд:"},{num:2,verse:"доколе будете вы судить неправедно и оказывать лицеприятие нечестивым?"},{num:3,verse:"Давайте суд бедному и сироте; угнетенному и нищему оказывайте справедливость;"},{num:4,verse:"избавляйте бедного и нищего; исторгайте <i>его</i> из руки нечестивых."},{num:5,verse:"Не знают, не разумеют, во тьме ходят; все основания земли колеблются."},{num:6,verse:"Я сказал: вы – боги, и сыны Всевышнего – все вы;"},{num:7,verse:"но вы умрете, как человеки, и падете, как всякий из князей."},{num:8,verse:"Восстань, Боже, суди землю, ибо Ты наследуешь все народы."}]},{chapter:"82",verses:[{num:1,verse:"Песнь. Псалом Асафа."},{num:0,verse:""},{num:2,verse:"Боже! Не премолчи, не безмолвствуй и не оставайся в покое, Боже,"},{num:3,verse:"ибо вот, враги Твои шумят, и ненавидящие Тебя подняли голову;"},{num:4,verse:"против народа Твоего составили коварный умысел и совещаются против хранимых Тобою;"},{num:5,verse:'сказали: "пойдем и истребим их из народов, чтобы не вспоминалось более имя Израиля".'},{num:6,verse:"Сговорились единодушно, заключили против Тебя союз:"},{num:7,verse:"селения Едомовы и Измаильтяне, Моав и Агаряне,"},{num:8,verse:"Гевал и Аммон и Амалик, Филистимляне с жителями Тира."},{num:9,verse:"И Ассур пристал к ним: они стали мышцею для сынов Лотовых."},{num:10,verse:"Сделай им то же, что Мадиаму, что Сисаре, что Иавину у потока Киссона,"},{num:11,verse:"которые истреблены в Аендоре, сделались навозом для земли."},{num:12,verse:"Поступи с ними, с князьями их, как с Оривом и Зивом и со всеми вождями их, как с Зевеем и Салманом,"},{num:13,verse:'которые говорили: "возьмем себе во владение селения Божии".'},{num:14,verse:"Боже мой! Да будут они, как пыль в вихре, как солома перед ветром."},{num:15,verse:"Как огонь сжигает лес, и как пламя опаляет горы,"},{num:16,verse:"так погони их бурею Твоею и вихрем Твоим приведи их в смятение;"},{num:17,verse:"исполни лица их бесчестием, чтобы они взыскали имя Твое, Господи!"},{num:18,verse:"Да постыдятся и смятутся на веки, да посрамятся и погибнут,"},{num:19,verse:"и да познают, что Ты, Которого одного имя Господь, Всевышний над всею землею."}]},{chapter:"83",verses:[{num:1,verse:"Начальнику хора. На Гефском <i>орудии</i>. Кореевых сынов. Псалом."},{num:0,verse:""},{num:2,verse:"Как вожделенны жилища Твои, Господи сил!"},{num:3,verse:"Истомилась душа моя, желая во дворы Господни; сердце мое и плоть моя восторгаются к Богу живому."},{num:4,verse:"И птичка находит себе жилье, и ласточка гнездо себе, где положить птенцов своих, у алтарей Твоих, Господи сил, Царь мой и Бог мой!"},{num:5,verse:"Блаженны живущие в доме Твоем: они непрестанно будут восхвалять Тебя."},{num:6,verse:"Блажен человек, которого сила в Тебе и у которого в сердце стези направлены <i>к Тебе</i>."},{num:7,verse:"Проходя долиною плача, они открывают в ней источники, и дождь покрывает ее благословением;"},{num:8,verse:"приходят от силы в силу, являются пред Богом на Сионе."},{num:9,verse:"Господи, Боже сил! Услышь молитву мою, внемли, Боже Иаковлев!"},{num:10,verse:"Боже, защитник наш! Приникни и призри на лице помазанника Твоего."},{num:11,verse:"Ибо один день во дворах Твоих лучше тысячи. Желаю лучше быть у порога в доме Божием, нежели жить в шатрах нечестия."},{num:12,verse:"Ибо Господь Бог есть солнце и щит, Господь дает благодать и славу; ходящих в непорочности Он не лишает благ."},{num:13,verse:"Господи сил! Блажен человек, уповающий на Тебя!"}]},{chapter:"84",verses:[{num:1,verse:"Начальнику хора. Кореевых сынов. Псалом."},{num:0,verse:""},{num:2,verse:"Господи! Ты умилосердился к земле Твоей, возвратил плен Иакова;"},{num:3,verse:"простил беззаконие народа Твоего, покрыл все грехи его,"},{num:4,verse:"отъял всю ярость Твою, отвратил лютость гнева Твоего."},{num:5,verse:"Восстанови нас, Боже спасения нашего, и прекрати негодование Твое на нас."},{num:6,verse:"Неужели вечно будешь гневаться на нас, прострешь гнев Твой от рода в род?"},{num:7,verse:"Неужели снова не оживишь нас, чтобы народ Твой возрадовался о Тебе?"},{num:8,verse:"Яви нам, Господи, милость Твою, и спасение Твое даруй нам."},{num:9,verse:"Послушаю, что скажет Господь Бог. Он скажет мир народу Своему и избранным Своим, но да не впадут они снова в безрассудство."},{num:10,verse:"Так, близко к боящимся Его спасение Его, чтобы обитала слава в земле нашей!"},{num:11,verse:"Милость и истина сретятся, правда и мир облобызаются;"},{num:12,verse:"истина возникнет из земли, и правда приникнет с небес;"},{num:13,verse:"и Господь даст благо, и земля наша даст плод свой;"},{num:14,verse:"правда пойдет пред Ним и поставит на путь стопы свои."}]},{chapter:"85",verses:[{num:0,verse:"Молитва Давида."},{num:0,verse:""},{num:1,verse:"Приклони, Господи, ухо Твое и услышь меня, ибо я беден и нищ."},{num:2,verse:"Сохрани душу мою, ибо я благоговею пред Тобою; спаси, Боже мой, раба Твоего, уповающего на Тебя."},{num:3,verse:"Помилуй меня, Господи, ибо к Тебе взываю каждый день."},{num:4,verse:"Возвесели душу раба Твоего, ибо к Тебе, Господи, возношу душу мою,"},{num:5,verse:"ибо Ты, Господи, благ и милосерд и многомилостив ко всем, призывающим Тебя."},{num:6,verse:"Услышь, Господи, молитву мою и внемли гласу моления моего."},{num:7,verse:"В день скорби моей взываю к Тебе, потому что Ты услышишь меня."},{num:8,verse:"Нет между богами, как Ты, Господи, и нет дел, как Твои."},{num:9,verse:"Все народы, Тобою сотворенные, приидут и поклонятся пред Тобою, Господи, и прославят имя Твое,"},{num:10,verse:"ибо Ты велик и творишь чудеса, – Ты, Боже, един Ты."},{num:11,verse:"Наставь меня, Господи, на путь Твой, и буду ходить в истине Твоей; утверди сердце мое в страхе имени Твоего."},{num:12,verse:"Буду восхвалять Тебя, Господи, Боже мой, всем сердцем моим и славить имя Твое вечно,"},{num:13,verse:"ибо велика милость Твоя ко мне: Ты избавил душу мою от ада преисподнего."},{num:14,verse:"Боже! гордые восстали на меня, и скопище мятежников ищет души моей: не представляют они Тебя пред собою."},{num:15,verse:"Но Ты, Господи, Боже щедрый и благосердный, долготерпеливый и многомилостивый и истинный,"},{num:16,verse:"призри на меня и помилуй меня; даруй крепость Твою рабу Твоему, и спаси сына рабы Твоей;"},{num:17,verse:"покажи на мне знамение во благо, да видят ненавидящие меня и устыдятся, потому что Ты, Господи, помог мне и утешил меня."}]},{chapter:"86",verses:[{num:1,verse:"Сынов Кореевых. Псалом. Песнь."},{num:0,verse:""},{num:2,verse:"Основание его на горах святых. Господь любит врата Сиона более всех селений Иакова."},{num:3,verse:"Славное возвещается о тебе, град Божий!"},{num:4,verse:'Упомяну знающим меня о Рааве и Вавилоне; вот Филистимляне и Тир с Ефиопиею, – <i>скажут</i>: "такой-то родился там".'},{num:5,verse:'О Сионе же будут говорить: "такой-то и такой-то муж родился в нем, и Сам Всевышний укрепил его".'},{num:6,verse:'Господь в переписи народов напишет: "такой-то родился там".'},{num:7,verse:"И поющие и играющие, – все источники мои в тебе."}]},{chapter:"87",verses:[{num:1,verse:"Песнь. Псалом, Сынов Кореевых. Начальнику хора на Махалаф, для пения. Учение Емана Езрахита."},{num:0,verse:""},{num:2,verse:"Господи, Боже спасения моего! днем вопию и ночью пред Тобою:"},{num:3,verse:"да внидет пред лице Твое молитва моя; приклони ухо Твое к молению моему,"},{num:4,verse:"ибо душа моя насытилась бедствиями, и жизнь моя приблизилась к преисподней."},{num:5,verse:"Я сравнялся с нисходящими в могилу; я стал, как человек без силы,"},{num:6,verse:"между мертвыми брошенный, – как убитые, лежащие во гробе, о которых Ты уже не вспоминаешь и которые от руки Твоей отринуты."},{num:7,verse:"Ты положил меня в ров преисподний, во мрак, в бездну."},{num:8,verse:"Отяготела на мне ярость Твоя, и всеми волнами Твоими Ты поразил [меня]."},{num:9,verse:"Ты удалил от меня знакомых моих, сделал меня отвратительным для них; я заключен, и не могу выйти."},{num:10,verse:"Око мое истомилось от горести: весь день я взывал к Тебе, Господи, простирал к Тебе руки мои."},{num:11,verse:"Разве над мертвыми Ты сотворишь чудо? Разве мертвые встанут и будут славить Тебя?"},{num:12,verse:"или во гробе будет возвещаема милость Твоя, и истина Твоя – в месте тления?"},{num:13,verse:"разве во мраке познают чудеса Твои, и в земле забвения – правду Твою?"},{num:14,verse:"Но я к Тебе, Господи, взываю, и рано утром молитва моя предваряет Тебя."},{num:15,verse:"Для чего, Господи, отреваешь душу мою, скрываешь лице Твое от меня?"},{num:16,verse:"Я несчастен и истаеваю с юности; несу ужасы Твои и изнемогаю."},{num:17,verse:"Надо мною прошла ярость Твоя, устрашения Твои сокрушили меня,"},{num:18,verse:"всякий день окружают меня, как вода: облегают меня все вместе."},{num:19,verse:"Ты удалил от меня друга и искреннего; знакомых моих не видно."}]},{chapter:"88",verses:[{num:1,verse:"Учение Ефама Езрахита."},{num:0,verse:""},{num:2,verse:"Милости [Твои], Господи, буду петь вечно, в род и род возвещать истину Твою устами моими."},{num:3,verse:"Ибо говорю: навек основана милость, на небесах утвердил Ты истину Твою, <i>когда сказал</i>:"},{num:4,verse:'"Я поставил завет с избранным Моим, клялся Давиду, рабу Моему:'},{num:5,verse:'навек утвержу семя твое, в род и род устрою престол твой".'},{num:6,verse:"И небеса прославят чудные дела Твои, Господи, и истину Твою в собрании святых."},{num:7,verse:"Ибо кто на небесах сравнится с Господом? кто между сынами Божиими уподобится Господу?"},{num:8,verse:"Страшен Бог в великом сонме святых, страшен Он для всех окружающих Его."},{num:9,verse:"Господи, Боже сил! кто силен, как Ты, Господи? И истина Твоя окрест Тебя."},{num:10,verse:"Ты владычествуешь над яростью моря: когда воздымаются волны его, Ты укрощаешь их."},{num:11,verse:"Ты низложил Раава, как пораженного; крепкою мышцею Твоею рассеял врагов Твоих."},{num:12,verse:"Твои небеса и Твоя земля; вселенную и что наполняет ее, Ты основал."},{num:13,verse:"Север и юг Ты сотворил; Фавор и Ермон о имени Твоем радуются."},{num:14,verse:"Крепка мышца Твоя, сильна рука Твоя, высока десница Твоя!"},{num:15,verse:"Правосудие и правота – основание престола Твоего; милость и истина предходят пред лицем Твоим."},{num:16,verse:"Блажен народ, знающий трубный зов! Они ходят во свете лица Твоего, Господи,"},{num:17,verse:"о имени Твоем радуются весь день и правдою Твоею возносятся,"},{num:18,verse:"ибо Ты украшение силы их, и благоволением Твоим возвышается рог наш."},{num:19,verse:"От Господа – щит наш, и от Святаго Израилева – царь наш."},{num:20,verse:'Некогда говорил Ты в видении святому Твоему, и сказал: "Я оказал помощь мужественному, вознес избранного из народа.'},{num:21,verse:"Я обрел Давида, раба Моего, святым елеем Моим помазал его."},{num:22,verse:"Рука Моя пребудет с ним, и мышца Моя укрепит его."},{num:23,verse:"Враг не превозможет его, и сын беззакония не притеснит его."},{num:24,verse:"Сокрушу пред ним врагов его и поражу ненавидящих его."},{num:25,verse:"И истина Моя и милость Моя с ним, и Моим именем возвысится рог его."},{num:26,verse:"И положу на море руку его, и на реки – десницу его."},{num:27,verse:"Он будет звать Меня: Ты отец мой, Бог мой и твердыня спасения моего."},{num:28,verse:"И Я сделаю его первенцем, превыше царей земли,"},{num:29,verse:"вовек сохраню ему милость Мою, и завет Мой с ним будет верен."},{num:30,verse:"И продолжу вовек семя его, и престол его – как дни неба."},{num:31,verse:"Если сыновья его оставят закон Мой и не будут ходить по заповедям Моим;"},{num:32,verse:"если нарушат уставы Мои и повелений Моих не сохранят:"},{num:33,verse:"посещу жезлом беззаконие их, и ударами – неправду их;"},{num:34,verse:"милости же Моей не отниму от него, и не изменю истины Моей."},{num:35,verse:"Не нарушу завета Моего, и не переменю того, что вышло из уст Моих."},{num:36,verse:"Однажды Я поклялся святостью Моею: солгу ли Давиду?"},{num:37,verse:"Семя его пребудет вечно, и престол его, как солнце, предо Мною,"},{num:38,verse:'вовек будет тверд, как луна, и верный свидетель на небесах".'},{num:39,verse:"Но <i>ныне</i> Ты отринул и презрел, прогневался на помазанника Твоего;"},{num:40,verse:"пренебрег завет с рабом Твоим, поверг на землю венец его;"},{num:41,verse:"разрушил все ограды его, превратил в развалины крепости его."},{num:42,verse:"Расхищают его все проходящие путем; он сделался посмешищем у соседей своих."},{num:43,verse:"Ты возвысил десницу противников его, обрадовал всех врагов его;"},{num:44,verse:"Ты обратил назад острие меча его и не укрепил его на брани;"},{num:45,verse:"отнял у него блеск и престол его поверг на землю;"},{num:46,verse:"сократил дни юности его и покрыл его стыдом."},{num:47,verse:"Доколе, Господи, будешь скрываться непрестанно, будет пылать ярость Твоя, как огонь?"},{num:48,verse:"Вспомни, какой мой век: на какую суету сотворил Ты всех сынов человеческих?"},{num:49,verse:"Кто из людей жил – и не видел смерти, избавил душу свою от руки преисподней?"},{num:50,verse:"Где прежние милости Твои, Господи? Ты клялся Давиду истиною Твоею."},{num:51,verse:"Вспомни, Господи, поругание рабов Твоих, которое я ношу в недре моем от всех сильных народов;"},{num:52,verse:"как поносят враги Твои, Господи, как бесславят следы помазанника Твоего."},{num:53,verse:"Благословен Господь вовек! Аминь, аминь."}]},{chapter:"89",verses:[{num:1,verse:"Молитва Моисея, человека Божия."},{num:0,verse:""},{num:2,verse:"Господи! Ты нам прибежище в род и род."},{num:3,verse:"Прежде нежели родились горы, и Ты образовал землю и вселенную, и от века и до века Ты – Бог."},{num:4,verse:"Ты возвращаешь человека в тление и говоришь: [возвратитесь, сыны человеческие!]"},{num:5,verse:"Ибо пред очами Твоими тысяча лет, как день вчерашний, когда он прошел, и <i>как</i> стража в ночи."},{num:6,verse:"Ты <i>как</i> наводнением уносишь их; они – <i>как</i> сон, как трава, которая утром вырастает, утром цветет и зеленеет, вечером подсекается и засыхает;"},{num:7,verse:"ибо мы исчезаем от гнева Твоего и от ярости Твоей мы в смятении."},{num:8,verse:"Ты положил беззакония наши пред Тобою и тайное наше пред светом лица Твоего."},{num:9,verse:"Все дни наши прошли во гневе Твоем; мы теряем лета наши, как звук."},{num:10,verse:"Дней лет наших – семьдесят лет, а при большей крепости – восемьдесят лет; и самая лучшая пора их – труд и болезнь, ибо проходят быстро, и мы летим."},{num:11,verse:"Кто знает силу гнева Твоего, и ярость Твою по мере страха Твоего?"},{num:12,verse:"Научи нас так счислять дни наши, чтобы нам приобрести сердце мудрое."},{num:13,verse:"Обратись, Господи! Доколе? Умилосердись над рабами Твоими."},{num:14,verse:"Рано насыти нас милостью Твоею, и мы будем радоваться и веселиться во все дни наши."},{num:15,verse:"Возвесели нас за дни, <i>в которые</i> Ты поражал нас, за лета, <i>в которые</i> мы видели бедствие."},{num:16,verse:"Да явится на рабах Твоих дело Твое и на сынах их слава Твоя;"},{num:17,verse:"и да будет благоволение Господа Бога нашего на нас, и в деле рук наших споспешествуй нам, в деле рук наших споспешествуй."}]},{chapter:"90",verses:[{num:0,verse:"[Хвалебная песнь Давида.]"},{num:0,verse:""},{num:1,verse:"Живущий под кровом Всевышнего под сенью Всемогущего покоится,"},{num:2,verse:'говорит Господу: "прибежище мое и защита моя, Бог мой, на Которого я уповаю!"'},{num:3,verse:"Он избавит тебя от сети ловца, от гибельной язвы,"},{num:4,verse:"перьями Своими осенит тебя, и под крыльями Его будешь безопасен; щит и ограждение – истина Его."},{num:5,verse:"Не убоишься ужасов в ночи, стрелы, летящей днем,"},{num:6,verse:"язвы, ходящей во мраке, заразы, опустошающей в полдень."},{num:7,verse:"Падут подле тебя тысяча и десять тысяч одесную тебя; но к тебе не приблизится:"},{num:8,verse:"только смотреть будешь очами твоими и видеть возмездие нечестивым."},{num:9,verse:'Ибо ты <i>сказал</i>: "Господь – упование мое"; Всевышнего избрал ты прибежищем твоим;'},{num:10,verse:"не приключится тебе зло, и язва не приблизится к жилищу твоему;"},{num:11,verse:"ибо Ангелам Своим заповедает о тебе – охранять тебя на всех путях твоих:"},{num:12,verse:"на руках понесут тебя, да не преткнешься о камень ногою твоею;"},{num:13,verse:"на аспида и василиска наступишь; попирать будешь льва и дракона."},{num:14,verse:'"За то, что он возлюбил Меня, избавлю его; защищу его, потому что он познал имя Мое.'},{num:15,verse:"Воззовет ко Мне, и услышу его; с ним Я в скорби; избавлю его и прославлю его,"},{num:16,verse:'долготою дней насыщу его, и явлю ему спасение Мое".'}]},{chapter:"91",verses:[{num:1,verse:"Псалом. Песнь на день субботний."},{num:0,verse:""},{num:2,verse:"Благо есть славить Господа и петь имени Твоему, Всевышний,"},{num:3,verse:"возвещать утром милость Твою и истину Твою в ночи,"},{num:4,verse:"на десятиструнном и псалтири, с песнью на гуслях."},{num:5,verse:"Ибо Ты возвеселил меня, Господи, творением Твоим: я восхищаюсь делами рук Твоих."},{num:6,verse:"Как велики дела Твои, Господи! дивно глубоки помышления Твои!"},{num:7,verse:"Человек несмысленный не знает, и невежда не разумеет того."},{num:8,verse:"Тогда как нечестивые возникают, как трава, и делающие беззаконие цветут, чтобы исчезнуть на веки, -"},{num:9,verse:"Ты, Господи, высок во веки!"},{num:10,verse:"Ибо вот, враги Твои, Господи, – вот, враги Твои гибнут, и рассыпаются все делающие беззаконие;"},{num:11,verse:"а мой рог Ты возносишь, как рог единорога, и я умащен свежим елеем;"},{num:12,verse:"и око мое смотрит на врагов моих, и уши мои слышат о восстающих на меня злодеях."},{num:13,verse:"Праведник цветет, как пальма, возвышается подобно кедру на Ливане."},{num:14,verse:"Насажденные в доме Господнем, они цветут во дворах Бога нашего;"},{num:15,verse:"они и в старости плодовиты, сочны и свежи,"},{num:16,verse:"чтобы возвещать, что праведен Господь, твердыня моя, и нет неправды в Нем."}]},{chapter:"92",verses:[{num:0,verse:"[Хвалебная песнь Давида. В день предсубботний, когда населена земля.]"},{num:0,verse:""},{num:1,verse:"Господь царствует; Он облечен величием, облечен Господь могуществом [и] препоясан: потому вселенная тверда, не подвигнется."},{num:2,verse:"Престол Твой утвержден искони: Ты – от века."},{num:3,verse:"Возвышают реки, Господи, возвышают реки голос свой, возвышают реки волны свои."},{num:4,verse:"Но паче шума вод многих, сильных волн морских, силен в вышних Господь."},{num:5,verse:"Откровения Твои несомненно верны. Дому Твоему, Господи, принадлежит святость на долгие дни."}]},{chapter:"93",verses:[{num:0,verse:"[Псалом Давида в четвертый день недели.]"},{num:0,verse:""},{num:1,verse:"Боже отмщений, Господи, Боже отмщений, яви Себя!"},{num:2,verse:"Восстань, Судия земли, воздай возмездие гордым."},{num:3,verse:"Доколе, Господи, нечестивые, доколе нечестивые торжествовать будут?"},{num:4,verse:"Они изрыгают дерзкие речи; величаются все делающие беззаконие;"},{num:5,verse:"попирают народ Твой, Господи, угнетают наследие Твое;"},{num:6,verse:"вдову и пришельца убивают, и сирот умерщвляют"},{num:7,verse:'и говорят: "не увидит Господь, и не узнает Бог Иаковлев".'},{num:8,verse:"Образумьтесь, бессмысленные люди! когда вы будете умны, невежды?"},{num:9,verse:"Насадивший ухо не услышит ли? и образовавший глаз не увидит ли?"},{num:10,verse:"Вразумляющий народы неужели не обличит, – Тот, Кто учит человека разумению?"},{num:11,verse:"Господь знает мысли человеческие, что они суетны."},{num:12,verse:"Блажен человек, которого вразумляешь Ты, Господи, и наставляешь законом Твоим,"},{num:13,verse:"чтобы дать ему покой в бедственные дни, доколе нечестивому выроется яма!"},{num:14,verse:"Ибо не отринет Господь народа Своего и не оставит наследия Своего."},{num:15,verse:"Ибо суд возвратится к правде, и за ним <i>последуют</i> все правые сердцем."},{num:16,verse:"Кто восстанет за меня против злодеев? кто станет за меня против делающих беззаконие?"},{num:17,verse:"Если бы не Господь был мне помощником, вскоре вселилась бы душа моя в <i>страну</i> молчания."},{num:18,verse:'Когда я говорил: "колеблется нога моя", – милость Твоя, Господи, поддерживала меня.'},{num:19,verse:"При умножении скорбей моих в сердце моем, утешения Твои услаждают душу мою."},{num:20,verse:"Станет ли близ Тебя седалище губителей, умышляющих насилие вопреки закону?"},{num:21,verse:"Толпою устремляются они на душу праведника и осуждают кровь неповинную."},{num:22,verse:"Но Господь – защита моя, и Бог мой – твердыня убежища моего,"},{num:23,verse:"и обратит на них беззаконие их, и злодейством их истребит их, истребит их Господь Бог наш."}]},{chapter:"94",verses:[{num:0,verse:"[Хвалебная песнь Давида.]"},{num:0,verse:""},{num:1,verse:"Приидите, воспоем Господу, воскликнем [Богу], твердыне спасения нашего;"},{num:2,verse:"предстанем лицу Его со славословием, в песнях воскликнем Ему,"},{num:3,verse:"ибо Господь есть Бог великий и Царь великий над всеми богами."},{num:4,verse:"В Его руке глубины земли, и вершины гор – Его же;"},{num:5,verse:"Его – море, и Он создал его, и сушу образовали руки Его."},{num:6,verse:"Приидите, поклонимся и припадем, преклоним колени пред лицем Господа, Творца нашего;"},{num:7,verse:"ибо Он есть Бог наш, и мы – народ паствы Его и овцы руки Его. О, если бы вы ныне послушали гласа Его:"},{num:8,verse:'"не ожесточите сердца вашего, как в Мериве, как в день искушения в пустыне,'},{num:9,verse:"где искушали Меня отцы ваши, испытывали Меня, и видели дело Мое."},{num:10,verse:"Сорок лет Я был раздражаем родом сим, и сказал: это народ, заблуждающийся сердцем; они не познали путей Моих,"},{num:11,verse:'и потому Я поклялся во гневе Моем, что они не войдут в покой Мой".'}]},{chapter:"95",verses:[{num:0,verse:"[Хвалебная песнь Давида. На построение дома.]"},{num:0,verse:""},{num:1,verse:"Воспойте Господу песнь новую; воспойте Господу, вся земля;"},{num:2,verse:"пойте Господу, благословляйте имя Его, благовествуйте со дня на день спасение Его;"},{num:3,verse:"возвещайте в народах славу Его, во всех племенах чудеса Его;"},{num:4,verse:"ибо велик Господь и достохвален, страшен Он паче всех богов."},{num:5,verse:"Ибо все боги народов – идолы, а Господь небеса сотворил."},{num:6,verse:"Слава и величие пред лицем Его, сила и великолепие во святилище Его."},{num:7,verse:"Воздайте Господу, племена народов, воздайте Господу славу и честь;"},{num:8,verse:"воздайте Господу славу имени Его, несите дары и идите во дворы Его;"},{num:9,verse:"поклонитесь Господу во благолепии святыни. Трепещи пред лицем Его, вся земля!"},{num:10,verse:"Скажите народам: Господь царствует! потому тверда вселенная, не поколеблется. Он будет судить народы по правде."},{num:11,verse:"Да веселятся небеса и да торжествует земля; да шумит море и что наполняет его;"},{num:12,verse:"да радуется поле и все, что на нем, и да ликуют все дерева дубравные"},{num:13,verse:"пред лицем Господа; ибо идет, ибо идет судить землю. Он будет судить вселенную по правде, и народы – по истине Своей."}]},{chapter:"96",verses:[{num:0,verse:"[Псалом Давида, когда устроялась земля его.]"},{num:0,verse:""},{num:1,verse:"Господь царствует: да радуется земля; да веселятся многочисленные острова."},{num:2,verse:"Облако и мрак окрест Его; правда и суд – основание престола Его."},{num:3,verse:"Пред Ним идет огонь и вокруг попаляет врагов Его."},{num:4,verse:"Молнии Его освещают вселенную; земля видит и трепещет."},{num:5,verse:"Горы, как воск, тают от лица Господа, от лица Господа всей земли."},{num:6,verse:"Небеса возвещают правду Его, и все народы видят славу Его."},{num:7,verse:"Да постыдятся все служащие истуканам, хвалящиеся идолами. Поклонитесь пред Ним, все боги."},{num:8,verse:"Слышит Сион и радуется, и веселятся дщери Иудины ради судов Твоих, Господи,"},{num:9,verse:"ибо Ты, Господи, высок над всею землею, превознесен над всеми богами."},{num:10,verse:"Любящие Господа, ненавидьте зло! Он хранит души святых Своих; из руки нечестивых избавляет их."},{num:11,verse:"Свет сияет на праведника, и на правых сердцем – веселие."},{num:12,verse:"Радуйтесь, праведные, о Господе и славьте память святыни Его."}]},{chapter:"97",verses:[{num:0,verse:"Псалом [Давида]."},{num:0,verse:""},{num:1,verse:"Воспойте Господу новую песнь, ибо Он сотворил чудеса. Его десница и святая мышца Его доставили Ему победу."},{num:2,verse:"Явил Господь спасение Свое, открыл пред очами народов правду Свою."},{num:3,verse:"Вспомнил Он милость Свою [к Иакову] и верность Свою к дому Израилеву. Все концы земли увидели спасение Бога нашего."},{num:4,verse:"Восклицайте Господу, вся земля; торжествуйте, веселитесь и пойте;"},{num:5,verse:"пойте Господу с гуслями, с гуслями и с гласом псалмопения;"},{num:6,verse:"при звуке труб и рога торжествуйте пред Царем Господом."},{num:7,verse:"Да шумит море и что наполняет его, вселенная и живущие в ней;"},{num:8,verse:"да рукоплещут реки, да ликуют вместе горы"},{num:9,verse:"пред лицем Господа, ибо Он идет судить землю. Он будет судить вселенную праведно и народы – верно."}]},{chapter:"98",verses:[{num:0,verse:"[Псалом Давида]."},{num:0,verse:""},{num:1,verse:"Господь царствует: да трепещут народы! Он восседает на Херувимах: да трясется земля!"},{num:2,verse:"Господь на Сионе велик, и высок Он над всеми народами."},{num:3,verse:"Да славят великое и страшное имя Твое: свято оно!"},{num:4,verse:"И могущество царя любит суд. Ты утвердил справедливость; суд и правду Ты совершил в Иакове."},{num:5,verse:"Превозносите Господа, Бога нашего, и поклоняйтесь подножию Его: свято оно!"},{num:6,verse:"Моисей и Аарон между священниками и Самуил между призывающими имя Его взывали к Господу, и Он внимал им."},{num:7,verse:"В столпе облачном говорил Он к ним; они хранили Его заповеди и устав, который Он дал им."},{num:8,verse:"Господи, Боже наш! Ты внимал им; Ты был для них Богом прощающим и наказывающим за дела их."},{num:9,verse:"Превозносите Господа, Бога нашего, и поклоняйтесь на святой горе Его, ибо свят Господь, Бог наш."}]},{chapter:"99",verses:[{num:0,verse:"Псалом [Давида] хвалебный."},{num:0,verse:""},{num:1,verse:"Воскликните Господу, вся земля!"},{num:2,verse:"Служите Господу с веселием; идите пред лице Его с восклицанием!"},{num:3,verse:"Познайте, что Господь есть Бог, что Он сотворил нас, и мы – Его, Его народ и овцы паствы Его."},{num:4,verse:"Входите во врата Его со славословием, во дворы Его – с хвалою. Славьте Его, благословляйте имя Его,"},{num:5,verse:"ибо благ Господь: милость Его вовек, и истина Его в род и род."}]},{chapter:"100",verses:[{num:0,verse:"Псалом Давида."},{num:0,verse:""},{num:1,verse:"Милость и суд буду петь; Тебе, Господи, буду петь."},{num:2,verse:'Буду размышлять о пути непорочном: "когда ты придешь ко мне?" Буду ходить в непорочности моего сердца посреди дома моего.'},{num:3,verse:"Не положу пред очами моими вещи непотребной; дело преступное я ненавижу: не прилепится оно ко мне."},{num:4,verse:"Сердце развращенное будет удалено от меня; злого я не буду знать."},{num:5,verse:"Тайно клевещущего на ближнего своего изгоню; гордого очами и надменного сердцем не потерплю."},{num:6,verse:"Глаза мои на верных земли, чтобы они пребывали при мне; кто ходит путем непорочности, тот будет служить мне."},{num:7,verse:"Не будет жить в доме моем поступающий коварно; говорящий ложь не останется пред глазами моими."},{num:8,verse:"С раннего утра буду истреблять всех нечестивцев земли, дабы искоренить из града Господня всех делающих беззаконие."}]},{chapter:"101",verses:[{num:1,verse:"Молитва страждущего, когда он унывает и изливает пред Господом печаль свою."},{num:0,verse:""},{num:2,verse:"Господи! услышь молитву мою, и вопль мой да придет к Тебе."},{num:3,verse:"Не скрывай лица Твоего от меня; в день скорби моей приклони ко мне ухо Твое; в день, [когда] воззову [к Тебе], скоро услышь меня;"},{num:4,verse:"ибо исчезли, как дым, дни мои, и кости мои обожжены, как головня;"},{num:5,verse:"сердце мое поражено, и иссохло, как трава, так что я забываю есть хлеб мой;"},{num:6,verse:"от голоса стенания моего кости мои прильпнули к плоти моей."},{num:7,verse:"Я уподобился пеликану в пустыне; я стал как филин на развалинах;"},{num:8,verse:"не сплю и сижу, как одинокая птица на кровле."},{num:9,verse:"Всякий день поносят меня враги мои, и злобствующие на меня клянут мною."},{num:10,verse:"Я ем пепел, как хлеб, и питье мое растворяю слезами,"},{num:11,verse:"от гнева Твоего и негодования Твоего, ибо Ты вознес меня и низверг меня."},{num:12,verse:"Дни мои – как уклоняющаяся тень, и я иссох, как трава."},{num:13,verse:"Ты же, Господи, вовек пребываешь, и память о Тебе в род и род."},{num:14,verse:"Ты восстанешь, умилосердишься над Сионом, ибо время помиловать его, – ибо пришло время;"},{num:15,verse:"ибо рабы Твои возлюбили и камни его, и о прахе его жалеют."},{num:16,verse:"И убоятся народы имени Господня, и все цари земные – славы Твоей."},{num:17,verse:"Ибо созиждет Господь Сион и явится во славе Своей;"},{num:18,verse:"призрит на молитву беспомощных и не презрит моления их."},{num:19,verse:"Напишется о сем для рода последующего, и поколение грядущее восхвалит Господа,"},{num:20,
verse:"ибо Он приникнул со святой высоты Своей, с небес призрел Господь на землю,"},{num:21,verse:"чтобы услышать стон узников, разрешить сынов смерти,"},{num:22,verse:"дабы возвещали на Сионе имя Господне и хвалу Его – в Иерусалиме,"},{num:23,verse:"когда соберутся народы вместе и царства для служения Господу."},{num:24,verse:"Изнурил Он на пути силы мои, сократил дни мои."},{num:25,verse:"Я сказал: Боже мой! не восхити меня в половине дней моих. Твои лета в роды родов."},{num:26,verse:"В начале Ты, [Господи,] основал землю, и небеса – дело Твоих рук;"},{num:27,verse:"они погибнут, а Ты пребудешь; и все они, как риза, обветшают, и, как одежду, Ты переменишь их, и изменятся;"},{num:28,verse:"но Ты – тот же, и лета Твои не кончатся."},{num:29,verse:"Сыны рабов Твоих будут жить, и семя их утвердится пред лицем Твоим."}]},{chapter:"102",verses:[{num:0,verse:"Псалом Давида."},{num:0,verse:""},{num:1,verse:"Благослови, душа моя, Господа, и вся внутренность моя – святое имя Его."},{num:2,verse:"Благослови, душа моя, Господа и не забывай всех благодеяний Его."},{num:3,verse:"Он прощает все беззакония твои, исцеляет все недуги твои;"},{num:4,verse:"избавляет от могилы жизнь твою, венчает тебя милостью и щедротами;"},{num:5,verse:"насыщает благами желание твое: обновляется, подобно орлу, юность твоя."},{num:6,verse:"Господь творит правду и суд всем обиженным."},{num:7,verse:"Он показал пути Свои Моисею, сынам Израилевым – дела Свои."},{num:8,verse:"Щедр и милостив Господь, долготерпелив и многомилостив:"},{num:9,verse:"не до конца гневается, и не вовек негодует."},{num:10,verse:"Не по беззакониям нашим сотворил нам, и не по грехам нашим воздал нам:"},{num:11,verse:"ибо как высоко небо над землею, так велика милость [Господа] к боящимся Его;"},{num:12,verse:"как далеко восток от запада, так удалил Он от нас беззакония наши;"},{num:13,verse:"как отец милует сынов, так милует Господь боящихся Его."},{num:14,verse:"Ибо Он знает состав наш, помнит, что мы – персть."},{num:15,verse:"Дни человека – как трава; как цвет полевой, так он цветет."},{num:16,verse:"Пройдет над ним ветер, и нет его, и место его уже не узнает его."},{num:17,verse:"Милость же Господня от века и до века к боящимся Его,"},{num:18,verse:"и правда Его на сынах сынов, хранящих завет Его и помнящих заповеди Его, чтобы исполнять их."},{num:19,verse:"Господь на небесах поставил престол Свой, и царство Его всем обладает."},{num:20,verse:"Благословите Господа, [все] Ангелы Его, крепкие силою, исполняющие слово Его, повинуясь гласу слова Его;"},{num:21,verse:"благословите Господа, все воинства Его, служители Его, исполняющие волю Его;"},{num:22,verse:"благословите Господа, все дела Его, во всех местах владычества Его. Благослови, душа моя, Господа!"}]},{chapter:"103",verses:[{num:0,verse:"[Псалом Давида о сотворении мира.]"},{num:0,verse:""},{num:1,verse:"Благослови, душа моя, Господа! Господи, Боже мой! Ты дивно велик, Ты облечен славою и величием;"},{num:2,verse:"Ты одеваешься светом, как ризою, простираешь небеса, как шатер;"},{num:3,verse:"устрояешь над водами горние чертоги Твои, делаешь облака Твоею колесницею, шествуешь на крыльях ветра."},{num:4,verse:"Ты творишь ангелами Твоими духов, служителями Твоими – огонь пылающий."},{num:5,verse:"Ты поставил землю на твердых основах: не поколеблется она во веки и веки."},{num:6,verse:"Бездною, как одеянием, покрыл Ты ее, на горах стоят воды."},{num:7,verse:"От прещения Твоего бегут они, от гласа грома Твоего быстро уходят;"},{num:8,verse:"восходят на горы, нисходят в долины, на место, которое Ты назначил для них."},{num:9,verse:"Ты положил предел, которого не перейдут, и не возвратятся покрыть землю."},{num:10,verse:"Ты послал источники в долины: между горами текут [воды],"},{num:11,verse:"поят всех полевых зверей; дикие ослы утоляют жажду свою."},{num:12,verse:"При них обитают птицы небесные, из среды ветвей издают голос."},{num:13,verse:"Ты напояешь горы с высот Твоих, плодами дел Твоих насыщается земля."},{num:14,verse:"Ты произращаешь траву для скота, и зелень на пользу человека, чтобы произвести из земли пищу,"},{num:15,verse:"и вино, которое веселит сердце человека, и елей, от которого блистает лице его, и хлеб, который укрепляет сердце человека."},{num:16,verse:"Насыщаются древа Господа, кедры Ливанские, которые Он насадил;"},{num:17,verse:"на них гнездятся птицы: ели – жилище аисту,"},{num:18,verse:"высокие горы – сернам; каменные утесы – убежище зайцам."},{num:19,verse:"Он сотворил луну для <i>указания</i> времен, солнце знает свой запад."},{num:20,verse:"Ты простираешь тьму и бывает ночь: во время нее бродят все лесные звери;"},{num:21,verse:"львы рыкают о добыче и просят у Бога пищу себе."},{num:22,verse:"Восходит солнце, [и] они собираются и ложатся в свои логовища;"},{num:23,verse:"выходит человек на дело свое и на работу свою до вечера."},{num:24,verse:"Как многочисленны дела Твои, Господи! Все соделал Ты премудро; земля полна произведений Твоих."},{num:25,verse:"Это – море великое и пространное: там пресмыкающиеся, которым нет числа, животные малые с большими;"},{num:26,verse:"там плавают корабли, там этот левиафан, которого Ты сотворил играть в нем."},{num:27,verse:"Все они от Тебя ожидают, чтобы Ты дал им пищу их в свое время."},{num:28,verse:"Даешь им – принимают, отверзаешь руку Твою – насыщаются благом;"},{num:29,verse:"скроешь лице Твое – мятутся, отнимешь дух их – умирают и в персть свою возвращаются;"},{num:30,verse:"пошлешь дух Твой – созидаются, и Ты обновляешь лице земли."},{num:31,verse:"Да будет Господу слава во веки; да веселится Господь о делах Своих!"},{num:32,verse:"Призирает на землю, и она трясется; прикасается к горам, и дымятся."},{num:33,verse:"Буду петь Господу во <i>всю</i> жизнь мою, буду петь Богу моему, доколе есмь."},{num:34,verse:"Да будет благоприятна Ему песнь моя; буду веселиться о Господе."},{num:35,verse:"Да исчезнут грешники с земли, и беззаконных да не будет более. Благослови, душа моя, Господа! Аллилуия!"}]},{chapter:"104",verses:[{num:1,verse:"Славьте Господа; призывайте имя Его; возвещайте в народах дела Его;"},{num:2,verse:"воспойте Ему и пойте Ему; поведайте о всех чудесах Его."},{num:3,verse:"Хвалитесь именем Его святым; да веселится сердце ищущих Господа."},{num:4,verse:"Ищите Господа и силы Его, ищите лица Его всегда."},{num:5,verse:"Воспоминайте чудеса Его, которые сотворил, знамения Его и суды уст Его,"},{num:6,verse:"вы, семя Авраамово, рабы Его, сыны Иакова, избранные Его."},{num:7,verse:"Он Господь Бог наш: по всей земле суды Его."},{num:8,verse:"Вечно помнит завет Свой, слово, [которое] заповедал в тысячу родов,"},{num:9,verse:"которое завещал Аврааму, и клятву Свою Исааку,"},{num:10,verse:"и поставил то Иакову в закон и Израилю в завет вечный,"},{num:11,verse:'говоря: "тебе дам землю Ханаанскую в удел наследия вашего".'},{num:12,verse:"Когда их было еще мало числом, очень мало, и они были пришельцами в ней"},{num:13,verse:"и переходили от народа к народу, из царства к иному племени,"},{num:14,verse:"никому не позволял обижать их и возбранял о них царям:"},{num:15,verse:"[не прикасайтесь к помазанным Моим, и пророкам Моим не делайте зла]."},{num:16,verse:"И призвал голод на землю; всякий стебель хлебный истребил."},{num:17,verse:"Послал пред ними человека: в рабы продан был Иосиф."},{num:18,verse:"Стеснили оковами ноги его; в железо вошла душа его,"},{num:19,verse:"доколе исполнилось слово Его: слово Господне испытало его."},{num:20,verse:"Послал царь, и разрешил его владетель народов и освободил его;"},{num:21,verse:"поставил его господином над домом своим и правителем над всем владением своим,"},{num:22,verse:"чтобы он наставлял вельмож его по своей душе и старейшин его учил мудрости."},{num:23,verse:"Тогда пришел Израиль в Египет, и переселился Иаков в землю Хамову."},{num:24,verse:"И весьма размножил <i>Бог</i> народ Свой и сделал его сильнее врагов его."},{num:25,verse:"Возбудил в сердце их ненависть против народа Его и ухищрение против рабов Его."},{num:26,verse:"Послал Моисея, раба Своего, Аарона, которого избрал."},{num:27,verse:"Они показали между ними слова знамений Его и чудеса [Его] в земле Хамовой."},{num:28,verse:"Послал тьму и сделал мрак, и не воспротивились слову Его."},{num:29,verse:"Преложил воду их в кровь, и уморил рыбу их."},{num:30,verse:"Земля их произвела множество жаб <i>даже</i> в спальне царей их."},{num:31,verse:"Он сказал, и пришли разные насекомые, скнипы во все пределы их."},{num:32,verse:"Вместо дождя послал на них град, палящий огонь на землю их,"},{num:33,verse:"и побил виноград их и смоковницы их, и сокрушил дерева в пределах их."},{num:34,verse:"Сказал, и пришла саранча и гусеницы без числа;"},{num:35,verse:"и съели всю траву на земле их, и съели плоды на полях их."},{num:36,verse:"И поразил всякого первенца в земле их, начатки всей силы их."},{num:37,verse:"И вывел <i>Израильтян</i> с серебром и золотом, и не было в коленах их болящего."},{num:38,verse:"Обрадовался Египет исшествию их; ибо страх от них напал на него."},{num:39,verse:"Простер облако в покров [им] и огонь, чтобы светить [им] ночью."},{num:40,verse:"Просили, и Он послал перепелов, и хлебом небесным насыщал их."},{num:41,verse:"Разверз камень, и потекли воды, потекли рекою по местам сухим,"},{num:42,verse:"ибо вспомнил Он святое слово Свое к Аврааму, рабу Своему,"},{num:43,verse:"и вывел народ Свой в радости, избранных Своих в веселии,"},{num:44,verse:"и дал им земли народов, и они наследовали труд иноплеменных,"},{num:45,verse:"чтобы соблюдали уставы Его и хранили законы Его. Аллилуия!"}]},{chapter:"105",verses:[{num:0,verse:"Аллилуия."},{num:0,verse:""},{num:1,verse:"Славьте Господа, ибо Он благ, ибо вовек милость Его."},{num:2,verse:"Кто изречет могущество Господа, возвестит все хвалы Его?"},{num:3,verse:"Блаженны хранящие суд и творящие правду во всякое время!"},{num:4,verse:"Вспомни о мне, Господи, в благоволении к народу Твоему; посети меня спасением Твоим,"},{num:5,verse:"дабы мне видеть благоденствие избранных Твоих, веселиться веселием народа Твоего, хвалиться с наследием Твоим."},{num:6,verse:"Согрешили мы с отцами нашими, совершили беззаконие, соделали неправду."},{num:7,verse:"Отцы наши в Египте не уразумели чудес Твоих, не помнили множества милостей Твоих, и возмутились у моря, у Чермного моря."},{num:8,verse:"Но Он спас их ради имени Своего, дабы показать могущество Свое."},{num:9,verse:"Грозно рек морю Чермному, и оно иссохло; и провел их по безднам, как по суше;"},{num:10,verse:"и спас их от руки ненавидящего и избавил их от руки врага."},{num:11,verse:"Воды покрыли врагов их, ни одного из них не осталось."},{num:12,verse:"И поверили они словам Его, [и] воспели хвалу Ему."},{num:13,verse:"<i>Но</i> скоро забыли дела Его, не дождались Его изволения;"},{num:14,verse:"увлеклись похотением в пустыне, и искусили Бога в необитаемой."},{num:15,verse:"И Он исполнил прошение их, <i>но</i> послал язву на души их."},{num:16,verse:"И позавидовали в стане Моисею <i>и</i> Аарону, святому Господню."},{num:17,verse:"Разверзлась земля, и поглотила Дафана и покрыла скопище Авирона."},{num:18,verse:"И возгорелся огонь в скопище их, пламень попалил нечестивых."},{num:19,verse:"Сделали тельца у Хорива и поклонились истукану;"},{num:20,verse:"и променяли славу свою на изображение вола, ядущего траву."},{num:21,verse:"Забыли Бога, Спасителя своего, совершившего великое в Египте,"},{num:22,verse:"дивное в земле Хамовой, страшное у Чермного моря."},{num:23,verse:"И хотел истребить их, если бы Моисей, избранный Его, не стал пред Ним в расселине, чтобы отвратить ярость Его, да не погубит [их]."},{num:24,verse:"И презрели они землю желанную, не верили слову Его;"},{num:25,verse:"и роптали в шатрах своих, не слушались гласа Господня."},{num:26,verse:"И поднял Он руку Свою на них, чтобы низложить их в пустыне,"},{num:27,verse:"низложить племя их в народах и рассеять их по землям."},{num:28,verse:"Они прилепились к Ваалфегору и ели жертвы бездушным,"},{num:29,verse:"и раздражали <i>Бога</i> делами своими, и вторглась к ним язва."},{num:30,verse:"И восстал Финеес и произвел суд, – и остановилась язва."},{num:31,verse:"И <i>это</i> вменено ему в праведность в роды и роды во веки."},{num:32,verse:"И прогневали <i>Бога</i> у вод Меривы, и Моисей потерпел за них,"},{num:33,verse:"ибо они огорчили дух его, и он погрешил устами своими."},{num:34,verse:"Не истребили народов, о которых сказал им Господь,"},{num:35,verse:"но смешались с язычниками и научились делам их;"},{num:36,verse:"служили истуканам их, <i>которые</i> были для них сетью,"},{num:37,verse:"и приносили сыновей своих и дочерей своих в жертву бесам;"},{num:38,verse:"проливали кровь невинную, кровь сыновей своих и дочерей своих, которых приносили в жертву идолам Ханаанским, – и осквернилась земля кровью;"},{num:39,verse:"оскверняли себя делами своими, блудодействовали поступками своими."},{num:40,verse:"И воспылал гнев Господа на народ Его, и возгнушался Он наследием Своим"},{num:41,verse:"и предал их в руки язычников, и ненавидящие их стали обладать ими."},{num:42,verse:"Враги их утесняли их, и они смирялись под рукою их."},{num:43,verse:"Много раз Он избавлял их; они же раздражали [Его] упорством своим, и были уничижаемы за беззаконие свое."},{num:44,verse:"Но Он призирал на скорбь их, когда слышал вопль их,"},{num:45,verse:"и вспоминал завет Свой с ними и раскаивался по множеству милости Своей;"},{num:46,verse:"и возбуждал к ним сострадание во всех, пленявших их."},{num:47,verse:"Спаси нас, Господи, Боже наш, и собери нас от народов, дабы славить святое имя Твое, хвалиться Твоею славою."},{num:48,verse:"Благословен Господь, Бог Израилев, от века и до века! И да скажет весь народ: аминь! Аллилуия!"}]},{chapter:"106",verses:[{num:0,verse:"[Аллилуия.]"},{num:0,verse:""},{num:1,verse:"Славьте Господа, ибо Он благ, ибо вовек милость Его!"},{num:2,verse:"Так да скажут избавленные Господом, которых избавил Он от руки врага,"},{num:3,verse:"и собрал от стран, от востока и запада, от севера и моря."},{num:4,verse:"Они блуждали в пустыне по безлюдному пути и не находили населенного города;"},{num:5,verse:"терпели голод и жажду, душа их истаевала в них."},{num:6,verse:"Но воззвали к Господу в скорби своей, и Он избавил их от бедствий их,"},{num:7,verse:"и повел их прямым путем, чтобы они шли к населенному городу."},{num:8,verse:"Да славят Господа за милость Его и за чудные дела Его для сынов человеческих:"},{num:9,verse:"ибо Он насытил душу жаждущую и душу алчущую исполнил благами."},{num:10,verse:"Они сидели во тьме и тени смертной, окованные скорбью и железом;"},{num:11,verse:"ибо не покорялись словам Божиим и небрегли о воле Всевышнего."},{num:12,verse:"Он смирил сердце их работами; они преткнулись, и не было помогающего."},{num:13,verse:"Но воззвали к Господу в скорби своей, и Он спас их от бедствий их;"},{num:14,verse:"вывел их из тьмы и тени смертной, и расторгнул узы их."},{num:15,verse:"Да славят Господа за милость Его и за чудные дела Его для сынов человеческих:"},{num:16,verse:"ибо Он сокрушил врата медные и вереи железные сломил."},{num:17,verse:"Безрассудные страдали за беззаконные пути свои и за неправды свои;"},{num:18,verse:"от всякой пищи отвращалась душа их, и они приближались ко вратам смерти."},{num:19,verse:"Но воззвали к Господу в скорби своей, и Он спас их от бедствий их;"},{num:20,verse:"послал слово Свое и исцелил их, и избавил их от могил их."},{num:21,verse:"Да славят Господа за милость Его и за чудные дела Его для сынов человеческих!"},{num:22,verse:"Да приносят Ему жертву хвалы и да возвещают о делах Его с пением!"},{num:23,verse:"Отправляющиеся на кораблях в море, производящие дела на больших водах,"},{num:24,verse:"видят дела Господа и чудеса Его в пучине:"},{num:25,verse:"Он речет, – и восстанет бурный ветер и высоко поднимает волны его:"},{num:26,verse:"восходят до небес, нисходят до бездны; душа их истаевает в бедствии;"},{num:27,verse:"они кружатся и шатаются, как пьяные, и вся мудрость их исчезает."},{num:28,verse:"Но воззвали к Господу в скорби своей, и Он вывел их из бедствия их."},{num:29,verse:"Он превращает бурю в тишину, и волны умолкают."},{num:30,verse:"И веселятся, что они утихли, и Он приводит их к желаемой пристани."},{num:31,verse:"Да славят Господа за милость Его и за чудные дела Его для сынов человеческих!"},{num:32,verse:"Да превозносят Его в собрании народном и да славят Его в сонме старейшин!"},{num:33,verse:"Он превращает реки в пустыню и источники вод – в сушу,"},{num:34,verse:"землю плодородную – в солончатую, за нечестие живущих на ней."},{num:35,verse:"Он превращает пустыню в озеро, и землю иссохшую – в источники вод;"},{num:36,verse:"и поселяет там алчущих, и они строят город для обитания;"},{num:37,verse:"засевают поля, насаждают виноградники, которые приносят им обильные плоды."},{num:38,verse:"Он благословляет их, и они весьма размножаются, и скота их не умаляет."},{num:39,verse:"Уменьшились они и упали от угнетения, бедствия и скорби, -"},{num:40,verse:"он изливает бесчестие на князей и оставляет их блуждать в пустыне, где нет путей."},{num:41,verse:"Бедного же извлекает из бедствия и умножает род его, как стада овец."},{num:42,verse:"Праведники видят сие и радуются, а всякое нечестие заграждает уста свои."},{num:43,verse:"Кто мудр, тот заметит сие и уразумеет милость Господа."}]},{chapter:"107",verses:[{num:1,verse:"Песнь. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Готово сердце мое, Боже, [готово сердце мое]; буду петь и воспевать во славе моей."},{num:3,verse:"Воспрянь, псалтирь и гусли! Я встану рано."},{num:4,verse:"Буду славить Тебя, Господи, между народами; буду воспевать Тебя среди племен,"},{num:5,verse:"ибо превыше небес милость Твоя и до облаков истина Твоя."},{num:6,verse:"Будь превознесен выше небес, Боже; над всею землею <i>да будет</i> слава Твоя,"},{num:7,verse:"дабы избавились возлюбленные Твои: спаси десницею Твоею и услышь меня."},{num:8,verse:'Бог сказал во святилище Своем: "восторжествую, разделю Сихем и долину Сокхоф размерю;'},{num:9,verse:"Мой Галаад, Мой Манассия, Ефрем – крепость главы Моей, Иуда – скипетр Мой,"},{num:10,verse:'Моав – умывальная чаша Моя, на Едома простру сапог Мой, над землею Филистимскою восклицать буду".'},{num:11,verse:"Кто введет меня в укрепленный город? Кто доведет меня до Едома?"},{num:12,verse:"Не Ты ли, Боже, <i>Который</i> отринул нас и не выходишь, Боже, с войсками нашими?"},{num:13,verse:"Подай нам помощь в тесноте, ибо защита человеческая суетна."},{num:14,verse:"С Богом мы окажем силу: Он низложит врагов наших. Начальнику хора."}]},{chapter:"108",verses:[{num:0,verse:"Начальнику хора. Псалом Давида."},{num:0,verse:""},{num:1,verse:"Боже хвалы моей! не премолчи,"},{num:2,verse:"ибо отверзлись на меня уста нечестивые и уста коварные; говорят со мною языком лживым;"},{num:3,verse:"отвсюду окружают меня словами ненависти, вооружаются против меня без причины;"},{num:4,verse:"за любовь мою они враждуют на меня, а я молюсь;"},{num:5,verse:"воздают мне за добро злом, за любовь мою – ненавистью."},{num:6,verse:"Поставь над ним нечестивого, и диавол да станет одесную его."},{num:7,verse:"Когда будет судиться, да выйдет виновным, и молитва его да будет в грех;"},{num:8,verse:"да будут дни его кратки, и достоинство его да возьмет другой;"},{num:9,verse:"дети его да будут сиротами, и жена его – вдовою;"},{num:10,verse:"да скитаются дети его и нищенствуют, и просят <i>хлеба</i> из развалин своих;"},{num:11,verse:"да захватит заимодавец все, что есть у него, и чужие да расхитят труд его;"},{num:12,verse:"да не будет сострадающего ему, да не будет милующего сирот его;"},{num:13,verse:"да будет потомство его на погибель, и да изгладится имя их в следующем роде;"},{num:14,verse:"да будет воспомянуто пред Господом беззаконие отцов его, и грех матери его да не изгладится;"},{num:15,verse:"да будут они всегда в очах Господа, и да истребит Он память их на земле,"},{num:16,verse:"за то, что он не думал оказывать милость, но преследовал человека бедного и нищего и сокрушенного сердцем, чтобы умертвить его;"},{num:17,verse:"возлюбил проклятие, – оно и придет на него; не восхотел благословения, – оно и удалится от него;"},{num:18,verse:"да облечется проклятием, как ризою, и да войдет оно, как вода, во внутренность его и, как елей, в кости его;"},{num:19,verse:"да будет оно ему, как одежда, в которую он одевается, и как пояс, которым всегда опоясывается."},{num:20,verse:"Таково воздаяние от Господа врагам моим и говорящим злое на душу мою!"},{num:21,verse:"Со мною же, Господи, Господи, твори ради имени Твоего, ибо блага милость Твоя; спаси меня,"},{num:22,verse:"ибо я беден и нищ, и сердце мое уязвлено во мне."},{num:23,verse:"Я исчезаю, как уклоняющаяся тень; гонят меня, как саранчу."},{num:24,verse:"Колени мои изнемогли от поста, и тело мое лишилось тука."},{num:25,verse:"Я стал для них посмешищем: увидев меня, кивают головами [своими]."},{num:26,verse:"Помоги мне, Господи, Боже мой, спаси меня по милости Твоей,"},{num:27,verse:"да познают, что это – Твоя рука, и что Ты, Господи, соделал это."},{num:28,verse:"Они проклинают, а Ты благослови; они восстают, но да будут постыжены; раб же Твой да возрадуется."},{num:29,verse:"Да облекутся противники мои бесчестьем и, как одеждою, покроются стыдом своим."},{num:30,verse:"И я громко буду устами моими славить Господа и среди множества прославлять Его,"},{num:31,verse:"ибо Он стоит одесную бедного, чтобы спасти его от судящих душу его."}]},{chapter:"109",verses:[{num:0,verse:"Псалом Давида."},{num:0,verse:""},{num:1,verse:"Сказал Господь Господу моему: седи одесную Меня, доколе положу врагов Твоих в подножие ног Твоих."},{num:2,verse:"Жезл силы Твоей пошлет Господь с Сиона: господствуй среди врагов Твоих."},{num:3,verse:"В день силы Твоей народ Твой готов во благолепии святыни; из чрева прежде денницы подобно росе рождение Твое."},{num:4,verse:"Клялся Господь и не раскается: Ты священник вовек по чину Мелхиседека."},{num:5,verse:"Господь одесную Тебя. Он в день гнева Своего поразит царей;"},{num:6,verse:"совершит суд над народами, наполнит <i>землю</i> трупами, сокрушит голову в земле обширной."},{num:7,verse:"Из потока на пути будет пить, и потому вознесет главу."}]},{chapter:"110",verses:[{num:0,verse:"Аллилуия."},{num:0,verse:""},{num:1,verse:"Славлю [Тебя], Господи, всем сердцем [моим] в совете праведных и в собрании."},{num:2,verse:"Велики дела Господни, вожделенны для всех, любящих оные."},{num:3,verse:"Дело Его – слава и красота, и правда Его пребывает вовек."},{num:4,verse:"Памятными соделал Он чудеса Свои; милостив и щедр Господь."},{num:5,verse:"Пищу дает боящимся Его; вечно помнит завет Свой."},{num:6,verse:"Силу дел Своих явил Он народу Своему, чтобы дать ему наследие язычников."},{num:7,verse:"Дела рук Его – истина и суд; все заповеди Его верны,"},{num:8,verse:"тверды на веки и веки, основаны на истине и правоте."},{num:9,verse:"Избавление послал Он народу Своему; заповедал на веки завет Свой. Свято и страшно имя Его!"},{num:10,verse:"Начало мудрости – страх Господень; разум верный у всех, исполняющих <i>заповеди Его</i>. Хвала Ему пребудет вовек."}]},{chapter:"111",verses:[{num:0,verse:"Аллилуия."},{num:0,verse:""},{num:1,verse:"Блажен муж, боящийся Господа и крепко любящий заповеди Его."},{num:2,verse:"Сильно будет на земле семя его; род правых благословится."},{num:3,verse:"Обилие и богатство в доме его, и правда его пребывает вовек."},{num:4,verse:"Во тьме восходит свет правым; благ он и милосерд и праведен."},{num:5,verse:"Добрый человек милует и взаймы дает; он даст твердость словам своим на суде."},{num:6,verse:"Он вовек не поколеблется; в вечной памяти будет праведник."},{num:7,verse:"Не убоится худой молвы: сердце его твердо, уповая на Господа."},{num:8,verse:"Утверждено сердце его: он не убоится, когда посмотрит на врагов своих."},{num:9,verse:"Он расточил, роздал нищим; правда его пребывает во веки; рог его вознесется во славе."},{num:10,verse:"Нечестивый увидит <i>это</i> и будет досадовать, заскрежещет зубами своими и истает. Желание нечестивых погибнет."}]},{chapter:"112",verses:[{num:0,verse:"Аллилуия."},{num:0,verse:""},{num:1,verse:"Хвалите, рабы Господни, хвалите имя Господне."},{num:2,verse:"Да будет имя Господне благословенно отныне и вовек."},{num:3,verse:"От восхода солнца до запада <i>да будет</i> прославляемо имя Господне."},{num:4,verse:"Высок над всеми народами Господь; над небесами слава Его."},{num:5,verse:"Кто, как Господь, Бог наш, Который, обитая на высоте,"},{num:6,verse:"приклоняется, чтобы призирать на небо и на землю;"},{num:7,verse:"из праха поднимает бедного, из брения возвышает нищего,"},{num:8,verse:"чтобы посадить его с князьями, с князьями народа его;"},{num:9,verse:"неплодную вселяет в дом матерью, радующеюся о детях? Аллилуия!"}]},{chapter:"113",verses:[{num:0,verse:"[Аллилуия.]"},{num:0,verse:""},{num:1,verse:"Когда вышел Израиль из Египта, дом Иакова – из народа иноплеменного,"},{num:2,verse:"Иуда сделался святынею Его, Израиль – владением Его."},{num:3,verse:"Море увидело и побежало; Иордан обратился назад."},{num:4,verse:"Горы прыгали, как овны, и холмы, как агнцы."},{num:5,verse:"Что с тобою, море, что ты побежало, и [с тобою], Иордан, что ты обратился назад?"},{num:6,verse:"Что вы прыгаете, горы, как овны, и вы, холмы, как агнцы?"},{num:7,verse:"Пред лицем Господа трепещи, земля, пред лицем Бога Иаковлева,"},{num:8,verse:"превращающего скалу в озеро воды и камень в источник вод."},{num:9,verse:"Не нам, Господи, не нам, но имени Твоему дай славу, ради милости Твоей, ради истины Твоей."},{num:10,verse:'Для чего язычникам говорить: "где же Бог их"?'},{num:11,verse:"Бог наш на небесах [и на земле]; творит все, что хочет."},{num:12,verse:"А их идолы – серебро и золото, дело рук человеческих."},{num:13,verse:"Есть у них уста, но не говорят; есть у них глаза, но не видят;"},{num:14,verse:"есть у них уши, но не слышат; есть у них ноздри, но не обоняют;"},{num:15,verse:"есть у них руки, но не осязают; есть у них ноги, но не ходят; и они не издают голоса гортанью своею."},{num:16,verse:"Подобны им да будут делающие их и все, надеющиеся на них."},{num:17,verse:"[Дом] Израилев! уповай на Господа: Он наша помощь и щит."},{num:18,verse:"Дом Ааронов! уповай на Господа: Он наша помощь и щит."},{num:19,verse:"Боящиеся Господа! уповайте на Господа: Он наша помощь и щит."},{num:20,verse:"Господь помнит нас, благословляет [нас], благословляет дом Израилев, благословляет дом Ааронов;"},{num:21,verse:"благословляет боящихся Господа, малых с великими."},{num:22,verse:"Да приложит вам Господь более и более, вам и детям вашим."},{num:23,verse:"Благословенны вы Господом, сотворившим небо и землю."},{num:24,verse:"Небо – небо Господу, а землю Он дал сынам человеческим."},{num:25,verse:"Ни мертвые восхвалят Господа, ни все нисходящие в могилу;"},{num:26,verse:"но мы [живые] будем благословлять Господа отныне и вовек. Аллилуия."}]},{chapter:"114",verses:[{num:0,verse:"[Аллилуия.]"},{num:0,verse:""},{num:1,verse:"Я радуюсь, что Господь услышал голос мой, моление мое;"},{num:2,verse:"приклонил ко мне ухо Свое, и потому буду призывать Его во <i>все</i> дни мои."},{num:3,verse:"Объяли меня болезни смертные, муки адские постигли меня; я встретил тесноту и скорбь."},{num:4,verse:"Тогда призвал я имя Господне: Господи! избавь душу мою."},{num:5,verse:"Милостив Господь и праведен, и милосерд Бог наш."},{num:6,verse:"Хранит Господь простодушных: я изнемог, и Он помог мне."},{num:7,verse:"Возвратись, душа моя, в покой твой, ибо Господь облагодетельствовал тебя."},{num:8,verse:"Ты избавил душу мою от смерти, очи мои от слез и ноги мои от преткновения. Буду ходить пред лицем Господним на земле живых."}]},{chapter:"115",verses:[{num:0,verse:"[Аллилуия.]"},{num:0,verse:""},{num:1,verse:"Я веровал, и потому говорил: я сильно сокрушен."},{num:2,verse:"Я сказал в опрометчивости моей: всякий человек ложь."},{num:3,verse:"Что воздам Господу за все благодеяния Его ко мне?"},{num:4,verse:"Чашу спасения прииму и имя Господне призову."},{num:5,verse:"Обеты мои воздам Господу пред всем народом Его."},{num:6,verse:"Дорога в очах Господних смерть святых Его!"},{num:7,verse:"О, Господи! я раб Твой, я раб Твой и сын рабы Твоей; Ты разрешил узы мои."},{num:8,verse:"Тебе принесу жертву хвалы, и имя Господне призову."},{num:9,verse:"Обеты мои воздам Господу пред всем народом Его,"},{num:10,verse:"во дворах дома Господня, посреди тебя, Иерусалим! Аллилуия."}]},{chapter:"116",verses:[{num:0,verse:"[Аллилуия.]"},{num:0,verse:""},{num:1,verse:"Хвалите Господа, все народы, прославляйте Его, все племена;"},{num:2,verse:"ибо велика милость Его к нам, и истина Господня [пребывает] вовек. Аллилуия."}]},{chapter:"117",verses:[{num:0,verse:"[Аллилуия.]"},{num:0,verse:""},{num:1,verse:"Славьте Господа, ибо Он благ, ибо вовек милость Его."},{num:2,verse:"Да скажет ныне [дом] Израилев: [Он благ,] ибо вовек милость Его."},{num:3,verse:"Да скажет ныне дом Ааронов: [Он благ,] ибо вовек милость Его."},{num:4,verse:"Да скажут ныне боящиеся Господа: [Он благ,] ибо вовек милость Его."},{num:5,verse:"Из тесноты воззвал я к Господу, – и услышал меня, и на пространное место <i>вывел меня</i> Господь."},{num:6,verse:"Господь за меня – не устрашусь: что сделает мне человек?"},{num:7,verse:"Господь мне помощник: буду смотреть на врагов моих."},{num:8,verse:"Лучше уповать на Господа, нежели надеяться на человека."},{num:9,verse:"Лучше уповать на Господа, нежели надеяться на князей."},{num:10,verse:"Все народы окружили меня, но именем Господним я низложил их;"},{num:11,verse:"обступили меня, окружили меня, но именем Господним я низложил их;"},{num:12,verse:"окружили меня, как пчелы [сот], и угасли, как огонь в терне: именем Господним я низложил их."},{num:13,verse:"Сильно толкнули меня, чтобы я упал, но Господь поддержал меня."},{num:14,verse:"Господь – сила моя и песнь; Он соделался моим спасением."},{num:15,verse:"Глас радости и спасения в жилищах праведников: десница Господня творит силу!"},{num:16,verse:"Десница Господня высока, десница Господня творит силу!"},{num:17,verse:"Не умру, но буду жить и возвещать дела Господни."},{num:18,verse:"Строго наказал меня Господь, но смерти не предал меня."},{num:19,verse:"Отворите мне врата правды; войду в них, прославлю Господа."},{num:20,verse:"Вот врата Господа; праведные войдут в них."},{num:21,verse:"Славлю Тебя, что Ты услышал меня и соделался моим спасением."},{num:22,verse:"Камень, который отвергли строители, соделался главою угла:"},{num:23,verse:"это – от Господа, и есть дивно в очах наших."},{num:24,verse:"Сей день сотворил Господь: возрадуемся и возвеселимся в оный!"},{num:25,verse:"О, Господи, спаси же! О, Господи, споспешествуй же!"},{num:26,verse:"Благословен грядущий во имя Господне! Благословляем вас из дома Господня."},{num:27,verse:"Бог – Господь, и осиял нас; вяжите вервями жертву, <i>ведите</i> к рогам жертвенника."},{num:28,verse:"Ты Бог мой: буду славить Тебя; Ты Бог мой: буду превозносить Тебя, [буду славить Тебя, ибо Ты услышал меня и соделался моим спасением]."},{num:29,verse:"Славьте Господа, ибо Он благ, ибо вовек милость Его."}]},{chapter:"118",verses:[{num:0,verse:"[Аллилуия.]"},{num:0,verse:""},{num:1,verse:'"Алеф" Блаженны непорочные в пути, ходящие в законе Господнем.'},{num:2,verse:"Блаженны хранящие откровения Его, всем сердцем ищущие Его."},{num:3,verse:"Они не делают беззакония, ходят путями Его."},{num:4,verse:"Ты заповедал повеления Твои хранить твердо."},{num:5,verse:"О, если бы направлялись пути мои к соблюдению уставов Твоих!"},{num:6,verse:"Тогда я не постыдился бы, взирая на все заповеди Твои:"},{num:7,verse:"я славил бы Тебя в правоте сердца, поучаясь судам правды Твоей."},{num:8,verse:"Буду хранить уставы Твои; не оставляй меня совсем."},{num:9,verse:"Как юноше содержать в чистоте путь свой? – Хранением себя по слову Твоему."},{num:10,verse:"Всем сердцем моим ищу Тебя; не дай мне уклониться от заповедей Твоих."},{num:11,verse:"В сердце моем сокрыл я слово Твое, чтобы не грешить пред Тобою."},{num:12,verse:"Благословен Ты, Господи! научи меня уставам Твоим."},{num:13,
verse:"Устами моими возвещал я все суды уст Твоих."},{num:14,verse:"На пути откровений Твоих я радуюсь, как во всяком богатстве."},{num:15,verse:"О заповедях Твоих размышляю, и взираю на пути Твои."},{num:16,verse:"Уставами Твоими утешаюсь, не забываю слова Твоего."},{num:17,verse:"Яви милость рабу Твоему, и буду жить и хранить слово Твое."},{num:18,verse:"Открой очи мои, и увижу чудеса закона Твоего."},{num:19,verse:"Странник я на земле; не скрывай от меня заповедей Твоих."},{num:20,verse:"Истомилась душа моя желанием судов Твоих во всякое время."},{num:21,verse:"Ты укротил гордых, проклятых, уклоняющихся от заповедей Твоих."},{num:22,verse:"Сними с меня поношение и посрамление, ибо я храню откровения Твои."},{num:23,verse:"Князья сидят и сговариваются против меня, а раб Твой размышляет об уставах Твоих."},{num:24,verse:"Откровения Твои – утешение мое, [и уставы Твои] – советники мои."},{num:25,verse:"Душа моя повержена в прах; оживи меня по слову Твоему."},{num:26,verse:"Объявил я пути мои, и Ты услышал меня; научи меня уставам Твоим."},{num:27,verse:"Дай мне уразуметь путь повелений Твоих, и буду размышлять о чудесах Твоих."},{num:28,verse:"Душа моя истаевает от скорби: укрепи меня по слову Твоему."},{num:29,verse:"Удали от меня путь лжи, и закон Твой даруй мне."},{num:30,verse:"Я избрал путь истины, поставил пред собою суды Твои."},{num:31,verse:"Я прилепился к откровениям Твоим, Господи; не постыди меня."},{num:32,verse:"Потеку путем заповедей Твоих, когда Ты расширишь сердце мое."},{num:33,verse:"Укажи мне, Господи, путь уставов Твоих, и я буду держаться его до конца."},{num:34,verse:"Вразуми меня, и буду соблюдать закон Твой и хранить его всем сердцем."},{num:35,verse:"Поставь меня на стезю заповедей Твоих, ибо я возжелал ее."},{num:36,verse:"Приклони сердце мое к откровениям Твоим, а не к корысти."},{num:37,verse:"Отврати очи мои, чтобы не видеть суеты; животвори меня на пути Твоем."},{num:38,verse:"Утверди слово Твое рабу Твоему, ради благоговения пред Тобою."},{num:39,verse:"Отврати поношение мое, которого я страшусь, ибо суды Твои благи."},{num:40,verse:"Вот, я возжелал повелений Твоих; животвори меня правдою Твоею."},{num:41,verse:"Да придут ко мне милости Твои, Господи, спасение Твое по слову Твоему, -"},{num:42,verse:"и я дам ответ поносящему меня, ибо уповаю на слово Твое."},{num:43,verse:"Не отнимай совсем от уст моих слова истины, ибо я уповаю на суды Твои"},{num:44,verse:"и буду хранить закон Твой всегда, во веки и веки;"},{num:45,verse:"буду ходить свободно, ибо я взыскал повелений Твоих;"},{num:46,verse:"буду говорить об откровениях Твоих пред царями и не постыжусь;"},{num:47,verse:"буду утешаться заповедями Твоими, которые возлюбил;"},{num:48,verse:"руки мои буду простирать к заповедям Твоим, которые возлюбил, и размышлять об уставах Твоих."},{num:49,verse:"Вспомни слово [Твое] к рабу Твоему, на которое Ты повелел мне уповать:"},{num:50,verse:"это – утешение в бедствии моем, что слово Твое оживляет меня."},{num:51,verse:"Гордые крайне ругались надо мною, но я не уклонился от закона Твоего."},{num:52,verse:"Вспоминал суды Твои, Господи, от века, и утешался."},{num:53,verse:"Ужас овладевает мною при виде нечестивых, оставляющих закон Твой."},{num:54,verse:"Уставы Твои были песнями моими на месте странствований моих."},{num:55,verse:"Ночью вспоминал я имя Твое, Господи, и хранил закон Твой."},{num:56,verse:"Он стал моим, ибо повеления Твои храню."},{num:57,verse:"Удел мой, Господи, сказал я, соблюдать слова Твои."},{num:58,verse:"Молился я Тебе всем сердцем: помилуй меня по слову Твоему."},{num:59,verse:"Размышлял о путях моих и обращал стопы мои к откровениям Твоим."},{num:60,verse:"Спешил и не медлил соблюдать заповеди Твои."},{num:61,verse:"Сети нечестивых окружили меня, но я не забывал закона Твоего."},{num:62,verse:"В полночь вставал славословить Тебя за праведные суды Твои."},{num:63,verse:"Общник я всем боящимся Тебя и хранящим повеления Твои."},{num:64,verse:"Милости Твоей, Господи, полна земля; научи меня уставам Твоим."},{num:65,verse:"Благо сотворил Ты рабу Твоему, Господи, по слову Твоему."},{num:66,verse:"Доброму разумению и ведению научи меня, ибо заповедям Твоим я верую."},{num:67,verse:"Прежде страдания моего я заблуждался; а ныне слово Твое храню."},{num:68,verse:"Благ и благодетелен Ты, [Господи]; научи меня уставам Твоим."},{num:69,verse:"Гордые сплетают на меня ложь; я же всем сердцем буду хранить повеления Твои."},{num:70,verse:"Ожирело сердце их, как тук; я же законом Твоим утешаюсь."},{num:71,verse:"Благо мне, что я пострадал, дабы научиться уставам Твоим."},{num:72,verse:"Закон уст Твоих для меня лучше тысяч золота и серебра."},{num:0,verse:""},{num:73,verse:"Руки Твои сотворили меня и устроили меня; вразуми меня, и научусь заповедям Твоим."},{num:74,verse:"Боящиеся Тебя увидят меня – и возрадуются, что я уповаю на слово Твое."},{num:75,verse:"Знаю, Господи, что суды Твои праведны и по справедливости Ты наказал меня."},{num:76,verse:"Да будет же милость Твоя утешением моим, по слову Твоему к рабу Твоему."},{num:77,verse:"Да придет ко мне милосердие Твое, и я буду жить; ибо закон Твой – утешение мое."},{num:78,verse:"Да будут постыжены гордые, ибо безвинно угнетают меня; я размышляю о повелениях Твоих."},{num:79,verse:"Да обратятся ко мне боящиеся Тебя и знающие откровения Твои."},{num:80,verse:"Да будет сердце мое непорочно в уставах Твоих, чтобы я не посрамился."},{num:81,verse:"Истаевает душа моя о спасении Твоем; уповаю на слово Твое."},{num:82,verse:"Истаевают очи мои о слове Твоем; я говорю: когда Ты утешишь меня?"},{num:83,verse:"Я стал, как мех в дыму, <i>но</i> уставов Твоих не забыл."},{num:84,verse:"Сколько дней раба Твоего? Когда произведешь суд над гонителями моими?"},{num:85,verse:"Яму вырыли мне гордые, вопреки закону Твоему."},{num:86,verse:"Все заповеди Твои – истина; несправедливо преследуют меня: помоги мне;"},{num:87,verse:"едва не погубили меня на земле, но я не оставил повелений Твоих."},{num:88,verse:"По милости Твоей оживляй меня, и буду хранить откровения уст Твоих."},{num:89,verse:"На веки, Господи, слово Твое утверждено на небесах;"},{num:90,verse:"истина Твоя в род и род. Ты поставил землю, и она стоит."},{num:91,verse:"По определениям Твоим все стоит доныне, ибо все служит Тебе."},{num:92,verse:"Если бы не закон Твой был утешением моим, погиб бы я в бедствии моем."},{num:93,verse:"Вовек не забуду повелений Твоих, ибо ими Ты оживляешь меня."},{num:94,verse:"Твой я, спаси меня; ибо я взыскал повелений Твоих."},{num:95,verse:"Нечестивые подстерегают меня, чтобы погубить; <i>а</i> я углубляюсь в откровения Твои."},{num:96,verse:"Я видел предел всякого совершенства, <i>но</i> Твоя заповедь безмерно обширна."},{num:97,verse:"Как люблю я закон Твой! весь день размышляю о нем."},{num:98,verse:"Заповедью Твоею Ты соделал меня мудрее врагов моих, ибо она всегда со мною."},{num:99,verse:"Я стал разумнее всех учителей моих, ибо размышляю об откровениях Твоих."},{num:100,verse:"Я сведущ более старцев, ибо повеления Твои храню."},{num:101,verse:"От всякого злого пути удерживаю ноги мои, чтобы хранить слово Твое;"},{num:102,verse:"от судов Твоих не уклоняюсь, ибо Ты научаешь меня."},{num:103,verse:"Как сладки гортани моей слова Твои! лучше меда устам моим."},{num:104,verse:"Повелениями Твоими я вразумлен; потому ненавижу всякий путь лжи."},{num:105,verse:"Слово Твое – светильник ноге моей и свет стезе моей."},{num:106,verse:"Я клялся хранить праведные суды Твои, и исполню."},{num:107,verse:"Сильно угнетен я, Господи; оживи меня по слову Твоему."},{num:108,verse:"Благоволи же, Господи, принять добровольную жертву уст моих, и судам Твоим научи меня."},{num:109,verse:"Душа моя непрестанно в руке моей, но закона Твоего не забываю."},{num:110,verse:"Нечестивые поставили для меня сеть, но я не уклонился от повелений Твоих."},{num:111,verse:"Откровения Твои я принял, как наследие на веки, ибо они веселие сердца моего."},{num:112,verse:"Я приклонил сердце мое к исполнению уставов Твоих навек, до конца."},{num:113,verse:"Вымыслы <i>человеческие</i> ненавижу, а закон Твой люблю."},{num:114,verse:"Ты покров мой и щит мой; на слово Твое уповаю."},{num:115,verse:"Удалитесь от меня, беззаконные, и буду хранить заповеди Бога моего."},{num:116,verse:"Укрепи меня по слову Твоему, и буду жить; не посрами меня в надежде моей;"},{num:117,verse:"поддержи меня, и спасусь; и в уставы Твои буду вникать непрестанно."},{num:118,verse:"Всех, отступающих от уставов Твоих, Ты низлагаешь, ибо ухищрения их – ложь."},{num:119,verse:"<i>Как</i> изгарь, отметаешь Ты всех нечестивых земли; потому я возлюбил откровения Твои."},{num:120,verse:"Трепещет от страха Твоего плоть моя, и судов Твоих я боюсь."},{num:121,verse:"Я совершал суд и правду; не предай меня гонителям моим."},{num:122,verse:"Заступи раба Твоего ко благу <i>его</i>, чтобы не угнетали меня гордые."},{num:123,verse:"Истаевают очи мои, ожидая спасения Твоего и слова правды Твоей."},{num:124,verse:"Сотвори с рабом Твоим по милости Твоей, и уставам Твоим научи меня."},{num:125,verse:"Я раб Твой: вразуми меня, и познаю откровения Твои."},{num:126,verse:"Время Господу действовать: закон Твой разорили."},{num:127,verse:"А я люблю заповеди Твои более золота, и золота чистого."},{num:128,verse:"Все повеления Твои – все признаю справедливыми; всякий путь лжи ненавижу."},{num:129,verse:"Дивны откровения Твои; потому хранит их душа моя."},{num:130,verse:"Откровение слов Твоих просвещает, вразумляет простых."},{num:131,verse:"Открываю уста мои и вздыхаю, ибо заповедей Твоих жажду."},{num:0,verse:""},{num:132,verse:"Призри на меня и помилуй меня, как поступаешь с любящими имя Твое."},{num:133,verse:"Утверди стопы мои в слове Твоем и не дай овладеть мною никакому беззаконию;"},{num:134,verse:"избавь меня от угнетения человеческого, и буду хранить повеления Твои;"},{num:135,verse:"осияй раба Твоего светом лица Твоего и научи меня уставам Твоим."},{num:136,verse:"Из глаз моих текут потоки вод оттого, что не хранят закона Твоего."},{num:137,verse:"Праведен Ты, Господи, и справедливы суды Твои."},{num:138,verse:"Откровения Твои, которые Ты заповедал, – правда и совершенная истина."},{num:139,verse:"Ревность моя снедает меня, потому что мои враги забыли слова Твои."},{num:140,verse:"Слово Твое весьма чисто, и раб Твой возлюбил его."},{num:141,verse:"Мал я и презрен, <i>но</i> повелений Твоих не забываю."},{num:142,verse:"Правда Твоя – правда вечная, и закон Твой – истина."},{num:143,verse:"Скорбь и горесть постигли меня; заповеди Твои – утешение мое."},{num:144,verse:"Правда откровений Твоих вечна: вразуми меня, и буду жить."},{num:145,verse:"Взываю всем сердцем [моим]: услышь меня, Господи, – и сохраню уставы Твои."},{num:146,verse:"Призываю Тебя: спаси меня, и буду хранить откровения Твои."},{num:147,verse:"Предваряю рассвет и взываю; на слово Твое уповаю."},{num:148,verse:"Очи мои предваряют <i>утреннюю</i> стражу, чтобы мне углубляться в слово Твое."},{num:149,verse:"Услышь голос мой по милости Твоей, Господи; по суду Твоему оживи меня."},{num:150,verse:"Приблизились замышляющие лукавство; далеки они от закона Твоего."},{num:151,verse:"Близок Ты, Господи, и все заповеди Твои – истина."},{num:152,verse:"Издавна узнал я об откровениях Твоих, что Ты утвердил их на веки."},{num:153,verse:"Воззри на бедствие мое и избавь меня, ибо я не забываю закона Твоего."},{num:154,verse:"Вступись в дело мое и защити меня; по слову Твоему оживи меня."},{num:155,verse:"Далеко от нечестивых спасение, ибо они уставов Твоих не ищут."},{num:156,verse:"Много щедрот Твоих, Господи; по суду Твоему оживи меня."},{num:157,verse:"Много у меня гонителей и врагов, <i>но</i> от откровений Твоих я не удаляюсь."},{num:158,verse:"Вижу отступников, и сокрушаюсь, ибо они не хранят слова Твоего."},{num:159,verse:"Зри, как я люблю повеления Твои; по милости Твоей, Господи, оживи меня."},{num:160,verse:"Основание слова Твоего истинно, и вечен всякий суд правды Твоей."},{num:161,verse:"Князья гонят меня безвинно, но сердце мое боится слова Твоего."},{num:162,verse:"Радуюсь я слову Твоему, как получивший великую прибыль."},{num:163,verse:"Ненавижу ложь и гнушаюсь ею; закон же Твой люблю."},{num:164,verse:"Семикратно в день прославляю Тебя за суды правды Твоей."},{num:165,verse:"Велик мир у любящих закон Твой, и нет им преткновения."},{num:166,verse:"Уповаю на спасение Твое, Господи, и заповеди Твои исполняю."},{num:167,verse:"Душа моя хранит откровения Твои, и я люблю их крепко."},{num:168,verse:"Храню повеления Твои и откровения Твои, ибо все пути мои пред Тобою."},{num:169,verse:"Да приблизится вопль мой пред лице Твое, Господи; по слову Твоему вразуми меня."},{num:170,verse:"Да придет моление мое пред лице Твое; по слову Твоему избавь меня."},{num:171,verse:"Уста мои произнесут хвалу, когда Ты научишь меня уставам Твоим."},{num:172,verse:"Язык мой возгласит слово Твое, ибо все заповеди Твои праведны."},{num:173,verse:"Да будет рука Твоя в помощь мне, ибо я повеления Твои избрал."},{num:174,verse:"Жажду спасения Твоего, Господи, и закон Твой – утешение мое."},{num:175,verse:"Да живет душа моя и славит Тебя, и суды Твои да помогут мне."},{num:176,verse:"Я заблудился, как овца потерянная: взыщи раба Твоего, ибо я заповедей Твоих не забыл."}]},{chapter:"119",verses:[{num:0,verse:"Песнь восхождения."},{num:0,verse:""},{num:1,verse:"К Господу воззвал я в скорби моей, и Он услышал меня."},{num:2,verse:"Господи! избавь душу мою от уст лживых, от языка лукавого."},{num:3,verse:"Что даст тебе и что прибавит тебе язык лукавый?"},{num:4,verse:"Изощренные стрелы сильного, с горящими углями дроковыми."},{num:5,verse:"Горе мне, что я пребываю у Мосоха, живу у шатров Кидарских."},{num:6,verse:"Долго жила душа моя с ненавидящими мир."},{num:7,verse:"Я мирен: но только заговорю, они – к войне."}]},{chapter:"120",verses:[{num:0,verse:"Песнь восхождения."},{num:0,verse:""},{num:1,verse:"Возвожу очи мои к горам, откуда придет помощь моя."},{num:2,verse:"Помощь моя от Господа, сотворившего небо и землю."},{num:3,verse:"Не даст Он поколебаться ноге твоей, не воздремлет хранящий тебя;"},{num:4,verse:"не дремлет и не спит хранящий Израиля."},{num:5,verse:"Господь – хранитель твой; Господь – сень твоя с правой руки твоей."},{num:6,verse:"Днем солнце не поразит тебя, ни луна ночью."},{num:7,verse:"Господь сохранит тебя от всякого зла; сохранит душу твою [Господь]."},{num:8,verse:"Господь будет охранять выхождение твое и вхождение твое отныне и вовек."}]},{chapter:"121",verses:[{num:0,verse:"Песнь восхождения. Давида."},{num:0,verse:""},{num:1,verse:'Возрадовался я, когда сказали мне: "пойдем в дом Господень".'},{num:2,verse:"Вот, стоят ноги наши во вратах твоих, Иерусалим, -"},{num:3,verse:"Иерусалим, устроенный как город, слитый в одно,"},{num:4,verse:"куда восходят колена, колена Господни, по закону Израилеву, славить имя Господне."},{num:5,verse:"Там стоят престолы суда, престолы дома Давидова."},{num:6,verse:"Просите мира Иерусалиму: да благоденствуют любящие тебя!"},{num:7,verse:"Да будет мир в стенах твоих, благоденствие – в чертогах твоих!"},{num:8,verse:'Ради братьев моих и ближних моих говорю я: "мир тебе!"'},{num:9,verse:"Ради дома Господа, Бога нашего, желаю блага тебе."}]},{chapter:"122",verses:[{num:0,verse:"Песнь восхождения."},{num:0,verse:""},{num:1,verse:"К Тебе возвожу очи мои, Живущий на небесах!"},{num:2,verse:"Вот, как очи рабов <i>обращены</i> на руку господ их, как очи рабы – на руку госпожи ее, так очи наши – к Господу, Богу нашему, доколе Он помилует нас."},{num:3,verse:"Помилуй нас, Господи, помилуй нас, ибо довольно мы насыщены презрением;"},{num:4,verse:"довольно насыщена душа наша поношением от надменных и уничижением от гордых."}]},{chapter:"123",verses:[{num:0,verse:"Песнь восхождения. Давида."},{num:0,verse:""},{num:1,verse:"Если бы не Господь был с нами, – да скажет Израиль, -"},{num:2,verse:"если бы не Господь был с нами, когда восстали на нас люди,"},{num:3,verse:"то живых они поглотили бы нас, когда возгорелась ярость их на нас;"},{num:4,verse:"воды потопили бы нас, поток прошел бы над душею нашею;"},{num:5,verse:"прошли бы над душею нашею воды бурные."},{num:6,verse:"Благословен Господь, Который не дал нас в добычу зубам их!"},{num:7,verse:"Душа наша избавилась, как птица, из сети ловящих: сеть расторгнута, и мы избавились."},{num:8,verse:"Помощь наша – в имени Господа, сотворившего небо и землю."}]},{chapter:"124",verses:[{num:0,verse:"Песнь восхождения."},{num:0,verse:""},{num:1,verse:"Надеющийся на Господа, как гора Сион, не подвигнется: пребывает вовек."},{num:2,verse:"Горы окрест Иерусалима, а Господь окрест народа Своего отныне и вовек."},{num:3,verse:"Ибо не оставит [Господь] жезла нечестивых над жребием праведных, дабы праведные не простерли рук своих к беззаконию."},{num:4,verse:"Благотвори, Господи, добрым и правым в сердцах своих;"},{num:5,verse:"а совращающихся на кривые пути свои да оставит Господь ходить с делающими беззаконие. Мир на Израиля!"}]},{chapter:"125",verses:[{num:0,verse:"Песнь восхождения."},{num:0,verse:""},{num:1,verse:"Когда возвращал Господь плен Сиона, мы были как бы видящие во сне:"},{num:2,verse:'тогда уста наши были полны веселья, и язык наш – пения; тогда между народами говорили: "великое сотворил Господь над ними!"'},{num:3,verse:"Великое сотворил Господь над нами: мы радовались."},{num:4,verse:"Возврати, Господи, пленников наших, как потоки на полдень."},{num:5,verse:"Сеявшие со слезами будут пожинать с радостью."},{num:6,verse:"С плачем несущий семена возвратится с радостью, неся снопы свои."}]},{chapter:"126",verses:[{num:0,verse:"Песнь восхождения. Соломона."},{num:0,verse:""},{num:1,verse:"Если Господь не созиждет дома, напрасно трудятся строящие его; если Господь не охранит города, напрасно бодрствует страж."},{num:2,verse:"Напрасно вы рано встаете, поздно просиживаете, едите хлеб печали, тогда как возлюбленному Своему Он дает сон."},{num:3,verse:"Вот наследие от Господа: дети; награда от Него – плод чрева."},{num:4,verse:"Что стрелы в руке сильного, то сыновья молодые."},{num:5,verse:"Блажен человек, который наполнил ими колчан свой! Не останутся они в стыде, когда будут говорить с врагами в воротах."}]},{chapter:"127",verses:[{num:0,verse:"Песнь восхождения."},{num:0,verse:""},{num:1,verse:"Блажен всякий боящийся Господа, ходящий путями Его!"},{num:2,verse:"Ты будешь есть от трудов рук твоих: блажен ты, и благо тебе!"},{num:3,verse:"Жена твоя, как плодовитая лоза, в доме твоем; сыновья твои, как масличные ветви, вокруг трапезы твоей:"},{num:4,verse:"так благословится человек, боящийся Господа!"},{num:5,verse:"Благословит тебя Господь с Сиона, и увидишь благоденствие Иерусалима во все дни жизни твоей;"},{num:6,verse:"увидишь сыновей у сыновей твоих. Мир на Израиля!"}]},{chapter:"128",verses:[{num:0,verse:"Песнь восхождения."},{num:0,verse:""},{num:1,verse:"Много теснили меня от юности моей, да скажет Израиль:"},{num:2,verse:"много теснили меня от юности моей, но не одолели меня."},{num:3,verse:"На хребте моем орали оратаи, проводили длинные борозды свои."},{num:4,verse:"Но Господь праведен: Он рассек узы нечестивых."},{num:5,verse:"Да постыдятся и обратятся назад все ненавидящие Сион!"},{num:6,verse:"Да будут, как трава на кровлях, которая прежде, нежели будет исторгнута, засыхает,"},{num:7,verse:"которою жнец не наполнит руки своей, и вяжущий снопы – горсти своей;"},{num:8,verse:'и проходящие мимо не скажут: "благословение Господне на вас; благословляем вас именем Господним!"'}]},{chapter:"129",verses:[{num:0,verse:"Песнь восхождения."},{num:0,verse:""},{num:1,verse:"Из глубины взываю к Тебе, Господи."},{num:2,verse:"Господи! услышь голос мой. Да будут уши Твои внимательны к голосу молений моих."},{num:3,verse:"Если Ты, Господи, будешь замечать беззакония, – Господи! кто устоит?"},{num:4,verse:"Но у Тебя прощение, да благоговеют пред Тобою."},{num:5,verse:"Надеюсь на Господа, надеется душа моя; на слово Его уповаю."},{num:6,verse:"Душа моя ожидает Господа более, нежели стражи – утра, более, нежели стражи – утра."},{num:7,verse:"Да уповает Израиль на Господа, ибо у Господа милость и многое у Него избавление,"},{num:8,verse:"и Он избавит Израиля от всех беззаконий его."}]},{chapter:"130",verses:[{num:0,verse:"Песнь восхождения. Давида."},{num:0,verse:""},{num:1,verse:"Господи! не надмевалось сердце мое и не возносились очи мои, и я не входил в великое и для меня недосягаемое."},{num:2,verse:"Не смирял ли я и не успокаивал ли души моей, как дитяти, отнятого от груди матери? душа моя была во мне, как дитя, отнятое от груди."},{num:3,verse:"Да уповает Израиль на Господа отныне и вовек."}]},{chapter:"131",verses:[{num:0,verse:"Песнь восхождения."},{num:0,verse:""},{num:1,verse:"Вспомни, Господи, Давида и все сокрушение его:"},{num:2,verse:"как он клялся Господу, давал обет Сильному Иакова:"},{num:3,verse:'"не войду в шатер дома моего, не взойду на ложе мое;'},{num:4,verse:"не дам сна очам моим и веждам моим – дремания,"},{num:5,verse:'доколе не найду места Господу, жилища – Сильному Иакова".'},{num:6,verse:"Вот, мы слышали о нем в Ефрафе, нашли его на полях Иарима."},{num:7,verse:"Пойдем к жилищу Его, поклонимся подножию ног Его."},{num:8,verse:"Стань, Господи, на <i>место</i> покоя Твоего, – Ты и ковчег могущества Твоего."},{num:9,verse:"Священники Твои облекутся правдою, и святые Твои возрадуются."},{num:10,verse:"Ради Давида, раба Твоего, не отврати лица помазанника Твоего."},{num:11,verse:'Клялся Господь Давиду в истине, и не отречется ее: "от плода чрева твоего посажу на престоле твоем.'},{num:12,verse:'Если сыновья твои будут сохранять завет Мой и откровения Мои, которым Я научу их, то и их сыновья во веки будут сидеть на престоле твоем".'},{num:13,verse:"Ибо избрал Господь Сион, возжелал [его] в жилище Себе."},{num:14,verse:'"Это покой Мой на веки: здесь вселюсь, ибо Я возжелал его.'},{num:15,verse:"Пищу его благословляя благословлю, нищих его насыщу хлебом;"},{num:16,verse:"священников его облеку во спасение, и святые его радостью возрадуются."},{num:17,verse:"Там возращу рог Давиду, поставлю светильник помазаннику Моему."},{num:18,verse:'Врагов его облеку стыдом, а на нем будет сиять венец его".'}]},{chapter:"132",verses:[{num:0,verse:"Песнь восхождения. Давида."},{num:0,verse:""},{num:1,verse:"Как хорошо и как приятно жить братьям вместе!"},{num:2,verse:"<i>Это</i> – как драгоценный елей на голове, стекающий на бороду, бороду Ааронову, стекающий на края одежды его;"},{num:3,verse:"как роса Ермонская, сходящая на горы Сионские, ибо там заповедал Господь благословение и жизнь на веки."}]},{chapter:"133",verses:[{num:0,verse:"Песнь восхождения."},{num:0,verse:""},{num:1,verse:"Благословите ныне Господа, все рабы Господни, стоящие в доме Господнем, [во дворах дома Бога нашего,] во время ночи."},{num:2,verse:"Воздвигните руки ваши к святилищу, и благословите Господа."},{num:3,verse:"Благословит тебя Господь с Сиона, сотворивший небо и землю. Аллилуия."}]},{chapter:"134",verses:[{num:0,verse:"Аллилуия."},{num:0,verse:""},{num:1,verse:"Хвалите имя Господне, хвалите, рабы Господни,"},{num:2,verse:"стоящие в доме Господнем, во дворах дома Бога нашего."},{num:3,verse:"Хвалите Господа, ибо Господь благ; пойте имени Его, ибо это сладостно,"},{num:4,verse:"ибо Господь избрал Себе Иакова, Израиля в собственность Свою."},{num:5,verse:"Я познал, что велик Господь, и Господь наш превыше всех богов."},{num:6,verse:"Господь творит все, что хочет, на небесах и на земле, на морях и во всех безднах;"},{num:7,verse:"возводит облака от края земли, творит молнии при дожде, изводит ветер из хранилищ Своих."},{num:8,verse:"Он поразил первенцев Египта, от человека до скота,"},{num:9,verse:"послал знамения и чудеса среди тебя, Египет, на фараона и на всех рабов его,"},{num:10,verse:"поразил народы многие и истребил царей сильных:"},{num:11,verse:"Сигона, царя Аморрейского, и Ога, царя Васанского, и все царства Ханаанские;"},{num:12,verse:"и отдал землю их в наследие, в наследие Израилю, народу Своему."},{num:13,verse:"Господи! имя Твое вовек; Господи! память о Тебе в род и род."},{num:14,verse:"Ибо Господь будет судить народ Свой и над рабами Своими умилосердится."},{num:15,verse:"Идолы язычников – серебро и золото, дело рук человеческих:"},{num:16,verse:"есть у них уста, но не говорят; есть у них глаза, но не видят;"},{num:17,verse:"есть у них уши, но не слышат, и нет дыхания в устах их."},{num:18,verse:"Подобны им будут делающие их и всякий, кто надеется на них."},{num:19,verse:"Дом Израилев! благословите Господа. Дом Ааронов! благословите Господа."},{num:20,verse:"Дом Левиин! благословите Господа. Боящиеся Господа! благословите Господа."},{num:21,verse:"Благословен Господь от Сиона, живущий в Иерусалиме! Аллилуия!"}]},{chapter:"135",verses:[{num:0,verse:"[Аллилуия.]"},{num:0,verse:""},{num:1,verse:"Славьте Господа, ибо Он благ, ибо вовек милость Его."},{num:2,verse:"Славьте Бога богов, ибо вовек милость Его."},{num:3,verse:"Славьте Господа господствующих, ибо вовек милость Его;"},{num:4,verse:"Того, Который один творит чудеса великие, ибо вовек милость Его;"},{num:5,verse:"Который сотворил небеса премудро, ибо вовек милость Его;"},{num:6,verse:"утвердил землю на водах, ибо вовек милость Его;"},{num:7,verse:"сотворил светила великие, ибо вовек милость Его;"},{num:8,verse:"солнце – для управления днем, ибо вовек милость Его;"},{num:9,verse:"луну и звезды – для управления ночью, ибо вовек милость Его;"},{num:10,verse:"поразил Египет в первенцах его, ибо вовек милость Его;"},{num:11,verse:"и вывел Израиля из среды его, ибо вовек милость Его;"},{num:12,verse:"рукою крепкою и мышцею простертою, ибо вовек милость Его;"},{num:13,verse:"разделил Чермное море, ибо вовек милость Его;"},{num:14,verse:"и провел Израиля посреди его, ибо вовек милость Его;"},{num:15,verse:"и низверг фараона и войско его в море Чермное, ибо вовек милость Его;"},{num:16,verse:"провел народ Свой чрез пустыню, ибо вовек милость Его;"},{num:17,verse:"поразил царей великих, ибо вовек милость Его;"},{num:18,verse:"и убил царей сильных, ибо вовек милость Его;"},{num:19,verse:"Сигона, царя Аморрейского, ибо вовек милость Его;"},{num:20,verse:"и Ога, царя Васанского, ибо вовек милость Его;"},{num:21,verse:"и отдал землю их в наследие, ибо вовек милость Его;"},{num:22,verse:"в наследие Израилю, рабу Своему, ибо вовек милость Его;"},{num:23,verse:"вспомнил нас в унижении нашем, ибо вовек милость Его;"},{num:24,verse:"и избавил нас от врагов наших, ибо вовек милость Его;"},{num:25,verse:"дает пищу всякой плоти, ибо вовек милость Его."},{num:26,verse:"Славьте Бога небес, ибо вовек милость Его."}]},{chapter:"136",verses:[{num:0,verse:"[Давида.]"},{num:0,verse:""},{num:1,verse:"При реках Вавилона, там сидели мы и плакали, когда вспоминали о Сионе;"},{num:2,verse:"на вербах, посреди его, повесили мы наши арфы."},{num:3,verse:'Там пленившие нас требовали от нас слов песней, и притеснители наши – веселья: "пропойте нам из песней Сионских".'},{num:4,verse:"Как нам петь песнь Господню на земле чужой?"},{num:5,verse:"Если я забуду тебя, Иерусалим, – забудь меня десница моя;"},{num:6,verse:"прилипни язык мой к гортани моей, если не буду помнить тебя, если не поставлю Иерусалима во главе веселия моего."},{num:7,verse:'Припомни, Господи, сынам Едомовым день Иерусалима, когда они говорили: "разрушайте, разрушайте до основания его".'},{num:8,verse:"Дочь Вавилона, опустошительница! блажен, кто воздаст тебе за то, что ты сделала нам!"},{num:9,verse:"Блажен, кто возьмет и разобьет младенцев твоих о камень!"}]},{chapter:"137",verses:[{num:0,verse:"Давида."},{num:0,verse:""},{num:1,verse:"Славлю Тебя всем сердцем моим, пред богами пою Тебе, [что Ты услышал все слова уст моих]."},{num:2,verse:"Поклоняюсь пред святым храмом Твоим и славлю имя Твое за милость Твою и за истину Твою, ибо Ты возвеличил слово Твое превыше всякого имени Твоего."},{num:3,verse:"В день, когда я воззвал, Ты услышал меня, вселил в душу мою бодрость."},{num:4,verse:"Прославят Тебя, Господи, все цари земные, когда услышат слова уст Твоих"},{num:5,verse:"и воспоют пути Господни, ибо велика слава Господня."},{num:6,verse:"Высок Господь: и смиренного видит, и гордого узнает издали."},{num:7,verse:"Если я пойду посреди напастей, Ты оживишь меня, прострешь на ярость врагов моих руку Твою, и спасет меня десница Твоя."},{num:8,verse:"Господь совершит за меня! Милость Твоя, Господи, вовек: дело рук Твоих не оставляй."}]},{chapter:"138",verses:[{num:0,verse:"Начальнику хора. Псалом Давида."},{num:0,verse:""},{num:1,verse:"Господи! Ты испытал меня и знаешь."},{num:2,verse:"Ты знаешь, когда я сажусь и когда встаю; Ты разумеешь помышления мои издали."},{num:3,verse:"Иду ли я, отдыхаю ли – Ты окружаешь меня, и все пути мои известны Тебе."},{num:4,verse:"Еще нет слова на языке моем, – Ты, Господи, уже знаешь его совершенно."},{num:5,verse:"Сзади и спереди Ты объемлешь меня, и полагаешь на мне руку Твою."},{num:6,verse:"Дивно для меня ведение [Твое], – высоко, не могу постигнуть его!"},{num:7,verse:"Куда пойду от Духа Твоего, и от лица Твоего куда убегу?"},{num:8,verse:"Взойду ли на небо – Ты там; сойду ли в преисподнюю – и там Ты."},{num:9,verse:"Возьму ли крылья зари и переселюсь на край моря, -"},{num:10,verse:"и там рука Твоя поведет меня, и удержит меня десница Твоя."},{num:11,verse:'Скажу ли: "может быть, тьма скроет меня, и свет вокруг меня <i>сделается</i> ночью";'},{num:12,verse:"но и тьма не затмит от Тебя, и ночь светла, как день: как тьма, так и свет."},{num:13,verse:"Ибо Ты устроил внутренности мои и соткал меня во чреве матери моей."},{num:14,verse:"Славлю Тебя, потому что я дивно устроен. Дивны дела Твои, и душа моя вполне сознает это."},{num:15,verse:"Не сокрыты были от Тебя кости мои, когда я созидаем был в тайне, образуем был во глубине утробы."},{num:16,verse:"Зародыш мой видели очи Твои; в Твоей книге записаны все дни, для меня назначенные, когда ни одного из них еще не было."},{num:17,verse:"Как возвышенны для меня помышления Твои, Боже, и как велико число их!"},{num:18,verse:"Стану ли исчислять их, но они многочисленнее песка; когда я пробуждаюсь, я все еще с Тобою."},{num:19,verse:"О, если бы Ты, Боже, поразил нечестивого! Удалитесь от меня, кровожадные!"},{num:20,verse:"Они говорят против Тебя нечестиво; суетное замышляют враги Твои."},{num:21,verse:"Мне ли не возненавидеть ненавидящих Тебя, Господи, и не возгнушаться восстающими на Тебя?"},{num:22,verse:"Полною ненавистью ненавижу их: враги они мне."},{num:23,verse:"Испытай меня, Боже, и узнай сердце мое; испытай меня и узнай помышления мои;"},{num:24,verse:"и зри, не на опасном ли я пути, и направь меня на путь вечный."}]},{chapter:"139",verses:[{num:0,verse:"Псалом."},{num:1,verse:"Начальнику хора. Псалом Давида."},{num:0,verse:""},{num:2,verse:"Избавь меня, Господи, от человека злого; сохрани меня от притеснителя:"},{num:3,verse:"они злое мыслят в сердце, всякий день ополчаются на брань,"},{num:4,verse:"изощряют язык свой, как змея; яд аспида под устами их."},{num:5,verse:"Соблюди меня, Господи, от рук нечестивого, сохрани меня от притеснителей, которые замыслили поколебать стопы мои."},{num:6,verse:"Гордые скрыли силки для меня и петли, раскинули сеть по дороге, тенета разложили для меня."},{num:7,verse:"Я сказал Господу: Ты Бог мой; услышь, Господи, голос молений моих!"},{num:8,verse:"Господи, Господи, сила спасения моего! Ты покрыл голову мою в день брани."},{num:9,verse:"Не дай, Господи, желаемого нечестивому; не дай успеха злому замыслу его: они возгордятся."},{num:10,verse:"Да покроет головы окружающих меня зло собственных уст их."},{num:11,verse:"Да падут на них горящие угли; да будут они повержены в огонь, в пропасти, так, чтобы не встали."},{num:12,verse:"Человек злоязычный не утвердится на земле; зло увлечет притеснителя в погибель."},{num:13,verse:"Знаю, что Господь сотворит суд угнетенным и справедливость бедным."},{num:14,verse:"Так! праведные будут славить имя Твое; непорочные будут обитать пред лицем Твоим."}]},{chapter:"140",verses:[{num:0,verse:"Псалом Давида."},{num:0,verse:""},{num:1,
verse:"Господи! к Тебе взываю: поспеши ко мне, внемли голосу моления моего, когда взываю к Тебе."},{num:2,verse:"Да направится молитва моя, как фимиам, пред лице Твое, воздеяние рук моих – как жертва вечерняя."},{num:3,verse:"Положи, Господи, охрану устам моим, и огради двери уст моих;"},{num:4,verse:"не дай уклониться сердцу моему к словам лукавым для извинения дел греховных вместе с людьми, делающими беззаконие, и да не вкушу я от сластей их."},{num:5,verse:"Пусть наказывает меня праведник: это милость; пусть обличает меня: это лучший елей, который не повредит голове моей; но мольбы мои – против злодейств их."},{num:6,verse:"Вожди их рассыпались по утесам и слышат слова мои, что они кротки."},{num:7,verse:"Как будто землю рассекают и дробят нас; сыплются кости наши в челюсти преисподней."},{num:8,verse:"Но к Тебе, Господи, Господи, очи мои; на Тебя уповаю, не отринь души моей!"},{num:9,verse:"Сохрани меня от силков, поставленных для меня, от тенет беззаконников."},{num:10,verse:"Падут нечестивые в сети свои, а я перейду."}]},{chapter:"141",verses:[{num:0,verse:"Учение Давида. Молитва его, когда он был в пещере."},{num:0,verse:""},{num:1,verse:"Голосом моим к Господу воззвал я, голосом моим к Господу помолился;"},{num:2,verse:"излил пред Ним моление мое; печаль мою открыл Ему."},{num:3,verse:"Когда изнемогал во мне дух мой, Ты знал стезю мою. На пути, которым я ходил, они скрытно поставили сети для меня."},{num:4,verse:"Смотрю на правую сторону, и вижу, что никто не признает меня: не стало для меня убежища, никто не заботится о душе моей."},{num:5,verse:"Я воззвал к Тебе, Господи, я сказал: Ты прибежище мое и часть моя на земле живых."},{num:6,verse:"Внемли воплю моему, ибо я очень изнемог; избавь меня от гонителей моих, ибо они сильнее меня."},{num:7,verse:"Выведи из темницы душу мою, чтобы мне славить имя Твое. Вокруг меня соберутся праведные, когда Ты явишь мне благодеяние."}]},{chapter:"142",verses:[{num:0,verse:"Псалом Давида, [когда он преследуем был сыном своим Авессаломом]."},{num:0,verse:""},{num:1,verse:"Господи! услышь молитву мою, внемли молению моему по истине Твоей; услышь меня по правде Твоей"},{num:2,verse:"и не входи в суд с рабом Твоим, потому что не оправдается пред Тобой ни один из живущих."},{num:3,verse:"Враг преследует душу мою, втоптал в землю жизнь мою, принудил меня жить во тьме, как давно умерших, -"},{num:4,verse:"и уныл во мне дух мой, онемело во мне сердце мое."},{num:5,verse:"Вспоминаю дни древние, размышляю о всех делах Твоих, рассуждаю о делах рук Твоих."},{num:6,verse:"Простираю к Тебе руки мои; душа моя – к Тебе, как жаждущая земля."},{num:7,verse:"Скоро услышь меня, Господи: дух мой изнемогает; не скрывай лица Твоего от меня, чтобы я не уподобился нисходящим в могилу."},{num:8,verse:"Даруй мне рано услышать милость Твою, ибо я на Тебя уповаю. Укажи мне, [Господи,] путь, по которому мне идти, ибо к Тебе возношу я душу мою."},{num:9,verse:"Избавь меня, Господи, от врагов моих; к Тебе прибегаю."},{num:10,verse:"Научи меня исполнять волю Твою, потому что Ты Бог мой; Дух Твой благий да ведет меня в землю правды."},{num:11,verse:"Ради имени Твоего, Господи, оживи меня; ради правды Твоей выведи из напасти душу мою."},{num:12,verse:"И по милости Твоей истреби врагов моих и погуби всех, угнетающих душу мою, ибо я Твой раб."}]},{chapter:"143",verses:[{num:0,verse:"Давида. [Против Голиафа.]"},{num:0,verse:""},{num:1,verse:"Благословен Господь, твердыня моя, научающий руки мои битве и персты мои брани,"},{num:2,verse:"милость моя и ограждение мое, прибежище мое и Избавитель мой, щит мой, – и я на Него уповаю; Он подчиняет мне народ мой."},{num:3,verse:"Господи! что есть человек, что Ты знаешь о нем, и сын человеческий, что обращаешь на него внимание?"},{num:4,verse:"Человек подобен дуновению; дни его – как уклоняющаяся тень."},{num:5,verse:"Господи! Приклони небеса Твои и сойди; коснись гор, и воздымятся;"},{num:6,verse:"блесни молниею и рассей их; пусти стрелы Твои и расстрой их;"},{num:7,verse:"простри с высоты руку Твою, избавь меня и спаси меня от вод многих, от руки сынов иноплеменных,"},{num:8,verse:"которых уста говорят суетное и которых десница – десница лжи."},{num:9,verse:"Боже! новую песнь воспою Тебе, на десятиструнной псалтири воспою Тебе,"},{num:10,verse:"дарующему спасение царям и избавляющему Давида, раба Твоего, от лютого меча."},{num:11,verse:"Избавь меня и спаси меня от руки сынов иноплеменных, которых уста говорят суетное и которых десница – десница лжи."},{num:12,verse:"Да будут сыновья наши, как разросшиеся растения в их молодости; дочери наши – как искусно изваянные столпы в чертогах."},{num:13,verse:"Да будут житницы наши полны, обильны всяким хлебом; да плодятся овцы наши тысячами и тьмами на пажитях наших;"},{num:14,verse:"<i>да будут</i> волы наши тучны; да не будет ни расхищения, ни пропажи, ни воплей на улицах наших."},{num:15,verse:"Блажен народ, у которого это есть. Блажен народ, у которого Господь есть Бог."}]},{chapter:"144",verses:[{num:0,verse:"Хвала Давида."},{num:0,verse:""},{num:1,verse:"Буду превозносить Тебя, Боже мой, Царь [мой], и благословлять имя Твое во веки и веки."},{num:2,verse:"Всякий день буду благословлять Тебя и восхвалять имя Твое во веки и веки."},{num:3,verse:"Велик Господь и достохвален, и величие Его неисследимо."},{num:4,verse:"Род роду будет восхвалять дела Твои и возвещать о могуществе Твоем."},{num:5,verse:"А я буду размышлять о высокой славе величия Твоего и о дивных делах Твоих."},{num:6,verse:"Будут говорить о могуществе страшных дел Твоих, и я буду возвещать о величии Твоем."},{num:7,verse:"Будут провозглашать память великой благости Твоей и воспевать правду Твою."},{num:8,verse:"Щедр и милостив Господь, долготерпелив и многомилостив."},{num:9,verse:"Благ Господь ко всем, и щедроты Его на всех делах Его."},{num:10,verse:"Да славят Тебя, Господи, все дела Твои, и да благословляют Тебя святые Твои;"},{num:11,verse:"да проповедуют славу царства Твоего, и да повествуют о могуществе Твоем,"},{num:12,verse:"чтобы дать знать сынам человеческим о могуществе Твоем и о славном величии царства Твоего."},{num:13,verse:"Царство Твое – царство всех веков, и владычество Твое во все роды. [Верен Господь во всех словах Своих и свят во всех делах Своих.]"},{num:14,verse:"Господь поддерживает всех падающих и восставляет всех низверженных."},{num:15,verse:"Очи всех уповают на Тебя, и Ты даешь им пищу их в свое время;"},{num:16,verse:"открываешь руку Твою и насыщаешь все живущее по благоволению."},{num:17,verse:"Праведен Господь во всех путях Своих и благ во всех делах Своих."},{num:18,verse:"Близок Господь ко всем призывающим Его, ко всем призывающим Его в истине."},{num:19,verse:"Желание боящихся Его Он исполняет, вопль их слышит и спасает их."},{num:20,verse:"Хранит Господь всех любящих Его, а всех нечестивых истребит."},{num:21,verse:"Уста мои изрекут хвалу Господню, и да благословляет всякая плоть святое имя Его во веки и веки."}]},{chapter:"145",verses:[{num:0,verse:"[Аллилуия. <i>Аггея и Захарии</i>.]"},{num:0,verse:""},{num:1,verse:"Хвали, душа моя, Господа."},{num:2,verse:"Буду восхвалять Господа, доколе жив; буду петь Богу моему, доколе есмь."},{num:3,verse:"Не надейтесь на князей, на сына человеческого, в котором нет спасения."},{num:4,verse:"Выходит дух его, и он возвращается в землю свою: в тот день исчезают [все] помышления его."},{num:5,verse:"Блажен, кому помощник Бог Иаковлев, у кого надежда на Господа Бога его,"},{num:6,verse:"сотворившего небо и землю, море и все, что в них, вечно хранящего верность,"},{num:7,verse:"творящего суд обиженным, дающего хлеб алчущим. Господь разрешает узников,"},{num:8,verse:"Господь отверзает очи слепым, Господь восставляет согбенных, Господь любит праведных."},{num:9,verse:"Господь хранит пришельцев, поддерживает сироту и вдову, а путь нечестивых извращает."},{num:10,verse:"Господь будет царствовать во веки, Бог твой, Сион, в род и род. Аллилуия."}]},{chapter:"146",verses:[{num:0,verse:"[Аллилуия.]"},{num:0,verse:""},{num:1,verse:"Хвалите Господа, ибо благо петь Богу нашему, ибо это сладостно, – хвала подобающая."},{num:2,verse:"Господь созидает Иерусалим, собирает изгнанников Израиля."},{num:3,verse:"Он исцеляет сокрушенных сердцем и врачует скорби их;"},{num:4,verse:"исчисляет количество звезд; всех их называет именами их."},{num:5,verse:"Велик Господь наш и велика крепость [Его], и разум Его неизмерим."},{num:6,verse:"Смиренных возвышает Господь, а нечестивых унижает до земли."},{num:7,verse:"Пойте поочередно славословие Господу; пойте Богу нашему на гуслях."},{num:8,verse:"Он покрывает небо облаками, приготовляет для земли дождь, произращает на горах траву [и злак на пользу человеку];"},{num:9,verse:"дает скоту пищу его и птенцам ворона, взывающим <i>к Нему</i>."},{num:10,verse:"Не на силу коня смотрит Он, не к <i>быстроте</i> ног человеческих благоволит, -"},{num:11,verse:"благоволит Господь к боящимся Его, к уповающим на милость Его."}]},{chapter:"147",verses:[{num:0,verse:"[Аллилуия.]"},{num:0,verse:""},{num:1,verse:"Хвали, Иерусалим, Господа; хвали, Сион, Бога твоего,"},{num:2,verse:"ибо Он укрепляет вереи ворот твоих, благословляет сынов твоих среди тебя;"},{num:3,verse:"утверждает в пределах твоих мир; туком пшеницы насыщает тебя;"},{num:4,verse:"посылает слово Свое на землю; быстро течет слово Его;"},{num:5,verse:"дает снег, как волну; сыплет иней, как пепел;"},{num:6,verse:"бросает град Свой кусками; перед морозом Его кто устоит?"},{num:7,verse:"Пошлет слово Свое, и все растает; подует ветром Своим, и потекут воды."},{num:8,verse:"Он возвестил слово Свое Иакову, уставы Свои и суды Свои Израилю."},{num:9,verse:"Не сделал Он того никакому <i>другому</i> народу, и судов Его они не знают. Аллилуия."}]},{chapter:"148",verses:[{num:0,verse:"[Аллилуия.]"},{num:0,verse:""},{num:1,verse:"Хвалите Господа с небес, хвалите Его в вышних."},{num:2,verse:"Хвалите Его, все Ангелы Его, хвалите Его, все воинства Его."},{num:3,verse:"Хвалите Его, солнце и луна, хвалите Его, все звезды света."},{num:4,verse:"Хвалите Его, небеса небес и воды, которые превыше небес."},{num:5,verse:"Да хвалят имя Господа, ибо Он [сказал, и они сделались,] повелел, и сотворились;"},{num:6,verse:"поставил их на веки и веки; дал устав, который не прейдет."},{num:7,verse:"Хвалите Господа от земли, великие рыбы и все бездны,"},{num:8,verse:"огонь и град, снег и туман, бурный ветер, исполняющий слово Его,"},{num:9,verse:"горы и все холмы, дерева плодоносные и все кедры,"},{num:10,verse:"звери и всякий скот, пресмыкающиеся и птицы крылатые,"},{num:11,verse:"цари земные и все народы, князья и все судьи земные,"},{num:12,verse:"юноши и девицы, старцы и отроки"},{num:13,verse:"да хвалят имя Господа, ибо имя Его единого превознесенно, слава Его на земле и на небесах."},{num:14,verse:"Он возвысил рог народа Своего, славу всех святых Своих, сынов Израилевых, народа, близкого к Нему. Аллилуия."}]},{chapter:"149",verses:[{num:0,verse:"[Аллилуия.]"},{num:0,verse:""},{num:1,verse:"Пойте Господу песнь новую; хвала Ему в собрании святых."},{num:2,verse:"Да веселится Израиль о Создателе своем; сыны Сиона да радуются о Царе своем."},{num:3,verse:"да хвалят имя Его с ликами, на тимпане и гуслях да поют Ему,"},{num:4,verse:"ибо благоволит Господь к народу Своему, прославляет смиренных спасением."},{num:5,verse:"Да торжествуют святые во славе, да радуются на ложах своих."},{num:6,verse:"Да будут славословия Богу в устах их, и меч обоюдоострый в руке их,"},{num:7,verse:"для того, чтобы совершать мщение над народами, наказание над племенами,"},{num:8,verse:"заключать царей их в узы и вельмож их в оковы железные,"},{num:9,verse:"производить над ними суд писанный. Честь сия – всем святым Его. Аллилуия."}]},{chapter:"150",verses:[{num:0,verse:"[Аллилуия.]"},{num:0,verse:""},{num:1,verse:"Хвалите Бога во святыне Его, хвалите Его на тверди силы Его."},{num:2,verse:"Хвалите Его по могуществу Его, хвалите Его по множеству величия Его."},{num:3,verse:"Хвалите Его со звуком трубным, хвалите Его на псалтири и гуслях."},{num:4,verse:"Хвалите Его с тимпаном и ликами, хвалите Его на струнах и органе."},{num:5,verse:"Хвалите Его на звучных кимвалах, хвалите Его на кимвалах громогласных."},{num:6,verse:"Все дышащее да хвалит Господа! Аллилуия."}]},{chapter:"151",verses:[{num:0,verse:"[<i>Псалом Давида на единоборство с Голиафом</i>"},{num:0,verse:""},{num:1,verse:"Я был меньший между братьями моими и юнейший в доме отца моего; пас овец отца моего."},{num:2,verse:"Руки мои сделали орган, персты мои настраивали псалтирь."},{num:3,verse:"И кто возвестил бы Господу моему? – Сам Господь, Сам услышал меня."},{num:4,verse:"Он послал вестника Своего и взял меня от овец отца моего, и помазал меня елеем помазания Своего."},{num:5,verse:"Братья мои прекрасны и велики, но Господь не благоволил избрать из них."},{num:6,verse:"Я вышел навстречу иноплеменнику, и он проклял меня идолами своими."},{num:7,verse:"Но я, исторгнув у него меч, обезглавил его и избавил сынов Израилевых от поношения.]"}]}]};
